-%}

{%- style -%}
  {{  heading_font | font_face: font_display: 'swap' }}
  {{  link_font | font_face: font_display: 'swap' }}
  {{  question_font | font_face: font_display: 'swap' }}
  {{  answer_font | font_face: font_display: 'swap' }}
  {{  block_heading_font | font_face: font_display: 'swap' }}
  {{  bottom_text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;  
  }

  .faq-heading-{{ section.id }} {
    text-align: {{ heading_align_mobile }};
  }

  .faq-heading-{{ section.id }} * {
    margin: 0px;
    font-size: {{ heading_size_mobile }}px; 
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    word-break: break-word;
    text-transform: unset;
    font-weight: 500;
  }

  .faq-body-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
    gap: {{ layout_gap_mobile }}px;
    margin-top: {{ layout_mt_mobile }}px;
  }

  .faq-toggles-{{ section.id }} {
    display: flex;
    flex-direction: column;
    flex-wrap: wrap;
  }

  .faq-link-text-{{ section.id }} {
    margin: 0px;
    font-size: {{ link_size_mobile }}px; 
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    word-break: break-word;
    text-transform: unset;
    font-weight: 700;
  }

  .faq-link-{{ section.id }} {
    display: flex;
    width: 100%;
    justify-content: space-between;
    align-items: center;
    padding: {{ link_padding_vertical_mobile }}px {{ link_padding_horizontal_mobile }}px;
    border-top: {{ link_border_thickness }}px solid {{ link_border_color }};   
    cursor: pointer;
    transition: all 0.25s ease 0s;
    text-decoration: none;
  }

  .faq-link-{{ section.id }}:nth-last-child(2) {
    border-top: {{ link_border_thickness }}px solid {{ link_border_color }};   
    border-bottom: {{ link_border_thickness }}px solid {{ link_border_color }};   
  }

  .faq-bottom-text-{{ section.id }} {
    text-align: {{ bottom_text_align_mobile }};
    margin-top: {{ bottom_text_mt_mobile }}px;
  }

  .faq-bottom-text-{{ section.id }} * {
    margin: 0px;
    font-size: {{ bottom_text_size_mobile }}px; 
    color: {{ bottom_text_color }};
    line-height: {{ bottom_text_height }}%;
    word-break: break-word;
    text-transform: unset;
  }

  .faq-link-{{ section.id }}:last-child {
    border-top: {{ link_border_thickness }}px solid {{ link_border_color }};
    border-bottom: {{ link_border_thickness }}px solid {{ link_border_color }};
  }

  .faq-blocks-{{ section.id }} {
    display: flex;
    flex-direction: column;
    gap: {{ blocks_gap_mobile }}px;
  }

  .faq-block-heading-{{ section.id }} {
    margin: 0px 0px {{ item_mt_mobile }}px 0px;
    font-size: {{ block_heading_size_mobile }}px; 
    color: {{ block_heading_color }};
    line-height: {{ block_heading_height }}%;
    word-break: break-word;
    text-transform: unset;
    font-weight: 500;
  }

  .faq-item-{{ section.id }} {
    padding: {{ item_padding_vertical_mobile }}px {{ item_padding_horizontal_mobile }}px;
    border-top: {{ item_border_thickness }}px solid {{ item_border_color }};
    width: 100%;
    position: relative;
    transition: all 0.25s ease 0s;
  }

  .faq-item-toggle-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: space-between;
    cursor: pointer;
    gap: 12px;
  }

  .faq-icon-{{ section.id }} {
    display: flex !important;
    align-items: center;
    justify-content: center;
    flex: 0 0 {{ icon_size_mobile }}px;
    border-radius: {{ icon_radius }}px;
    background-color: transparent;
    height: {{ icon_size_mobile }}px;
    border: {{ icon_border_thickness }}px solid {{ icon_border_color }};
    transition: all 0.25s ease 0s;
    width: {{ icon_size_mobile }}px;
    transition: all 0.25s ease 0s;
  }


  .faq-link-{{ section.id }}.active .faq-icon-{{ section.id }},
  .faq-item-{{ section.id }}.active .faq-icon-{{ section.id }} {
    background-color: {{ icon_bg }};
    transition: all 0.25s ease 0s;
  }

  .faq-question-{{ section.id }} {
    margin: 0px;
    font-size: {{ question_size_mobile }}px;
    color: {{ question_color }};
    line-height: {{ question_height }}%;
    text-transform: unset;
    font-weight: 700;
  }

  .faq-answer-{{ section.id }} {
    transition: all 0.2s ease-in-out;
    overflow: hidden;
  }

  .faq-answer-{{ section.id }} *,
  .faq-height-cal-{{ section.id }} *  {
    margin: 0px;
    font-size: {{ answer_size_mobile }}px;
    color: {{ answer_color }};
    line-height: {{ answer_height }}%;
    text-transform: unset;
  }

  .faq-answer-{{ section.id }} p,
  .faq-height-cal-{{ section.id }} p {
    min-height: 10px;
  }

  .faq-answer-{{ section.id }} p:not(:first-child ),
  .faq-height-cal-{{ section.id }} p:not(:first-child )  {
    margin-top: 10px;
  } 

  .faq-height-cal-{{ section.id }} {
    visibility: hidden;
    opacity: 0;
    pointer-events: none;
    position: absolute;
    top: 0;
    left: 0;
    width: calc(100% - {{ item_padding_horizontal_mobile | times: 2 }}px);
    padding-top: {{ answer_padding_top }}px;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .faq-heading-{{ section.id }} {
      text-align: {{ heading_align }};
    }

    .faq-heading-{{ section.id }} * {   
      font-size: {{ heading_size }}px; 
    }

    .faq-body-{{ section.id }} {
      grid-template-columns: 0.5fr 1fr;
      gap: {{ layout_gap }}px;
      margin-top: {{ layout_mt }}px;
    }

    .faq-toggles-{{ section.id }} {
      position: sticky;
      max-height: fit-content;
      top: {{ layout_mt }}px;    
    }

    .faq-link-{{ section.id }} {
      padding: {{ link_padding_vertical }}px {{ link_padding_horizontal }}px;
    }

    .faq-link-{{ section.id }}:hover .faq-icon-{{ section.id }} {
      background-color: {{ icon_bg }};
      transition: all 0.25s ease 0s;
    }

    .faq-item-{{ section.id }}:hover .faq-icon-{{ section.id }} {
      background-color: {{ icon_bg }};
      transition: all 0.25s ease 0s;
    }

    .faq-link-text-{{ section.id }} {
      font-size: {{ link_size }}px; 
    }

    .faq-bottom-text-{{ section.id }} {
      text-align: {{ bottom_text_align }};
      margin-top: {{ bottom_text_mt }}px;
    }

    .faq-bottom-text-{{ section.id }} * {
      font-size: {{ bottom_text_size }}px; 
    }

    .faq-block-heading-{{ section.id }} {
      font-size: {{ block_heading_size }}px;
      margin: 0px 0px {{ item_mt }}px 0px;
    }

    .faq-blocks-{{ section.id }} {
      gap: {{ blocks_gap }}px;
    }

    .faq-item-{{ section.id }} {
      padding: {{ item_padding_vertical }}px {{ item_padding_horizontal }}px;
    }

    .faq-question-{{ section.id }} {
      font-size: {{ question_size }}px;
    }

    .faq-answer-{{ section.id }} *,
    .faq-height-cal-{{ section.id }} *  {
      font-size: {{ answer_size }}px;
    }

    .faq-icon-{{ section.id }} {
      flex: 0 0 {{ icon_size }}px;
      height: {{ icon_size }}px;
    }

    .faq-link-{{ section.id }}:hover {
      color: {{ link_active_color }};
      transition: all 0.25s ease 0s;
    }

    .faq-icon-{{ section.id }} svg {
      width: {{ icon_size }}px;
      height: {{ icon_size }}px;
    }

    .faq-height-cal-{{ section.id }} {
      width: calc(100% - {{ item_padding_horizontal | times: 2 }}px);
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
    .faq-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if link_custom %}
  <style>
    .faq-link-text-{{ section.id }} {
      font-family: {{ link_font.family }}, {{ link_font.fallback_families }};
      font-weight: {{ link_font.weight }};
      font-style: {{ link_font.style }};
    }
  </style>
{% endif %}

{% if bottom_text_custom %}
  <style>
    .faq-bottom-text-{{ section.id }} * {
      font-family: {{ bottom_text_font.family }}, {{ bottom_text_font.fallback_families }};
      font-weight: {{ bottom_text_font.weight }};
      font-style: {{ bottom_text_font.style }};
    }
  </style>
{% endif %}

{% if block_heading_custom %}
  <style>
    .faq-block-heading-{{ section.id }} {
      font-family: {{ block_heading_font.family }}, {{ block_heading_font.fallback_families }};
      font-weight: {{ block_heading_font.weight }};
      font-style: {{ block_heading_font.style }};
    }
  </style>
{% endif %}

{% if question_custom %}
  <style>
    .faq-question-{{ section.id }} {
      font-family: {{ question_font.family }}, {{ question_font.fallback_families }};
      font-weight: {{ question_font.weight }};
      font-style: {{ question_font.style }};
    }
  </style>
{% endif %}

{% if answer_custom %}
  <style>
    .faq-answer-{{ section.id }} *,
    .faq-height-cal-{{ section.id }} * {
      font-family: {{ answer_font.family }}, {{ answer_font.fallback_families }};
      font-weight: {{ answer_font.weight }};
      font-style: {{ answer_font.style }};
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} faq-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      {% if heading != blank %}
        <div class="faq-heading-{{ section.id }}">{{ heading }}</div>
      {% endif %}
      <div class="faq-body-{{ section.id }}">      
        <div class="faq-toggles-{{ section.id }}">
          {% for block in section.blocks %}
            {% if block.type == 'block' %}
              <a href="#{{ block.id }}" class="faq-link-{{ section.id }} {% if forloop.first %}active{% endif %}" data-id="{{ block.id }}">
                <p class="faq-link-text-{{ section.id }}">{{ block.settings.title }}</p>               
                <span class="faq-icon-{{ section.id }}"></span>
              </a>
            {% endif %}          
          {% endfor %}
          {% if bottom_text != blank %}
            <div class="faq-bottom-text-{{ section.id }}">{{ bottom_text }}</div>
          {% endif %}
        </div> 
        <div class="faq-blocks-{{ section.id }}">
          {% assign inside_item = false %} 
          {% for block in section.blocks %}
            {% if block.type == 'block' %}
              {% if inside_item %}
                </div>
              {% endif %}  
              <div class="faq-block-{{ section.id }}" id="{{ block.id }}" data-id="{{ block.id }}">   
                {% if block.type == 'block' %}
                  <p class="faq-block-heading-{{ section.id }}">{{ block.settings.title }}</p>
                {% endif %}       
                {% assign inside_item = true %}                
            {% elsif block.type == 'faq_item' %}
              <div class="faq-item-{{ section.id }}">        
                <div class="faq-item-toggle-{{ section.id }}">
                  <p class="faq-question-{{ section.id }}">{{ block.settings.question }}</p>
                  <div class="faq-icon-{{ section.id }}"></div>
                </div>
                <div class="faq-answer-{{ section.id }}" style="display: none; height: 0px; padding-top: 0px; padding-bottom: 0px;">
                    {{ block.settings.answer }}
                  </div>
                <div class="faq-height-cal-{{ section.id }}">
                  {{ block.settings.answer }}
                </div>                      
              </div>   

            {% endif %}              
          {% endfor %}
          {% if inside_item %}
            </div>
          {% endif %}
        </div>       
      </div>
    </div>
</div>

<script>
function initFaq15() {
  // Navigation (dot) handling
  const toggles = document.querySelectorAll('.faq-link-{{ section.id }}');
  const blocks = document.querySelectorAll('.faq-block-{{ section.id }}');
  const items = document.querySelectorAll('.faq-item-{{ section.id }}');
  let isScrolling = false; // Block switching when scrolling programmatically

  if (toggles) {
    // Click events on navigation dots:
    toggles.forEach(toggle => {
      toggle.addEventListener('click', (e) => {
        e.preventDefault();
        if (isScrolling) return;
        isScrolling = true;

        const targetId = toggle.getAttribute('data-id');
        const targetBlock = Array.from(blocks).find(block => block.getAttribute('data-id') === targetId);
        if (targetBlock) {
          const offset = 200;
          const targetPosition = targetBlock.getBoundingClientRect().top + window.scrollY - offset;
          window.scrollTo({ top: targetPosition, behavior: 'smooth' });

          toggles.forEach(item => item.classList.remove('active'));
          toggle.classList.add('active');

          setTimeout(() => {
            const firstItem = targetBlock.querySelector('.faq-item-{{ section.id }}');
            const firstItemIndex = Array.from(items).indexOf(firstItem);
            openAnswer(firstItemIndex);
            closeAllAnswerBut(firstItemIndex);
          }, 500);

          setTimeout(() => {
            isScrolling = false;
          }, 800);
        }
      });
    });

    // Scroll event listener for updating active navigation dot:
    window.addEventListener('scroll', () => {
      if (isScrolling) return;
      const offset = window.innerHeight * 0.25; // 25% of viewport height
      let activeBlock = null;
      blocks.forEach(block => {
        const rect = block.getBoundingClientRect();
        if (rect.top <= offset) {
          activeBlock = block;
        }
      });
      if (!activeBlock && blocks.length > 0) {
        activeBlock = blocks[0];
      }
      if (activeBlock) {
        const activeId = activeBlock.getAttribute('data-id');
        toggles.forEach(toggle => {
          if (toggle.getAttribute('data-id') === activeId) {
            toggle.classList.add('active');
          } else {
            toggle.classList.remove('active');
          }
        });
      }
    });
  }

  // --- FAQ card toggle functionality ---
  const answerPaddingTop = {{ answer_padding_top }};
  const answerPaddingBottom = 0;

  const buttons = document.querySelectorAll('.faq-item-toggle-{{ section.id }}');
  const answers = document.querySelectorAll('.faq-answer-{{ section.id }}');
  const answersHeightCalElements = document.querySelectorAll('.faq-height-cal-{{ section.id }}');
  const faqIcons = document.querySelectorAll('.faq-icon-{{ section.id }}');

  let activeIndex = -1;

  function openAnswer(index) {
    const height = answersHeightCalElements[index].offsetHeight;
    answers[index].style.display = 'block';
    setTimeout(() => {
      answers[index].style.paddingTop = `${answerPaddingTop}px`;
      answers[index].style.paddingBottom = `${answerPaddingBottom}px`;
      answers[index].style.height = `${height}px`;
    });
    items[index].classList.add('active');
    faqIcons[index].classList.add('active');
  }

  function closeAnswer(index) {
    answers[index].style.paddingTop = 0;
    answers[index].style.paddingBottom = 0;
    answers[index].style.height = 0;
    setTimeout(() => {
      answers[index].style.display = 'none';
    }, 300);
    items[index].classList.remove('active');
    faqIcons[index].classList.remove('active');
  }

  function toggleAnswer(index) {
    if (answers[index].style.display === 'none') {
      openAnswer(index);
      activeIndex = index;
      closeAllAnswerBut(index);
    } else {
      closeAnswer(index);
      activeIndex = -1;
    }
  }

  function closeAllAnswerBut(index) {
    answers.forEach((answer, i) => {
      if (i !== index) {
        closeAnswer(i);
      }
    });
  }

  buttons.forEach((button, index) => {
    button.addEventListener('click', () => {
      toggleAnswer(index);
    });
  });

  // Open the first FAQ item by default
  openAnswer(0);
  activeIndex = 0;

  // Adjust answer heights on window resize
  let resizeTimer;
  window.addEventListener('resize', () => {
    clearTimeout(resizeTimer);
    resizeTimer = setTimeout(() => {
      if (activeIndex !== -1) {
        const height = answersHeightCalElements[activeIndex].offsetHeight;
        answers[activeIndex].style.height = `${height}px`;
      }
    }, 250);
  });
}

document.addEventListener('DOMContentLoaded', initFaq15);

if (Shopify.designMode) {
  document.addEventListener('shopify:section:unload', initFaq15);
  document.addEventListener('shopify:section:load', initFaq15);
}

</script>
