      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}


{% if cards_shadow %}
  <style>
    .faq-card-{{ section.id }} {
      box-shadow: 2px 2px 12px {{ cards_shadow_color }}0d;
    }

    .faq-media-{{ section.id }} {
      box-shadow: 2px 2px 12px {{ cards_shadow_color }}0d;
