{%- style -%}
  .section-{{ section.id }}-padding {
    padding-top: {{ section.settings.padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ section.settings.padding_bottom | times: 0.75 | round: 0 }}px;
  }

  @media screen and (min-width: 750px) {
    .section-{{ section.id }}-padding {
      padding-top: {{ section.settings.padding_top }}px;
      padding-bottom: {{ section.settings.padding_bottom }}px;
      padding-left: 5rem;
      padding-right: 5rem;
    }
  }

  .section-{{ section.id }} {
    --faq-header-font-size: {{ section.settings.header-font-size }}px;
    --faq-question-font-size: {{ section.settings.question-font-size }}px;
    --faq-answer-font-size: {{ section.settings.answer-font-size }}px;
    --faq-more-questions-font-size: {{ section.settings.more-questions-font-size }}px;
    --faq-background-color: {{ section.settings.background-color }};
    --faq-header-color: {{ section.settings.header_color }};
    --faq-content-color: {{ section.settings.content_color }};
    --faq-link-color: {{ section.settings.link_color }};
    --faq-line-color: {{ section.settings.line-color }};
  }

  /* General style to remove arrows */
    details > summary {
      list-style: none;
    }

    /* Remove default arrow icon from Chrome, Safari and Opera */
    details > summary::-webkit-details-marker {
      display: none;
    }

    /* Remove default arrow icon from Firefox */
    details > summary {
      /* Your custom styles for the summary element */
    }

    details > summary::-moz-list-bullet {
      display: none;
    }

  .ss-faq-details {
    transition: all 0.2s;
    padding: 10px 2px;
  }

  .ss-faq-question {
    display:flex;
    justify-content: space-between;
    align-items: center;
    transition: all 0.2s;
    font-weight: {{section.settings.font_weight}};
    color: var(--faq-content-color);
    font-size: var(--faq-question-font-size);
  }

  .ss-faq-question::after {
    content: '\{{section.settings.open_close_sign}}';
    font-size:calc({{ section.settings.question-font-size }}px + 4px);
    transition: all 0.2s ease;
  }

    details[open] .ss-faq-question::after {
          transform: rotate(90deg);
    }

  .ss-accordion {
    border-bottom: {{ section.settings.line-seperator-size }}px var(--faq-line-color) solid;
  }

  .ss-accordion-wrapper {
    max-width: 800px;
    margin: 0 auto;
  }

  .ss-faq-content p {
    font-size: var(--faq-answer-font-size);
    padding-left:2px;
    margin: 5px 0;
    color: var(--faq-content-color);
  }

  .ss-faq-header {
    text-align:center;
    font-size: var(--faq-header-font-size);
    font-weight: {{section.settings.font_weight}};
    color: var(--faq-header-color);
    margin: 16px 0;
    line-height: calc(1 + .3/max(1,var(--font-heading-scale)));
  }

  .ss-extra-text {
    font-size: var(--faq-more-questions-font-size);
  }

  .ss-extra-text a {
    font-weight: {{section.settings.font_weight}};
    color: var(--faq-link-color);
    font-size: var(--faq-more-questions-font-size);
  }

  @media screen and (max-width: 750px) {
    .ss-faq-header {
       font-size:calc({{ section.settings.header-font-size }}px * 0.8);
    }
  }

{%- endstyle -%}

<div style="background-color:{{section.settings.background-color}};" class="section-{{ section.id }}">
  <div class="page-width section-{{ section.id }}-padding">
    <div class="ss_faq_wrapper">
      <p class="ss-faq-header">{{ section.settings.heading }}</p>
      <div class="ss-accordion-wrapper">
      {%for block in section.blocks %}
        <div class="ss-accordion">
          <details class="ss-faq-details" {% if section.settings.open_first_collapsible_row and forloop.first %} open{% endif %}>
            <summary class="ss-faq-question">
              {{ block.settings.question }}
            </summary>
            <div class="ss-faq-content">
              {{ block.settings.answer }}
            </div>
          </details>
        </div>
      {% endfor %}
      </div>
      {% if section.settings.hide_more_questions_text %}
        <p class="ss-extra-text"style="text-align: center;">{{ section.settings.more_questions }} <a href="{{ section.settings.full_faq_url }}">{{ section.settings.full_faq }}</a></p>
      {% endif %}
    </div>
  </div>
</div>
