liquid_code:
{%- style -%}
  .section-{{ section.id }}-padding {
    padding-top: {{ section.settings.padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ section.settings.padding_bottom | times: 0.75 | round: 0 }}px;
  }

  @media screen and (min-width: 750px) {
    .section-{{ section.id }}-padding {
      padding-top: {{ section.settings.padding_top }}px;
      padding-bottom: {{ section.settings.padding_bottom }}px;
      padding-left: 5rem;
      padding-right: 5rem;
    }
  }

  .ss-faq-details {
    transition: all 0.2s;
    padding: 5px 2px;
  }

  .ss-faq-question {
    display:flex;
    justify-content: space-between;
    align-items: center;
    transition: all 0.2s;
    font-weight: {{section.settings.font_weight}};
    color: {{section.settings.content_color}};
    font-size:{{ section.settings.question-font-size }}px;
    padding: 7px 15px;
    background-color: {{section.settings.line-color}}
  }

  .ss-faq-question::after {
    content: '\FE62';
    font-size:calc({{ section.settings.question-font-size }}px + 4px);
    transition: all 0.2s ease;
  }

    details[open] .ss-faq-question::after {
          transform: rotate(45deg);
    }

  .ss-accordion {
  }

  .ss-accordion-wrapper {
    max-width: 800px;
    margin: 0 auto;
  }

  .ss-faq-content p {
    font-size:{{ section.settings.answer-font-size }}px;
    padding:10px 15px;
    margin: 0;
    color: {{section.settings.content_color}};
    border: 1px solid {{section.settings.line-color}};
  }

  .ss-faq-header {
    text-align:center;
    font-size:{{ section.settings.header-font-size }}px;
    font-weight: {{section.settings.font_weight}};
    color: {{section.settings.header_color}};
    margin: 16px 0;
    line-height: calc(1 + .3/max(1,var(--font-heading-scale)));
  }

  .ss-extra-text {
    font-size:{{ section.settings.more-questions-font-size }}px;
  }

  .ss-extra-text a {
    font-weight: {{section.settings.font_weight}};
    color: {{section.settings.link_color}};
    font-size:{{ section.settings.more-questions-font-size }}px;
  }

  @media screen and (max-width: 750px) {
    .ss-faq-header {
       font-size:calc({{ section.settings.header-font-size }}px * 0.8);
    }
  }

  DETAILS[open] SUMMARY ~ * {
    animation: sweep 0.2s ease-in-out;
  }

  @keyframes sweep {
    0% {
      opacity: 0;
      margin-top: -10px
    }
    100% {
      opacity: 1;
      margin-top: 0px
    }
  }

{%- endstyle -%}

<div style="background-color:{{section.settings.background-color}};">
  <div class="page-width section-{{ section.id }}-padding">
    <div class="ss_faq_wrapper">
      <p class="ss-faq-header">{{ section.settings.heading }}</p>
      <div class="ss-accordion-wrapper">
      {%for block in section.blocks %}
        <div class="ss-accordion">
          <details class="ss-faq-details" {% if section.settings.open_first_collapsible_row and forloop.first %} open{% endif %}>
            <summary class="ss-faq-question">
              {{ block.settings.question }}
            </summary>
            <div class="ss-faq-content">
              {{ block.settings.answer }}
            </div>
          </details>
        </div>
      {% endfor %}
      </div>
      {% if section.settings.hide_more_questions_text %}
        <p class="ss-extra-text"style="text-align: center;">{{ section.settings.more_questions }} <a href="{{ section.settings.full_faq_url }}">{{ section.settings.full_faq }}</a></p>
      {% endif %}
    </div>
  </div>
</div>
