{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width

  assign heading = section.settings.heading
  assign heading_size = section.settings.heading_size
  assign heading_size_mobile = section.settings.heading_size_mobile
  assign heading_color = section.settings.heading_color
  assign heading_custom = section.settings.heading_custom
  assign heading_font = section.settings.heading_font
  assign heading_height = section.settings.heading_height
  assign heading_align = section.settings.heading_align
  assign heading_align_mobile = section.settings.heading_align_mobile

  assign body_border_color = section.settings.body_border_color
  assign body_border_thickness = section.settings.body_border_thickness
  assign body_bg_color = section.settings.body_bg_color
  assign body_radius = section.settings.body_radius
  assign body_mt = section.settings.body_mt
  assign body_padding_vertical = section.settings.body_padding_vertical
  assign body_padding_horizontal = section.settings.body_padding_horizontal

  assign close_item = section.settings.close_item

  assign item_border_color = section.settings.item_border_color
  assign item_border_thickness = section.settings.item_border_thickness
  assign item_bg_color = section.settings.item_bg_color

  assign question_size = section.settings.question_size
  assign question_size_mobile = section.settings.question_size_mobile
  assign question_color = section.settings.question_color
  assign question_custom = section.settings.question_custom
  assign question_font = section.settings.question_font
  assign question_height = section.settings.question_height
  assign question_border_color = section.settings.question_border_color
  assign question_border_thickness = section.settings.question_border_thickness
  assign question_padding_bottom = section.settings.question_padding_bottom
  assign question_padding_top = section.settings.question_padding_top
  assign question_padding_sides = section.settings.question_padding_sides

  assign answer_size = section.settings.answer_size
  assign answer_size_mobile = section.settings.answer_size_mobile
  assign answer_color = section.settings.answer_color
  assign answer_link_color = section.settings.answer_link_color
  assign answer_custom = section.settings.answer_custom
  assign answer_font = section.settings.answer_font
  assign answer_height = section.settings.answer_height
  assign answer_padding_bottom = section.settings.answer_padding_bottom
  assign answer_padding_top = section.settings.answer_padding_top
  assign answer_align_mobile = section.settings.answer_align_mobile
  assign answer_align = section.settings.answer_align

  assign link_url = section.settings.link_url
  assign link = section.settings.link
  assign link_size = section.settings.link_size
  assign link_size_mobile = section.settings.link_size_mobile
  assign link_color = section.settings.link_color
  assign link_hover_color = section.settings.link_hover_color
  assign link_custom = section.settings.link_custom
  assign link_font = section.settings.link_font
  assign link_height = section.settings.link_height
  assign link_align_mobile = section.settings.link_align_mobile
  assign link_align = section.settings.link_align
  assign link_mt = section.settings.link_mt
-%}

{%- style -%}

  {{  heading_font | font_face: font_display: 'swap' }}
  {{  question_font | font_face: font_display: 'swap' }}
  {{  answer_font | font_face: font_display: 'swap' }}
  {{  link_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .faq-heading-{{ section.id }} {
    text-align: {{ heading_align_mobile }};
  }

  .faq-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
  }

  .faq-items-{{ section.id }} {
    margin-top: {{ body_mt | times: 0.75 | round: 0 }}px;
    background-color: {{ body_bg_color }};
    border: {{ body_border_thickness }}px solid {{ body_border_color }};
    border-radius: {{ body_radius }}px;
    padding: {{ body_padding_vertical | times: 0.75 | round: 0 }}px {{ body_padding_horizontal | times: 0.75 | round: 0 }}px;
  }

  .faq-item-{{ section.id }} {
    background-color: {{ item_bg_color }};
    border-bottom: {{ item_border_thickness }}px solid {{ item_border_color }};
    padding: 0px {{ question_padding_sides | times: 0.75 | round: 0 }}px;
  }

  .faq-item-{{ section.id }} {
    border-top-left-radius: {{ body_radius }}px;
    border-top-right-radius: {{ body_radius }}px;
  }

  .faq-item-{{ section.id }}:last-child {
     border-bottom: 0px;
     border-bottom-left-radius: {{ body_radius }}px;
     border-bottom-right-radius: {{ body_radius }}px;
  }

  .faq-top-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: space-between;
    gap: 20px;
    transition: all 0.25s ease 0s;
    padding: {{ question_padding_top | times: 0.75 | round: 0 }}px 0px {{ question_padding_bottom | times: 0.75 | round: 0 }}px;
    cursor: pointer;
  }

  .faq-item-{{ section.id }}.active .faq-top-{{ section.id }} {
    transition: all 0.25s ease 0s;
    border-bottom: {{ question_border_thickness }}px solid {{ question_border_color }};
  }

  .faq-top-{{ section.id }} svg {
    display: block;
    flex: 0 0 24px;
    height: 13px;
    transition: all 0.25s ease 0s; 
  }

  .faq-item-{{ section.id }}.active .faq-top-{{ section.id }} svg {
    transition: all 0.25s ease 0s; 
    transform: rotate(180deg);
  }

  .faq-question-{{ section.id }} {
    margin: 0px;
    font-size: {{ question_size_mobile }}px;
    color: {{ question_color }};
    line-height: {{ question_height }}%;
    text-transform: unset;
    font-weight: 700;
  }

  .faq-bottom-{{ section.id }} {
    transition: all 0.25s ease 0s;
    max-height: 0px;
    overflow: hidden;
  }

  .faq-item-{{ section.id }}.active .faq-bottom-{{ section.id }} {
    max-height: max-content;
    transition: all 0.25s ease 0s;
    padding-top: {{ answer_padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ answer_padding_bottom | times: 0.75 | round: 0 }}px;
  }

  .faq-answer-{{ section.id }} {
    text-align: {{ answer_align_mobile }};
  }

  .faq-answer-{{ section.id }} * {
    margin: 0px;
    font-size: {{ answer_size_mobile }}px;
    color: {{ answer_color }};
    line-height: {{ answer_height }}%;
    text-transform: unset;
  }

  .faq-answer-{{ section.id }} p {
    display: block;
    min-height: 16px;
  }

  .faq-answer-{{ section.id }} a {
    color: {{ answer_link_color }};
    text-decoration: underline;
  }

  .faq-link-{{ section.id }} {
    margin-top: {{ link_mt | times: 0.75 | round: 0 }}px;
    display: block;
    width: 100%;
    max-width: fit-content;
    font-size: {{ link_size_mobile }}px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
    font-weight: 700;
    transition: all 0.25s ease 0s;
    position: relative;
  }

  .faq-link-{{ section.id }}:hover {
    transition: all 0.25s ease 0s;
    color: {{ link_hover_color }};
  }

  .faq-link-{{ section.id }}:after {
    position: absolute;
    width: 100%;
    height: 2px;
    left: 0;
    bottom: -2px;
    content: '';
    background-color: {{ link_color }};
    transition: all 0.25s ease 0s;
  }

  .faq-link-{{ section.id }}:hover:after {
    transition: all 0.25s ease 0s;
    background-color: {{ link_hover_color }};
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .faq-heading-{{ section.id }} {
      text-align: {{ heading_align }};
    }

    .faq-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .faq-items-{{ section.id }} {
      margin-top: {{ body_mt }}px;
      padding: {{ body_padding_vertical }}px {{ body_padding_horizontal }}px;
    }

    .faq-item-{{ section.id }} {
      padding: 0px {{ question_padding_sides }}px;
    }

    .faq-top-{{ section.id }} {
      padding: {{ question_padding_top }}px 0px {{ question_padding_bottom }}px;
    }

    .faq-question-{{ section.id }} {
      font-size: {{ question_size }}px;
    }

    .faq-item-{{ section.id }}.active .faq-bottom-{{ section.id }} {
      padding-top: {{ answer_padding_top }}px;
      padding-bottom: {{ answer_padding_bottom }}px;
    }

    .faq-answer-{{ section.id }} {
      text-align: {{ answer_align }};
    }

    .faq-answer-{{ section.id }} * {
      font-size: {{ answer_size }}px;
    }

    .faq-link-{{ section.id }} {
      margin-top: {{ link_mt }}px;
      font-size: {{ link_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if heading_custom %}
  <style>
    .faq-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if question_custom %}
  <style>
    .faq-question-{{ section.id }} {
      font-family: {{ question_font.family }}, {{ question_font.fallback_families }};
      font-weight: {{ question_font.weight }};
      font-style: {{ question_font.style }};
    }
  </style>
{% endif %}

{% if answer_custom %}
  <style>
    .faq-answer-{{ section.id }} * {
      font-family: {{ answer_font.family }}, {{ answer_font.fallback_families }};
      font-weight: {{ answer_font.weight }};
      font-style: {{ answer_font.style }};
    }
  </style>
{% endif %}

{% if link_custom %}
  <style>
    .faq-link-{{ section.id }} {
      font-family: {{ link_font.family }}, {{ link_font.fallback_families }};
      font-weight: {{ link_font.weight }};
      font-style: {{ link_font.style }};
    }
  </style>
{% endif %}

{% if link_align_mobile == "center" %}
  <style>
   .faq-link-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    } 
  </style>
{% elsif link_align_mobile == "right" %}
  <style>
   .faq-link-{{ section.id }} {
      margin-left: auto;
    } 
  </style>
{% endif %}

{% if link_align == "center" %}
  <style>
    @media(min-width: 1024px) {
     .faq-link-{{ section.id }} {
        margin-left: auto;
        margin-right: auto;
      } 
    }
  </style>
{% elsif link_align == "right" %}
  <style>
    @media(min-width: 1024px) {
     .faq-link-{{ section.id }} {
        margin-left: auto;
        margin-right: 0px;
      } 
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
     .faq-link-{{ section.id }} {
        margin-left: 0px;
        margin-right: 0px;
      } 
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} faq-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
        {% if heading != blank %}
          <div class="faq-heading-{{ section.id }}">{{ heading }}</div>
        {% endif %}
      <div class="faq-items-{{ section.id }}">
        {% for block in section.blocks %}
          <div class="faq-item-{{ section.id }}">
            <div class="faq-top-{{ section.id }}">
              <p class="faq-question-{{ section.id }}">{{ block.settings.question }}</p>
              <svg xmlns="http://www.w3.org/2000/svg" width="24" height="13" viewBox="0 0 24 13"><g><g><path d="M1.32 0C1 0 .633.141.38.384.138.634 0 1 0 1.314c0 .342.139.686.38.91l10.28 10.07a1.32 1.32 0 0 0 1.87 0l10.29-10.07c.241-.224.378-.568.38-.91 0-.314-.141-.684-.39-.93a1.33 1.33 0 0 0-1.87 0L11.6 9 2.27.404C2 .148 1.678 0 1.32 0z"></path></g></g></svg>
            </div>
            <div class="faq-bottom-{{ section.id }}">
              <div class="faq-answer-{{ section.id }}">{{ block.settings.answer }}</div>
            </div>
          </div>
        {% endfor %}
      </div>
      {% if link != blank %}
        <a href="{{ link_url }}" class="faq-link-{{ section.id }}">{{ link }}</a>
      {% endif %}
    </div>
</div>

<script>
  function initFAQ5() {

    let faqs{{ section.id | replace: '-', '' }} = document.querySelectorAll('.faq-item-{{ section.id }}');

    faqs{{ section.id | replace: '-', '' }}.forEach(el => {
      let toggle = el.querySelector('.faq-top-{{ section.id }}');

      toggle.addEventListener('click', (e) => {
        let parent = e.currentTarget.closest('.faq-item-{{ section.id }}');

        if (parent) {
          {% if close_item %}
          faqs{{ section.id | replace: '-', '' }}.forEach(element => {
            if (element !== parent) {
              element.classList.remove('active');
            }
          });
          {% endif %}

          parent.classList.toggle('active');
        }
      });
    });
  }

  document.addEventListener('DOMContentLoaded', initFAQ5);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:unload', initFAQ5);
     document.addEventListener('shopify:section:load', initFAQ5);
  }
</script>
