{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign section_radius = section.settings.section_radius
  assign lazy = section.settings.lazy

  assign body_gap = section.settings.body_gap
  assign body_gap_mobile = section.settings.body_gap_mobile

  assign content_horizontal_align_mobile = section.settings.content_horizontal_align_mobile
  assign content_horizontal_align = section.settings.content_horizontal_align

  assign text_align = ""
  if content_horizontal_align == "start"
    assign text_align = "left"
  elsif content_horizontal_align == "center"
    assign text_align = "center"
  else
    assign text_align = "right"
  endif

  assign text_align_mobile = ""
  if content_horizontal_align_mobile == "start"
    assign text_align_mobile = "left"
  elsif content_horizontal_align_mobile == "center"
    assign text_align_mobile = "center"
  else
    assign text_align_mobile = "right"
  endif

  assign heading = section.settings.heading
  assign heading_size = section.settings.heading_size
  assign heading_size_mobile = section.settings.heading_size_mobile
  assign heading_color = section.settings.heading_color
  assign heading_custom = section.settings.heading_custom
  assign heading_font = section.settings.heading_font
  assign heading_height = section.settings.heading_height

  assign items_mt = section.settings.items_mt
  assign items_mt_mobile = section.settings.items_mt_mobile

  assign item_padding_vertical_mobile = section.settings.item_padding_vertical_mobile
  assign item_padding_vertical = section.settings.item_padding_vertical
  assign item_gap = section.settings.item_gap
  assign item_gap_mobile = section.settings.item_gap_mobile
  assign item_border_thickness = section.settings.item_border_thickness
  assign item_border_color = section.settings.item_border_color

  assign circle_progress = section.settings.circle_progress
  assign circle_bg_color = section.settings.circle_bg_color
  assign circle_size = section.settings.circle_size
  assign circle_size_mobile = section.settings.circle_size_mobile

  assign percent_size = section.settings.percent_size
  assign percent_size_mobile = section.settings.percent_size_mobile
  assign percent_color = section.settings.percent_color
  assign percent_custom = section.settings.percent_custom
  assign percent_font = section.settings.percent_font
  assign percent_height = section.settings.percent_height

  assign item_text_size = section.settings.item_text_size
  assign item_text_size_mobile = section.settings.item_text_size_mobile
  assign item_text_color = section.settings.item_text_color
  assign item_text_custom = section.settings.item_text_custom
  assign item_text_font = section.settings.item_text_font
  assign item_text_height = section.settings.item_text_height

  assign bottom_text = section.settings.bottom_text
  assign bottom_text_size = section.settings.bottom_text_size
  assign bottom_text_size_mobile = section.settings.bottom_text_size_mobile
  assign bottom_text_color = section.settings.bottom_text_color
  assign bottom_text_custom = section.settings.bottom_text_custom
  assign bottom_text_font = section.settings.bottom_text_font
  assign bottom_text_height = section.settings.bottom_text_height
  assign bottom_text_mt = section.settings.bottom_text_mt
  assign bottom_text_mt_mobile = section.settings.bottom_text_mt_mobile
  assign bottom_text_align = section.settings.bottom_text_align
  assign bottom_text_align_mobile = section.settings.bottom_text_align_mobile

-%}

{%- style -%}

  {{  heading_font | font_face: font_display: 'swap' }}
  {{  percent_font | font_face: font_display: 'swap' }}
  {{  item_text_font | font_face: font_display: 'swap' }}
  {{  bottom_text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .feature-body-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
    gap: {{ body_gap_mobile }}px;
  }

  .feature-content-{{ section.id }} {
    display: flex;
    flex-direction: column;
    align-items: {{ content_horizontal_align_mobile }};
  }

  .feature-heading-{{ section.id }} {
    text-align: {{ text_align_mobile }};
  }

  .feature-heading-{{ section.id }} * {
    margin: 0px;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
    transition: all 0.25s ease 0s;
  }

  .feature-items-{{ section.id }} {
    margin-top: {{ items_mt_mobile }}px;
    border-bottom: {{ item_border_thickness }}px solid {{ item_border_color }};
  }

  .feature-item-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: {{ item_gap_mobile }}px;
    padding: {{ item_padding_vertical_mobile }}px 0px;
    border-top: {{ item_border_thickness }}px solid {{ item_border_color }};
  }

  .feature-circle-{{ section.id }} {
    display: flex;
    justify-content: center;
    align-items: center;
    flex: 0 0 {{ circle_size_mobile }}px;
    height: {{ circle_size_mobile }}px;
    border-radius: 50%;
    padding: 5px;
    background: radial-gradient(closest-side, {{ circle_bg_color }} 79%, transparent 80% 100%), conic-gradient({{ circle_progress }} var(--percent), transparent 0);
  }

  .feature-percent-{{ section.id }} {
    margin: 0px;
    font-size: {{ percent_size_mobile }}px;
    color: {{ percent_color }};
    line-height: {{ percent_height }}%;
    text-transform: unset;
    transition: all 0.25s ease 0s;
    word-break: break-word;
  }

  .feature-item-text-{{ section.id }} {
    margin: 0px;
    font-size: {{ item_text_size_mobile }}px;
    color: {{ item_text_color }};
    line-height: {{ item_text_height }}%;
    text-transform: unset;
    transition: all 0.25s ease 0s;
  }

  .feature-bottom-text-{{ section.id }} {
    margin-top: {{ bottom_text_mt_mobile }}px;
    text-align: {{ bottom_text_align_mobile }};
  }

  .feature-bottom-text-{{ section.id }} * {
    margin: 0px;
    font-size: {{ bottom_text_size_mobile }}px;
    color: {{ bottom_text_color }};
    line-height: {{ bottom_text_height }}%;
    text-transform: unset;
    transition: all 0.25s ease 0s;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .feature-body-{{ section.id }} {
      grid-template-columns: 1fr 1fr;
      gap: {{ body_gap }}px;
      align-items: center;
    }

    .feature-content-{{ section.id }} {
      align-items: {{ content_horizontal_align }};
    }

    .feature-heading-{{ section.id }} {
      text-align: {{ text_align }};
    }

    .feature-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .feature-items-{{ section.id }} {
      margin-top: {{ items_mt }}px;
    }

    .feature-item-{{ section.id }} {
      gap: {{ item_gap }}px;
      padding: {{ item_padding_vertical }}px 0px;
    }

    .feature-circle-{{ section.id }} {
      flex: 0 0 {{ circle_size }}px;
      height: {{ circle_size }}px;
    }

    .feature-percent-{{ section.id }} {
      font-size: {{ percent_size }}px;
    }

    .feature-item-text-{{ section.id }} {
      font-size: {{ item_text_size }}px;
    }

    .feature-bottom-text-{{ section.id }} {
      margin-top: {{ bottom_text_mt }}px;
      text-align: {{ bottom_text_align }};
    }

    .feature-bottom-text-{{ section.id }} * {
      font-size: {{ bottom_text_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
    .feature-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if percent_custom %}
  <style>
    .feature-percent-{{ section.id }} {
      font-family: {{ percent_font.family }}, {{ percent_font.fallback_families }};
      font-weight: {{ percent_font.weight }};
      font-style: {{ percent_font.style }};
    }
  </style>
{% endif %}

{% if item_text_custom %}
  <style>
    .feature-item-text-{{ section.id }} {
      font-family: {{ item_text_font.family }}, {{ item_text_font.fallback_families }};
      font-weight: {{ item_text_font.weight }};
      font-style: {{ item_text_font.style }};
    }
  </style>
{% endif %}

{% if bottom_text_custom %}
  <style>
    .feature-bottom-text-{{ section.id }} * {
      font-family: {{ bottom_text_font.family }}, {{ bottom_text_font.fallback_families }};
      font-weight: {{ bottom_text_font.weight }};
      font-style: {{ bottom_text_font.style }};
    }
  </style>
{% endif %}

{% for block in section.blocks %}
    {% if block.type == "heading" %}
        <style>
            {{  block.settings.heading_font | font_face: font_display: 'swap' }}

            .feature-heading-{{ block.id }} {
                text-align: {{ block.settings.heading_align_mobile }};
                margin-top: {{ block.settings.heading_mt_mobile }}px;
            }

            .feature-heading-{{ block.id }} * {
                margin: 0;
                font-size: {{ block.settings.heading_size_mobile }}px;
                color: {{ block.settings.heading_color }};
                line-height: {{ block.settings.heading_height }}%;
                text-transform: unset;
            }

            @media(min-width: 1024px) {

                .feature-heading-{{ block.id }} {
                    text-align: {{ block.settings.heading_align }};
                    margin-top: {{ block.settings.heading_mt }}px;
                }

                .feature-heading-{{ block.id }} * {
                    font-size: {{ block.settings.heading_size }}px;
                }
            }
        </style>

        {% if block.settings.heading_custom %}
            <style>
                .feature-heading-{{ block.id }} * {
                    font-family: {{ block.settings.heading_font.family }}, {{ block.settings.heading_font.fallback_families }};
                    font-weight: {{ block.settings.heading_font.weight }};
                    font-style: {{ block.settings.heading_font.style }};
                }
            </style>
        {% endif %}

    {% elsif block.type == "text" %}
        <style>
            {{ block.settings.text_font | font_face: font_display: 'swap' }}

            .feature-text-{{ block.id }} {
                text-align: {{ text_align_mobile }};
                margin-top: {{ block.settings.text_mt_mobile }}px;
            }

            .feature-text-{{ block.id }} * {
                margin: 0;
                font-size: {{ block.settings.text_size_mobile }}px;
                color: {{ block.settings.text_color }};
                line-height: {{ block.settings.text_height }}%;
                text-transform: unset;
            }

            @media(min-width: 1024px) {

                .feature-text-{{ block.id }} {
                    text-align: {{ text_align }};
                    margin-top: {{ block.settings.text_mt }}px;
                }

                .feature-text-{{ block.id }} * {
                    font-size: {{ block.settings.text_size }}px;
                }
            }
        </style>

        {% if block.settings.text_custom %}
            <style>
                .feature-text-{{ block.id }} {
                    font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
                    font-weight: {{ block.settings.text_font.weight }};
                    font-style: {{ block.settings.text_font.style }};
                }
            </style>
        {% endif %}

    {% elsif block.type == "button" %}

        <style>
            .feature-button-{{ block.id }} {
            display: flex;
            align-items: center;
            justify-content: center;
            gap: 10px;
            width: 100%;
            max-width: fit-content;
            margin: 0;
            margin-top: {{ block.settings.button_mt_mobile }}px;
            font-size: {{ block.settings.button_size_mobile }}px;
            color: {{ block.settings.button_color }};
            line-height: {{ block.settings.button_height }}%;
            text-align: center;
            text-transform: unset;
            text-decoration: none;
            padding: {{ block.settings.button_padding_vertical_mobile }}px {{ block.settings.button_padding_horizontal_mobile }}px;
            border-radius: {{ block.settings.button_radius }}px;
            transition: all 0.25s ease 0s;
            position: relative;
            font-weight: 700;
            z-index: 2;
            }

            .feature-button-{{ block.id }}:hover {
            color: {{ block.settings.button_hover_color }};
            transition: all 0.25s ease 0s;
            }

            .feature-button-{{ block.id }} svg {
            width: 14px;
            height: 14px;
            }

            .feature-button-{{ block.id }} svg path {
            fill: {{ block.settings.button_color }};
            transition: all 0.25s ease 0s;
            }

            .feature-button-{{ block.id }}:hover svg path {
            fill: {{ block.settings.button_hover_color }};
            transition: all 0.25s ease 0s;
            }

            @media(min-width: 1024px) {
            .feature-button-{{ block.id }} {
                margin-top: {{ block.settings.button_mt }}px;
                padding: {{ block.settings.button_padding_vertical }}px {{ block.settings.button_padding_horizontal }}px;
                font-size: {{ block.settings.button_size }}px;
            }
            }
        </style>

        {% if block.settings.button_custom %}
            <style>
            .feature-button-{{ block.id }} {
                font-family: {{ block.settings.button_font.family }}, {{ block.settings.button_font.fallback_families }};
                font-weight: {{ block.settings.button_font.weight }};
                font-style: {{ block.settings.button_font.style }};
            }
            </style>
        {% endif %}

        {% if block.settings.button_style == "non_outline" or block.settings.button_style == "non_outline_arrow" %}
            <style>
            .feature-button-{{ block.id }} {
                background-color: {{ block.settings.button_bg_color }};
            }

            .feature-button-{{ block.id }}:hover {
                background-color: {{ block.settings.button_bg_hover_color }};
            }
            </style>
        {% elsif block.settings.button_style == "outline" or block.settings.button_style == "outline_arrow" %}
            <style>
            .feature-button-{{ block.id }} {
                background-color: {{ block.settings.button_bg_color }};
                border: {{ block.settings.button_border_thickness }}px solid {{ block.settings.button_border_color }};
            }

            .feature-button-{{ block.id }}:hover {
                background-color: {{ block.settings.button_bg_hover_color }};
                border: {{ block.settings.button_border_thickness }}px solid {{ block.settings.button_border_hover_color }};
            }
            </style>
        {% elsif block.settings.button_style == "link" %}
            <style>
                .feature-button-{{ block.id }} {
                    max-width: fit-content;
                    padding: 0px;
                }
            </style>
        {% endif %}

    {% elsif block.type == "media" %}

      <style>
        .feature-media-{{ block.id }} {
          width: 100%;
          margin-top: {{ block.settings.media_mt_mobile }}px;
          max-width: {{ block.settings.media_width_mobile }}%;
          overflow: hidden;
          border-radius: {{ block.settings.media_radius }}px;
          border: {{ block.settings.media_border_thickness }}px solid {{ block.settings.media_border_color }};
        }

        .feature-media-{{ block.id }} img,
        .feature-media-{{ block.id }} svg,
        .feature-media-{{ block.id }} video,
        .feature-media-{{ block.id }} iframe {
          display: block;
          width: 100%;
          height: 100%;
          object-fit: cover;
          box-shadow: none;
          border: 0px;
        }

        .feature-media-{{ block.id }} svg {
          background-color: #AFAFAF;
        }

        @media(min-width: 1024px) {
          .feature-media-{{ block.id }} {
            margin-top: {{ block.settings.media_mt }}px;
            max-width:  {{ block.settings.media_width }}%;
          }
        }
      </style>

      {% if block.settings.media_ratio_mobile == "portrait" %}
        <style>
          .feature-media-{{ block.id }} {
           aspect-ratio: 9.6/12; 
          }
        </style>
      {% elsif block.settings.media_ratio_mobile == "landscape" %}
        <style>
          .feature-media-{{ block.id }}{
           aspect-ratio: 12/10; 
          }
        </style>
      {% elsif block.settings.media_ratio_mobile == "square" %}
        <style>
          .feature-media-{{ block.id }} {
           aspect-ratio: 12/12; 
          }
        </style>
      {% else %}
        <style>
          .feature-media-{{ block.id }} {
           aspect-ratio: auto; 
          }
        </style>
      {% endif %}

      {% if block.settings.media_ratio == "portrait" %}
        <style>
          @media(min-width: 1024px) {
           .feature-media-{{ block.id }} {
             aspect-ratio: 9.6/12; 
            } 
          }
        </style>
      {% elsif block.settings.media_ratio == "landscape" %}
        <style>
          @media(min-width: 1024px) {
            .feature-media-{{ block.id }}{
             aspect-ratio: 12/10; 
            }
          }
        </style>
      {% elsif block.settings.media_ratio == "square" %}
        <style>
          @media(min-width: 1024px) {
            .feature-media-{{ block.id }} {
             aspect-ratio: 12/12; 
            }
          }
        </style>
      {% else %}
        <style>
          @media(min-width: 1024px) {
            .feature-media-{{ block.id }} {
             aspect-ratio: auto; 
            }
          }
        </style>
      {% endif %}

    {% endif %}

{% endfor %}


<div class="section-{{ section.id }} feature-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="feature-body-{{ section.id }}">
        <div class="feature-content-{{ section.id }}">
          {% if heading != blank %}
            <div class="feature-heading-{{ section.id }}">
                {{ heading }}
            </div>
          {% endif %}
          {% for block in section.blocks %}

            {% if block.type == "text" %}

                <div class="feature-text-{{ block.id }}">
                    {{ block.settings.text }}
                </div>

            {% elsif block.type == "media" %}

                <div class="feature-media-{{ block.id }}">
                  {% if block.settings.media_type == "image" %}
                    {% if block.settings.image != blank %}
                      <img src="{{ block.settings.image | image_url }}" alt="{{ block.settings.image.alt }}" {% if forloop.first %}{% if lazy %}loading="lazy"{% endif %}{% endif %}>
                    {% else %}
                      {{ 'image' | placeholder_svg_tag }}
                    {% endif %}
                  {% else %}
                    {% assign video_id = block.settings.video.id | default: block.settings.video_url.id %}
                    {% assign loop = '&loop=1&playlist=' | append: video_id %}  
                    {%- if block.settings.video == null and block.settings.video_url != null -%}
                      {%- if block.settings.video_url.type == 'youtube' -%}
                        <iframe
                          data-src="https://www.youtube.com/embed/{{ video_id }}?enablejsapi=1&mute=1&autoplay=1&controls=0&showinfo=0{{ loop }}"
                          data-src1="https://www.youtube.com/embed/{{ video_id }}?enablejsapi=1&mute=0&autoplay=1&controls=0&showinfo=0{{ loop }}"
                          allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                          allowfullscreen
                          class="lazy"
                        ></iframe>
                      {%- else -%}
                        <iframe
                          data-src="https://player.vimeo.com/video/{{ video_id }}?enablejsapi=1&mute=1&autoplay=1&controls=0&showinfo=0{{ loop }}"
                          data-src1="https://player.vimeo.com/video/{{ video_id }}?enablejsapi=1&mute=0&autoplay=1&controls=0&showinfo=0{{ loop }}"
                          allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                          allowfullscreen
                          class="lazy"
                        ></iframe>
                      {% endif %}

                    {% else %}

                      <video data-src="{{ block.settings.video.sources[1].url }}" playsinline autoplay muted preload="none" loop {% if block.settings.use_controls %}controls{% endif %} class="lazy"></video>

                    {% endif %}
                  {% endif %}
                </div>

            {% elsif block.type == "button" %}

                {% if block.settings.button != blank %}
                    <a href="{{ block.settings.button_url }}" class="feature-button-{{ block.id }}">
                        {{ block.settings.button }}
                        {% if block.settings.button_style == "non_outline_arrow" or block.settings.button_style == "outline_arrow" %}
