  assign top_content_padding_horizontal_mobile = section.settings.top_content_padding_horizontal_mobile
  assign top_content_padding_vertical = section.settings.top_content_padding_vertical
  assign top_content_padding_vertical_mobile = section.settings.top_content_padding_vertical_mobile
  assign top_content_gap = section.settings.top_content_gap
  assign top_content_gap_mobile = section.settings.top_content_gap_mobile
  assign top_content_align = section.settings.top_content_align
  assign top_content_align_mobile = section.settings.top_content_align_mobile

  assign top_content_horizontal_align_mobile = ""
