{%- style -%}
  {{  button_font | font_face: font_display: 'swap' }}
  {{ toggle_font | font_face: font_display: 'swap' }}
  {{ text_font | font_face: font_display: 'swap' }}
  {{ scrolling_text_font | font_face: font_display: 'swap' }}  

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
    overflow: hidden;
  }

  .feature-body-{{ section.id }} {
    display: grid;
    border-radius: {{ layout_radius }}px;
    border: {{ layout_border_thickness }}px solid {{ layout_border_color }};
    overflow: hidden;
  }

  .feature-content-{{ section.id }} {
    grid-area: content;
    display: flex;
    flex-direction: column;
    overflow: hidden;
  }

  .feature-top-content-{{ section.id }} {
    display: flex;
    align-items: {{ top_content_horizontal_align_mobile }};
    justify-content: space-between;
    flex-direction: column;
    gap: {{ top_content_gap_mobile }}px;
    padding: {{ top_content_padding_vertical_mobile }}px {{ top_content_padding_horizontal_mobile }}px;
  }

  .featured-toggles-{{ section.id }} {
    display: flex;
    flex-direction: column;
    gap: 5px;
  }

  .feature-toggle-{{ section.id }} {
    text-align: {{ top_content_align_mobile }};
    margin: 0;
    font-size: {{ toggle_size_mobile }}px;
    color: {{ toggle_color }};
    line-height: {{ toggle_height }}%;
    text-transform: unset;
    text-decoration: none;
    cursor: pointer;
    transition: all 0.25s ease 0s;
  }

  .feature-toggle-{{ section.id }}:hover {
    color: {{ toggle_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .feature-toggle-{{ section.id }}.active {
    color: {{ toggle_active_color }};
    transition: all 0.25s ease 0s;
  }

  .feature-text-wrapper-{{ section.id }} {
    max-width: 100%;
  }

  .feature-text-{{ section.id }} {
    display: none;
  }

  .feature-text-{{ section.id }} {
    text-align: {{ top_content_align_mobile }};
  }

  .feature-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease 0s;
  }

  .feature-text-{{ section.id }}.active {
    display: block;
    animation: opacity{{ section.id | replace: '-', '' }} 1s ease-out forwards; 
  }

  .feature-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    position: relative;
    gap: 10px;
    width: 100%;
    margin: 0;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    transition: all 0.25s ease 0s;
    background-color: transparent;
    border: 0px;
    cursor: pointer;
    overflow: hidden;
    font-weight: 700;
  }

  .feature-button-inner-{{ section.id }} {
    margin: 0px;
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 1;
    gap: 10px;
  }

  .feature-button-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .feature-button-{{ section.id }} svg {
    width: 14px;
    height: 14px;
  }

  .feature-button-{{ section.id }} svg path {
    fill: {{ button_color }};
    transition: all 0.25s ease 0s;
  }

  .feature-button-{{ section.id }}:hover svg path {
    fill: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .feature-scrolling-text-wrapper-{{ section.id }} {
    position: relative;
    display: -webkit-box;
    display: -ms-flexbox;
    display: none;
    -ms-flex-wrap: nowrap;
    flex-wrap: nowrap;
    width: 100%;
    position: relative;
    z-index: 0;
    left: -10%;
    background-attachment: scroll !important;       
    padding: {{ scrolling_padding_vertical_mobile }}px 0px;
  }

  .feature-scrolling-text-wrapper-{{ section.id }}.active {
    display: flex;
    animation: opacity{{ section.id | replace: '-', '' }} 1s ease-out forwards; 
  }

  @keyframes opacity{{ section.id | replace: '-', '' }} {
    0% {
      opacity: 0;
    }
    100% {
      opacity: 1;
    }
  }

  .feature-scrolling-text-list-{{ section.id }} {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    white-space: nowrap;
    background-attachment: scroll !important;
    -webkit-animation: ticker{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile }}s infinite linear;
    animation: ticker{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile }}s infinite linear;       
    flex-shrink: 0;
  }

  .feature-scrolling-text-{{ section.id }} {
    margin: 0;    
    font-weight: 700;
    font-size: {{ scrolling_text_size_mobile }}px;
    color: {{ scrolling_text_color }};
    line-height: {{ scrolling_text_height }}%;
    margin-right: {{ scrolling_gap_mobile }}px;
    text-transform: unset;
    word-wrap: break-word;
    background-attachment: scroll !important;
    position: relative;
    flex-shrink: 0;
  }  

  .feature-images-{{ section.id }} {   
    grid-area: images;
    overflow: hidden;
    border-bottom: {{ layout_border_thickness }}px solid {{ layout_border_color }}; 
    border-left: 0px;
    position: relative;
    overflow: hidden;
  }

  .feature-image-{{ section.id }} {
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0;
    z-index: 0;
    transition: all 0.5s ease-out;
    will-change: transform;
  }

  .feature-image-{{ section.id }}:before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0,0,0,0.3);
    transition: all 0.25s ease 0s;
    z-index: 0;
    opacity: 0;
  }

  .feature-image-{{ section.id }}.active {
    z-index: 2;
    animation: slideIn{{ section.id | replace: '-', '' }} 0.7s linear forwards; 
  }

  .feature-image-{{ section.id }}.overlay:before {
    opacity: 1;
    transition: all 0.25s ease 0s;
  }

  .feature-image-{{ section.id }}.current {
    z-index: 1;
  }

  .feature-image-{{ section.id }} img,
  .feature-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .feature-image-{{ section.id }} svg,
  .feature-image-{{ section.id }} img {
    background-color: {{ background_color }};
  }

  @keyframes slideIn{{ section.id | replace: '-', '' }} {
    0% {
      transform: translateX(130%);
    }
    100% {
      transform: translateX(0);
    }
  }


  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .feature-top-content-{{ section.id }} {
      flex-direction: row;
      align-items: start;
      gap: {{ top_content_gap }}px;
      padding: {{ top_content_padding_vertical }}px {{ top_content_padding_horizontal }}px;
    }

    .feature-toggle-{{ section.id }} {
      text-align: {{ top_content_align }};
      font-size: {{ toggle_size }}px;
    }

    .feature-text-wrapper-{{ section.id }} {
      max-width: 60%;
    }

    .feature-text-{{ section.id }} {
      text-align: {{ top_content_align }};
    }

    .feature-text-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }

    .feature-button-{{ section.id }} {
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }

    .feature-scrolling-text-wrapper-{{ section.id }} {
      margin-top: {{ scrolling_mt }}px;     
      padding: {{ scrolling_padding_vertical }}px 0px;
    }

    .feature-scrolling-text-list-{{ section.id }} {
      -webkit-animation: ticker{{ section.id | replace: '-', '' }} {{ scrolling_velocity }}s infinite linear;
      animation: ticker{{ section.id | replace: '-', '' }} {{ scrolling_velocity }}s infinite linear;        
    }

    .feature-scrolling-text-{{ section.id }} {
      font-size: {{ scrolling_text_size }}px;
      margin-right: {{ scrolling_gap }}px;
    }  

    .feature-images-{{ section.id }}  {
      aspect-ratio: auto !important;
      border-left: {{ layout_border_thickness }}px solid {{ layout_border_color }}; 
      border-bottom: 0px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if toggle_custom %}
  <style>
    .feature-toggle-{{ section.id }} {
      font-family: {{ toggle_font.family }}, {{ toggle_font.fallback_families }};
      font-weight: {{ toggle_font.weight }};
      font-style: {{ toggle_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .feature-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .feature-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if scrolling_text_custom %}
  <style>
    .feature-scrolling-text-{{ section.id }} {
      font-family: {{ scrolling_text_font.family }}, {{ scrolling_text_font.fallback_families }};
      font-weight: {{ scrolling_text_font.weight }};
      font-style: {{ scrolling_text_font.style }};
    }
  </style>
{% endif %}

{% if layout_mobile == 'images_content'%}
  <style>
    .feature-body-{{ section.id }} {
      grid-template-areas: 
        "images"
        "content";     
    }
  </style>
{% elsif layout_mobile == 'content_images' %}
  <style>
    .feature-body-{{ section.id }} {
      grid-template-areas: 
        "content"
        "images";     
    }

    .feature-images-{{ section.id }} {
      border-top: {{ layout_border_thickness }}px solid {{ layout_border_color }}; 
      border-bottom: 0px;
    }
  </style>
{% endif %}

{% if layout == 'images_content'%}
  <style>
    @media(min-width: 1024px) {
      .feature-body-{{ section.id }} {
        grid-template-areas: "images content";
        grid-template-columns: 0.8fr 1fr;
      }

      .feature-images-{{ section.id }} {
        border-right: {{ layout_border_thickness }}px solid {{ layout_border_color }}; 
        border-left: 0px;
      }
    }    
  </style>
{% elsif layout == 'content_images' %}
  <style>
    @media(min-width: 1024px) {
      .feature-body-{{ section.id }} {
        grid-template-areas: "content images";
        grid-template-columns: 1fr 0.8fr;
      }      
    }   
  </style>
{% endif %}

{% if button_style == "non_outline" or button_style == "non_outline_arrow" %}
  <style>
    .feature-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
    }

    .feature-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
    }
  </style>
{% elsif button_style == "outline" or button_style == "outline_arrow" %}
  <style>
    .feature-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
      border-top: {{ button_border_thickness }}px solid {{ button_border_color }};
      border-bottom: {{ button_border_thickness }}px solid {{ button_border_color }};
    }

    .feature-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
      border-top: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
      border-bottom: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    }
  </style>
{% elsif button_style == "vertical_hover" %}  
  <style>    
    .feature-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
      border-top: {{ button_border_thickness }}px solid {{ button_border_color }};
      border-bottom: {{ button_border_thickness }}px solid {{ button_border_color }};
    }

    .feature-button-{{ section.id }}::after {
      content: '';
      position: absolute;
      width: 100%;
      height: 100%;
      background-color: {{ button_bg_hover_color }};
      transform: translateY(100%);
      transition: all 0.25s ease 0s;
    }

    @media(min-width: 1024px) {      
      .feature-button-{{ section.id }}:hover::after {
        transform: translateY(0%);
        transition: all 0.25s ease 0s;
      }

    }
  </style>
{% elsif button_style == "link" %}
  <style>
    .feature-button-{{ section.id }} {
      padding: 0px !important;
    }
  </style>
{% endif %}

{% if reverse_direction %}
  <style>
    @keyframes ticker{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(-100%);
      }
      100% {
        transform: translateX(0%);
      }
    }
  </style>
{% else %}
  <style>
    @keyframes ticker{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(0%);
      }
      100% {
        transform: translateX(-100%);
      }
    }
  </style>
{% endif %}

{% if image_ratio_mobile == "portrait" %}
  <style>
    .feature-images-{{ section.id }} {
     aspect-ratio: 9.6/13; 
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
    .feature-images-{{ section.id }}{
     aspect-ratio: 12/9; 
    }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .feature-images-{{ section.id }} {
     aspect-ratio: 12/12; 
    }
  </style>
{% endif %}

