    <div class="section-{{ section.id }}-settings">
      <div class="feature-body-{{ section.id }}">
        <div class="feature-content-{{ section.id }}">
          <div class="feature-top-content-{{ section.id }}">
            <div class="feature-toggles-{{ section.id }}">
              {% for block in section.blocks %}
                {% if block.settings.toggle != blank %}
                  <h3 class="feature-toggle-{{ section.id }} {% if forloop.first %}active{% endif %}" id="{{ block.id }}">{{ block.settings.toggle }}</h3>
                {% endif %}            
              {% endfor %}
            </div>
            <div class="feature-text-wrapper-{{ section.id }}">
              {% for block in section.blocks %}
                {% if block.settings.text != blank %}
                  <div class="feature-text-{{ section.id }} {% if forloop.first %}active{% endif %}" data-id="{{ block.id }}">{{ block.settings.text }}</div>
                {% endif %}             
              {% endfor %}
            </div>
          </div>           
          {% if button != blank %}
            <{% if button_url != blank %}a href="{{ button_url }}"{% else %}div{% endif %} class="feature-button-{{ section.id }}">
              <p class="feature-button-inner-{{ section.id }}">
