<script>
function initFeature28() {
  const toggles = document.querySelectorAll('.feature-toggle-{{ section.id }}');
  const featuresText = document.querySelectorAll('.feature-text-{{ section.id }}');
  const images = document.querySelectorAll('.feature-image-{{ section.id }}');
  const scrollItems = document.querySelectorAll('.feature-scrolling-text-wrapper-{{ section.id }}');

  toggles.forEach(toggle => {
    toggle.addEventListener('click', () => {
      toggles.forEach(anotherToggle => {
        anotherToggle.classList.remove('active');
      });
      featuresText.forEach(anotherText => {
        anotherText.classList.remove('active');
      });
      scrollItems.forEach(item => {
        item.classList.remove('active');
      })

      featuresText.forEach(text => {
        if (toggle.id == text.getAttribute('data-id')) {
          toggle.classList.add('active');
          text.classList.add('active');
        }
      });

      images.forEach(image => {

        if(image.classList.contains('active')) {
          image.classList.add('current');

          setTimeout(()=> {
            image.classList.remove('current');
          },800)
        }

        if (toggle.id == image.getAttribute('data-id')) {
          toggle.classList.add('active');
          image.classList.add('active');
          image.classList.remove('overlay');
        } else {
          image.classList.add('overlay');
          image.classList.remove('active');
        }
      });

      scrollItems.forEach(item => {
        if (toggle.id == item.getAttribute('data-id')) {
          toggle.classList.add('active');
          item.classList.add('active');
        }
      });
    });
  });
}



  document.addEventListener('DOMContentLoaded', initFeature28);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:load', initFeature28);
  }
</script>
