{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign block_size = section.blocks.size | divided_by: 2

  assign body_gap = section.settings.body_gap
  assign body_gap_mobile = section.settings.body_gap_mobile

  assign features_gap = section.settings.features_gap
  assign features_gap_mobile = section.settings.features_gap_mobile
  assign body_vertical_align = section.settings.body_vertical_align

  assign feature_align = section.settings.feature_align
  assign feature_align_mobile = section.settings.feature_align_mobile 

  assign title_custom = section.settings.title_custom
  assign title_font = section.settings.title_font
  assign title_size = section.settings.title_size
  assign title_size_mobile = section.settings.title_size_mobile
  assign title_height = section.settings.title_height
  assign title_color = section.settings.title_color

  assign progress_height = section.settings.progress_height
  assign progress_height_mobile = section.settings.progress_height_mobile
  assign progress_color = section.settings.progress_color
  assign progress_border_thickness = section.settings.progress_border_thickness
  assign progress_border_color = section.settings.progress_border_color
  assign progress_mt = section.settings.progress_mt
  assign progress_mt_mobile = section.settings.progress_mt_mobile

  assign text_custom = section.settings.text_custom
  assign text_font = section.settings.text_font
  assign text_size = section.settings.text_size
  assign text_size_mobile = section.settings.text_size_mobile
  assign text_height = section.settings.text_height
  assign text_color = section.settings.text_color
  assign text_mt = section.settings.text_mt
  assign text_mt_mobile = section.settings.text_mt_mobile

  assign media_type = section.settings.media_type
  assign image = section.settings.image
  assign video = section.settings.video
  assign video_url = section.settings.video_url
  assign media_border_thickness = section.settings.media_border_thickness
  assign media_border_color = section.settings.media_border_color
  assign media_radius = section.settings.media_radius
  assign media_ratio = section.settings.media_ratio
  assign media_ratio_mobile = section.settings.media_ratio_mobile
-%}

{%- style -%}
  {{ title_font | font_face: font_display: 'swap' }}
  {{ text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .feature-body-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
    gap: 0px;
    align-items: center;
  }

  .feature-features-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
    gap: {{ features_gap_mobile }}px;

  }

  .feature-features-{{ section.id }}:last-child {  
    margin-top: {{ body_gap_mobile }}px;
  }

  .feature-features-{{ section.id }}:first-child {
    order: 2;
    margin-top: {{ features_gap_mobile }}px;
  }

  .feature-title-{{ section.id }} {
    margin: 0;    
    text-align: {{ feature_align_mobile }};
    text-transform: unset;
    word-wrap: break-word;
    font-size: {{ title_size_mobile }}px;
    line-height: {{ title_height }}%;
    color: {{ title_color }};  
  }

  .feature-progress-{{ section.id }} {
    position: relative;
    display: block !important;
    margin-top: {{ progress_mt_mobile }}px;
    border-top: {{ progress_border_thickness }}px solid {{ progress_border_color }};
    height: calc({{ progress_border_thickness }}px + {{ progress_height_mobile }}px);
  }

  .feature-progress-{{ section.id }}::after {
    content: "";
    position: absolute;
    background-color: {{ progress_color }};
    height: 100%;
    top: 0;
    left: 0;
  }

  .feature-text-{{ section.id }} {
    text-align: {{ feature_align_mobile }};
    margin-top: {{ text_mt_mobile }}px;    
  }

  .feature-text-{{ section.id }} * {
    margin: 0;    
    text-transform: unset;
    word-wrap: break-word;
    font-size: {{ text_size_mobile }}px;
    line-height: {{ text_height }}%;
    color: {{ text_color }};  
  }

  .feature-media-{{ section.id }} {
    border: {{ media_border_thickness }}px solid {{ media_border_color }};
    border-radius: {{ media_radius }}px;
    overflow: hidden;
  }

  .feature-media-{{ section.id }} img,
  .feature-media-{{ section.id }} svg,
  .feature-media-{{ section.id }} video,
  .feature-media-{{ section.id }} iframe {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    border: 0;
  }

  .feature-media-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .feature-body-{{ section.id }} {
      grid-template-columns: 1fr 1fr 1fr;
      gap: {{ body_gap }}px;
      align-items: {{ body_vertical_align }};
    }

    .feature-features-{{ section.id }} { 
      gap: {{ features_gap }}px;
    }

    .feature-features-{{ section.id }}:last-child { 
      margin-top: 0;
    }

    .feature-features-{{ section.id }}:first-child {
      margin-top: 0px;
      order: initial;
    }

    .feature-title-{{ section.id }} {
      font-size: {{ title_size }}px;
      text-align: {{ feature_align }};
    }

    .feature-progress-{{ section.id }} {
      margin-top: {{ progress_mt }}px;
      height: calc({{ progress_border_thickness }}px + {{ progress_height }}px);
    }

    .feature-text-{{ section.id }} {
      text-align: {{ feature_align }};
      margin-top: {{ text_mt }}px;    
    }

    .feature-text-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .feature-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .feature-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}


{% if media_ratio_mobile == "portrait" %}
  <style>
    .feature-media-{{ section.id }} {
      aspect-ratio: 9.6/13;
    }
  </style>
{% elsif media_ratio_mobile == "landscape" %}
  <style>
  .feature-media-{{ section.id }} {
    aspect-ratio: 12/8;
  }
  </style>
{% elsif media_ratio_mobile == "square" %}
  <style>
    .feature-media-{{ section.id }} {
      aspect-ratio: 12/12;
    }
  </style>
{% else %}
  <style>
    .feature-media-{{ section.id }} {
      aspect-ratio: auto;
    }
  </style>
{% endif %}

{% if media_ratio == "portrait" %}
  <style>
    @media (min-width: 1024px) {
      .feature-media-{{ section.id }} {
        aspect-ratio: 9.6/13;
      }
    }
  </style>
{% elsif media_ratio == "landscape" %}
  <style>
    @media (min-width: 1024px) {
      .feature-media-{{ section.id }} {
        aspect-ratio: 12/8;
      }
    }
  </style>
{% elsif media_ratio == "square" %}
  <style>
    @media (min-width: 1024px) {
      .feature-media-{{ section.id }} {
        aspect-ratio: 12/12;
      }
    }
  </style>
{% else %}
  <style>
    @media (min-width: 1024px) {
      .feature-media-{{ section.id }} {
        aspect-ratio: auto;
      }
    }
  </style>
{% endif %}

{% for block in section.blocks %}
  <style>
    .feature-progress-{{ block.id }}::after {
      width: {{ block.settings.percent }}%;
    }
  </style>
{% endfor %}

<div class="section-{{ section.id }} feature-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="feature-body-{{ section.id }}">
        <div class="feature-features-{{ section.id }}">
          {% for block in section.blocks limit: block_size %}
            <div class="feature-feature-{{ section.id }}">
              {% if block.settings.title != blank %}
                <h3 class="feature-title-{{ section.id }}">{{ block.settings.title }}</h3>
              {% endif %}
              <div class="feature-progress-{{ section.id }} feature-progress-{{ block.id }}"></div>
              {% if block.settings.text != blank %}
                <div class="feature-text-{{ section.id }}">{{ block.settings.text }}</div>
              {% endif %}
            </div>
          {% endfor %}
        </div>
        <div class="feature-media-{{ section.id }}">
          {% if media_type == 'video' %}           
            {% assign video_id = video.id | default: video_url.id %}
            {% assign loop = '&loop=1&playlist=' | append: video_id %}
            {%- if video == null and video_url != null -%}
            {%- if video_url.type == 'youtube' -%}
            <iframe
              data-src="https://www.youtube.com/embed/{{ video_id }}?enablejsapi=1&mute=1&autoplay=1&controls=0&modestbranding=1&showinfo=0&rel=0{{ loop }}"             
              allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
              allowfullscreen
              class="lazy"
            ></iframe>
            {%- else -%}
            <iframe
              data-src="https://player.vimeo.com/video/{{ video_id }}?enablejsapi=1&mute=1&muted=1&autoplay=1&controls=0&modestbranding=1&showinfo=0&rel=0{{ loop }}"             
              allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
              allowfullscreen
              class="lazy"
            ></iframe>
            {% endif %}
            {% else %}
              <video src="{{ video.sources[1].url }}" muted autoplay playsinline preload="none" loop></video>
            {% endif %}         
          {% elsif media_type == 'image' %}            
            {% if image != blank %}
              <img src="{{ image | image_url }}" alt="{{ bg_image.alt }}">
            {% else %}
              {{ 'image' | placeholder_svg_tag }}
            {% endif %}        
          {% endif %}
        </div>
        <div class="feature-features-{{ section.id }}">
          {% for block in section.blocks offset: block_size %}
            <div class="feature-feature-{{ section.id }}">
              {% if block.settings.title != blank %}
                <h3 class="feature-title-{{ section.id }}">{{ block.settings.title }}</h3>
              {% endif %}
              <div class="feature-progress-{{ section.id }} feature-progress-{{ block.id }}"></div>
              {% if block.settings.text != blank %}
                <div class="feature-text-{{ section.id }}">{{ block.settings.text }}</div>
              {% endif %}
            </div>
          {% endfor %}
        </div>
      </div>
    </div>
</div>

<script src="https://cdn.jsdelivr.net/npm/vanilla-lazyload@19.1.3/dist/lazyload.min.js"></script>

<script>
  function initFeature29() {
    let lazyLoadInstance = new LazyLoad();

    window.addEventListener(
      "LazyLoad::Initialized",
      function (event) {
        window.lazyLoadInstance = event.detail.instance;
      },
      false
    );
  }

  document.addEventListener('DOMContentLoaded', initFeature29);

  if (Shopify.designMode) {
    document.addEventListener('shopify:section:unload', initFeature29);
    document.addEventListener('shopify:section:load', initFeature29);
  }
</script>
