{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign heading = section.settings.heading
  assign heading_custom = section.settings.heading_custom
  assign heading_font = section.settings.heading_font
  assign heading_size = section.settings.heading_size
  assign heading_size_mobile = section.settings.heading_size_mobile
  assign heading_height = section.settings.heading_height
  assign heading_color = section.settings.heading_color
  assign heading_align = section.settings.heading_align
  assign heading_align_mobile = section.settings.heading_align_mobile

  assign cards_per_row = section.settings.cards_per_row
  assign cards_gap = section.settings.cards_gap 
  assign cards_gap_mobile = section.settings.cards_gap_mobile
  assign cards_mt = section.settings.cards_mt 
  assign cards_mt_mobile = section.settings.cards_mt_mobile

  assign image_radius = section.settings.image_radius
  assign image_border_thickness = section.settings.image_border_thickness
  assign image_border_color = section.settings.image_border_color
  assign image_ratio = section.settings.image_ratio
  assign image_ratio_mobile = section.settings.image_ratio_mobile

  assign card_content_align = section.settings.card_content_align
  assign card_content_align_mobile = section.settings.card_content_align_mobile

  assign card_content_horizontal_align = ""
  if card_content_align == "center"
    assign card_content_horizontal_align = "center"
  elsif card_content_align == "right"
    assign card_content_horizontal_align = "end"
  else
    assign card_content_horizontal_align = "start"
  endif

  assign card_content_horizontal_align_mobile = ""
  if card_content_align_mobile == "center"
    assign card_content_horizontal_align_mobile = "center"
  elsif card_content_align_mobile == "right"
    assign card_content_horizontal_align_mobile = "end"
  else
    assign card_content_horizontal_align_mobile = "start"
  endif

  assign title_custom = section.settings.title_custom
  assign title_font = section.settings.title_font
  assign title_size = section.settings.title_size
  assign title_size_mobile = section.settings.title_size_mobile
  assign title_height = section.settings.title_height
  assign title_color = section.settings.title_color
  assign title_mt = section.settings.title_mt
  assign title_mt_mobile = section.settings.title_mt_mobile

  assign text_custom = section.settings.text_custom
  assign text_font = section.settings.text_font
  assign text_size = section.settings.text_size
  assign text_size_mobile = section.settings.text_size_mobile
  assign text_height = section.settings.text_height
  assign text_color = section.settings.text_color
  assign text_mt = section.settings.text_mt
  assign text_mt_mobile = section.settings.text_mt_mobile

  assign features_gap = section.settings.features_gap
  assign features_gap_mobile = section.settings.features_gap_mobile
  assign features_mt = section.settings.features_mt
  assign features_mt_mobile = section.settings.features_mt_mobile

  assign feature_icon_width = section.settings.feature_icon_width
  assign feature_icon_width_mobile = section.settings.feature_icon_width_mobile
  assign show_icon_text = section.settings.show_icon_text
  assign show_icon_text_mobile = section.settings.show_icon_text_mobile

  assign feature_title_custom = section.settings.feature_title_custom
  assign feature_title_font = section.settings.feature_title_font
  assign feature_title_size = section.settings.feature_title_size
  assign feature_title_size_mobile = section.settings.feature_title_size_mobile
  assign feature_title_height = section.settings.feature_title_height
  assign feature_title_color = section.settings.feature_title_color
  assign feature_title_bg_color = section.settings.feature_title_bg_color
  assign feature_title_radius = section.settings.feature_title_radius
-%}

{%- style -%}
  {{ heading_font | font_face: font_display: 'swap' }}
  {{ title_font | font_face: font_display: 'swap' }}
  {{ text_font | font_face: font_display: 'swap' }}
  {{ feature_title_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .feature-heading-{{ section.id }} {
    text-align: {{ heading_align_mobile }};
  }

  .feature-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
    text-decoration: none;
    font-weight: 700;
  }

  .feature-cards-{{ section.id }} {
    display: flex;
    flex-direction: column;
    gap: {{ cards_gap_mobile }}px;
    max-width: 100%;    
    margin-top: {{ cards_mt_mobile }}px;
  }

  .feature-image-{{ section.id }} {
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
    border-radius: {{ image_radius }}px;
    overflow: hidden;
  }

  .feature-image-{{ section.id }} img,
  .feature-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .feature-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .feature-title-{{ section.id }} {
    margin: 0;
    margin-top: {{ title_mt_mobile }}px;
    text-align: {{ card_content_align_mobile }};
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease 0s;
  }

  .feature-text-{{ section.id }} {
    text-align: {{ card_content_align_mobile }};
    margin-top: {{ text_mt_mobile }}px;    
  }

  .feature-text-{{ section.id }} * {
    margin: 0;    
    text-transform: unset;
    word-wrap: break-word;
    font-size: {{ text_size_mobile }}px;
    line-height: {{ text_height }}%;
    color: {{ text_color }};  
  }

  .feature-features-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: {{ features_gap_mobile }}px;
    flex-wrap: nowrap;
    overflow-x: scroll;
    margin-top: {{ features_mt_mobile }}px;
    justify-content: {{ card_content_horizontal_align_mobile }};
    scrollbar-width: none;
    margin-left: -{{ padding_horizontal_mobile }}rem !important;
    margin-right: -{{ padding_horizontal_mobile }}rem !important;
    padding-left: {{ padding_horizontal_mobile }}rem !important;
    padding-right: {{ padding_horizontal_mobile }}rem !important;
  }

  .feature-features-{{ section.id }}::-webkit-scrollbar {
    display: none;
  }

  .feature-icon-{{ section.id }} {
    position: relative;
    display: flex;
    flex-direction: column;
    align-items: center;
    cursor: default;
  }

  .feature-icon-image-{{ section.id }} {
    width: {{ feature_icon_width_mobile }}px;
  }

  .feature-icon-image-{{ section.id }} img,
  .feature-icon-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .feature-icon-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }  

  .feature-icon-feature-{{ section.id }} {
    margin: 0;
    display: none;
    position: relative;
    margin-top: 5px;
    border-radius: {{ feature_title_radius }}px;
    background-color: {{ feature_title_bg_color }};
    font-size: {{ feature_title_size_mobile }}px;
    color: {{ feature_title_color }};
    line-height: {{ feature_title_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease 0s;
    font-weight: 700;
    opacity: 1;
    padding: 5px 10px;
    text-align: center;
    white-space: nowrap;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .feature-heading-{{ section.id }} {
      text-align: {{ heading_align }};
    }

    .feature-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }   

    .feature-cards-{{ section.id }} {
      display: grid;
      grid-template-columns: repeat({{ cards_per_row }}, 1fr); 
      gap: {{ cards_gap }}px;
      margin-top: {{ cards_mt }}px;
    }

    .feature-title-{{ section.id }} { 
      margin-top: {{ title_mt }}px;
      font-size: {{ title_size }}px;
      text-align: {{ card_content_align }};
    }

    .feature-text-{{ section.id }} {
      text-align: {{ card_content_align }};
      margin-top: {{ text_mt }}px;    
    }

    .feature-text-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }

    .feature-features-{{ section.id }} {
      overflow-x: visible;
      flex-wrap: wrap;
      gap: {{ features_gap }}px;
      margin-top: {{ features_mt }}px;
      justify-content: {{ card_content_horizontal_align }};
    }

    .feature-icon-image-{{ section.id }} {
      width: {{ feature_icon_width }}px;
    }

    .feature-icon-feature-{{ section.id }} {
      position: absolute;
      bottom: 110%;
      left: 50%;
      transform: translateX(-50%);
      display: none;
      font-size: {{ feature_title_size }}px;
      opacity: 0;
    }

    .feature-icon-feature-{{ section.id }}::after {
      content: "";
      position: absolute;
      bottom: -12px;
      left: 50%;
      transform: translateX(-50%);
      border-width: 6px; 
      border-style: solid;
      border-color: {{ feature_title_bg_color }} transparent transparent transparent;
      display: block;
      width: 0;
      height: 0;
    }

    .feature-icon-{{ section.id }}:hover  .feature-icon-feature-{{ section.id }} {
      opacity: 1;
      transition: all 0.25s ease 0s;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
    .feature-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .feature-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .feature-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if feature_title_custom %}
  <style>
    .feature-icon-feature-{{ section.id }} {
      font-family: {{ feature_title_font.family }}, {{ feature_title_font.fallback_families }};
      font-weight: {{ feature_title_font.weight }};
      font-style: {{ feature_title_font.style }};
    }
  </style>
{% endif %}

{% if image_ratio_mobile == "portrait" %}
  <style>
    .feature-image-{{ section.id }} {
      aspect-ratio: 9.6/13;
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
  .feature-image-{{ section.id }} {
    aspect-ratio: 12/8;
  }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .feature-image-{{ section.id }} {
      aspect-ratio: 12/12;
    }
  </style>
{% else %}
  <style>
    .feature-image-{{ section.id }} {
      aspect-ratio: auto;
    }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
      .feature-image-{{ section.id }} {
        aspect-ratio: 9.6/13;
      }
    }

  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .feature-image-{{ section.id }} {
        aspect-ratio: 12/8;
      }
    }  
  </style>
{% elsif image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .feature-image-{{ section.id }} {
        aspect-ratio: 12/12;
      }
    }    
  </style>
{% else %}
  <style>
     @media(min-width: 1024px) {
      .feature-image-{{ section.id }} {
        aspect-ratio: auto;
      }
    }
  </style>
{% endif %}

{% if show_icon_text_mobile %}
  <style>
    .feature-icon-feature-{{ section.id }} {
      display: block;
    }
  </style>
{% endif %}

{% if show_icon_text %}
  <style>
    @media(min-width: 1024px) {
      .feature-icon-feature-{{ section.id }} {
        display: block;
      }
      .feature-icon-{{ section.id }} {
        cursor: pointer;
      }
    }    
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
      .feature-icon-feature-{{ section.id }} {
        display: none;
      }
    }    
  </style>
{% endif %}



<div class="section-{{ section.id }} feature-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      {% if heading != blank %}
        <div class="feature-heading-{{ section.id }}">{{ heading }}</div>
      {% endif %}      
      <div class="feature-cards-{{ section.id }}">
        {% assign inside_card = false %}
        {% for block in section.blocks %}
          {% if block.type == 'card' %}
            {% if inside_card %}
              </div>
            </div>
            </div>
            {% endif %}
            <div class="feature-card-{{ section.id }}">
              <div class="feature-image-{{ section.id }}">
                {% if block.settings.image != blank %}
                  <img src="{{ block.settings.image | img_url: '1200x1200' }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>            
                {% else %}
                  {{ 'image' | placeholder_svg_tag }}
                {% endif %} 
              </div>
              {% if block.settings.title != blank %} 
                <p class="feature-title-{{ section.id }}">{{ block.settings.title }}</p>
              {% endif %}   
              {% if block.settings.text != blank %}
                <div class="feature-text-{{ section.id }}">{{ block.settings.text }}</div>
              {% endif %}
              <div class="feature-features-wrapper-{{ section.id }}">
                <div class="feature-features-{{ section.id }}">
                  {% assign inside_card = true %}
          {% elsif block.type == 'feature' %}
            {% if inside_card %}
              <div class="feature-icon-{{ section.id }}">
                <div class="feature-icon-image-{{ section.id }}">
                  {% if block.settings.icon != blank %}
                    <img src="{{ block.settings.icon | img_url: '100x100' }}" alt="{{ block.settings.icon.alt }}" {% if lazy %}loading="lazy"{% endif %}>            
                  {% else %}
                    {{ 'image' | placeholder_svg_tag }}
                  {% endif %} 
                </div>
                {% if block.settings.feature != blank %}
                  <p class="feature-icon-feature-{{ section.id }}">{{ block.settings.feature }}</p>           
                {% endif %}               
              </div>
            {% endif %}
          {% endif %}
        {% endfor %}
        {% if inside_card %}
                </div>
              </div>
            </div>
        {% endif %}

      </div>  
    </div>
</div>
