{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign toggles_gap = section.settings.toggles_gap  
  assign toggles_gap_mobile = section.settings.toggles_gap_mobile

  assign toggle_custom = section.settings.toggle_custom
  assign toggle_font = section.settings.toggle_font
  assign toggle_size = section.settings.toggle_size
  assign toggle_size_mobile = section.settings.toggle_size_mobile
  assign toggle_height = section.settings.toggle_height
  assign toggle_color = section.settings.toggle_color
  assign toggle_bg_color = section.settings.toggle_bg_color
  assign toggle_padding_horizontal = section.settings.toggle_padding_horizontal
  assign toggle_padding_horizontal_mobile = section.settings.toggle_padding_horizontal_mobile
  assign toggle_padding_vertical = section.settings.toggle_padding_vertical
  assign toggle_padding_vertical_mobile = section.settings.toggle_padding_vertical_mobile
  assign toggle_radius = section.settings.toggle_radius
  assign toggle_border_thickness = section.settings.toggle_border_thickness
  assign toggle_border_color = section.settings.toggle_border_color
  assign toggle_border_hover_color = section.settings.toggle_border_hover_color
  assign toggle_border_active_color = section.settings.toggle_border_active_color
  assign toggle_bg_hover_color = section.settings.toggle_bg_hover_color
  assign toggle_bg_active_color = section.settings.toggle_bg_active_color
  assign toggle_hover_color = section.settings.toggle_hover_color
  assign toggle_active_color = section.settings.toggle_active_color

  assign layout_gap = section.settings.layout_gap
  assign layout_gap_mobile = section.settings.layout_gap_mobile
  assign layout_mt = section.settings.layout_mt
  assign layout_mt_mobile = section.settings.layout_mt_mobile

  assign image_border_thickness = section.settings.image_border_thickness
  assign image_border_color = section.settings.image_border_color
  assign image_radius = section.settings.image_radius
  assign image_ratio = section.settings.image_ratio
  assign image_ratio_mobile = section.settings.image_ratio_mobile

  assign second_image_width = section.settings.second_image_width
  assign second_image_width_mobile = section.settings.second_image_width_mobile
  assign second_image_border_thickness = section.settings.second_image_border_thickness
  assign second_image_border_color = section.settings.second_image_border_color
  assign second_image_radius = section.settings.second_image_radius
  assign second_image_ratio = section.settings.second_image_ratio
  assign second_image_ratio_mobile = section.settings.second_image_ratio_mobile

  assign feature_layout_align = section.settings.feature_layout_align
  assign feature_layout_align_mobile = section.settings.feature_layout_align_mobile

  assign title_size = section.settings.title_size
  assign title_size_mobile = section.settings.title_size_mobile
  assign title_color = section.settings.title_color
  assign title_custom = section.settings.title_custom
  assign title_font = section.settings.title_font
  assign title_height = section.settings.title_height

  assign text_size = section.settings.text_size
  assign text_size_mobile = section.settings.text_size_mobile
  assign text_color = section.settings.text_color
  assign text_custom = section.settings.text_custom
  assign text_font = section.settings.text_font
  assign text_height = section.settings.text_height
  assign text_mt = section.settings.text_mt
  assign text_mt_mobile = section.settings.text_mt_mobile

  assign lazy_load_setting = 'lazy'
  unless lazy
    assign lazy_load_setting = 'eiger'
  endunless
-%}

{%- style -%}
  {{ toggle_font | font_face: font_display: 'swap' }}
  {{ title_font | font_face: font_display: 'swap' }}
  {{ text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .feature-toggles-{{ section.id }} {
    display: flex;
    flex-wrap: nowrap;
    overflow-x: scroll;
    scrollbar-width: none;
    align-items: center;   
    justify-content: start; 
    gap: {{ toggles_gap_mobile }}px;
    margin-left: -{{ padding_horizontal_mobile }}rem;
    margin-right: -{{ padding_horizontal_mobile }}rem;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .feature-toggles-{{ section.id }}::-webkit-scrollbar {
    display: none;
  }

  .feature-toggle-{{ section.id }} {
    margin: 0;
    cursor: pointer;
    display: flex;
    align-items: center;
    gap: 10px;
    font-size: {{ toggle_size_mobile }}px;
    color: {{ toggle_color }};
    line-height: {{ toggle_height }}%;
    background-color: {{ toggle_bg_color }};
    padding: {{ toggle_padding_vertical_mobile }}px {{ toggle_padding_horizontal_mobile }}px;
    border: {{ toggle_border_thickness }}px solid {{ toggle_border_color }};
    border-radius: {{ toggle_radius }}px;
    text-transform: unset;
    text-decoration: none;
    white-space: nowrap;
    transition: all 0.25s ease 0s;
  }

  .feature-toggle-{{ section.id }}.active {
    background-color: {{ toggle_bg_active_color }};
    border: {{ toggle_border_thickness }}px solid {{ toggle_border_active_color }};
    color: {{ toggle_active_color }};
    transition: all 0.25s ease 0s;
  }

  .feature-item-{{ section.id }} {
    display: none;
    flex-direction: column;
    gap: {{ layout_gap_mobile }}px;
    align-items: center;
    margin-top: {{ layout_mt_mobile }}px;
  }

  .feature-item-{{ section.id }}.active {
    display: flex;
  }

  .feature-images-{{ section.id }} {
    position: relative;
    width: 70%;
  }

  .feature-image-{{ section.id }} {
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
    border-radius: {{ image_radius }}px;
    overflow: hidden;
    width: 100%;
  }

  .feature-second-image-{{ section.id }} {
    position: absolute;
    bottom: -{{ second_image_width_mobile | times: 0.25 }}px;
    left: -{{ second_image_width_mobile | times: 0.25 }}px;
    width: {{ second_image_width_mobile }}px;
    border: {{ second_image_border_thickness }}px solid {{ second_image_border_color }};
    border-radius: {{ second_image_radius }}px;
    overflow: hidden;
  }

  .feature-image-{{ section.id }} img,
  .feature-image-{{ section.id }} svg,
  .feature-second-image-{{ section.id }} img,
  .feature-second-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .feature-image-{{ section.id }} svg,
  .feature-second-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .feature-title-{{ section.id }} {
    margin: 0px;
    width: 100%;
    text-align: {{ feature_layout_align_mobile }};
    font-size: {{ title_size_mobile }}px;
    line-height: {{ title_height }}%;
    color: {{ title_color }};
    text-transform: unset;
    word-break: break-word;
    font-weight: 700;
  }

  .feature-text-{{ section.id }} {
    margin-top: {{ text_mt_mobile }}px;
    text-align: {{ feature_layout_align_mobile }};
    width: 100%;
  }

  .feature-text-{{ section.id }} * {
    margin: 0px;
    font-size: {{ text_size_mobile }}px;
    line-height: {{ text_height }}%;
    color: {{ text_color }};
    text-transform: unset;
    word-break: break-word;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 0rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: calc({{ padding_bottom }}px + {{ second_image_width | times: 0.25 }}px);
      padding-left: calc({{ padding_horizontal }}rem + {{ second_image_width | times: 0.25 }}px);
      padding-right: {{ padding_horizontal }}rem;
    }

    .feature-toggles-{{ section.id }} {
      flex-wrap: wrap;
      justify-content: center;
      gap: {{ toggles_gap }}px;
      margin-left: 0rem;
      margin-right: 0rem;
      padding-left: 0rem;
      padding-right: 0rem;
    }

    .feature-toggle-{{ section.id }} {
      font-size: {{ toggle_size }}px;
      padding: {{ toggle_padding_vertical }}px {{ toggle_padding_horizontal }}px;
    }


    .feature-toggle-{{ section.id }}:not(.active):hover {
      background-color: {{ toggle_bg_hover_color }};
      border: {{ toggle_border_thickness }}px solid {{ toggle_border_hover_color }};
      color: {{ toggle_hover_color }};
      transition: all 0.25s ease 0s;
    }

    .feature-item-{{ section.id }} {
      display: none;
      grid-template-columns: 1fr 1fr;
      gap: {{ layout_gap }}px;
      margin-top: {{ layout_mt }}px;
    }

    .feature-item-{{ section.id }}.active {
      display: grid;
    }

    .feature-images-{{ section.id }} {
      width: 100%;
    }

    .feature-second-image-{{ section.id }} {
      width: {{ second_image_width }}px;
      bottom: -{{ second_image_width | times: 0.25 }}px;
      left: -{{ second_image_width | times: 0.25 }}px;
    }

    .feature-title-{{ section.id }} {
      text-align: {{ feature_layout_align }};
      font-size: {{ title_size }}px;
    }

    .feature-text-{{ section.id }} {
      margin-top: {{ text_mt }}px;
      text-align: {{ feature_layout_align }};
    }

    .feature-text-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if toggle_custom %}
  <style>
    .feature-toggle-{{ section.id }} {
      font-family: {{ toggle_font.family }}, {{ toggle_font.fallback_families }};
      font-weight: {{ toggle_font.weight }};
      font-style: {{ toggle_font.style }};
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .feature-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .feature-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if image_ratio_mobile == "portrait" %}
  <style>
    .feature-image-{{ section.id }} {
      aspect-ratio: 9.6/11;
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
  .feature-image-{{ section.id }} {
    aspect-ratio: 12/8;
  }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .feature-image-{{ section.id }} {
      aspect-ratio: 12/12;
    }
  </style>
{% else %}
  <style>
    .feature-image-{{ section.id }} {
      aspect-ratio: auto;
    }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
      .feature-image-{{ section.id }} {
        aspect-ratio: 9.6/11;
      }
    }

  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .feature-image-{{ section.id }} {
        aspect-ratio: 12/8;
      }
    }  
  </style>
{% elsif image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .feature-image-{{ section.id }} {
        aspect-ratio: 12/12;
      }
    }    
  </style>
{% else %}
  <style>
     @media(min-width: 1024px) {
      .feature-image-{{ section.id }} {
        aspect-ratio: auto;
      }
    }
  </style>
{% endif %}

{% if second_image_ratio_mobile == "portrait" %}
  <style>
    .feature-second-image-{{ section.id }} {
      aspect-ratio: 9.6/11;
    }
  </style>
{% elsif second_image_ratio_mobile == "landscape" %}
  <style>
  .feature-second-image-{{ section.id }} {
    aspect-ratio: 12/8;
  }
  </style>
{% elsif second_image_ratio_mobile == "square" %}
  <style>
    .feature-second-image-{{ section.id }} {
      aspect-ratio: 12/12;
    }
  </style>
{% else %}
  <style>
    .feature-second-image-{{ section.id }} {
      aspect-ratio: auto;
    }
  </style>
{% endif %}

{% if second_image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
      .feature-second-image-{{ section.id }} {
        aspect-ratio: 9.6/11;
      }
    }

  </style>
{% elsif second_image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .feature-second-image-{{ section.id }} {
        aspect-ratio: 12/8;
      }
    }  
  </style>
{% elsif second_image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .feature-second-image-{{ section.id }} {
        aspect-ratio: 12/12;
      }
    }    
  </style>
{% else %}
  <style>
     @media(min-width: 1024px) {
      .feature-second-image-{{ section.id }} {
        aspect-ratio: auto;
      }
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} feature-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="feature-body-{{ section.id }}">
        <div class="feature-toggles-{{ section.id }}">
          {% for block in section.blocks %}
            <p class="feature-toggle-{{ section.id }} {% if forloop.first %}active{% endif %}" id="{{ block.id }}">{{ block.settings.toggle }}</p>
          {% endfor %}
        </div>
        <div class="feature-items-{{ section.id }}">
          {% for block in section.blocks %}
            <div class="feature-item-{{ section.id }} {% if forloop.first %}active{% endif %}" data-id="{{ block.id }}">
              <div class="feature-images-{{ section.id }}">
                <div class="feature-image-{{ section.id }}">
                  {% if block.settings.image != blank %}
                  {%- capture sizes -%}(min-width: {{ content_width }}px) {{ content_width | minus: 100 | divided_by: 2 }}px, (min-width: 750px) calc((100vw - 130px) / 2), calc((100vw - 50px) / 2){%- endcapture -%}
                    {{ block.settings.image | image_url: width: 1000 | image_tag:
                      loading: lazy_load_setting,
                      width: block.settings.image.width,
                      height: block.settings.image.height,
                      sizes: sizes,
                      widths: '360, 535, 750, 1070, 1500'
                    }}
                  {% else %}  
                    {{ 'image' | placeholder_svg_tag }}
                  {% endif %}  
                </div>
                {% if block.settings.use_second_image != blank %}
                  <div class="feature-second-image-{{ section.id }}">
                    {% if block.settings.second_image != blank %}
                    {%- capture sizes -%}(min-width: {{ content_width }}px) {{ content_width | minus: 100 | divided_by: 2 }}px, (min-width: 750px) calc((100vw - 130px) / 2), calc((100vw - 50px) / 2){%- endcapture -%}
                      {{ block.settings.second_image | image_url: width: 700 | image_tag:
                        loading: lazy_load_setting,
                        width: block.settings.second_image.width,
                        height: block.settings.second_image.height,
                        sizes: sizes,
                        widths: '360, 535, 750, 1070, 1500'
                      }}
                    {% else %}  
                      {{ 'image' | placeholder_svg_tag }}
                    {% endif %}  
                  </div>
                {% endif %}
              </div>
              <div class="feature-content-{{ section.id }}">
                {% if block.settings.title != blank %}
                  <h3 class="feature-title-{{ section.id }}">{{ block.settings.title }}</h3>
                {% endif %}
                {% if block.settings.text != blank %}
                  <div class="feature-text-{{ section.id }}">{{ block.settings.text }}</div>
                {% endif %}
              </div>
            </div>
          {% endfor %}
        </div>
      </div>
    </div>
</div>

<script>
  function initFeature37() {
    const toggles = document.querySelectorAll('.feature-toggle-{{ section.id }}');
    const contents = document.querySelectorAll('.feature-item-{{ section.id }}');

    toggles.forEach(toggle => {
      toggle.addEventListener('click', () => {
        contents.forEach(otherContent => otherContent.classList.remove('active'));
        toggles.forEach(otherToggle => otherToggle.classList.remove('active'));
        contents.forEach(content => {
          if(toggle.id == content.getAttribute('data-id')) {          
            content.classList.add('active');
            toggle.classList.add('active');
          }
        })
      })
    })
  }

  document.addEventListener('DOMContentLoaded', initFeature37);

  if (Shopify.designMode) {
    document.addEventListener('shopify:section:unload', initFeature37);
    document.addEventListener('shopify:section:load', initFeature37);
  }
</script>
