{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign width = section.settings.width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius
  assign background_style = section.settings.background_style

  assign layout_height = section.settings.layout_height
  assign layout_fixed_height = section.settings.layout_fixed_height
  assign layout_gap = section.settings.layout_gap

  assign content_align = section.settings.content_align
  assign content_align_mobile = section.settings.content_align_mobile

  assign content_horizontal_align = ""
  if content_align == "center"
    assign content_horizontal_align = "center"
  elsif content_align == "right"
    assign content_horizontal_align = "end"
  else
    assign content_horizontal_align = "start"
  endif

  assign content_horizontal_align_mobile = ""
  if content_align_mobile == "center"
    assign content_horizontal_align_mobile = "center"
  elsif content_align_mobile == "right"
    assign content_horizontal_align_mobile = "end"
  else
    assign content_horizontal_align_mobile = "start"
  endif

  assign scrolling_velocity = section.settings.scrolling_velocity
  assign scrolling_velocity_mobile = section.settings.scrolling_velocity_mobile
  assign reverse_direction = section.settings.reverse_direction
  assign scrolling_mt_mobile = section.settings.scrolling_mt_mobile
  assign pause_hover = section.settings.pause_hover

  assign columns_gap = section.settings.columns_gap
  assign columns_gap_mobile = section.settings.columns_gap_mobile

  assign card_content_align = section.settings.card_content_align
  assign card_content_align_mobile = section.settings.card_content_align_mobile

  assign card_content_horizontal_align = ""
  if card_content_align == "center"
    assign card_content_horizontal_align = "center"
  elsif card_content_align == "right"
    assign card_content_horizontal_align = "end"
  else
    assign card_content_horizontal_align = "start"
  endif

  assign card_content_horizontal_align_mobile = ""
  if card_content_align_mobile == "center"
    assign card_content_horizontal_align_mobile = "center"
  elsif card_content_align_mobile == "right"
    assign card_content_horizontal_align_mobile = "end"
  else
    assign card_content_horizontal_align_mobile = "start"
  endif

  assign card_padding_horizontal = section.settings.card_padding_horizontal
  assign card_padding_horizontal_mobile = section.settings.card_padding_horizontal_mobile
  assign card_padding_vertical = section.settings.card_padding_vertical
  assign card_padding_vertical_mobile = section.settings.card_padding_vertical_mobile
  assign card_border_thickness = section.settings.card_border_thickness
  assign card_border_color = section.settings.card_border_color
  assign card_border_hover_color = section.settings.card_border_hover_color
  assign card_radius = section.settings.card_radius
  assign card_bg_color = section.settings.card_bg_color
  assign card_bg_hover_color = section.settings.card_bg_hover_color

  assign image_ratio = section.settings.image_ratio
  assign image_ratio_mobile = section.settings.image_ratio_mobile
  assign image_radius = section.settings.image_radius
  assign image_border_thickness = section.settings.image_border_thickness
  assign image_border_color = section.settings.image_border_color
  assign image_border_hover_color = section.settings.image_border_hover_color

  assign title_custom = section.settings.title_custom
  assign title_font = section.settings.title_font
  assign title_size = section.settings.title_size
  assign title_size_mobile = section.settings.title_size_mobile
  assign title_height = section.settings.title_height
  assign title_color = section.settings.title_color    
  assign title_hover_color = section.settings.title_hover_color   
  assign title_mt = section.settings.title_mt
  assign title_mt_mobile = section.settings.title_mt_mobile

  assign details_size = section.settings.details_size
  assign details_size_mobile = section.settings.details_size_mobile
  assign details_custom = section.settings.details_custom
  assign details_font = section.settings.details_font
  assign details_height = section.settings.details_height
  assign details_color = section.settings.details_color
  assign details_hover_color = section.settings.details_hover_color
  assign details_mt = section.settings.details_mt
  assign details_mt_mobile = section.settings.details_mt_mobile

  assign price_custom = section.settings.price_custom
  assign price_font = section.settings.price_font
  assign price_size = section.settings.price_size
  assign price_size_mobile = section.settings.price_size_mobile
  assign price_height = section.settings.price_height
  assign price_color = section.settings.price_color    
  assign price_original_color = section.settings.price_original_color
  assign price_mt = section.settings.price_mt
  assign price_mt_mobile = section.settings.price_mt_mobile
  assign price_compare = section.settings.price_compare

  assign first_row_count = 0
  assign second_row_count = 0
  
  for block in section.blocks
    if block.type == 'card'
      if block.settings.row == 'first_row'
        assign first_row_count = first_row_count | plus: 1
      elsif block.settings.row == "second_row"
        assign second_row_count = second_row_count | plus: 1
      endif
    endif  
  endfor
-%}

{%- style -%}
  {{  title_font | font_face: font_display: 'swap' }}
  {{  details_font | font_face: font_display: 'swap' }}
  {{  price_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }
  
  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
    height: 100%;
  }

  .featured-body-{{ section.id }} {
    display: flex;
    height: 100%;
    align-items: center; 
  }  

  .featured-content-{{ section.id }} {
    display: flex;
    flex-direction: column;
    width: 100%;
    align-items: {{ content_horizontal_align_mobile }};
  }
  
  .featured-button-mobile-{{ section.id }} {
    display: flex;
  }

  .featured-button-desktop-{{ section.id }} {
    display: none;
  }

  .featured-scrolling-wrapper-{{ section.id }} {
    display: none;
    gap: {{ columns_gap_mobile }}px;
    width: 60%;
  }

  .featured-scrolling-wrap-{{ section.id }} {
    flex: 1 0;
    display: none;
    background-attachment: scroll !important;
    transform-origin: bottom;
    flex-direction: column;
    height: 200vh;
    position: relative;
    gap: {{ columns_gap_mobile }}px;
  }

  .featured-scrolling-wrap-mobile-{{ section.id }} {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -ms-flex-wrap: nowrap;
    flex-wrap: nowrap;
    background-attachment: scroll !important;
    transform-origin: center;
    width: 200vw;
    position: relative;
    left: -100vw;
    margin-top: {{ scrolling_mt_mobile }}px;
  }

  .featured-scrolling-list-mobile-{{ section.id }} {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    align-items: stretch;
    white-space: nowrap;
    background-attachment: scroll !important;
    flex-shrink: 0;
    margin-left: {{ columns_gap_mobile }}px;
    gap: {{ columns_gap_mobile }}px;
    -webkit-animation: tickerMobile{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile }}s infinite linear;
    animation: tickerMobile{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile }}s infinite linear;
  }

  .featured-scrolling-list-{{ section.id }} {
    display: none;
    flex-direction: column;
    align-items: stretch;
    white-space: nowrap;
    background-attachment: scroll !important;
    gap: {{ columns_gap_mobile }}px;
  }

  .featured-scrolling-list-mobile-{{ section.id }} .featured-card-{{ section.id }} {
    width: 200px;
  }

  .featured-card-{{ section.id }} {
    display: flex;
    flex-direction: column;
    align-items: {{ card_content_horizontal_align_mobile }};
    padding: {{ card_padding_vertical_mobile }}px {{ card_padding_horizontal_mobile }}px;
    border: {{ card_border_thickness }}px solid {{ card_border_color }};
    border-radius: {{ card_radius }}px;
    background-color: {{ card_bg_color }};
    transition: all 0.25s ease 0s;
    overflow: hidden;
    text-decoration: none;
  }

  .featured-card-image-{{ section.id }} {
    width: 100%;
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
    border-radius: {{ image_radius }}px;
    overflow: hidden;
  }

  .featured-card-{{ section.id }}:hover .featured-card-image-{{ section.id }} {
    border: {{ image_border_thickness }}px solid {{ image_border_hover_color }};
  }

  .featured-card-image-{{ section.id }} img,
  .featured-card-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .featured-card-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .featured-card-title-{{ section.id }} {
    margin: 0;   
    display: block; 
    margin-top: {{ title_mt_mobile }}px;
    text-align: {{ card_content_align_mobile }};
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-transform: unset;
    text-wrap: wrap;
    text-decoration: none;
    word-break: break-word;
    transition: all 0.25s ease 0s;
  }   
  
  .featured-card-text-{{ section.id }} {
    width: 80%;
    margin-top: {{ details_mt_mobile }}px;
  }

  .featured-card-text-{{ section.id }} * {
    margin: 0;   
    display: block; 
    text-align: {{ card_content_align_mobile }};
    font-size: {{ details_size_mobile }}px;
    color: {{ details_color }};
    line-height: {{ details_height }}%;
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
    text-wrap: wrap;
    transition: all 0.25s ease 0s;
  }   

  
  .featured-price-{{ section.id }} {
    margin-top: {{ price_mt_mobile }}px;  
    width: 100%;
    display: flex;
    align-items: center;
    gap: 5px;
    justify-content: {{ product_content_horizontal_align_mobile }};
  }

  .featured-price-{{ section.id }} * {
    margin: 0;        
    text-align: {{ product_content_align_mobile }};
    font-size: {{ price_size_mobile }}px;
    color: {{ price_color }};
    line-height: {{ price_height }}%;
    text-transform: unset;
    word-break: break-word;
    transition: all 0.25s ease 0s;
  }   

  .featured-compare-price-{{ section.id }} {
    text-decoration: line-through;
    color: {{ price_original_color }};
  }

  
  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }
    
    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .featured-button-mobile-{{ section.id }} {
      display: none;
    }

    .featured-button-desktop-{{ section.id }} {
      display: flex;
    }

    .featured-body-{{ section.id }} {
      gap: {{ layout_gap }}px;    
    }

    .featured-content-{{ section.id }} {
      width: 40%;
      align-items: {{ content_horizontal_align }};
    }

    .featured-card-{{ section.id }} {
      align-items: {{ card_content_horizontal_align }};
      padding: {{ card_padding_vertical }}px {{ card_padding_horizontal }}px;
    }
      
    .featured-card-{{ section.id }}:hover {
      background-color: {{ card_bg_hover_color }};
      border: {{ card_border_thickness }}px solid {{ card_border_hover_color }};
      transition: all 0.25s ease 0s;
    }

    .featured-card-title-{{ section.id }} {     
      margin-top: {{ title_mt }}px;
      text-align: {{ card_content_align }};
      font-size: {{ title_size }}px;
    }  

    .featured-card-{{ section.id }}:hover .featured-card-title-{{ section.id }} {
      color: {{ title_hover_color }};
      transition: all 0.25s ease 0s;
    }

    .featured-card-text-{{ section.id }} {
      margin-top: {{ details_mt }}px;
    }

    .featured-card-text-{{ section.id }} * {     
      text-align: {{ card_content_align }};
      font-size: {{ details_size }}px;
    }  

    .featured-card-{{ section.id }}:hover .featured-card-text-{{ section.id }} * {
      color: {{ details_hover_color }};
      transition: all 0.25s ease 0s;
    }

    .featured-scrolling-wrapper-{{ section.id }} {
      display: flex;
      gap: {{ columns_gap }}px;
    }

    .featured-scrolling-wrap-{{ section.id }} {
      display: -webkit-box;
      display: -ms-flexbox;
      display: flex;
      gap: {{ columns_gap }}px;
    }

    .featured-scrolling-wrap-mobile-{{ section.id }} {
      display: none;
    }

    .featured-scrolling-list-{{ section.id }} {
      display: -webkit-box;
      display: -ms-flexbox;
      display: flex;
      gap: {{ columns_gap }}px;
    }

    .featured-scrolling-list-mobile-{{ section.id }} {
      display: none;
    }

    .featured-scrolling-wrap-{{ section.id }}:first-child .featured-scrolling-list-{{ section.id }} {
      -webkit-animation: tickerUp{{ section.id | replace: '-', '' }} {{ scrolling_velocity | plus: 5 }}s infinite linear;
      animation: tickerUp{{ section.id | replace: '-', '' }} {{ scrolling_velocity | plus: 5 }}s infinite linear;
    }

    .featured-scrolling-wrap-{{ section.id }}:nth-child(2) .featured-scrolling-list-{{ section.id }} {
      -webkit-animation: tickerDown{{ section.id | replace: '-', '' }} {{ scrolling_velocity }}s infinite linear;
      animation: tickerDown{{ section.id | replace: '-', '' }} {{ scrolling_velocity }}s infinite linear;
    }

    .featured-scrolling-wrap-{{ section.id }}:nth-child(3) .featured-scrolling-list-{{ section.id }} {
      -webkit-animation: tickerUp{{ section.id | replace: '-', '' }} {{ scrolling_velocity | plus: 10 }}s infinite linear;
      animation: tickerUp{{ section.id | replace: '-', '' }} {{ scrolling_velocity | plus: 10 }}s infinite linear;
    }

    .featured-scrolling-wrap-{{ section.id }}:nth-child(4) .featured-scrolling-list-{{ section.id }} {
      -webkit-animation: tickerDown{{ section.id | replace: '-', '' }} {{ scrolling_velocity }}s infinite linear;
      animation: tickerDown{{ section.id | replace: '-', '' }} {{ scrolling_velocity }}s infinite linear;
    }

    
    .featured-price-{{ section.id }} {    
      margin-top: {{ price_mt }}px;       
      justify-content: {{ product_content_horizontal_align }};
    }

    .featured-price-{{ section.id }} * {        
      text-align: {{ product_content_align }};
      font-size: {{ price_size }}px;    
    }   
  }
  
{%- endstyle -%}

{% if width == 'custom' %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% elsif width == 'page' %}
  <style>
    .section-{{ section.id }}-settings {      
      max-width: clamp(100px, var(--page-content-width, var(--page-width)), 100%);
    }
  </style>
{% endif %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }
    
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if price_custom %}
  <style>
    .featured-price-{{ section.id }} * {
      font-family: {{ price_font.family }}, {{ price_font.fallback_families }};
      font-weight: {{ price_font.weight }};
      font-style: {{ price_font.style }};
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .featured-card-title-{{ section.id }}  {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if details_custom %}
  <style>
    .featured-card-text-{{ section.id }} *  {
      font-family: {{ details_font.family }}, {{ details_font.fallback_families }};
      font-weight: {{ details_font.weight }};
      font-style: {{ details_font.style }};
    }
  </style>
{% endif %}

{% if layout_height == "full_screen" %}
  <style>

    @media(max-width: 1024px) {
      .featured-{{ section.id }} {
        min-height: 95vh;
      }
    }

    @media(min-width: 1024px) {      
      .featured-{{ section.id }} {
        height: 95vh;
      }
    }
  </style>
{% elsif layout_height == 'fixed_height' %}  
  <style>      

    .featured-{{ section.id }} {
      height: 100%;
    }

    @media(min-width: 1024px) {
      .featured-{{ section.id }} {
        height: {{ layout_fixed_height }}px;
      }
    }
  </style>
{% endif %}


{% if image_ratio_mobile == "portrait" %}
  <style>
    .featured-card-image-{{ section.id }} {
      aspect-ratio: 9.6/13;
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
  .featured-card-image-{{ section.id }} {
    aspect-ratio: 12/8;
  }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .featured-card-image-{{ section.id }} {
      aspect-ratio: 12/12;
    }
  </style>
{% else %}
  <style>
    .featured-card-image-{{ section.id }} {
      aspect-ratio: auto;
    }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
      .featured-card-image-{{ section.id }} {
        aspect-ratio: 9.6/13;
      }
    }
    
  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .featured-card-image-{{ section.id }} {
        aspect-ratio: 12/8;
      }
    }  
  </style>
{% elsif image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .featured-card-image-{{ section.id }} {
        aspect-ratio: 12/12;
      }
    }    
  </style>
{% else %}
  <style>
     @media(min-width: 1024px) {
      .featured-card-image-{{ section.id }} {
        aspect-ratio: auto;
      }
    }
  </style>
{% endif %}


{% if reverse_direction %}
	<style>
    @keyframes tickerUp{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateY(-100%);
      }
      100% {
        transform: translateY(0%);
      }
    }

    @keyframes tickerDown{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateY(0%);
      }
      100% {
        transform: translateY(-100%);
      }
    }

    @keyframes tickerMobile{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(-100%);
      }
      100% {
        transform: translateX(0%);
      }
    }
	</style>
{% else %}
	<style>

    @keyframes tickerUp{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateY(0%);
      }
      100% {
        transform: translateY(-100%);
      }
    }

    @keyframes tickerDown{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateY(-100%);
      }
      100% {
        transform: translateY(0%);
      }
    }

    @keyframes tickerMobile{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(0%);
      }
      100% {
        transform: translateX(-100%);
      }
    }
	</style>
{% endif %}

{% if pause_hover %}
	<style>
    .featured-scrolling-wrap-{{ section.id }}:hover .featured-scrolling-list-{{ section.id }} {
      animation-play-state: paused;
    }
	</style>
{% endif %}

{% for block in section.blocks %}
  {% case block.type %}
    {% when 'heading' %}  
      <style>
        {{  block.settings.heading_font | font_face: font_display: 'swap' }}

        .featured-heading-{{ block.id }} {
          margin-top: {{ block.settings.heading_mt_mobile }}px;
          text-align: {{ content_horizontal_align_mobile }};
        }
      
        .featured-heading-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.heading_size_mobile }}px;
          color: {{ block.settings.heading_color }};
          line-height: {{ block.settings.heading_height }}%;
          text-transform: unset;
          word-break: break-word;
          font-weight: 700;
        }

        .featured-heading-{{ block.id }} * strong {
          text-decoration: underline {{ block.settings.heading_border_color }};
        }

        @media(min-width: 1024px) {
          
          .featured-heading-{{ block.id }} {
            margin-top: {{ block.settings.heading_mt }}px;
            text-align: {{ content_horizontal_align }};
          }
        
          .featured-heading-{{ block.id }} * {
            font-size: {{ block.settings.heading_size }}px;
          }
        }          
      </style>
        
      {% if block.settings.heading_custom %}
        <style>
          .featured-heading-{{ block.id }} * {
            font-family: {{ block.settings.heading_font.family }}, {{ block.settings.heading_font.fallback_families }};
            font-weight: {{ block.settings.heading_font.weight }};
            font-style: {{ block.settings.heading_font.style }};
          }
        </style>
      {% endif %}

    {% when 'text' %}   
      <style>
        {{  block.settings.text_font | font_face: font_display: 'swap' }}
        
        .featured-text-{{ block.id }} {
          margin-top: {{ block.settings.text_mt_mobile }}px;
          text-align: {{ content_horizontal_align_mobile }};
          position: relative;
          z-index: 2;
        }
      
        .featured-text-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.text_size_mobile }}px;
          color: {{ block.settings.text_color }};
          line-height: {{ block.settings.text_height }}%;
          text-transform: unset;
        }

        @media(min-width: 1024px) {
          
          .featured-text-{{ block.id }} {
            margin-top: {{ block.settings.text_mt }}px;
            text-align: {{  content_horizontal_align }};
          }
      
          .featured-text-{{ block.id }} * {
