{%- style -%}

  {{ heading_font | font_face: font_display: 'swap' }}
  {{ title_font | font_face: font_display: 'swap' }}
  {{ price_font | font_face: font_display: 'swap' }}
  {{ button_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .collection-heading-{{ section.id }} {
    text-align: {{ heading_align_mobile }};
  }

  .collection-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
  }

  .collection-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    max-width: fit-content;
    margin: 0;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical | times: 0.75 | round: 0 }}px {{ button_padding_horizontal | times: 0.75 | round: 0 }}px;
    border-radius: {{ button_radius }}px;
    border: {{ button_border_thickness }}px solid {{ button_border_color }};
    background-color: {{ button_bg_color }};
    transition: all 0.25s ease 0s;
    position: relative;
    font-weight: 700;
    z-index: 2;
  }

  .collection-button-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    background-color: {{ button_bg_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .collection-grid-{{ section.id }} {
    margin-top: {{ collection_mt | times: 0.75 | round: 0 }}px;
    display: grid;
    grid-template-columns: repeat(1,1fr);
    grid-auto-rows: 1fr 0.5fr;
    gap: {{ collection_gap_mobile }}px;
    grid: auto-flow dense {{ row_height_mobile }}px / repeat({{ collection_row_mobile }},minmax(0,1fr));
  }

  .collection-item-{{ section.id }} {
    display: flex;
    flex-direction: column;
    justify-content: {{ content_position_mobile }};
    padding: {{ card_padding_vertical_mobile }}px {{ card_padding_horizontal_mobile }}px;
    border-radius: {{ card_radius }}px;
    border: {{ card_border_thickness }}px solid {{ card_border_color }};
    background-color: {{ card_bg_color }};
    position: relative;
    overflow: hidden;
    text-decoration: none;
  }

  .collection-item-{{ section.id }}.portrait:nth-child(1),
  .collection-item-{{ section.id }}.portrait:nth-child(4) {
    grid-column-end: span 1;
    grid-row-end: span 1;
  }

  .collection-item-{{ section.id }}.landscape {
    grid-row-end: span 1;
  }

  .collection-item-{{ section.id }}.hero {
    grid-column-end: span 1;
    grid-row-end: span 1;
  }

  .collection-image-{{ section.id }} {
    width: 100%;
    height: 100%;
    position: absolute;
    left: 0;
    top: 0;
    z-index: 0;
  }

  .collection-image-{{ section.id }} img,
  .collection-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .collection-image-{{ section.id }}:after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
    background-color: {{ card_overlay_color | hex_to_rgba: card_overlay }};
  }

  .collection-content-{{ section.id }} {
    display: flex;
    align-items: center;
    flex-wrap: wrap;
    gap: 12px;
    justify-content: space-between;
    position: relative;
    z-index: 1;
    padding: {{ content_padding_vertical_mobile }}px {{ content_padding_horizontal_mobile }}px;
    border-radius: {{ content_radius }}px;
    border-top-right-radius: {{ section.settings.content_radius_top }}px;
    border-top-left-radius: {{ section.settings.content_radius_top }}px;
    border: {{ content_border_thickness }}px solid {{ content_border_color }};
    background-color: {{ content_bg_color | hex_to_rgba: content_bg_opacity }};
  }

  .collection-title-{{ section.id }} {
    margin: 0px;
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-transform: unset;
    font-weight: 700;
    text-decoration: none;
  }

  .collection-price-{{ section.id }} {
    margin: 0px;
    font-size: {{ price_size_mobile }}px;
    color: {{ price_color }};
    line-height: {{ price_height }}%;
    text-transform: unset;
    text-decoration: none;
  }

   @media(min-width: 768px) {

     .collection-grid-{{ section.id }} {
      grid-template-columns: repeat(3,1fr);
      grid-auto-rows: 1fr 0.5fr; 
      --calculated-row-height: max(150px, min(100vw / 5, {{ row_height }}px));
      grid: auto-flow dense var(--calculated-row-height) / repeat(4,minmax(0,1fr));
     }

     .collection-item-{{ section.id }}.portrait {
        grid-column-end: unset;
        grid-row-end: span 2;
     }

     .collection-item-{{ section.id }}.portrait:nth-child(1),
     .collection-item-{{ section.id }}.portrait:nth-child(4) {
      grid-column-end: unset;
      grid-row-end: span 2;
     }

     .collection-item-{{ section.id }}.hero {
      grid-column-end: span 2;
      grid-row-end: span 2;
     }
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .collection-{{ section.id }} .collection-heading-{{ section.id }} {
      text-align: left;
    }

    .collection-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .collection-{{ section.id }} .collection-button-{{ section.id }} {
      margin-left: auto;
      margin-right: 0px;
      margin-top: 0px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }

    .collection-grid-{{ section.id }} {
      grid-template-columns: repeat(4,1fr);
      gap: {{ collection_gap }}px;
    }

    .collection-item-{{ section.id }} {
      justify-content: {{ content_position }};
      padding: {{ card_padding_vertical }}px {{ card_padding_horizontal }}px;
    }

    .collection-item-{{ section.id }}.portrait:nth-child(1) {
      grid-row: 1/span 2;
    }

    .collection-item-{{ section.id }}.landscape:nth-child(5) {
      grid-row: 3/span 1;
    }

    .collection-item-{{ section.id }}.hero:nth-child(1) {
      grid-row: 1/span 2;
    }

    .collection-content-{{ section.id }} {
      padding: {{ content_padding_vertical }}px {{ content_padding_horizontal }}px;
    }

    .collection-title-{{ section.id }} {
      font-size: {{ title_size }}px;
    }

    .collection-price-{{ section.id }} {
      font-size: {{ price_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if heading_custom %}
  <style>
    .collection-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .collection-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if price_custom %}
  <style>
    .collection-price-{{ section.id }} {
      font-family: {{ price_font.family }}, {{ price_font.fallback_families }};
      font-weight: {{ price_font.weight }};
      font-style: {{ price_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .collection-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if card_shadow %}
  <style>
    .collection-item-{{ section.id }} {
      box-shadow: {{ card_shadow_color | hex_to_rgba: 0.15 }} 0px 2px 8px;
    }
  </style>
{% endif %}

{% if hover_image %}
  <style>
    .collection-second-image-{{ section.id }} {
      opacity: 0;
      transition: all 0.25s ease 0s;
    }

    .collection-item-{{ section.id }}:hover .collection-second-image-{{ section.id }} {
      z-index: 2;
      opacity: 1;
      transition: all 0.25s ease 0s;
    }

    .collection-item-{{ section.id }}:hover .collection-first-image-{{ section.id }} {
      opacity: 0;
    }
  </style>
{% endif %}

{% if button_row_mobile %}
  <style>
    .collection-top-{{ section.id }} {
      display: flex;
      align-items: center;
      justify-content: space-between;
      gap: 20px;
      flex-direction: row;
      flex-wrap: wrap;
    }

    @media(min-width: 1024px) {
      .collection-top-{{ section.id }} {
        display: block;
      }
    }
  </style>
{% endif %}

{% unless button_row_mobile %}

  <style>
    .collection-button-{{ section.id }} {
      margin-top: {{ button_mt | times: 0.75 | round: 0 }}px;
    }
  </style>

  {% if button_align_mobile == "center" %}
    <style>
     .collection-button-{{ section.id }} {
        margin-left: auto;
        margin-right: auto;
      } 
    </style>
  {% elsif button_align_mobile == "right" %}
    <style>
     .collection-button-{{ section.id }} {
        margin-left: auto;
      } 
    </style>
  {% endif %}

{% endunless %}

{% if button_row %}
  <style>
    @media(min-width: 1024px) {
      .collection-top-{{ section.id }} {
        display: flex;
        align-items: center;
        justify-content: space-between;
        gap: 20px;
        flex-direction: row;
      }

      .collection-button-{{ section.id }} {
        margin-top: 0px;
      }
    }
  </style>
{% endif %}

{% unless button_row %}
  <style>

    @media(min-width: 1024px) {
      .collection-heading-{{ section.id }} {
        text-align: {{ heading_align }} !important;
      }

      .collection-button-{{ section.id }} {
        margin-top: {{ button_mt }}px !important;
      }
    }
  </style>

  {% if button_align == "center" %}
    <style>
      @media(min-width: 1024px) {
       .collection-button-{{ section.id }} {
          margin-left: auto !important;
          margin-right: auto !important;
        } 
      }
    </style>
  {% elsif button_align == "right" %}
    <style>
      @media(min-width: 1024px) {
       .collection-button-{{ section.id }} {
          margin-left: auto !important;
          margin-right: 0px !important;
        } 
      }
    </style>
  {% else %}
    <style>
      @media(min-width: 1024px) {
       .collection-button-{{ section.id }} {
          margin-left: 0px !important;
          margin-right: 0px !important;
        } 
      }
    </style>
  {% endif %}

{% endunless %}

<div class="section-{{ section.id }} collection-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">

      <div class="collection-top-{{ section.id }}">
        {% if heading != blank %}
          <div class="collection-heading-{{ section.id }}">{{ heading }}</div>
        {% endif %}

        {% if button != blank %}
          <a href="{{ collection.url }}" class="collection-button-{{ section.id }}">{{ button }}</a>
        {% endif %}
