                    </div>
                  {% endif %}

                  {% if product.images.size > 1 and hover_image %}
                    {% for img in product.images %}
                      {% if forloop.index == 2 %}
                        <div class="collection-image-{{ section.id }} collection-second-image-{{ section.id }}">
                          <img src="{{ img | image_url }}" alt="{{ img.alt }}" {% if lazy %}lazy="loading"{% endif %}>
                        </div>
                      {% endif %}
                    {% endfor %}
