{%- style -%}

  {{  heading_font | font_face: font_display: 'swap' }}
  {{  tab_title_font | font_face: font_display: 'swap' }}
  {{  sub_title_font | font_face: font_display: 'swap' }}
  {{  title_font | font_face: font_display: 'swap' }}
  {{  price_font | font_face: font_display: 'swap' }}
  {{  sku_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .collection-heading-{{ section.id }} {
    text-align: {{ heading_align_mobile }};
  }

  .collection-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
  }

  .collection-tabs-titles-{{ section.id }} {
    display: flex;
    align-items: center;
    flex-wrap: wrap;
    justify-content: {{ tab_title_align_mobile }};
    gap: {{ tab_title_gap_mobile }}px;
    margin-top: {{ tab_title_mt_mobile }}px;
  }

  .collection-tabs-title-{{ section.id }} {
    margin: 0;
    font-size: {{ tab_title_size_mobile }}px;
    color: {{ tab_title_color }};
    line-height: {{ tab_title_height }}%;
    text-transform: unset;
    padding: {{ tab_title_padding_vertical_mobile }}px {{ tab_title_padding_horizontal_mobile }}px;
    border-radius: {{ tab_title_radius }}px;
    border: {{ tab_title_border_thickness }}px solid {{ tab_title_border_color }};
    background-color: transparent;
    background-color: {{ tab_title_bg_color }};
    transition: all 0.25s ease 0s;
    cursor: pointer;
    font-weight: 700;
  }

  .collection-tabs-title-{{ section.id }}:hover {
    transition: all 0.25s ease 0s;
    border: {{ tab_title_border_thickness }}px solid {{ tab_title_border_hover_color }};
    background-color: {{ tab_title_bg_hover_color }};
    color: {{ tab_title_hover_color }};
  }

  .collection-tabs-title-{{ section.id }}.active {
    transition: all 0.25s ease 0s;
    border: {{ tab_title_border_thickness }}px solid {{ tab_title_border_active_color }};
    background-color: {{ tab_title_bg_active_color }};
    color: {{ tab_title_active_color }};
  }

  .collection-tabs-{{ section.id }} {
    margin-top: {{ tab_title_mb | times: 0.75 | round: 0 }}px;
  }

  .collection-tab-{{ section.id }} {
    display: none;
  }

  .collection-tab-{{ section.id }}.active {
    display: grid;
  }

  .collection-tab-{{ section.id }} {
    grid-template-columns: repeat(1, 1fr);
    gap: {{ grid_gap_mobile }}px;
  }

  .collection-product-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 24px;
    padding: {{ product_padding_vertical_mobile }}px {{ product_padding_horizontal_mobile }}px;
    border-radius: {{ product_radius }}px;
    border: {{ product_border_thickness }}px solid {{ product_border_color }};
    background-color: {{ product_bg_color }};
    text-decoration: none;
  }

  .collection-product-{{ section.id }}:not(.collection-product-mobile-{{ section.id }}) {
    display: none;
  }

  .collection-image-{{ section.id }} {
    flex: 0 0 {{ image_size_mobile }}px;
    aspect-ratio: 1/1;
    border-radius: {{ image_radius }}px;
    overflow: hidden;
  }

  .collection-image-{{ section.id }} img,
  .collection-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: contain;
  }

  .collection-image-{{ section.id }} svg {
    background-color: #DADADA;
  }

  .collection-sku-{{ section.id }} {
    margin: 0;
    font-size: {{ sku_size_mobile }}px;
    color: {{ sku_color }};
    line-height: {{ sku_height }}%;
    text-transform: uppercase;
  }

  .collection-subtitle-{{ section.id }} {
    margin: 0;
    margin-top: {{ sub_title_mt | times: 0.75 | round: 0 }}px;
    font-size: {{ sub_title_size_mobile }}px;
    color: {{ sub_title_color }};
    line-height: {{ sub_title_height }}%;
    text-transform: uppercase;
  }

  .collection-title-{{ section.id }} {
    margin: 0;
    margin-top: {{ title_mt | times: 0.75 | round: 0 }}px;
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-transform: unset;
    font-weight: 700;
    transition: all 0.25s ease 0s;
  }

  .collection-product-{{ section.id }}:hover .collection-title-{{ section.id }} {
    color: {{ title_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .collection-price-{{ section.id }} {
    margin: 0;
    margin-left: auto;
    font-size: {{ price_size_mobile }}px;
    color: {{ price_color }};
    line-height: {{ price_height }}%;
    text-transform: unset;
    font-weight: 700;
  }

  @media(min-width: 768px) {
    .collection-tab-{{ section.id }} {
      grid-template-columns: repeat(2, 1fr);
    }
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .collection-heading-{{ section.id }} {
      text-align: {{ heading_align }};
    }

    .collection-heading-{{ section.id }} * {
      font-size: {{ heading_size | times: 0.75 }}px;
    }

    .collection-tabs-titles-{{ section.id }} {
      justify-content: {{ tab_title_align }};
      gap: {{ tab_title_gap }}px;
      margin-top: {{ tab_title_mt }}px;
    }

    .collection-tabs-title-{{ section.id }} {
      font-size: {{ tab_title_size }}px;
      padding: {{ tab_title_padding_vertical }}px {{ tab_title_padding_horizontal }}px;
    }

    .collection-tabs-{{ section.id }} {
      margin-top: {{ tab_title_mb }}px;
    }

    .collection-tab-{{ section.id }} {
      grid-template-columns: repeat({{ grid_row | times: 0.75 | round: 0 }}, 1fr);
      gap: {{ grid_gap }}px;
    }

    .collection-product-{{ section.id }} {
      padding: {{ product_padding_vertical }}px {{ product_padding_horizontal }}px;
    }

    .collection-product-{{ section.id }}:not(.collection-product-mobile-{{ section.id }}) {
      display: flex;
    }

    .collection-image-{{ section.id }} {
      flex: 0 0 {{ image_size }}px;
    }

    .collection-sku-{{ section.id }} {
      font-size: {{ sku_size }}px;
    }

    .collection-subtitle-{{ section.id }} {
      margin-top: {{ sub_title_mt }}px;
      font-size: {{ sub_title_size }}px;
    }

    .collection-title-{{ section.id }} {
      margin-top: {{ title_mt }}px;
      font-size: {{ title_size }}px;
    }

    .collection-price-{{ section.id }} {
      font-size: {{ price_size }}px;
    }
  }

  @media(min-width: 1200px) {
    .collection-tab-{{ section.id }} {
      grid-template-columns: repeat({{ grid_row | times: 0.75 | round: 0 }}, 1fr);
    }
  }

  @media(min-width: 1440px) {
    .collection-tab-{{ section.id }} {
      grid-template-columns: repeat({{ grid_row }}, 1fr);
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if heading_custom %}
  <style>
    .collection-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if tab_title_custom %}
  <style>
    .collection-tabs-title-{{ section.id }} {
      font-family: {{ tab_title_font.family }}, {{ tab_title_font.fallback_families }};
      font-weight: {{ tab_title_font.weight }};
      font-style: {{ tab_title_font.style }};
    }
  </style>
{% endif %}

{% if sku_custom %}
  <style>
    .collection-sku-{{ section.id }} {
      font-family: {{ sku_font.family }}, {{ sku_font.fallback_families }};
      font-weight: {{ sku_font.weight }};
      font-style: {{ sku_font.style }};
    }
  </style>
{% endif %}

{% if sub_title_custom %}
  <style>
    .collection-subtitle-{{ section.id }} {
      font-family: {{ sub_title_font.family }}, {{ sub_title_font.fallback_families }};
      font-weight: {{ sub_title_font.weight }};
      font-style: {{ sub_title_font.style }};
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .collection-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if price_custom %}
  <style>
    .collection-price-{{ section.id }} {
      font-family: {{ price_font.family }}, {{ price_font.fallback_families }};
      font-weight: {{ price_font.weight }};
      font-style: {{ price_font.style }};
    }
  </style>
{% endif %}

{% if product_shadow %}
  <style>
    .collection-product-{{ section.id }} {
      box-shadow: {{ product_shadow_color | hex_to_rgba: 0.15 }} 0px 2px 8px;
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} collection-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      {% if heading != blank %}
        <div class="collection-heading-{{ section.id }}">
          {{ heading }}
        </div>
      {% endif %}
      <div class="collection-tabs-titles-{{ section.id }}">
        {% if tab_all %}
          <button class="collection-tabs-title-{{ section.id }} active" id="{{ section.id }}">
            {{ tab_all_text }}
          </button>
        {% endif %}
        {% for block in section.blocks %}
          {% if block.settings.collection != blank %}
            <button class="collection-tabs-title-{{ section.id }} {% if tab_all == false and forloop.first %}active{% endif %}" id="{{ block.id }}">
              {% if block.settings.tab_title != blank %}
                {{ block.settings.tab_title }}
              {% else %}
                {{ block.settings.collection.title }}
              {% endif %}
            </button>
          {% endif %}
        {% endfor %}
      </div>
      <div class="collection-tabs-{{ section.id }}">
        {% if tab_all %}
          <div class="collection-tab-{{ section.id }} active" data-id="{{ section.id }}">
            {% if products != blank %}
              {% for product in products %}
                <a href="{{ product.url }}" class="collection-product-{{ section.id }} {% if forloop.index <= tab_all_show %}collection-product-mobile-{{ section.id }}{% endif %}">
                  {% if product.featured_image != blank %}
                    <div class="collection-image-{{ section.id }}">
                      <img src="{{ product.featured_image | image_url }}" alt="{{ product.featured_image.alt }}" {% if lazy %}loading="lazy"{% endif %}> 
                    </div>
                  {% endif %}
                  <div class="collection-info-{{ section.id }}">
                    {% if sku and product.selected_or_first_available_variant.sku != blank %}
                      <p class="collection-sku-{{ section.id }}">{{ product.selected_or_first_available_variant.sku }}</p>
                    {% endif %}
                    {% if sub_title and product.vendor != blank %}
                      <p class="collection-subtitle-{{ section.id }}">{{ product.vendor }}</p>
                    {% endif %}
                    <p class="collection-title-{{ section.id }}">{{ product.title }}</p>
                  </div>
                  <p class="collection-price-{{ section.id }}">{{ product.selected_or_first_available_variant.price | money }}</p>
                </a>
              {% endfor %}
            {% else %}
              {% for product in (1..12) %}
                <div class="collection-product-{{ section.id }}">
                  {% if product.featured_image != blank %}
                    <div class="collection-image-{{ section.id }}">
                      {{ 'image' | placeholder_svg_tag }}
                    </div>
                  {% endif %}
                  <div class="collection-info-{{ section.id }}">
                    {% if sku %}
                      <p class="collection-sku-{{ section.id }}">19678</p>
                    {% endif %}
                    {% if sub_title %}
                      <p class="collection-subtitle-{{ section.id }}">STOCKMART</p>
                    {% endif %}
                    <p class="collection-title-{{ section.id }}">Product Title</p>
                  </div>
                  <p class="collection-price-{{ section.id }}">209.95 $</p>
                </div>
              {% endfor %}
            {% endif %}
          </div>
        {% endif %}

        {% for block in section.blocks %}
          {% if block.settings.collection != blank %}
            <div class="collection-tab-{{ section.id }} {% if tab_all == false and forloop.first %}active{% endif %}" data-id="{{ block.id }}">
              {% if block.settings.collection_show_all %}

                {% for product in collections[block.settings.collection].products %}
                  <a href="{{ product.url }}" class="collection-product-{{ section.id }} {% if forloop.index <= block.settings.products_show %}collection-product-mobile-{{ section.id }}{% endif %}">
                    {% if product.featured_image != blank %}
                      <div class="collection-image-{{ section.id }}">
                        <img src="{{ product.featured_image | image_url }}" alt="{{ product.featured_image.alt }}" {% if lazy %}loading="lazy"{% endif %}> 
                      </div>
                    {% endif %}
                    <div class="collection-info-{{ section.id }}">
                      {% if sku and product.selected_or_first_available_variant.sku != blank %}
                        <p class="collection-sku-{{ section.id }}">{{ product.selected_or_first_available_variant.sku }}</p>
                      {% endif %}
                      {% if sub_title and product.vendor != blank %}
                        <p class="collection-subtitle-{{ section.id }}">{{ product.vendor }}</p>
                      {% endif %}
                      <p class="collection-title-{{ section.id }}">{{ product.title }}</p>
                    </div>
                    <p class="collection-price-{{ section.id }}">{{ product.selected_or_first_available_variant.price | money }}</p>
                  </a>
                {% endfor %}

              {% else %}

                {% for product in collections[block.settings.collection].products limit: block.settings.collection_show %}
                  <a href="{{ product.url }}" class="collection-product-{{ section.id }} {% if forloop.index <= block.settings.products_show %}collection-product-mobile-{{ section.id }}{% endif %}">
                    {% if product.featured_image != blank %}
                      <div class="collection-image-{{ section.id }}">
                        <img src="{{ product.featured_image | image_url }}" alt="{{ product.featured_image.alt }}" {% if lazy %}loading="lazy"{% endif %}> 
                      </div>
                    {% endif %}
                    <div class="collection-info-{{ section.id }}">
                      {% if sku and product.selected_or_first_available_variant.sku != blank %}
                        <p class="collection-sku-{{ section.id }}">{{ product.selected_or_first_available_variant.sku }}</p>
                      {% endif %}
                      {% if sub_title and product.vendor != blank %}
                        <p class="collection-subtitle-{{ section.id }}">{{ product.vendor }}</p>
                      {% endif %}
                      <p class="collection-title-{{ section.id }}">{{ product.title }}</p>
                    </div>
                    <p class="collection-price-{{ section.id }}">{{ product.selected_or_first_available_variant.price | money }}</p>
                  </a>
                {% endfor %}

              {% endif %}
            </div>
          {% endif %}
        {% endfor %}
      </div>
    </div>
</div>

<script>
  function initFeaturedCollection4() {
    if(document.querySelectorAll('.collection-tabs-title-{{ section.id }}')) {
      document.querySelectorAll('.collection-tabs-title-{{ section.id }}').forEach(el => {
        el.addEventListener('click', () => {
          document.querySelectorAll('.collection-tabs-title-{{ section.id }}').forEach(element => {
            element.classList.remove('active');
          })

          el.classList.add('active');

          if(document.querySelectorAll('.collection-tab-{{ section.id }}')) {
            document.querySelectorAll('.collection-tab-{{ section.id }}').forEach(element => {
              let data_id = element.getAttribute('data-id');
              element.classList.remove('active');
              if(data_id == el.id) {
                element.classList.add('active');
              }
            })
          }

        })
      })
    }
  }

  document.addEventListener('DOMContentLoaded', initFeaturedCollection4);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:unload', initFeaturedCollection4);
     document.addEventListener('shopify:section:load', initFeaturedCollection4);
  }
</script>
