{%- style -%}

  {{  heading_font | font_face: font_display: 'swap' }}
  {{  title_font | font_face: font_display: 'swap' }}
  {{  price_font | font_face: font_display: 'swap' }}
  {{  vendor_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .featured-body-{{ section.id }} {
    height: 100%;
    position: relative;
    display: flex;
    flex-direction: column;
    justify-content: {{ content_vertical_position_mobile }};
    align-items: {{ content_horizontal_position_mobile }};
    text-align: {{ text_align_mobile }};
  }

  .featured-heading-{{ section.id }} {
    text-align: {{ text_align_mobile }};
  }

  .featured-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
  }

  .featured-block-{{ section.id }} {
    margin-top: {{ block_mt_mobile }}px;
    display: flex;
    flex-direction: row;
    padding: {{ block_padding_vertical_mobile }}px {{ block_padding_horizontal_mobile }}px;
    background-color: {{ block_bg_color }};
    border: {{ block_border_thickness }}px solid {{ block_border_color }};
    border-radius: {{ block_radius }}px;
    overflow: hidden;
    text-decoration: none;
    max-width: fit-content;
  }

  .featured-top-{{ section.id }} {
    order: 2;
    display: flex;
    flex-direction: column;
    justify-content: center;
  }

  .featured-title-{{ section.id }} {
    margin: 0;
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-transform: unset;
    font-weight: 700;
    transition: all 0.25s ease 0s;
    max-width: 250px;
    text-align: left;
    text-decoration: none;
  }

  .featured-vendor-{{ section.id }} {
    margin: 0;
    margin-top: {{ vendor_mt_mobile }}px;
    font-size: {{ vendor_size_mobile }}px;
    color: {{ vendor_color }};
    line-height: {{ vendor_height }}%;
    text-transform: unset;
    transition: all 0.25s ease 0s;
    text-align: left;
  }

  .featured-product-price-{{ section.id }} {
    margin-top: {{ price_mt_mobile }}px;
    display: flex;
    align-items: center;
    gap: 5px;
    z-index: 1;
    position: relative;
  }

  .featured-product-price-{{ section.id }} * {
    margin: 0;
    font-size: {{ price_size_mobile }}px;
    color: {{ price_color }};
    line-height: {{ price_height }}%;
    text-transform: unset;
    font-style: normal;
  }

  .featured-product-compare-price-{{ section.id }} {
    opacity: 0.8;
    font-size: {{ price_size | times: 0.8 }}px;
    color: {{ price_original_color }};
    text-decoration: line-through !important;
  }

  .featured-image-{{ section.id }} {
    flex: 0 0 100px;
    order: 1;
    overflow: hidden;
    border-radius: {{ image_radius }}px;
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
    position: relative;
    margin-right: 20px;
  }

  .featured-image-{{ section.id }} img,
  .featured-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .featured-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .featured-button-{{ section.id }} {
    display: none;
    align-items: center;
    justify-content: center;
    gap: 10px;
    width: 100%;
    margin: 0;
    margin-top: {{ button_mt_mobile }}px;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    border-radius: {{ button_radius }}px;
    transition: all 1s ease 0s;
  }

  .featured-button-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .featured-button-{{ section.id }} svg {
    width: 14px;
    height: 14px;
  }

  .featured-button-{{ section.id }} svg path {
    fill: {{ button_color }};
    transition: all 0.25s ease 0s;
  }

  .featured-button-{{ section.id }}:hover svg path {
    fill: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .featured-mobile-button-{{ section.id }} {
    display: flex;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .featured-body-{{ section.id }} {
      justify-content: {{ content_vertical_position }};
      align-items: {{ content_horizontal_position }};
      text-align: {{ text_align }};
    }

    .featured-heading-{{ section.id }} {
      text-align: {{ text_align }};
    }

    .featured-heading-{{ section.id }} * {
      font-size: {{ heading_size | times: 0.75 }}px;
    }

    .featured-block-{{ section.id }} {
      margin-top: {{ block_mt }}px;
      flex-direction: column;
      padding: {{ block_padding_vertical }}px {{ block_padding_horizontal }}px;
    }

    .featured-top-{{ section.id }} {
      order: 1;
      flex-direction: row;
      gap: 20px;
      align-items: start;
      justify-content: space-between;
    }

    .featured-image-{{ section.id }} {
      flex: auto;
      margin-top: {{ image_mt }}px;
      margin-right: 0px;
      order: 2;
      max-width: 390px;
    }

    .featured-title-{{ section.id }} {
      font-size: {{ title_size }}px;
    }

    .featured-vendor-{{ section.id }} {
      margin-top: {{ vendor_mt }}px;
      font-size: {{ vendor_size }}px;
    }

    .featured-product-price-{{ section.id }} {
      margin-top: 0px;
    }

    .featured-product-price-{{ section.id }} * {
      font-size: {{ price_size }}px;
    }

    .featured-button-{{ section.id }} {
      display: flex;
      order: 3;
      margin-top: {{ button_mt }}px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
      position: relative;
      top: 150%;
      z-index: 2;
    }

    .featured-block-{{ section.id }}:hover .featured-button-{{ section.id }} {
      top: 0;
      transition: all 0.5s ease 0s;
    }

    .featured-mobile-button-{{ section.id }} {
      display: none;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if section_height == "adapt_first" %}
  <style>
    .featured-{{ section.id }} {
      height: 100vh;
    }
  </style>
{% else %}
  <style>
    .featured-{{ section.id }} {
      height: {{ section_fixed_height_mobile }}px;
    }

    @media(min-width: 768px) {
      .featured-{{ section.id }} {
        height: {{ section_fixed_height }}px;
      }
    }
  </style>
{% endif %}

{% if bg_image != blank %}
  <style>
    .featured-body-{{ section.id }} {
      background-image: url('{{ bg_image | image_url }}');
      background-position: center;
      background-size: cover;
      background-repeat: no-repeat;
    }
  </style>
{% endif %}

{% if bg_image_mobile != blank %}
  <style>
    @media(max-width: 1023px) {
      .featured-body-{{ section.id }} {
        background-image: url('{{ bg_image_mobile | image_url }}');
        background-position: center;
        background-size: cover;
        background-repeat: no-repeat;
      }
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
    .featured-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .featured-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if vendor_custom %}
  <style>
    .featured-vendor-{{ section.id }} {
      font-family: {{ vendor_font.family }}, {{ vendor_font.fallback_families }};
      font-weight: {{ vendor_font.weight }};
      font-style: {{ vendor_font.style }};
    }
  </style>
{% endif %}

{% if price_custom %}
  <style>
    .featured-product-price-{{ section.id }} * {
      font-family: {{ price_font.family }}, {{ price_font.fallback_families }};
      font-weight: {{ price_font.weight }};
      font-style: {{ price_font.style }};
    }
  </style>
{% endif %}

{% if image_ratio_mobile == "portrait" %}
  <style>
    .featured-image-{{ section.id }} img,
    .featured-image-{{ section.id }} svg {
     aspect-ratio: 9.6/14; 
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
    .featured-image-{{ section.id }} img,
    .featured-image-{{ section.id }} svg{
     aspect-ratio: 12/9; 
    }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .featured-image-{{ section.id }} img,
    .featured-image-{{ section.id }} svg {
     aspect-ratio: 12/12; 
    }
  </style>
{% else%}
  <style>
    .featured-image-{{ section.id }} img,
    .featured-image-{{ section.id }} svg {
     aspect-ratio: auto; 
    }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
     .featured-image-{{ section.id }} img,
      .featured-image-{{ section.id }} svg {
       aspect-ratio: 9.6/14; 
      } 
    }
  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .featured-image-{{ section.id }} img,
      .featured-image-{{ section.id }} svg {
       aspect-ratio: 12/9; 
      }
    }
  </style>
{% elsif image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .featured-image-{{ section.id }} img,
      .featured-image-{{ section.id }} svg {
       aspect-ratio: 12/12; 
      }
    }
  </style>
{% else%}
  <style>
    @media(min-width: 1024px) {
      .featured-image-{{ section.id }} img,
      .featured-image-{{ section.id }} svg {
       aspect-ratio: auto; 
      }
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .featured-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if button_style == "non_outline" or button_style == "non_outline_arrow" %}
  <style>
    .featured-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
    }

    .featured-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
    }
  </style>
{% elsif button_style == "outline" or button_style == "outline_arrow" %}
  <style>
    .featured-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_color }};
    }

    .featured-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    }
  </style>
{% endif %}

