<div class="section-{{ section.id }} features-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="features-grid-{{ section.id }}">

        <div class="features-content-{{ section.id }}">

          {% if heading != blank %}
            <div class="features-heading-{{ section.id }}">{{ heading }}</div>
          {% endif %}

          <div class="features-items-{{ section.id }}">
            {% for block in section.blocks %}
              <div class="features-item-{{ section.id }}">
                {% unless hide_icon %}
                  <div class="features-icon-{{ section.id }}">
                    {% if block.settings.icon != blank %}
                      <img src="{{ block.settings.icon | image_url }}" alt="{{ block.settings.icon.alt }}" {% if lazy %}lazy="loading"{% endif %}>
                    {% else %}
                      {% case forloop.index %}
                        {% when 1 %}
                          {{ svg_example_1 }}
                        {% when 2 %}
                          {{ svg_example_2 }}
                        {% when 3 %}
                          {{ svg_example_3 }}
                        {% when 4 %}
                          {{ svg_example_4 }}
                        {% else %}
                          {{ svg_example_1 }}
                        {% endcase %}
                    {% endif %}
                  </div>
                {% endunless %}
                <div class="features-item-content-{{ section.id }}">
                  {% if block.settings.title != blank %}
                    <div class="features-title-{{ section.id }}">{{ block.settings.title }}</div>
                  {% endif %}
                  {% if block.settings.text != blank %}
                    <div class="features-text-{{ section.id }}">{{ block.settings.text }}</div>
                  {% endif %}
                </div>
              </div>
            {% endfor %}
          </div>

        </div>

        {% unless hide_image %}
          <div class="features-image-{{ section.id }}">
            {% if image != blank %}
              <img src="{{ image | image_url }}" alt="{{ image.alt }}" {% if lazy %}lazy="loading"{% endif %}>
            {% else %}
              {{ 'image' | placeholder_svg_tag }}
            {% endif %}
          </div>
        {% endunless %}

      </div>
    </div>
</div>
