{% capture svg_example_3 %}
  <svg class='icon icon--medium icon--type-return' stroke-width='1' aria-hidden='true' focusable='false' role='presentation' xmlns='http://www.w3.org/2000/svg' viewBox='0 0 16 16'><path fill="#FFFFFF" d='M9 .5a.5.5 0 000 1h1a4.5 4.5 0 110 9H2.2l3.15-3.15a.5.5 0 10-.7-.7l-4 4a.5.5 0 000 .7l4 4a.5.5 0 00.7-.7L2.21 11.5H10a5.5 5.5 0 100-11H9z'></path></svg>
{% endcapture %}

<div class="section-{{ section.id }} features-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      {% if heading != blank %}
        <div class="features-heading-{{ section.id }}">{{ heading }}</div>
      {% endif %}
      <div class="features-items-{{ section.id }}">
        {% for block in section.blocks %}
          <div class="features-item-{{ section.id }}">
            {% unless hide_icon %}
              <div class="features-image-{{ section.id }}">
                {% if block.settings.icon != blank %}
                  <img src="{{ block.settings.icon | image_url }}" alt="{{ block.setitngs.icon.alt }}" {% if lazy %}lazy="loading"{% endif %}>
                {% else %}
                  {% case forloop.index %}
                    {% when 1 %}
                      {{ svg_example_1 }}
                    {% when 2 %}
                      {{ svg_example_2 }}
                    {% when 3 %}
                      {{ svg_example_3 }}
                    {% else %}
                      {{ svg_example_1 }}
                    {% endcase %}
                {% endif %}
              </div>
            {% endunless %}
            {% if block.settings.title != blank %}
              <p class="features-title-{{ section.id }}">{{ block.settings.title }}</p>
            {% endif %}
          </div>
        {% endfor %}
      </div>
    </div>
</div>
