liquid_code:
{%- liquid
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign width = section.settings.width
  assign content_width = section.settings.content_width
  assign section_radius = section.settings.section_radius
  assign background_style = section.settings.background_style

  assign vertical_offset = section.settings.vertical_offset

  assign tabs_align_mobile = section.settings.tabs_align_mobile
  assign tabs_align = section.settings.tabs_align
  assign tabs_gap_mobile = section.settings.tabs_gap_mobile
  assign tabs_gap = section.settings.tabs_gap
  assign tabs_titles = section.settings.tabs_titles

  assign item_size = section.settings.item_size
  assign item_size_mobile = section.settings.item_size_mobile
  assign item_color = section.settings.item_color
  assign item_hover_color = section.settings.item_hover_color
  assign item_active_color = section.settings.item_active_color
  assign item_custom = section.settings.item_custom
  assign item_font = section.settings.item_font
  assign item_height = section.settings.item_height
  assign item_padding_vertical = section.settings.item_padding_vertical
  assign item_padding_horizontal = section.settings.item_padding_horizontal
  assign item_radius = section.settings.item_radius
  assign item_border_thickness = section.settings.item_border_thickness
  assign item_border_color = section.settings.item_border_color
  assign item_border_hover_color = section.settings.item_border_hover_color
  assign item_border_active_color = section.settings.item_border_active_color
  assign item_bg_color = section.settings.item_bg_color
  assign item_bg_hover_color = section.settings.item_bg_hover_color
  assign item_bg_active_color = section.settings.item_bg_active_color

  assign liquid_glass = section.settings.liquid_glass
  assign liquid_glass_hover = section.settings.liquid_glass_hover

  assign enable_slide_effect = section.settings.enable_slide_effect
-%}

{%- style -%}
  {{  item_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border: solid {{ border_color }} {{ border_thickness }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    display: flex;
    align-items: center;
    justify-content: space-between;
    gap: 10px;
    {% if liquid_glass %}
      position: relative;
    {% endif %}
  }

  .navigation-tabs-toggle-{{ section.id }} {
    display: flex;
    flex-direction: column;
    justify-content: center;

    top: 15px;
    left: 15px;
    border-radius: 100%;
    {% if background_style == 'solid' %}
      background-color: {{ background_color }};
    {% else %}
      background-image: {{ background_gradient }};
    {% endif %}
    gap: 4px;
    border: none;
    cursor: pointer;
    padding: 12px;
    aspect-ratio: 1/1;
    z-index: 9999;
    position: relative;
  }

  .navigation-tabs-toggle-{{ section.id }} span {
    width: 24px;
    height: 3px;
    background-color: {{ item_color }};
    transition: all 0.3s ease;
    border-radius: 2px;
    z-index: 3;
  }

  .navigation-tabs-toggle-{{ section.id }}.active span:nth-child(1) {
    transform: rotate(45deg) translate(4px, 4px);;
  }

  .navigation-tabs-toggle-{{ section.id }}.active span:nth-child(2) {
    opacity: 0;
  }

  .navigation-tabs-toggle-{{ section.id }}.active span:nth-child(3) {
    transform: rotate(-45deg) translate(6px, -6px);
  }

  .navigation-tabs-modal-{{ section.id }} {
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    {% if background_style == 'solid' %}
    background-color: {{ background_color }};
    {% else %}
      background-image: {{ background_gradient }};
    {% endif %}
    z-index: 9998;
    display: none;
    opacity: 0;
    transition: opacity 0.3s ease;
  }

  .navigation-tabs-modal-{{ section.id }}.active {
    display: flex;
    opacity: 1;
  }

  {% if liquid_glass %}
    .navigation-tabs-toggle-{{ section.id }} {
      background: transparent !important;
      border: {{ border_thickness }}px solid {{ border_color }} !important;
      position: relative;
    }

    .navigation-tabs-toggle-{{ section.id }} .glass-filter-{{ section.id }},
    .navigation-tabs-toggle-{{ section.id }} .glass-overlay-{{ section.id }},
    .navigation-tabs-toggle-{{ section.id }} .glass-specular-{{ section.id }}  {
      display: block;
      position: absolute;
      inset: 0;
      border-radius: inherit;
      pointer-events: none;
    }

    .glass-filter-{{ section.id }} {
      display: block;
      -webkit-backdrop-filter: blur(8px);
      backdrop-filter: blur(8px);
      -webkit-filter: url(#lensFilter-{{ section.id }});
      filter: url(#lensFilter-{{ section.id }});
      will-change: filter;
      -webkit-transform: translateZ(0);
      transform: translateZ(0);
      z-index: 0;
    }

    .glass-overlay-{{ section.id }} {
      display: block;
      background: rgba(0, 0, 0, 0.15);
      -webkit-filter: saturate(1.2) brightness(1.1);
      filter: saturate(1.2) brightness(1.1);
      z-index: 1;
      transition: background .25s ease;
    }

    .glass-specular-{{ section.id }} {
      display: block;
      box-shadow: inset 1px 1px 0 rgba(255,255,255,.4),
                  inset 0 0 6px rgba(255,255,255,.2);
      z-index: 2;
      transition: box-shadow .25s ease;
    }

    @media(min-width: 1024px) {
    .section-{{ section.id }} {
      background: transparent !important;
      border: {{ border_thickness }}px solid {{ border_color }} !important;
      position: relative;
    }

    .section-{{ section.id }} .glass-filter-{{ section.id }},
    .section-{{ section.id }} .glass-overlay-{{ section.id }},
    .section-{{ section.id }} .glass-specular-{{ section.id }} {
      display: block;
      position: absolute;
      inset: 0;
      border-radius: inherit;
      pointer-events: none;
    }

    .section-{{ section.id }}-settings {
      position: relative;
      z-index: 3;
    }

    }
    {% if liquid_glass_hover %}
      .section-{{ section.id }},
      .navigation-tabs-toggle-{{ section.id }} {
        transition: transform .35s cubic-bezier(0.175, 0.885, 0.32, 2.2);
        will-change: transform;
      }

      .section-{{ section.id }}:hover,
      .navigation-tabs-toggle-{{ section.id }}:hover { transform: scale(1.02); }

      .section-{{ section.id }}:hover .glass-overlay-{{ section.id }},
      .navigation-tabs-toggle-{{ section.id }}:hover .glass-overlay-{{ section.id }} { background: rgba(0,0,0,.25); }

      .section-{{ section.id }}:hover .glass-specular-{{ section.id }},
      .navigation-tabs-toggle-{{ section.id }}:hover .glass-specular-{{ section.id }} {
        box-shadow: inset 1px 1px 0 rgba(255,255,255,.55),
                    inset 0 0 8px rgba(255,255,255,.3);
      }
    {% endif %}
  {% endif %}

  .navigation-tabs-body-{{ section.id }} {
    display: flex;
    flex-direction: column;
    align-items: center;
    width: 100%;
    gap: {{ tabs_gap_mobile }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
    margin-left: -{{ padding_horizontal_mobile }}rem;
    margin-right: -{{ padding_horizontal_mobile }}rem;
    position: relative;
    justify-content: center;
    height: 100%;
  }

  .navigation-tabs-body-{{ section.id }}::-webkit-scrollbar {
    display: none;
  }

  .navigation-tabs-item-{{ section.id }} {
    margin-top: 0px;
    width: 100%;
    max-width: fit-content;
    min-width: max-content;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: {{ item_size_mobile }}px;
    font-weight: 700;
    color: {{ item_color }};
    line-height: {{ item_height }}%;
    text-align: center;
    text-transform: unset;
    gap: 10px;
    text-decoration: none;
    padding: 6px;
    border-radius: {{ item_radius }}px;
    border: 0px;
    border: none;
    background-color: transparent;
    transition: all 0.3s ease 0s;
    box-shadow: none;
    cursor: pointer;
    position: relative;
    background-color: {{ item_bg_color }};
    border: {{ item_border_thickness }}px solid {{ item_border_color }};
    {% if enable_slide_effect %}
      background-color: transparent !important;
      border: 0 !important;
    {% endif %}
    margin-bottom: 1rem;
  }

  {% if enable_slide_effect %}
    .navigation-tabs-item-{{ section.id }} {
      transition: color 0.3s ease;
    }
    .navigation-tabs-item-{{ section.id }}:hover {
      background-color: transparent !important;
      border-color: transparent !important;
    }
    .navigation-tabs-item-{{ section.id }}.active {
      background-color: transparent !important;
      border-color: transparent !important;
    }

    .slide-indicator-{{ section.id }} {
      display: none !important;
      position: absolute;
      top: 0;
      left: 0;
      height: 100%;
      background-color: {{ item_bg_active_color }};
      border-radius: {{ item_radius }}px;
      transition: transform 0.3s cubic-bezier(0.4, 0, 0.2, 1), width 0.3s cubic-bezier(0.4, 0, 0.2, 1);
      z-index: 0;
    }
  {% endif %}

  .navigation-tabs-item-{{ section.id }}:hover {
    color: {{ item_hover_color }};
    background-color: {{ item_bg_hover_color }};
    border: {{ item_border_thickness }}px solid {{ item_border_hover_color }};
    transition: all 0.3s ease 0s;
  }

  .navigation-tabs-item-{{ section.id }}.active {
    color: {{ item_active_color }};
    background-color: {{ item_bg_active_color }};
    border: {{ item_border_thickness }}px solid {{ item_border_active_color }};
    transition: all 0.3s ease 0s;
  }

  .navigation-tabs-item-{{ section.id }} svg {
    opacity: 0;
    width: {{ item_size_mobile | minus: 3 }}px;
    height: {{ item_size_mobile | minus: 3 }}px;
    transition: all 0.3s ease 0s;
  }

  .navigation-tabs-item-{{ section.id }}:hover svg {
    opacity: 1;
    transition: all 0.3s ease 0s;
  }

  .navigation-tabs-item-{{ section.id }}.active svg {
    opacity: 1;
    transition: all 0.3s ease 0s;
  }

  .navigation-tabs-item-{{ section.id }}:hover svg path {
    transition: all 0.3s ease 0s;
    fill: {{ item_hover_color }};
  }

  .navigation-tabs-item-{{ section.id }}.active svg path {
    transition: all 0.3s ease 0s;
    fill: {{ item_active_color }};
  }

  @media(min-width: 1024px) {



    .navigation-tabs-toggle-{{ section.id }} {
      display: none;
    }

    .navigation-tabs-modal-{{ section.id }} {
      position: relative;
      width: auto;
      height: auto;
      background: none;
      display: block !important;
      opacity: 1 !important;
      z-index: auto;
    }

    .section-{{ section.id }} {
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
      {% if liquid_glass == false %}
        -webkit-backdrop-filter: blur(8px);
        backdrop-filter: blur(8px);
      {% endif %}
      {% if liquid_glass == false %}
        {% if background_style == 'solid' %}
          background-color:{{ background_color | hex_to_rgba: 0.5 }};
        {% else %}
          background-image: {{ background_gradient }};
          {% endif %}
      {% endif %}
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}px;
      padding-right: {{ padding_horizontal }}px;
    }

    .navigation-tabs-body-{{ section.id }} {
      gap: {{ tabs_gap }}px;
      padding-left: 0rem;
      padding-right: 0rem;
      margin-left: 0rem;
      margin-right: 0rem;
      flex-direction: row;
      height: auto;
    }

    .navigation-tabs-item-{{ section.id }} {
      padding: 0 {{ item_padding_horizontal }}px;
      padding-top: {{ item_padding_vertical }}px;
      padding-bottom: calc({{ item_padding_vertical }}px + {{ item_border_thickness }}px);
      font-size: {{ item_size }}px;
      {% if enable_slide_effect == false %}
        padding-bottom: calc({{ item_padding_vertical }}px + {{ item_border_thickness }}px);
      {% else %}
        padding-bottom: {{ item_padding_vertical }}px;
      {% endif %}
      margin-bottom: 0rem;
    }

    .navigation-tabs-item-{{ section.id }} svg {
      width: {{ item_size | minus: 3 }}px;
      height: {{ item_size | minus: 3 }}px;
    }

    .slide-indicator-{{ section.id }} {
      display: block !important;
    }
  }
{%- endstyle -%}

{% if width == 'custom' %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% elsif width == 'page' %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: clamp(100px, var(--page-content-width, var(--page-width)), 100%);
    }
  </style>
{% endif %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if item_custom %}
  <style>
    .navigation-tabs-item-{{ section.id }} {
      font-family: {{ item_font.family }}, {{ item_font.fallback_families }};
      font-weight: {{ item_font.weight }};
      font-style: {{ item_font.style }};
    }
  </style>
{% endif %}

{% if tabs_align_mobile == 'center' %}
  <style>
    .navigation-tabs-body-{{ section.id }} {
      justify-content: center;
    }
  </style>
{% elsif tabs_align_mobile == 'right' %}
  <style>
    .navigation-tabs-body-{{ section.id }} {
      justify-content: end;
    }
  </style>
{% endif %}

{% if tabs_align == 'center' %}
  <style>
    @media(min-width: 1024px) {
      .navigation-tabs-body-{{ section.id }} {
        justify-content: center;
      }
    }
  </style>
{% elsif tabs_align == 'right' %}
  <style>
    @media(min-width: 1024px) {
      .navigation-tabs-body-{{ section.id }} {
        justify-content: end;
      }
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
      .navigation-tabs-body-{{ section.id }} {
        justify-content: start;
      }
    }
  </style>
{% endif %}

{% if liquid_glass %}
  <svg xmlns="http://www.w3.org/2000/svg" style="display: none;">
    <filter id="lensFilter-{{ section.id }}" x="0%" y="0%" width="100%" height="100%" filterUnits="objectBoundingBox">
      <feComponentTransfer in="SourceAlpha" result="alpha">
        <feFuncA type="identity" />
      </feComponentTransfer>
      <feGaussianBlur in="alpha" stdDeviation="50" result="blur" />
      <feDisplacementMap in="SourceGraphic" in2="blur" scale="50" xChannelSelector="A" yChannelSelector="A" />
    </filter>
  </svg>
{% endif %}

<div class="section-{{ section.id }} navigation-tabs-{{ section.id }}">
    {% if liquid_glass %}
      <div class="glass-filter-{{ section.id }}"></div>
      <div class="glass-overlay-{{ section.id }}"></div>
      <div class="glass-specular-{{ section.id }}"></div>
    {% endif %}
    
  <div class="section-{{ section.id }}-settings">    
    <button class="navigation-tabs-toggle-{{ section.id }}" aria-expanded="false" aria-controls="modal-{{ section.id }}">
        <span></span>
        <span></span>
        <span></span>

        {% if liquid_glass %}
          <div class="glass-filter-{{ section.id }}"></div>
          <div class="glass-overlay-{{ section.id }}"></div>
          <div class="glass-specular-{{ section.id }}"></div>
        {% endif %}
    </button>
    
    <div class="navigation-tabs-modal-{{ section.id }}" id="modal-{{ section.id }}">      
      <div class="navigation-tabs-body-{{ section.id }}">
        {% if enable_slide_effect %}
          <div class="slide-indicator-{{ section.id }}" style="opacity: 0;"></div>
        {% endif %}
      </div>      
    </div>    
  </div>
</div>

<script>
function initNavigationTabs() {
  const enableSlideEffect = {{ section.settings.enable_slide_effect | json }};
  let sections = document.querySelectorAll('.shopify-section'); 
  let body = document.querySelector('.navigation-tabs-body-{{ section.id }}');
  const indicator = enableSlideEffect ? document.querySelector('.slide-indicator-{{ section.id }}') : null;
  const toggle = document.querySelector('.navigation-tabs-toggle-{{ section.id }}');
  const modal = document.querySelector('.navigation-tabs-modal-{{ section.id }}');
  let titles = "{{ tabs_titles }}";
  let titlesArray = titles.split(",");
  let belowThisSection = false;
  let count = 0;
  let isHovering = false;
  let hoverTimeout = null;
  
  if (toggle && modal) {
    toggle.addEventListener('click', () => {
      const isActive = toggle.classList.contains('active');
      
      if (isActive) {
        toggle.classList.remove('active');
        modal.classList.remove('active');
        toggle.setAttribute('aria-expanded', 'false');
        document.body.style.overflow = '';
      } else {
        toggle.classList.add('active');
        modal.classList.add('active');
        toggle.setAttribute('aria-expanded', 'true');
        document.body.style.overflow = 'hidden';
      }
    });
  }
  
  if (sections) { 
    sections.forEach((el, i) => {
      let itemId = el.id;
      if (![...el.children].some(child => child.tagName === 'TEMPLATE')) { 
    
        if(itemId != "shopify-section-header" && !itemId.includes("footer") && itemId != "shopify-section-announcement-bar") {
          if(belowThisSection) {
            let anchorText = titlesArray[count] || "Title";
            let anchor = `<a href="#${itemId}" class="navigation-tabs-item-{{ section.id }}">
                ${anchorText}</a>`;
            body.insertAdjacentHTML('beforeend', anchor);  
            count++;
          }
          if(itemId == "shopify-section-{{ section.id }}") {
            belowThisSection = true;
          }
        }
      } 
    }); 
  }

  const positionIndicator = (target) => {
    if (!enableSlideEffect || !indicator || !target) return;

    const bodyRect = body.getBoundingClientRect();
    const targetRect = target.getBoundingClientRect();

    if (targetRect.width === 0 || targetRect.height === 0) return;

    const leftPosition = targetRect.left - bodyRect.left + body.scrollLeft;
    const targetWidth = targetRect.width;

    requestAnimationFrame(() => {
      indicator.style.opacity = '1';
      indicator.style.width = `${targetWidth}px`;
      indicator.style.transform = `translateX(${leftPosition}px)`;
    });
  };

  const updateActiveState = (target) => {
    let items = document.querySelectorAll('.navigation-tabs-item-{{ section.id }}');
    items.forEach(el => {
      el.classList.remove('active');
      el.style.transform = 'scale(1)';
    });
    target.classList.add('active');
    target.style.transform = 'scale(1)';

    if (!isHovering) {
      positionIndicator(target);
    }
  };

  const handleResize = () => {
    if (hoverTimeout) {
      clearTimeout(hoverTimeout);
    }
    
    hoverTimeout = setTimeout(() => {
      const activeItem = document.querySelector('.navigation-tabs-item-{{ section.id }}.active');
      if (activeItem && !isHovering) {
        positionIndicator(activeItem);
      }
    }, 100);
  };

  setTimeout(() => {
    let items = document.querySelectorAll('.navigation-tabs-item-{{ section.id }}');
  
    if (items && items.length > 0) { 
      updateActiveState(items[0]);

      items.forEach(el => {
    
        if (enableSlideEffect) {
          el.addEventListener('mouseenter', () => {
            isHovering = true;

            if (hoverTimeout) {
              clearTimeout(hoverTimeout);
              hoverTimeout = null;
            }
            
            positionIndicator(el);
          });
          
          el.addEventListener('mouseleave', () => {
            isHovering = false;
            
            if (hoverTimeout) {
              clearTimeout(hoverTimeout);
            }
            
            hoverTimeout = setTimeout(() => {
              const activeItem = document.querySelector('.navigation-tabs-item-{{ section.id }}.active');
              if (activeItem) {
                positionIndicator(activeItem);
              }
            }, 150);
          });
        }
    
        el.addEventListener('click', (event) => { 
          event.preventDefault();
          
          isHovering = false;
          
          updateActiveState(el);
          
          if (window.innerWidth < 1024) {
            toggle.classList.remove('active');
            modal.classList.remove('active');
            toggle.setAttribute('aria-expanded', 'false');
            document.body.style.overflow = '';
          }
          
          const targetId = el.getAttribute('href'); 
          if (targetId) { 
            const targetElement = document.querySelector(targetId); 
            if (targetElement) { 
              const elementPosition = targetElement.getBoundingClientRect().top + window.scrollY;
              const offsetPosition = elementPosition - 100;
        
              window.scrollTo({ 
                top: offsetPosition, 
                behavior: 'smooth'
              }); 
            } 
          } 
        });
      });
    }

    if(sections) {
      sections.forEach(section => {
        if(section.id.includes('{{ section.id }}')) {
          if (window.innerWidth < 1024) {
            section.style.position = 'fixed';
            section.style.left = '0';
            section.style.top = '0';
            section.style.zIndex = '5';
          } else {
            section.style.position = 'fixed';
            section.style.left = '50%';
            section.style.transform = 'translateX(-50%)';
            section.style.bottom = '{{ vertical_offset }}px';
            section.style.zIndex = '5';
            section.style.borderRadius = '{{ section_radius }}px';
            {% if liquid_glass == false %}
              {% if background_style == 'solid' %}
                section.style.backgroundColor = '{{ background_color | hex_to_rgba: 0.5 }}';
              {% else %}
                section.style.backgroundImage = '{{ background_gradient }}';
              {% endif %}
            {% endif %}
          }
        }
      })
    }

    if (enableSlideEffect) {
      // Используем debounced resize handler
      window.addEventListener('resize', handleResize);
      
      // Инициализируем позицию индикатора
      const initialActive = document.querySelector('.navigation-tabs-item-{{ section.id }}.active');
      if(initialActive) {
        // Небольшая задержка для корректного расчёта размеров
        requestAnimationFrame(() => {
          positionIndicator(initialActive);
        });
      }
    }

  }, 400);  
}

document.addEventListener('DOMContentLoaded', initNavigationTabs);
</script>
