liquid_code:
{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius 

  assign menu_gap = section.settings.menu_gap
  assign menu_gap_mobile = section.settings.menu_gap_mobile
  assign menu_mt = section.settings.menu_mt
  assign menu_mt_mobile = section.settings.menu_mt_mobile
  assign menu_align = section.settings.menu_align
  assign menu_align_mobile = section.settings.menu_align_mobile

  assign menu_heading_size = section.settings.menu_heading_size
  assign menu_heading_size_mobile = section.settings.menu_heading_size_mobile
  assign menu_heading_color = section.settings.menu_heading_color
  assign menu_heading_custom = section.settings.menu_heading_custom
  assign menu_heading_font = section.settings.menu_heading_font
  assign menu_heading_height = section.settings.menu_heading_height

  assign link_size = section.settings.link_size
  assign link_size_mobile = section.settings.link_size_mobile
  assign link_color = section.settings.link_color
  assign link_hover_color = section.settings.link_hover_color
  assign link_custom = section.settings.link_custom
  assign link_font = section.settings.link_font
  assign link_height = section.settings.link_height

  assign bottom_mt = section.settings.bottom_mt
  assign bottom_mt_mobile = section.settings.bottom_mt_mobile

  assign loading_attribute = 'eager'
  if lazy
    assign loading_attribute = 'lazy'
  endif
-%}

{%- style -%}
  {{ menu_heading_font | font_face: font_display: 'swap' }}
  {{ link_font | font_face: font_display: 'swap' }}


  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .footer-top-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
  }


  .footer-top-menu-{{ section.id }} {
    flex: auto;
    width: calc(50% - 40px);
  }

  .footer-menu-heading-{{ section.id }} {
    margin: 0;
    text-align: {{ menu_align_mobile }};
    font-size: {{ menu_heading_size_mobile }}px;
    color: {{ menu_heading_color }};
    line-height: {{ menu_heading_height }}%;
    text-transform: unset;
    text-decoration: none;
    font-weight: 400;
  }

  .footer-top-menu-body-{{ section.id }} {
    display: flex;
    flex-wrap: wrap;
    gap: 40px;
    margin-top: 20px;
  }

  .footer-menu-list-{{ section.id }} {
    padding: 0px;
    margin: 0px;
    display: flex;
    flex-direction: column;
    gap: {{ menu_gap_mobile }}px;
    margin-top: {{ menu_mt_mobile }}px;
    list-style: none;
    flex-wrap: wrap;
  }

  .footer-menu-item-{{ section.id }} {
    line-height: 100%;
  }

  .footer-menu-link-{{ section.id }} {
    margin: 0;
    display: block;
    text-align: {{ menu_align_mobile }};
    font-size: {{ link_size_mobile }}px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
    font-weight: 400;
  }

  .footer-bottom-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
    align-items: end;
    margin-top: {{ bottom_mt_mobile }}px;
    gap: 20px;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .footer-top-{{ section.id }} {
      grid-template-columns: 1fr 1fr;
    }

    .footer-top-menu-body-{{ section.id }} {
      flex-wrap: nowrap;
      gap: 20px;
      margin-top: 0;
    }

    .footer-top-menu-{{ section.id }} {
      flex: 1 1 0;
      width: 100%;
    }

    .footer-menu-heading-{{ section.id }} {  
      font-size: {{ menu_heading_size }}px;
      text-align: {{ menu_align }};
    } 

    .footer-menu-link-{{ section.id }} {
      font-size: {{ link_size }}px;
      text-align: {{ menu_align }};
    } 

    .footer-menu-link-{{ section.id }}:hover {
      color: {{ link_hover_color }};
      transition: all 0.25s ease 0s;
    }

    .footer-menu-heading-{{ section.id }} {
      text-align: {{ menu_align }};
      font-size: {{ menu_heading_size }}px;
    }

    .footer-menu-list-{{ section.id }} {
      gap: {{ menu_gap }}px;
      margin-top: {{ menu_mt }}px;
    }

    .footer-menu-link-{{ section.id }} {    
      text-align: {{ menu_align }};
      font-size: {{ link_size }}px;
    }

    .footer-bottom-{{ section.id }} {
      grid-template-columns: 1fr 1fr;
      margin-top: {{ bottom_mt }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if menu_heading_custom %}
  <style>
    .footer-menu-heading-{{ section.id }} {
      font-family: {{ menu_heading_font.family }}, {{ menu_heading_font.fallback_families }};
      font-weight: {{ menu_heading_font.weight }};
      font-style: {{ menu_heading_font.style }};
    }
  </style>
{% endif %}

{% if link_custom %}
  <style>
    .footer-menu-link-{{ section.id }} {
      font-family: {{ link_font.family }}, {{ link_font.fallback_families }};
      font-weight: {{ link_font.weight }};
      font-style: {{ link_font.style }};
    }
  </style>
{% endif %}

{% if menu_align_mobile == "center" %}
  <style>
    .footer-menu-heading-{{ section.id }} {
      justify-content: center;
    }
  </style>
{% elsif menu_align_mobile == "right" %}
  <style>
    .footer-menu-heading-{{ section.id }} {
      justify-content: end;
    }
  </style>
{% endif %}

{% if menu_align == "center" %}
  <style>
    @media(min-width: 1024px) {
      .footer-menu-heading-{{ section.id }} {
        justify-content: center;
      }
    }
  </style>
{% elsif menu_align == "right" %}
  <style>
    @media(min-width: 1024px) {
      .footer-menu-heading-{{ section.id }} {
        justify-content: end;
      }
    }
  </style> 
{% else %}
  <style>
    @media(min-width: 1024px) {
      .footer-menu-heading-{{ section.id }} {
        justify-content: start;
      }
    }
  </style>
{% endif %}

{% for block in section.blocks %}
  {% case block.type %}
    {% when 'testimonial' %}  
      {%-liquid 
      assign content_horizontal_align = ""
      if block.settings.content_align == "center"
        assign content_horizontal_align = "center"
      elsif block.settings.content_align == "right"
        assign content_horizontal_align = "end"
      else
        assign content_horizontal_align = "start"
      endif

      assign content_horizontal_align_mobile = ""
      if block.settings.content_align_mobile == "center"
        assign content_horizontal_align_mobile = "center"
      elsif block.settings.content_align_mobile == "right"
        assign content_horizontal_align_mobile = "end"
      else
        assign content_horizontal_align_mobile = "start"
      endif
      -%}
      <style>
        {{ block.settings.heading_font | font_face: font_display: 'swap' }}
        {{ block.settings.title_font | font_face: font_display: 'swap' }}    
        {{ block.settings.text_font | font_face: font_display: 'swap' }}  
        {{ block.settings.rating_font | font_face: font_display: 'swap' }}             

        .footer-top-content-{{ section.id }} {
          padding-bottom: {{ block.settings.content_padding_bottom_mobile }}px;
          border-bottom: {{ block.settings.content_border_thickness }}px solid {{ block.settings.content_border_color }};
        }

        .footer-top-menu-body-{{ section.id }} {
          margin-top: {{ block.settings.content_padding_bottom_mobile }}px;
        }

        .footer-heading-{{ block.id }} {
          margin-top: {{ block.settings.heading_mt_mobile }}px;
          text-align: {{ block.settings.content_align_mobile }};
        }

        .footer-heading-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.heading_size_mobile }}px;
          color: {{ block.settings.testimonial_color }};
          line-height: {{ block.settings.heading_height }}%;
          text-transform: unset;
          text-decoration: none;
          word-break: break-word;
        }

        .footer-testimonial-{{ block.id }} {
          margin-top: {{ block.settings.testimonial_mt_mobile }}px;
          display: flex;
          align-items: center;
          justify-content: {{ content_horizontal_align_mobile }};
          gap: 12px;
        }

        .footer-testimonial-image-{{ block.id }} {
          width: 20%;
          border: {{ block.settings.image_border_thickness }}px solid {{ block.settings.image_border_color }};
          border-radius: {{ block.settings.image_radius }}px;
          overflow: hidden;          
        }

        .footer-testimonial-image-{{ block.id }} img,
        .footer-testimonial-image-{{ block.id }} svg {
          display: block;
          width: 100%;
          height: 100%;
          object-fit: cover;
        }

        .footer-testimonial-image-{{ block.id }} svg {
          background-color: #AFAFAF;
        }

        .footer-testimonial-title-{{ block.id }} {
          margin: 0;
          text-align: {{ block.settings.content_align_mobile }};
          font-size: {{ block.settings.title_size_mobile }}px;
          color: {{ block.settings.title_color }};
          line-height: {{ block.settings.title_height }}%;
          text-transform: unset;
          text-decoration: none;
          word-break: break-word;
          font-weight: 700;
        }

        .footer-testimonial-text-{{ block.id }} {
          margin-top: {{ block.settings.text_mt_mobile }}px;
          text-align: {{ block.settings.content_align_mobile }};
        }

        .footer-testimonial-text-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.text_size_mobile }}px;
          color: {{ block.settings.text_color }};
          line-height: {{ block.settings.text_height }}%;
          text-transform: unset;
          text-decoration: none;
          word-break: break-word;
        }        

        .footer-rating-wrap-{{ block.id }} {
          display: flex;
          align-items: center;
          margin-top: {{ block.settings.rating_mt_mobile }}px;
          gap: 10px;
          justify-content: {{ content_horizontal_align_mobile }};
        }

        .footer-rating-text-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.rating_size_mobile }}px;
          color: {{ block.settings.rating_color }};
          line-height: {{ block.settings.rating_height }}%;
          text-transform: unset;
          text-decoration: none;
          word-break: break-word;
        }

        .footer-stars-{{ block.id }} {
          display: flex;
          align-items: center;
          gap: 2px;
          max-width: fit-content;
        }

        .footer-stars-{{ block.id }} svg,
        .footer-stars-{{ block.id }} img {
          display: block;
          width: {{ block.settings.stars_size_mobile }}px;
          height: {{ block.settings.stars_size_mobile }}px;
          object-fit: cover;
        }

        @media(min-width: 1024px) {
          .footer-top-menu-body-{{ section.id }} {
            margin-top: {{ block.settings.content_padding_bottom }}px;
          }

          .footer-top-content-{{ section.id }} {
            padding-bottom: {{ block.settings.content_padding_bottom }}px;
          }


          .footer-heading-{{ block.id }} {
            margin-top: {{ block.settings.heading_mt }}px;
            text-align: {{ block.settings.content_align }};
          }

          .footer-heading-{{ block.id }} * {
            font-size: {{ block.settings.heading_size }}px;
          }

          .footer-testimonial-{{ block.id }} {
            margin-top: {{ block.settings.testimonial_mt }}px;
            justify-content: {{ content_horizontal_align }};
            gap: 20px;
          }

          .footer-testimonial-image-{{ block.id }} {
            width: 15%;     
          }

          .footer-testimonial-title-{{ block.id }} {
            text-align: {{ block.settings.content_align }};
            font-size: {{ block.settings.title_size }}px;
          }

          .footer-testimonial-text-{{ block.id }} {
            text-align: {{ block.settings.content_align }};
            margin-top: {{ block.settings.text_mt }}px;
          }

          .footer-testimonial-text-{{ block.id }} * {
            font-size: {{ block.settings.text_size }}px;
          }

          .footer-rating-wrap-{{ block.id }} {
            justify-content: {{ content_horizontal_align }};
            margin-top: {{ block.settings.rating_mt }}px;
          }

          .footer-rating-text-{{ block.id }} * {
            font-size: {{ block.settings.rating_size }}px;
          }

          .footer-stars-{{ block.id }} svg,
          .footer-stars-{{ block.id }} img {
            width: {{ block.settings.stars_size }}px;
            height: {{ block.settings.stars_size }}px;
          }
        }
      </style>

      {% if block.settings.title_custom %}
        <style>
          .footer-testimonial-title-{{ block.id }} {
            font-family: {{ block.settings.title_font.family }}, {{ block.settings.title_font.fallback_families }};
            font-weight: {{ block.settings.title_font.weight }};
            font-style: {{ block.settings.title_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.rating_custom %}
        <style>
          .footer-rating-text-{{ block.id }} {
            font-family: {{ block.settings.rating_font.family }}, {{ block.settings.rating_font.fallback_families }};
            font-weight: {{ block.settings.rating_font.weight }};
            font-style: {{ block.settings.rating_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.heading_custom %}
        <style>
          .footer-heading-{{ block.id }} * {
            font-family: {{ block.settings.heading_font.family }}, {{ block.settings.heading_font.fallback_families }};
            font-weight: {{ block.settings.heading_font.weight }};
            font-style: {{ block.settings.heading_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.text_custom %}
        <style>
          .footer-testimonial-text-{{ block.id }} * {
            font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
            font-weight: {{ block.settings.text_font.weight }};
            font-style: {{ block.settings.text_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.content_align_mobile == 'right' %}
        <style>
          .footer-testimonial-{{ block.id }} {
            flex-direction: row-reverse;
          }              
        </style>
      {% endif %}

      {% if block.settings.content_align == 'right' %}
        <style>
          @media(min-width: 1024px) {
            .footer-testimonial-{{ block.id }} {
              flex-direction: row-reverse;
            }
          }         
        </style>
      {% endif %}

      {% if block.settings.image_ratio_mobile == "portrait" %}
        <style>
          .footer-testimonial-image-{{ block.id }} {
            aspect-ratio: 9.6/13;
          }
        </style>
      {% elsif block.settings.image_ratio_mobile == "landscape" %}
        <style>
        .footer-testimonial-image-{{ block.id }} {
          aspect-ratio: 12/8;
        }
        </style>
      {% elsif block.settings.image_ratio_mobile == "square" %}
        <style>
          .footer-testimonial-image-{{ block.id }} {
            aspect-ratio: 12/12;
          }
        </style>
      {% else %}
        <style>
          .footer-testimonial-image-{{ block.id }} {
            aspect-ratio: auto;
          }
        </style>
      {% endif %}

      {% if block.settings.image_ratio == "portrait" %}
        <style>
          @media(min-width: 1024px) {
            .footer-testimonial-image-{{ block.id }} {
              aspect-ratio: 9.6/13;
            }
          }

        </style>
      {% elsif block.settings.image_ratio == "landscape" %}
        <style>
          @media(min-width: 1024px) {
            .footer-testimonial-image-{{ block.id }} {
              aspect-ratio: 12/8;
            }
          }  
        </style>
      {% elsif block.settings.image_ratio == "square" %}
        <style>
          @media(min-width: 1024px) {
            .footer-testimonial-image-{{ block.id }} {
              aspect-ratio: 12/12;
            }
          }    
        </style>
      {% else %}
        <style>
          @media(min-width: 1024px) {
            .footer-testimonial-image-{{ block.id }} {
              aspect-ratio: auto;
            }
          }
        </style>
      {% endif %}

    {% when 'newsletter' %}
      {%-liquid 
      assign text_horizontal_align = ""
      if block.settings.text_align == "center"
        assign text_horizontal_align = "center"
      elsif block.settings.text_align == "right"
        assign text_horizontal_align = "end"
      else
        assign text_horizontal_align = "start"
      endif

      assign text_horizontal_align_mobile = ""
      if block.settings.text_align_mobile == "center"
        assign text_horizontal_align_mobile = "center"
      elsif block.settings.text_align_mobile == "right"
        assign text_horizontal_align_mobile = "end"
      else
        assign text_horizontal_align_mobile = "start"
      endif
      -%}

      <style>
        {{  block.settings.text_font | font_face: font_display: 'swap' }}
        {{  block.settings.input_font | font_face: font_display: 'swap' }}
        {{  block.settings.submit_font | font_face: font_display: 'swap' }}
        {{  block.settings.thanks_text_font | font_face: font_display: 'swap' }}

        .footer-newsletter-text-{{ block.id }} {
          text-align: {{ block.settings.text_align_mobile }};
          max-width: 100%;
          width: 100%;
        }

        .footer-newsletter-text-{{ block.id }} * {
          margin: 0;       
          font-size: {{ block.settings.text_size_mobile }}px;
          color: {{ block.settings.text_color }};
          line-height: {{ block.settings.text_height }}%;
          text-transform: unset;
          text-decoration: none;
          word-break: break-word;
          font-weight: 700;
        }

        .footer-newsletter-row-{{ block.id }} {
          margin-top: 20px;
          position: relative;
          display: flex;
          align-items: stretch;
          gap: 16px;
          flex-direction: column;
          max-width: 100%;
          margin-top: {{ block.settings.form_mt_mobile }}px;
        }

        .footer-newsletter-input-{{ block.id }} {
          width: 100%;
          margin: 0;
          box-shadow: none;
          border: 0px;
          padding: {{ block.settings.input_padding_vertical_mobile }}px {{ block.settings.input_padding_horizontal_mobile }}px;
          font-size: {{ block.settings.input_size_mobile }}px;
          color: {{ block.settings.input_color }};
          line-height: {{ block.settings.input_height }}%;
          text-transform: unset;
          border-radius: {{ block.settings.input_radius }}px;
          background-color: transparent;
          background-color: {{ block.settings.input_bg_color }};
          font-family: 'Montserrat';
          border: {{ block.settings.input_border_thickness }}px solid {{ block.settings.input_border_color }};
        }

        .footer-newsletter-input-{{ block.id }}:focus {
          border: {{ block.settings.input_border_thickness }}px solid {{ block.settings.input_border_color }};
        }

        .footer-newsletter-input-{{ block.id }}:focus-visible {
          outline: .2rem solid {{ block.settings.input_border_focus_color }};
          outline-offset: 0rem; 
        }

        .footer-newsletter-input-{{ block.id }}::placeholder {
          color: {{ block.settings.input_placeholder_color }};
        }

        .footer-newsletter-submit-{{ block.id }} {
          margin-top: 0px;
          position: static !important;
          width: 100%;
          max-width: 100%;
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: {{ block.settings.submit_size_mobile }}px;
          color: {{ block.settings.submit_color }};
          line-height: {{ block.settings.submit_height }}%;
          text-align: center;
          text-transform: unset;
          text-decoration: none;
          padding: {{ block.settings.submit_padding_vertical_mobile }}px {{ block.settings.submit_padding_horizontal_mobile }}px;
          border-radius: {{ block.settings.submit_radius }}px;
          border: 0px;
          border: {{ block.settings.submit_border_thickness }}px solid {{ block.settings.submit_border_color }} ;
          background-color: transparent;
          background-color: {{ block.settings.submit_bg_color }};
          transition: all 0.25s ease 0s;
          box-shadow: none;
          cursor: pointer;
           font-family: 'Montserrat';
        }

        .footer-newsletter-submit-{{ block.id }}:hover {
          color: {{ block.settings.submit_hover_color }};
          border: {{ block.settings.submit_border_thickness }}px solid {{ block.settings.submit_border_hover_color }};
          background-color: {{ block.settings.submit_bg_hover_color }};
          transition: all 0.25s ease 0s;
        }

       .footer-newsletter-submit-{{ block.id }}[disabled="true"] {
         opacity: 0.6;
       }

       .footer-newsletter-checkbox-{{ block.id }} {
         opacity: 0;
         z-index: -100;
         position: absolute;
       }

       .footer-newsletter-label-{{ block.id }} {
         position: relative;
         display: inline-flex;
         align-items: center;
         gap: 10px;
         margin-top: {{ block.settings.privacy_mt_mobile }}px;
         cursor: pointer;
       }

       .footer-newsletter-label-{{ block.id }} .checkbox-view {
         content: '';
         display: inline-block;
         width: 18px;
         flex-shrink: 0;
         height: 18px;
         padding: 2px;
         border-radius: 4px;
         border: 1px solid {{ block.settings.privacy_checkbox_color }};
         transition: all 0.25s ease 0s;
       }

       .footer-newsletter-label-{{ block.id }} .checkbox-icon {
          width: 12px;
          opacity: 0;
          transition: .3s;
        }

       .footer-newsletter-label-{{ block.id }} input:checked + .checkbox-view {
          background-color: {{ block.settings.privacy_checkbox_active_color }};
          background-color: {{ block.settings.privacy_checkbox_active_color }};
          transition: all 0.25s ease 0s;
        }

