      <div class="footer-top-{{ section.id }}">
        <div class="footer-newsletter-body-{{ section.id }}">          
          {% for block in section.blocks %}
            {% if block.type == 'newsletter' %}
              <div class="footer-newsletter-form-{{ section.id }}">
                {% if block.settings.text != blank %}
                  <div class="footer-newsletter-text-{{ block.id }}">{{ block.settings.text }}</div>
                {% endif %}
                {% form 'customer', class: 'footer-newsletter-form' %}
                  <input type="hidden" name="contact[tags]" value="newsletter">

                  <div class="footer-newsletter-row-{{ block.id }}">
                    <input
                      type="text"
                      name="contact[text]"
                      class="footer-newsletter-input-{{ block.id }}"
                      value="{{ form.text }}"
                      aria-required="true"
                      autocorrect="off"
                      autocapitalize="off"
                      autocomplete="text"
                      {% if form.errors %}
                        autofocus
                        aria-invalid="true"
                        aria-describedby="Newsletter-error--{{ block.id }}"
                      {% elsif form.posted_successfully? %}
                        aria-describedby="Newsletter-success--{{ block.id }}"
                      {% endif %}
                      placeholder="{{ block.settings.placeholder_name }}"
                      required
                    >
                    <input
                      type="email"
                      name="contact[email]"
                      class="footer-newsletter-input-{{ block.id }}"
                      value="{{ form.email }}"
                      aria-required="true"
                      autocorrect="off"
                      autocapitalize="off"
                      autocomplete="email"
                      {% if form.errors %}
                        autofocus
                        aria-invalid="true"
                        aria-describedby="Newsletter-error--{{ block.id }}"
                      {% elsif form.posted_successfully? %}
                        aria-describedby="Newsletter-success--{{ block.id }}"
                      {% endif %}
                       placeholder="{{ block.settings.placeholder_mail }}"
                      required
                    >
                    <input type="submit" class="footer-newsletter-submit-{{ block.id }} footer-newsletter-submit-{{ section.id }}" name="commit" value="{{ block.settings.submit }}">
                  </div>

                  {% if block.settings.thanks_text != blank %}
                    <p class="footer-newsletter-thanks-text-{{ block.id }} footer-newsletter-thanks-text-{{ section.id }}">{{ block.settings.thanks_text }}</p>
                  {% endif %}

                {% endform %}
              </div>
            {% endif %}
          {% endfor %}

