{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius 

  assign layout_gap = section.settings.layout_gap
  assign layout_gap_mobile = section.settings.layout_gap_mobile

  assign menu_gap = section.settings.menu_gap
  assign menu_gap_mobile = section.settings.menu_gap_mobile
  assign menu_mt = section.settings.menu_mt
  assign menu_mt_mobile = section.settings.menu_mt_mobile
  assign menu_align = section.settings.menu_align
  assign menu_align_mobile = section.settings.menu_align_mobile

  assign menu_heading_size = section.settings.menu_heading_size
  assign menu_heading_size_mobile = section.settings.menu_heading_size_mobile
  assign menu_heading_color = section.settings.menu_heading_color
  assign menu_heading_custom = section.settings.menu_heading_custom
  assign menu_heading_font = section.settings.menu_heading_font
  assign menu_heading_height = section.settings.menu_heading_height
  assign menu_heading_mt = section.settings.menu_heading_mt
  assign menu_heading_mt_mobile = section.settings.menu_heading_mt_mobile

  assign link_size = section.settings.link_size
  assign link_size_mobile = section.settings.link_size_mobile
  assign link_color = section.settings.link_color
  assign link_hover_color = section.settings.link_hover_color
  assign link_custom = section.settings.link_custom
  assign link_font = section.settings.link_font
  assign link_height = section.settings.link_height

  assign newsletter_mt = section.settings.newsletter_mt
  assign newsletter_mt_mobile = section.settings.newsletter_mt_mobile

  assign loading_attribute = 'eager'
  if lazy
    assign loading_attribute = 'lazy'
  endif
-%}

{%- style -%}
  {{ menu_heading_font | font_face: font_display: 'swap' }}
  {{ link_font | font_face: font_display: 'swap' }}


  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .footer-body-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
    gap: {{ layout_gap_mobile }}px;
  }

  .footer-top-menu-body-{{ section.id }} {
    display: flex;
    flex-wrap: wrap;
    gap: 20px;
  }

  .footer-top-menu-{{ section.id }} {
    width: calc(50% - 10px);
  }

  .footer-newsletter-body-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
    align-items: center;
    gap: 40px;
    margin-top: {{ newsletter_mt_mobile }}px;
  } 

  .footer-menu-heading-{{ section.id }} {
    margin: 0;
    margin-top: {{ menu_heading_mt_mobile }}px;
    text-align: {{ menu_align_mobile }};
    font-size: {{ menu_heading_size_mobile }}px;
    color: {{ menu_heading_color }};
    line-height: {{ menu_heading_height }}%;
    text-transform: unset;
    text-decoration: none;
    font-weight: 700;
  }

  .footer-menu-list-{{ section.id }} {
    padding: 0px;
    margin: 0px;
    display: flex;
    flex-direction: column;
    gap: {{ menu_gap_mobile }}px;
    margin-top: {{ menu_mt_mobile }}px;
    list-style: none;
  }

  .footer-bottom-menu-list-{{ section.id }} {
    flex-direction: row;
    justify-content: center;
  }

  .footer-menu-item-{{ section.id }} {
    padding: 0;
    margin: 0;
    list-style: none;
    line-height: 100%;
  }

  .footer-menu-link-{{ section.id }} {
    margin: 0;
    display: block;
    text-align: {{ menu_align_mobile }};
    font-size: {{ link_size_mobile }}px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
  }

  .footer-bottom-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
    align-items: end;
    gap: 20px;
  }

  .footer-logo-{{ section.id }} {
    text-decoration: none;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .footer-body-{{ section.id }} {
      grid-template-columns: 35% 1fr;
      gap: {{ layout_gap }}px;
    }

    .footer-top-{{ section.id }} {
      grid-template-columns: 1fr 1fr;
    }

    .footer-top-menu-body-{{ section.id }} {
      display: flex;
      flex-wrap: nowrap;
      gap: 20px;
    }

    .footer-top-menu-{{ section.id }} {
      width: calc(25% - 10px);
    }

    .footer-bottom-menu-list-{{ section.id }} {
      justify-content: start;
    }

    .footer-newsletter-body-{{ section.id }} {
      grid-template-columns: 1fr 1fr;
      align-items: start;
      margin-top: {{ newsletter_mt }}px;
    } 

    .footer-menu-heading-{{ section.id }} {  
      margin-top: {{ menu_heading_mt }}px;
      font-size: {{ menu_heading_size }}px;
      text-align: {{ menu_align }};
    } 

    .footer-menu-link-{{ section.id }} {
      font-size: {{ link_size }}px;
      text-align: {{ menu_align }};
    } 

    .footer-menu-link-{{ section.id }}:hover {
      color: {{ link_hover_color }};
      transition: all 0.25s ease 0s;
    }

    .footer-menu-heading-{{ section.id }} {
      text-align: {{ menu_align }};
      font-size: {{ menu_heading_size }}px;
    }

    .footer-menu-list-{{ section.id }} {
      gap: {{ menu_gap }}px;
      margin-top: {{ menu_mt }}px;
    }

    .footer-menu-link-{{ section.id }} {    
      text-align: {{ menu_align }};
      font-size: {{ link_size }}px;
    }

    .footer-bottom-{{ section.id }} {
      grid-template-columns: 1fr 1fr;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if menu_heading_custom %}
  <style>
    .footer-menu-heading-{{ section.id }} {
      font-family: {{ menu_heading_font.family }}, {{ menu_heading_font.fallback_families }};
      font-weight: {{ menu_heading_font.weight }};
      font-style: {{ menu_heading_font.style }};
    }
  </style>
{% endif %}

{% if link_custom %}
  <style>
    .footer-menu-link-{{ section.id }} {
      font-family: {{ link_font.family }}, {{ link_font.fallback_families }};
      font-weight: {{ link_font.weight }};
      font-style: {{ link_font.style }};
    }
  </style>
{% endif %}



{% if menu_align_mobile == "center" %}
  <style>
    .footer-menu-heading-{{ section.id }} {
      justify-content: center;
    }
  </style>
{% elsif menu_align_mobile == "right" %}
  <style>
    .footer-menu-heading-{{ section.id }} {
      justify-content: end;
    }
  </style>
{% endif %}

{% if menu_align == "center" %}
  <style>
    @media(min-width: 1024px) {
      .footer-menu-heading-{{ section.id }} {
        justify-content: center;
      }
    }
  </style>
{% elsif menu_align == "right" %}
  <style>
    @media(min-width: 1024px) {
      .footer-menu-heading-{{ section.id }} {
        justify-content: end;
      }
    }
  </style> 
{% else %}
  <style>
    @media(min-width: 1024px) {
      .footer-menu-heading-{{ section.id }} {
        justify-content: start;
      }
    }
  </style>
{% endif %}

{% for block in section.blocks %}
  {% case block.type %}    
    {% when 'disclaimer' %}
      <style> 
        {{  block.settings.disclaimer_font | font_face: font_display: 'swap' }}

        .footer-disclaimer-{{ block.id }} {
          margin-top: {{ block.settings.disclaimer_mt_mobile }}px;
          text-align: {{ block.settings.disclaimer_align_mobile }};
        }

        .footer-disclaimer-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.disclaimer_size_mobile }}px;
          color: {{ block.settings.disclaimer_color }};
          line-height: {{ block.settings.disclaimer_height }}%;
          text-transform: unset;
          text-decoration: none;
        }

        @media(min-width: 1024px) {
          .footer-disclaimer-{{ block.id }} {
            margin-top: {{ block.settings.disclaimer_mt }}px;
            text-align: {{ block.settings.disclaimer_align }};
          }

          .footer-disclaimer-{{ block.id }} * {
            font-size: {{ block.settings.disclaimer_size }}px;
          }
        }
      </style>

      {% if block.settings.disclaimer_custom != blank  %}
        <style>
          .footer-disclaimer-{{ block.id }} * {
            font-family: {{ block.settings.disclaimer_font.family }}, {{ block.settings.disclaimer_font.fallback_families }};
            font-weight: {{ block.settings.disclaimer_font.weight }};
            font-style: {{ block.settings.disclaimer_font.style }};
          }
        </style>
      {% endif %}
    {% when 'newsletter' %}
      <style>
        {{  block.settings.text_font | font_face: font_display: 'swap' }}
        {{  block.settings.input_font | font_face: font_display: 'swap' }}
        {{  block.settings.submit_font | font_face: font_display: 'swap' }}
        {{  block.settings.thanks_text_font | font_face: font_display: 'swap' }}

        .footer-newsletter-heading-{{ block.id }} {
          text-align: {{ block.settings.text_align_mobile }};
          max-width: 100%;
          width: 100%;
        }

        .footer-newsletter-heading-{{ block.id }} * {
          margin: 0;       
          font-size: {{ block.settings.text_size_mobile }}px;
          color: {{ block.settings.text_color }};
          line-height: {{ block.settings.text_height }}%;
          text-transform: unset;
          text-decoration: none;
          word-break: break-word;
          font-weight: 700;
        }

        .footer-newsletter-row-{{ block.id }} {
          margin-top: {{ block.settings.form_mt_mobile }}px;
          position: relative;
          display: flex;
          align-items: stretch;
          gap: 16px;
          flex-direction: column;
          max-width: 100%;          
        }

        .footer-newsletter-input-{{ block.id }} {
          width: 100%;
          margin: 0;
          box-shadow: none;
          border: 0px;
          padding: {{ block.settings.input_padding_vertical_mobile }}px {{ block.settings.input_padding_horizontal_mobile }}px;
          font-size: {{ block.settings.input_size_mobile }}px;
          color: {{ block.settings.input_color }};
          line-height: {{ block.settings.input_height }}%;
          text-transform: unset;
          border-radius: {{ block.settings.input_radius }}px;
          background-color: transparent;
          background-color: {{ block.settings.input_bg_color }};
          font-family: 'Montserrat';
          border: {{ block.settings.input_border_thickness }}px solid {{ block.settings.input_border_color }};
        }

        .footer-newsletter-input-{{ block.id }}:focus {
          border: {{ block.settings.input_border_thickness }}px solid {{ block.settings.input_border_color }};
        }

        .footer-newsletter-input-{{ block.id }}:focus-visible {
          outline: .2rem solid {{ block.settings.input_border_focus_color }};
          outline-offset: .3rem; 
        }

        .footer-newsletter-input-{{ block.id }}::placeholder {
          color: {{ block.settings.input_placeholder_color }};
        }

        .footer-newsletter-submit-{{ block.id }} {
          padding: 0;
          border: 0;
          position: absolute;
          top: 50%;
          transform: translateY(-50%);
          right: 15px;
          height: 12px;
          background: transparent;
          cursor: pointer;
        }

        .footer-newsletter-submit-{{ block.id }} svg {
          display: block;
          width: 100%;
          height: 100%;
          object-fit: cover;
        }

        .footer-newsletter-submit-{{ block.id }} svg path {
          fill: {{ block.settings.arrow_color }}
        }

       .footer-newsletter-checkbox-{{ block.id }} {
         opacity: 0;
         z-index: -100;
         position: absolute;
       }

       .footer-newsletter-label-{{ block.id }} {
         position: relative;
         display: inline-flex;
         align-items: center;
         gap: 10px;
         margin-top: {{ block.settings.privacy_mt_mobile }}px;
         cursor: pointer;
       }

       .footer-newsletter-label-{{ block.id }} .checkbox-view {
         content: '';
         display: inline-block;
         width: 18px;
         flex-shrink: 0;
         height: 18px;
         padding: 2px;
         border-radius: 4px;
         border: 1px solid {{ block.settings.privacy_checkbox_color }};
         transition: all 0.25s ease 0s;
       }

       .footer-newsletter-label-{{ block.id }} .checkbox-icon {
          width: 12px;
          opacity: 0;
          transition: .3s;
        }

       .footer-newsletter-label-{{ block.id }} input:checked + .checkbox-view {
          background-color: {{ block.settings.privacy_checkbox_active_color }};
          background-color: {{ block.settings.privacy_checkbox_active_color }};
          transition: all 0.25s ease 0s;
        }

        .footer-newsletter-label-{{ block.id }} input:checked + .checkbox-view .checkbox-icon {
          opacity: 1;
        }

       .footer-newsletter-label-{{ block.id }} * {
          margin: 0px;
          font-size: {{ block.settings.privacy_size_mobile }}px;
          color: {{ block.settings.privacy_color }};
          line-height: {{ block.settings.privacy_height }}%;
          text-transform: unset;
       }

       .footer-newsletter-thanks-text-{{ block.id }} {
          display: none;
          margin: 0px;
          text-align: {{ block.settings.thanks_text_align_mobile }};
          margin-top: {{ block.settings.thanks_text_mt_mobile }}px;
          font-size: {{ block.settings.thanks_text_size_mobile }}px;
          color: {{ block.settings.thanks_text_color }};
          line-height: {{ block.settings.thanks_text_height }}%;
          text-transform: unset;
       }

       .footer-newsletter-thanks-text-{{ block.id }}.active {
         display: block;
       }

        @media(min-width: 1024px) {
          .footer-newsletter-heading-{{ block.id }} {
            max-width: 325px;
            text-align: {{ block.settings.text_align }};
          }

          .footer-newsletter-heading-{{ block.id }} * {
            font-size: {{ block.settings.text_size }}px;
          }

          .footer-newsletter-row-{{ block.id }} {
            max-width: {{ block.settings.input_width }}px;
            margin-top: {{ block.settings.form_mt }}px;
          }

          .footer-newsletter-input-{{ block.id }} {
            padding: {{ block.settings.input_padding_vertical }}px {{ block.settings.input_padding_horizontal }}px;
            font-size: {{ block.settings.input_size }}px;
          }

          .footer-newsletter-label-{{ block.id }} {
             margin-top: {{ block.settings.privacy_mt }}px;
           }

           .footer-newsletter-label-{{ block.id }} * {
              font-size: {{ block.settings.privacy_size }}px;
           }

           .footer-newsletter-thanks-text-{{ block.id }} {
             text-align: {{ block.settings.thanks_text_align }};
             margin-top: {{ block.settings.thanks_text_mt }}px;
             font-size: {{ block.settings.thanks_text_size }}px;
           }
        }
      </style>

      {% if block.settings.text_custom %}
        <style>
          .footer-newsletter-heading-{{ block.id }} * {
            font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
            font-weight: {{ block.settings.text_font.weight }};
            font-style: {{ block.settings.text_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.input_custom %}
        <style>
          .footer-newsletter-input-{{ block.id }} {
            font-family: {{ block.settings.input_font.family }}, {{ block.settings.input_font.fallback_families }};
            font-weight: {{ block.settings.input_font.weight }};
            font-style: {{ block.settings.input_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.thanks_text_custom %}
        <style>
          .footer-newsletter-thanks-text-{{ block.id }} {
            font-family: {{ block.settings.thanks_text_font.family }}, {{ block.settings.thanks_text_font.fallback_families }};
            font-weight: {{ block.settings.thanks_text_font.weight }};
            font-style: {{ block.settings.thanks_text_font.style }};
          }
        </style>
      {% endif %}

    {% when "socials" %}
      {%-liquid
          assign social_list_horizontal_align = ""
          if block.settings.social_list_align == "center"
            assign social_list_horizontal_align = "center"
          elsif block.settings.social_list_align == "right"
            assign social_list_horizontal_align = "end"
          else
            assign social_list_horizontal_align = "start"
          endif

          assign social_list_horizontal_align_mobile = ""
          if block.settings.social_list_align_mobile == "center"
            assign social_list_horizontal_align_mobile = "center"
          elsif block.settings.social_list_align_mobile == "right"
            assign social_list_horizontal_align_mobile = "end"
          else
            assign social_list_horizontal_align_mobile = "start"
          endif
        %}
      <style>   
        {{  block.settings.text_font | font_face: font_display: 'swap' }}

        .footer-socials-heading-{{ block.id }} {
          text-align: {{ block.settings.social_list_align_mobile }};
          max-width: 100%;
          width: 100%;
        }

        .footer-socials-heading-{{ block.id }} * {
          margin: 0;       
          font-size: {{ block.settings.text_size_mobile }}px;
          color: {{ block.settings.text_color }};
          line-height: {{ block.settings.text_height }}%;
          text-transform: unset;
          text-decoration: none;
          word-break: break-word;
          font-weight: 700;
        }   

        .footer-socials-wrapper-{{ section.id }} {
          display: flex;
          flex-direction: column;          
        }

        .footer-social-list-{{ section.id }} {
          padding: 0px;
          margin: 0px;
          list-style: none;
          margin-top: {{ block.settings.social_list_mt_mobile }}px;
          display: flex;
          flex-wrap: wrap;
          align-items: center;
          justify-content: {{ social_list_horizontal_align_mobile }};
          gap: {{ block.settings.social_list_gap_mobile }}px;
        }

        .footer-social-link-{{ section.id }} {
          padding: {{ block.settings.social_icon_size_mobile | times: 0.5 }}px;
          background-color: {{ block.settings.social_icon_bg_color }};
          border-radius: {{ block.settings.social_icon_radius }}px;
          border: {{ block.settings.social_icon_border_thickness }}px solid {{ block.settings.social_icon_border_color }};
          transition: all 0.25s ease 0s;
        }

        .footer-social-link-{{ section.id }} {
          display: block;
          box-sizing: content-box;
          width: {{ block.settings.social_icon_size_mobile }}px;
          height: {{ block.settings.social_icon_size_mobile }}px;
        }

        .footer-social-link-{{ section.id }} svg {
          display: block;
          width: 100%;
          height: 100%;
          object-fit: contain;
        }

        .footer-social-link-{{ section.id }} svg path {
          fill: {{ block.settings.social_icon_color }};
          transition: all 0.25s ease 0s;
        }

        @media(min-width: 1024px) {
          .footer-socials-heading-{{ block.id }} {
            max-width: 325px;
            text-align: {{ block.settings.social_list_align }};
          }

          .footer-socials-heading-{{ block.id }} * {
            font-size: {{ block.settings.text_size }}px;
          }

          .footer-social-list-{{ section.id }} {
            justify-content: {{ social_list_horizontal_align }};
            margin-top: {{ block.settings.social_list_mt }}px;
            gap: {{ block.settings.social_list_gap }}px;
          }

          .footer-social-link-{{ section.id }} {
            width: {{ block.settings.social_icon_size }}px;
            height: {{ block.settings.social_icon_size }}px;
          }

          .footer-social-link-{{ section.id }} {
            padding: {{ block.settings.social_icon_size | times: 0.5 }}px;
          }

          .footer-social-link-{{ section.id }}:hover {
            background-color: {{ block.settings.social_icon_bg_hover_color }};
            border: {{ block.settings.social_icon_border_thickness }}px solid {{ block.settings.social_icon_border_hover_color }};
            transition: all 0.25s ease 0s;
          }

          .footer-social-item-{{ section.id }}:hover .footer-social-link-{{ section.id }} svg path {
            fill: {{ block.settings.social_icon_hover_color }};
            transition: all 0.25s ease 0s;
          }


        }
      </style>

      {% if block.settings.text_custom %}
        <style>
          .footer-socials-heading-{{ block.id }} * {
            font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
            font-weight: {{ block.settings.text_font.weight }};
            font-style: {{ block.settings.text_font.style }};
          }
        </style>
      {% endif %}
      <style>

        }

          margin: 0;
          text-transform: unset;
          text-decoration: none;
          word-break: break-word;
        }

        @media(min-width: 1024px) {
          }

          }
        }
      </style>
