        </style>
      {% endif %}
    {% when 'bottom_menu' %}
      <style>
         {%-liquid
          assign links_horizontal_align = ""
          if block.settings.links_align == "center"
            assign links_horizontal_align = "center"
          elsif block.settings.links_align == "right"
            assign links_horizontal_align = "end"
          else
            assign links_horizontal_align = "start"
          endif

          assign links_horizontal_align_mobile = ""
          if block.settings.links_align_mobile == "center"
            assign links_horizontal_align_mobile = "center"
          elsif block.settings.links_align_mobile == "right"
            assign links_horizontal_align_mobile = "end"
          else
            assign links_horizontal_align_mobile = "start"
          endif
