                  </div>
                {% when 'newsletter' %} 
                  <div class="footer-newsletter-form-{{ section.id }} footer-newsletter-form-{{ block.id }}">
                    {% if block.settings.text != blank %}
                      <div class="footer-newsletter-heading-{{ block.id }}">{{ block.settings.text }}</div>
                    {% endif %}
                    {% form 'customer', class: 'footer-newsletter-form' %}
                      <input type="hidden" name="contact[tags]" value="newsletter">

                      <div class="footer-newsletter-row-{{ block.id }}">
                        <input
                          type="email"
                          name="contact[email]"
                          class="footer-newsletter-input-{{ block.id }}"
                          value="{{ form.email }}"
                          aria-required="true"
                          autocorrect="off"
                          autocapitalize="off"
                          autocomplete="email"
                          {% if form.errors %}
                            autofocus
                            aria-invalid="true"
                            aria-describedby="Newsletter-error--{{ block.id }}"
                          {% elsif form.posted_successfully? %}
                            aria-describedby="Newsletter-success--{{ block.id }}"
                          {% endif %}
                            placeholder="{{ block.settings.placeholder_mail }}"
                          required
                        >
                        <button type="submit" class="footer-newsletter-submit-{{ block.id }}">
                          <svg aria-hidden="true" fill="none" height="12" viewBox="0 0 21 12" width="21" xmlns="http://www.w3.org/2000/svg"><path d="M20.5303 6.53033C20.8232 6.23744 20.8232 5.76256 20.5303 5.46967L15.7574 0.696699C15.4645 0.403806 14.9896 0.403806 14.6967 0.696699C14.4038 0.989593 14.4038 1.46447 14.6967 1.75736L18.9393 6L14.6967 10.2426C14.4038 10.5355 14.4038 11.0104 14.6967 11.3033C14.9896 11.5962 15.4645 11.5962 15.7574 11.3033L20.5303 6.53033ZM0 6.75H20V5.25H0V6.75Z" fill="currentColor"></path></svg>
                        </button>
                      </div>

                      {% if form.posted_successfully? %}
                        {% if block.settings.thanks_text != blank %}
                          <p class="footer-newsletter-thanks-text-{{ block.id }} footer-newsletter-thanks-text-{{ section.id }}">{{ block.settings.thanks_text }}</p>
                        {% endif %}
                      {% endif %}

                    {% endform %}
                  </div>              
              {% endcase %}       

            {% endfor %}        
          </div>
          <div class="footer-bottom-{{ section.id }}">
            <div class="footer-bottom-right-{{ section.id }}">
              <div class="bottom-right-content-{{ section.id }}">
                {% for block in section.blocks %}
                  {% case block.type %}                
                    {% when 'bottom_menu' %}
                      {% if block.settings.menu != blank %}
                        <ul class="footer-menu-list-{{ block.id }} footer-bottom-menu-list-{{ section.id }}">
                          {% for link in block.settings.menu.links %}
                            <li class="footer-menu-item-{{ section.id }}">
                              <a href="{{ link.url }}" class="footer-menu-link-{{ section.id }} footer-bottom-menu-link-{{ block.id }}">{{ link.title }}</a>
                            </li>
                          {% endfor %}
                        </ul>
                      {% endif %}
                      {% endif %}  
                    {% when 'disclaimer' %}
                     {% if block.settings.disclaimer != blank %}
                      <div class="footer-disclaimer-{{ block.id }}">{{ block.settings.disclaimer }}</div>
                    {% endif %}  
                    {% else %}                  
                  {% endcase %}
                {% endfor %}
              </div>

            </div>        
          </div>
        </div>   
      </div>       
    </div>
</div>

<script>
  function initFooter14() {
    let thanksText = document.querySelector('.footer-newsletter-thanks-text-{{ section.id }}');
    let submitButton = document.querySelector('.footer-newsletter-submit-{{ section.id }}');
    const url = window.location.href;
    if (url.includes('customer_posted=true')) {
      if(thanksText) { 
        thanksText.classList.add('active');
      }
      if(submitButton) {
        submitButton.setAttribute('disabled', true);
      }
    }

    {% for block in section.blocks %}
      {% if block.type == 'newsletter' %}
        {% if block.settings.use_redirect %}
          const redirectUrl = '{{ block.settings.redirect_url }}';
          const delayInMilliseconds = 2000;

          if (thanksText) {
            setTimeout(function() {
              window.location.href = redirectUrl;
            }, delayInMilliseconds);
          }
        {% endif %}
      {% endif %}
    {% endfor %}
  }

  document.addEventListener('DOMContentLoaded', initFooter14);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:unload', initFooter14);
     document.addEventListener('shopify:section:load', initFooter14);
  }
</script>
