liquid_code:
{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius 
  assign background_style = section.settings.background_style  

  assign menus_mt = section.settings.menus_mt
  assign menus_mt_mobile = section.settings.menus_mt_mobile
  assign menus_gap = section.settings.menus_gap
  assign menus_gap_mobile = section.settings.menus_gap_mobile

  assign menu_gap = section.settings.menu_gap
  assign menu_gap_mobile = section.settings.menu_gap_mobile
  assign menu_mt = section.settings.menu_mt
  assign menu_mt_mobile = section.settings.menu_mt_mobile
  assign menu_align = section.settings.menu_align
  assign menu_align_mobile = section.settings.menu_align_mobile

  assign menu_heading_size = section.settings.menu_heading_size
  assign menu_heading_size_mobile = section.settings.menu_heading_size_mobile
  assign menu_heading_color = section.settings.menu_heading_color
  assign menu_heading_custom = section.settings.menu_heading_custom
  assign menu_heading_font = section.settings.menu_heading_font
  assign menu_heading_height = section.settings.menu_heading_height
  assign menu_heading_border_color = section.settings.menu_heading_border_color

  assign link_size = section.settings.link_size
  assign link_size_mobile = section.settings.link_size_mobile
  assign link_color = section.settings.link_color
  assign link_hover_color = section.settings.link_hover_color
  assign link_custom = section.settings.link_custom
  assign link_font = section.settings.link_font
  assign link_height = section.settings.link_height

  assign arrow_size = section.settings.arrow_size
  assign arrow_color = section.settings.arrow_color

  assign bottom_blocks_count = 0
  for block in section.blocks 
    case block.type 
      when 'bottom_menu' 
        if block.settings.menu != blank 
          assign bottom_blocks_count = bottom_blocks_count | plus: 1 
        endif 
      when 'payment' 
        assign bottom_blocks_count = bottom_blocks_count | plus: 1 
          assign bottom_blocks_count = bottom_blocks_count | plus: 1 
        endif 
    endcase 
  endfor 

  assign loading_attribute = 'eager'
  if lazy
    assign loading_attribute = 'lazy'
  endif
-%}

{%- style -%}
  {{ menu_heading_font | font_face: font_display: 'swap' }}
  {{ link_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .section-wrapper-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .footer-content-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
    align-items: start;
    gap: 20px;
    padding-top: {{ menus_mt_mobile | divided_by: 2 }}px;
  }

  .footer-menu-heading-{{ section.id }} {
    margin: 0;
    text-align: {{ menu_align_mobile }};
    font-size: {{ menu_heading_size_mobile }}px;
    color: {{ menu_heading_color }};
    line-height: {{ menu_heading_height }}%;
    text-transform: unset;
    text-decoration: none;
    font-weight: 700;
  }

  .footer-icon-{{ section.id }} svg {
    width: {{ arrow_size }}px;
    height: {{ arrow_size }}px;  
  }

  .footer-icon-{{ section.id }} {
    transition: all 0.25s ease 0s;
  }

  .footer-icon-{{ section.id }}.active {
    transform: rotate(180deg);
    transition: all 0.25s ease 0s;
  }

  .footer-icon-{{ section.id }} path {
    stroke: {{ arrow_color }};
  }

  .footer-top-menu-body-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
  }

  .footer-bottom-menu-list-{{ section.id }} {
    flex-direction: row;
    justify-content: center;
  }

  .footer-top-menu-{{ section.id }} {
    flex: 1 1 0;
  }

  .footer-menu-list-{{ section.id }},
  .footer-info-text-{{ section.id }} {
    padding: 0px;
    margin: 0px;
    display: flex;
    flex-direction: column;
    gap: {{ menu_gap_mobile }}px;
    margin-top: {{ menu_mt_mobile }}px !important;
    list-style: none;
    flex-wrap: wrap;
  }

  .footer-menu-item-{{ section.id }} {
    line-height: 100%;
  }

  .footer-menu-link-{{ section.id }} {
    margin: 0;
    display: block;
    text-align: {{ menu_align_mobile }};
    font-size: {{ link_size_mobile }}px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
    font-weight: 400;
  }

  .footer-bottom-{{ section.id }} {
    margin: 0 auto;
    display: flex;
    align-items: center;
    flex-direction: column;
    justify-content: space-between;
    gap: 20px;  
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
    padding-top: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
  }

  .footer-bottom-payments-wrapper-{{ section.id }} {
    display: flex;
    gap: 20px;
    align-items: center;
    justify-content: space-between;
    width: 100%;
  }

  .footer-main-wrapper-{{ section.id }} {
    position: relative; 
    overflow: hidden;
  }

  .footer-sticky-wrapper-{{ section.id }} {
    padding-bottom: {{ menus_mt_mobile | divided_by: 2 }}px;
    position: relative; 
    z-index: 2;
  }

  .footer-content-reveal-wrapper-{{ section.id }} {
    transform: translateY(-100%);
    transition: none; 
    z-index: 1; 
  }

  .footer-top-menu-mobile-{{ section.id }} {
    display: block;
    padding-top: {{ menus_gap_mobile }}px;
    padding-bottom: {{ menus_gap_mobile }}px;
    border-top: 1px solid {{ menu_heading_border_color }};
  }

  .footer-top-menu-mobile-{{ section.id }}:last-child {
    border-top: 1px solid {{ menu_heading_border_color }};
    border-bottom: 1px solid {{ menu_heading_border_color }};
  }

  .footer-top-menu-desktop-{{ section.id }} {
    display: none;
  }

  .footer-toggle-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 10px;
    justify-content: space-between;
  }

  .footer-answer-{{ section.id }} {
    transition: all 0.2s ease-in-out;
    overflow: hidden;
  }

  .footer-height-cal-{{ section.id }} {
    visibility: hidden;
    opacity: 0;
    pointer-events: none;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
  }


  @media(min-width: 1024px) {
    .section-{{ section.id }} {
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;      
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .section-wrapper-{{ section.id }} {
      border-radius: {{ section_radius }}px;
    }

    .footer-content-{{ section.id }} {
      grid-template-columns: 1fr 1fr;
      padding-top: {{ menus_mt | divided_by: 2 }}px;
    }

    .footer-top-{{ section.id }} {
      grid-template-columns: 1fr 1fr;
    }

    .footer-top-menu-body-{{ section.id }} {    
      display: flex;
      flex-wrap: nowrap;
      gap: {{ menus_gap }}px;     
    }

    .footer-top-menu-{{ section.id }} {
      flex: 1 1 0;
      width: 100%;
    }

    .footer-bottom-{{ section.id }} {
      flex-direction: row;
      padding-top: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal}}rem;
      padding-right: {{ padding_horizontal}}rem;
      padding-bottom: {{ padding_bottom }}px;
    }

    .footer-menu-heading-{{ section.id }} {  
      font-size: {{ menu_heading_size }}px;
      text-align: {{ menu_align }};
    } 

    .footer-menu-link-{{ section.id }} {
      font-size: {{ link_size }}px;
      text-align: {{ menu_align }};
    } 

    .footer-menu-link-{{ section.id }}:hover {
      color: {{ link_hover_color }};
      transition: all 0.25s ease 0s;
    }

    .footer-menu-heading-{{ section.id }} {
      text-align: {{ menu_align }};
      font-size: {{ menu_heading_size }}px;
    }

    .footer-menu-list-{{ section.id }},
    .footer-info-text-{{ section.id }} {
      gap: {{ menu_gap }}px;
      margin-top: {{ menu_mt }}px !important;
    }

    .footer-menu-link-{{ section.id }} {    
      text-align: {{ menu_align }};
      font-size: {{ link_size }}px;
    }

    .footer-flex-{{ section.id }} {      
      margin-top: {{ padding_bottom }}px;
    }

    .footer-sticky-wrapper-{{ section.id }} {
      padding-bottom: {{ menus_mt | divided_by: 2 }}px;
    }

    .footer-bottom-menu-list-{{ section.id }} {
      justify-content: start;
    }

    .footer-top-menu-mobile-{{ section.id }} {
      display: none;
    }

    .footer-top-menu-desktop-{{ section.id }} {
      display: block;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings,
    .footer-bottom-{{ section.id }} {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-wrapper-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-wrapper-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-wrapper-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if menu_heading_custom %}
  <style>
    .footer-menu-heading-{{ section.id }} {
      font-family: {{ menu_heading_font.family }}, {{ menu_heading_font.fallback_families }};
      font-weight: {{ menu_heading_font.weight }};
      font-style: {{ menu_heading_font.style }};
    }
  </style>
{% endif %}

{% if link_custom %}
  <style>
    .footer-menu-link-{{ section.id }} {
      font-family: {{ link_font.family }}, {{ link_font.fallback_families }};
      font-weight: {{ link_font.weight }};
      font-style: {{ link_font.style }};
    }
  </style>
{% endif %}

{% if menu_align_mobile == "center" %}
  <style>
    .footer-menu-heading-{{ section.id }} {
      justify-content: center;
    }
  </style>
{% elsif menu_align_mobile == "right" %}
  <style>
    .footer-menu-heading-{{ section.id }} {
      justify-content: end;
    }
  </style>
{% endif %}

{% if menu_align == "center" %}
  <style>
    @media(min-width: 1024px) {
      .footer-menu-heading-{{ section.id }} {
        justify-content: center;
      }
    }
  </style>
{% elsif menu_align == "right" %}
  <style>
    @media(min-width: 1024px) {
      .footer-menu-heading-{{ section.id }} {
        justify-content: end;
      }
    }
  </style> 
{% else %}
  <style>
    @media(min-width: 1024px) {
      .footer-menu-heading-{{ section.id }} {
        justify-content: start;
      }
    }
  </style>
{% endif %}

{% for block in section.blocks %}
  {% case block.type %}
    {% when 'heading' %}  
      <style>
        {{  block.settings.heading_font | font_face: font_display: 'swap' }}

        .footer-heading-{{ block.id }} {
          width: 100%;
          margin-top: {{ block.settings.heading_mt_mobile }}px;
          text-align: {{ block.settings.heading_content_align_mobile }};
        }

        .footer-heading-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.heading_size_mobile }}px;
          color: {{ block.settings.heading_color }};
          line-height: {{ block.settings.heading_height }}%;
          text-transform: unset;
          word-break: break-word;
          font-weight: 700;
        }

        .footer-heading-{{ block.id }} * strong {
          text-decoration: underline {{ block.settings.heading_border_color }};
        }

        @media(min-width: 1024px) {

          .footer-heading-{{ block.id }} {
            width: {{ block.settings.heading_width }}%;
            margin-top: {{ block.settings.heading_mt }}px;
            text-align: {{ block.settings.heading_content_align }};
          }

          .footer-heading-{{ block.id }} * {
            font-size: {{ block.settings.heading_size }}px;
          }
        }          
      </style>

      {% if block.settings.heading_custom %}
        <style>
          .footer-heading-{{ block.id }} * {
            font-family: {{ block.settings.heading_font.family }}, {{ block.settings.heading_font.fallback_families }};
            font-weight: {{ block.settings.heading_font.weight }};
            font-style: {{ block.settings.heading_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.heading_content_align_mobile == 'right' %}
        <style>
          .footer-heading-{{ block.id }} {
            margin-left: auto;         
          }
        </style>
      {% elsif block.settings.heading_content_align_mobile == 'center' %}
        <style>
          .footer-heading-{{ block.id }} {
            margin-left: auto;
            margin-right: auto;           
          }
        </style>  
      {% elsif block.settings.heading_content_align_mobile == 'left' %}
        <style>
          .footer-heading-{{ block.id }} {
            margin-right: auto;          
          }
        </style>  
      {% endif %}

      {% if block.settings.heading_content_align == 'right' %}
        <style>
          @media(min-width: 1024px) {
            .footer-heading-{{ block.id }} {
              margin-left: auto;
              margin-right: 0;
            }
          }   
        </style>
      {% elsif block.settings.heading_content_align == 'center' %}
        <style>
          @media(min-width: 1024px) {
            .footer-heading-{{ block.id }} {
              margin-left: auto;
              margin-right: auto;
            }
          } 
        </style>  
      {% elsif block.settings.heading_content_align == 'left' %}
        <style>
          @media(min-width: 1024px) {
            .footer-heading-{{ block.id }} {
              margin-right: 0;
              margin-left: 0;
            }
          } 
        </style>  
      {% endif %}

    {% when 'info_text' %}   
      <style>
        {{  block.settings.text_font | font_face: font_display: 'swap' }}

        .footer-info-text-{{ block.id }} {
          width: 100%;
          text-align: {{ block.settings.text_align_mobile }};
          margin-top: {{ block.settings.text_mt_mobile }}px;
          position: relative;
          z-index: 2;
        }

        .footer-info-text-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.text_size_mobile }}px;
          color: {{ block.settings.text_color }};
          line-height: {{ block.settings.text_height }}%;
          text-transform: unset;
          word-break: break-word;
          text-wrap: wrap;
        }

        @media(min-width: 1024px) {

          .footer-info-text-{{ block.id }} {
            text-align: {{ block.settings.text_align }};
            width: {{ block.settings.text_width }}%;
            margin-top: {{ block.settings.text_mt }}px;
          }

          .footer-info-text-{{ block.id }} * {
            font-size: {{ block.settings.text_size }}px;
          }

        }
      </style>

      {% if block.settings.text_custom %}
        <style>
          .footer-info-text-{{ block.id }} * {
            font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
            font-weight: {{ block.settings.text_font.weight }};
            font-style: {{ block.settings.text_font.style }};
          }
        </style>          
      {% endif %}  

      <style>

          text-align: center;
        }

          margin: 0;
          text-transform: unset;
          text-decoration: none;
          word-break: break-word;
        }

        @media(min-width: 1024px) {
            text-align: left;
          }

          }
        }
      </style>
        <style>
          }
        </style>
      {% endif %}

    {% when 'payment' %}
      <style>
        .footer-payments-{{ block.id }} {
          margin: 0px;
          display: flex;
          align-items: center;
          justify-content: center;
          flex-wrap: wrap;
          gap: 8px;
          padding: 0px;
        }

        .footer-payment-{{ block.id }} {
          flex: 0 1 {{ block.settings.payment_icon_size_mobile }}px;
          list-style: none;
        }

        .footer-payment-{{ block.id }} svg {
          display: block;
          width: 100%;
          height: 100%;
          object-fit: cover;
        }

        @media(min-width: 1024px) {
          .footer-payment-{{ block.id }} {
            flex: 0 1 {{ block.settings.payment_icon_size }}px;
            list-style: none;
          }
        }
      </style>

    {% when 'newsletter' %}          
      <style>     
        {{  block.settings.heading_font | font_face: font_display: 'swap' }}
        {{  block.settings.input_font | font_face: font_display: 'swap' }}
        {{  block.settings.thanks_text_font | font_face: font_display: 'swap' }}           

        .footer-newsletter-heading-{{ block.id }} {
          width: 100%;
          text-align: {{ block.settings.heading_align_mobile }};
          margin-top: {{ block.settings.heading_mt_mobile }}px;
        }

        .footer-newsletter-heading-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.heading_size_mobile }}px;
          color: {{ block.settings.heading_color }};
          line-height: {{ block.settings.heading_height }}%;
          text-transform: unset;
          word-break: break-word;
          font-weight: 700;
        }

        .footer-newsletter-row-{{ block.id }} {
          margin-top: 20px;
          position: relative;
          display: flex;
          align-items: stretch;
          gap: 16px;
          padding: 10px;
          max-width: 100%;
          background-color: {{ block.settings.input_bg_color }};        
          border-radius: {{ block.settings.input_radius }}px;
          border: {{ block.settings.input_border_thickness }}px solid {{ block.settings.input_border_color }};
        }

        .footer-newsletter-input-{{ block.id }} {
          width: 100%;
          margin: 0;
          box-shadow: none;
          border: 0px;     
          font-size: {{ block.settings.input_size_mobile }}px;
          color: {{ block.settings.input_color }};
          line-height: {{ block.settings.input_height }}%;
          text-transform: unset;
          border-radius: {{ block.settings.input_radius }}px;
          background-color: transparent;     
          font-family: inherit;
          padding: 10px 10px 10px 16px;
        }

        .footer-newsletter-input-{{ block.id }}::placeholder {
          color: {{ block.settings.input_placeholder_color }};
        }

        .footer-newsletter-input-{{ block.id }}:focus {
          outline: none;
        }

        .footer-newsletter-submit-{{ block.id }} {
          margin-top: 0px;
          position: static !important;
          max-width: 100%;
          display: flex;
          align-items: center;
          justify-content: center;         
          text-align: center;
          text-transform: unset;
          text-decoration: none;
          border-radius: {{ block.settings.submit_radius }}px;
          padding: {{ block.settings.submit_padding_vertical_mobile }}px {{ block.settings.submit_padding_horizontal_mobile }}px;         
          border: 0px;
          border: {{ block.settings.submit_border_thickness }}px solid {{ block.settings.submit_border_color }} ;
          background-color: transparent;
          background-color: {{ block.settings.submit_bg_color }};
          transition: all 0.25s ease 0s;
          box-shadow: none;
          cursor: pointer;
        }

        .footer-newsletter-submit-{{ block.id }} svg  {
          width: {{ block.settings.submit_size_mobile }}px;
        }

        .footer-newsletter-submit-{{ block.id }} svg path {
          fill: {{ block.settings.submit_color }};
        }

        .footer-newsletter-submit-{{ block.id }}:hover {          
          border: {{ block.settings.submit_border_thickness }}px solid {{ block.settings.submit_border_hover_color }};
          background-color: {{ block.settings.submit_bg_hover_color }};
          transition: all 0.25s ease 0s;
        }

        .footer-newsletter-submit-{{ block.id }}:hover svg path {
          fill: {{ block.settings.submit_hover_color }};
          transition: all 0.25s ease 0s;
        }      

       .footer-newsletter-thanks-text-{{ block.id }} {
          display: none;
          margin: 0px;
          text-align: {{ block.settings.thanks_text_align_mobile }};
          margin-top: {{ block.settings.thanks_text_mt_mobile }}px;
          font-size: {{ block.settings.thanks_text_size_mobile }}px;
          color: {{ block.settings.thanks_text_color }};
          line-height: {{ block.settings.thanks_text_height }}%;
          text-transform: unset;
       }

       .footer-newsletter-thanks-text-{{ block.id }}.active {
         display: block;
       }

        @media(min-width: 1024px) {            
          .footer-newsletter-heading-{{ block.id }} {
            text-align: {{ block.settings.heading_align }};
            width: {{ block.settings.heading_width }}%;
            margin-top: {{ block.settings.heading_mt }}px;
          }

          .footer-newsletter-heading-{{ block.id }} * {
            font-size: {{ block.settings.heading_size }}px;
          }

          .footer-newsletter-form-{{ section.id }} {
            width: {{ block.settings.input_width }}px
          }

          .footer-newsletter-row-{{ block.id }} {         
            padding: 10px;
            max-width: fit-content;
          }

          .footer-newsletter-input-{{ block.id }} {
            width: {{ block.settings.input_width }}px;         
            font-size: {{ block.settings.input_size }}px;
            padding: 10px 10px 10px 16px;
          }

          .footer-newsletter-submit-{{ block.id }} svg  {
            width: {{ block.settings.submit_size }}px;
          }

          .footer-newsletter-submit-{{ block.id }} {
            padding: {{ block.settings.submit_padding_vertical }}px {{ block.settings.submit_padding_horizontal }}px;           
          }


           .footer-newsletter-thanks-text-{{ block.id }} {
             text-align: {{ block.settings.thanks_text_align }};
             margin-top: {{ block.settings.thanks_text_mt }}px;
             font-size: {{ block.settings.thanks_text_size }}px;
           }
        }
      </style>

      {% if block.settings.input_custom %}
        <style>
          .footer-newsletter-input-{{ block.id }} {
