          .footer-scrolling-image-{{ section.id }} {
            aspect-ratio: 12/12;
          }
        </style>
      {% else %}
        <style>
          .footer-scrolling-image-{{ section.id }} {
            aspect-ratio: auto;
          }
        </style>
      {% endif %}

      {% if block.settings.image_ratio == "portrait" %}
