        <style>
          @media(min-width: 1024px) {
            .footer-scrolling-image-{{ section.id }} {
              aspect-ratio: 12/8;
            }
          }  
        </style>
      {% elsif block.settings.image_ratio == "square" %}
        <style>
          @media(min-width: 1024px) {
            .footer-scrolling-image-{{ section.id }} {
              aspect-ratio: 12/12;
            }
          }    
        </style>
      {% else %}
        <style>
          @media(min-width: 1024px) {
            .footer-scrolling-image-{{ section.id }} {
              aspect-ratio: auto;
            }
          }
        </style>
      {% endif %}

      {% if block.settings.reverse_direction %}
        <style>
          @keyframes ticker{{ section.id | replace: '-', '' }} {
            0% {
              transform: translateX(-100%);
            }
            100% {
              transform: translateX(0%);
            }
          }
        </style>
      {% else %}
        <style>
          @keyframes ticker{{ section.id | replace: '-', '' }} {
            0% {
              transform: translateX(0%);
            }
            100% {
              transform: translateX(-100%);
            }
          }
        </style>
      {% endif %}

      {% if block.settings.pause_hover %}
        <style>
          .footer-scrolling-wrapper-{{ section.id }}:hover .footer-scrolling-list-{{ section.id }} {
            animation-play-state: paused
          }
        </style>
      {% endif %}
