<script>
  function initFooter16() {
    let thanksText = document.querySelector('.footer-newsletter-thanks-text-{{ section.id }}');
    let submitButton = document.querySelector('.footer-newsletter-submit-{{ section.id }}');
    const url = window.location.href;
    if (url.includes('customer_posted=true')) {
      if (thanksText) {
        thanksText.classList.add('active');
      }
      if (submitButton) {
        submitButton.setAttribute('disabled', true);
      }
    }

    {% for block in section.blocks %}
    {% if block.type == 'newsletter' %}
    {% if block.settings.use_redirect %}
    const redirectUrl = '{{ block.settings.redirect_url }}';
    const delayInMilliseconds = 5000;

    if (thanksText) {
      setTimeout(function() {
        window.location.href = redirectUrl;
      }, delayInMilliseconds);
    }
    {% endif %}
    {% endif %}
    {% endfor %}

    const scrollingList = document.querySelector('.footer-scrolling-wrapper-{{ section.id }}');
    let lastScrollY = window.scrollY;
    let currentOffset = 0;

    function updateScrollEffect() {
      const scrollY = window.scrollY;
      const scrollDelta = scrollY - lastScrollY;
      lastScrollY = scrollY;

      const scrollSensitivity = 0.2;
      currentOffset -= scrollDelta * scrollSensitivity;

      if (scrollingList) {
        scrollingList.style.setProperty('--scroll-offset', `${currentOffset}px`);
      }

      requestAnimationFrame(updateScrollEffect);
    }

    requestAnimationFrame(updateScrollEffect);

    const sectionElement = document.querySelector('.footer-{{ section.id }}');
    const stickyWrapper = document.querySelector('.footer-sticky-wrapper-{{ section.id }}');
    const revealWrapper = document.querySelector('.footer-content-reveal-wrapper-{{ section.id }}');

    if (!sectionElement || !stickyWrapper || !revealWrapper) {
      return;
    }

    let isMobile = window.innerWidth <= 1024;

    function handleScrollReveal() {
      const currentScrollY = window.scrollY || document.documentElement.scrollTop;
      const sectionTop = sectionElement.offsetTop;
      const sectionHeight = sectionElement.offsetHeight;
      const viewportHeight = window.innerHeight;
      const revealWrapperHeight = revealWrapper.offsetHeight;

      const animationMultiplier = isMobile ? 1.5 : 3;

      const animationEndScrollPoint = sectionTop + sectionHeight - viewportHeight;
      const animationStartScrollPoint = animationEndScrollPoint - revealWrapperHeight * animationMultiplier;

      let progress = 0;

      if (currentScrollY >= animationEndScrollPoint) {
        progress = 1;
      } else if (animationEndScrollPoint > animationStartScrollPoint) {
        progress = (currentScrollY - animationStartScrollPoint) / (animationEndScrollPoint - animationStartScrollPoint);
      } else if (currentScrollY >= animationStartScrollPoint) {
        progress = 1;
      }

      progress = Math.max(0, Math.min(1, progress));

      let translateYValue;
      if (isMobile) {
        translateYValue = 0;
      } else {
        translateYValue = -110 + (progress * 110);
      }

      revealWrapper.style.transform = `translateY(${translateYValue}%)`;

      if (!isMobile) {
        const shadowOpacity = 0.8 * (1 - progress);
        stickyWrapper.style.boxShadow = `0px 20px 100px rgba(0, 0, 0, ${shadowOpacity.toFixed(2)})`;
      }
    }

    function handleResize() {
      isMobile = window.innerWidth <= 1024;
      handleScrollReveal();
      //          
      if (isMobile) {
        stickyWrapper.style.boxShadow = 'none';
      }
    }

    window.addEventListener('scroll', handleScrollReveal);
    window.addEventListener('resize', handleResize);

    handleScrollReveal();

    const answerPaddingBottom = 0;

    const buttons = document.querySelectorAll('.footer-toggle-{{ section.id }}');
    const answers = document.querySelectorAll('.footer-answer-{{ section.id }}');
    const answersHeightCalElements = document.querySelectorAll('.footer-height-cal-{{ section.id }}');
    const faqIcons = document.querySelectorAll('.footer-icon-{{ section.id }}');

    let activeIndex = -1;

    function openAnswer(index) {
      const height = answersHeightCalElements[index].offsetHeight;

      answers[index].style.display = 'block';
      setTimeout(() => {
        answers[index].style.paddingBottom = `${answerPaddingBottom}px`;
        answers[index].style.height = `${height}px`;
      });

      faqIcons[index].classList.add('active');
      buttons[index].classList.add('active');
    }

    function closeAnswer(index) {
      answers[index].style.paddingTop = 0;
      answers[index].style.paddingBottom = 0;
      answers[index].style.height = 0;

      setTimeout(() => {
        answers[index].style.display = 'none';
      }, 300);

      faqIcons[index].classList.remove('active');
      buttons[index].classList.remove('active');
    }

    function toggleAnswer(index) {
      if (answers[index].style.display === 'none') {
        openAnswer(index);
        activeIndex = index;
      } else {
        closeAnswer(index);
        activeIndex = -1;
      }
    }

    function closeAllAnswersBut(index) {
      answers.forEach((answer, i) => {
        if (i !== index) {
          closeAnswer(i);
        }
      });
    }

    buttons.forEach((button, index) => {
      button.addEventListener('click', () => {
        toggleAnswer(index);
        closeAllAnswersBut(index);
      });
    });

    let resizeTimer;
    window.addEventListener('resize', () => {
      clearTimeout(resizeTimer);
      resizeTimer = setTimeout(() => {
        if (activeIndex !== -1) {
          const height = answersHeightCalElements[activeIndex].offsetHeight;
          answers[activeIndex].style.height = `${height}px`;
        }
      }, 250);
    });
  }

  document.addEventListener('DOMContentLoaded', initFooter16);

  if (Shopify.designMode) {
      document.addEventListener('shopify:section:unload', initFooter16);
      document.addEventListener('shopify:section:load', initFooter16);
  }
</script>
