        </div>
      </div>

      <div class="footer-top-menu-wrapper-{{ section.id }}">
        <div class="footer-top-menu-body-{{ section.id }} footer-top-menu-body-desktop-{{ section.id }}">
          {% for block in section.blocks %}
            {% case block.type  %}
              {% when 'menu' %}
                <div class="footer-top-menu-{{ section.id }}">
                  <h3 class="footer-menu-heading-{{ section.id }}">{{ block.settings.heading }}</h3>

                  {% if block.settings.menu != blank %}
                    <ul class="footer-menu-list-{{ section.id }}">
                      {% for link in block.settings.menu.links %}
                        <li class="footer-menu-item-{{ section.id }}">
                          <a href="{{ link.url }}" class="footer-menu-link-{{ section.id }}">{{ link.title }}</a>
                        </li>
                      {% endfor %}
                    </ul>
                  {% endif %}
                </div>

            {% endcase %}
          {% endfor %}
        </div>
        <div class="footer-top-menu-body-{{ section.id }} footer-top-menu-body-mobile-{{ section.id }}">
          {% for block in section.blocks %}
            {% case block.type  %}
              {% when 'menu' %}
                <div class="footer-item-{{ section.id }}">        
                  <div class="footer-tab-toggle-{{ section.id }}">
                    <p class="footer-menu-heading-{{ section.id }}">{{ block.settings.heading }}</p>
                    <div class="footer-icon-{{ section.id }}">
                      <svg aria-hidden="true" focusable="false" role="presentation" class="icon icon-plus" viewBox="0 0 24 24"><path d="M6 12h6m6 0h-6m0 0V6m0 6v6" stroke="#000" stroke-linecap="round" stroke-linejoin="round"></path></svg>             
                      <svg aria-hidden="true" focusable="false" role="presentation" class="icon icon-minus" viewBox="0 0 24 24"><path d="M6 12h12" stroke="#000" stroke-linecap="round" stroke-linejoin="round"></path></svg>                  
                    </div>
                  </div>
                  <div class="footer-answer-{{ section.id }}" style="display: none; height: 0px; padding-top: 0px; padding-bottom: 0px;">
                    <ul class="footer-menu-list-{{ section.id }}">
                      {% for link in block.settings.menu.links %}
                        <li class="footer-menu-item-{{ section.id }}">
                          <a href="{{ link.url }}" class="footer-menu-link-{{ section.id }}">{{ link.title }}</a>
                        </li>
                      {% endfor %}
                    </ul>
                    </div>
                  <div class="footer-height-cal-{{ section.id }}">
                    <ul class="footer-menu-list-{{ section.id }}">
                      {% for link in block.settings.menu.links %}
                        <li class="footer-menu-item-{{ section.id }}">
                          <a href="{{ link.url }}" class="footer-menu-link-{{ section.id }}">{{ link.title }}</a>
                        </li>
                      {% endfor %}
                    </ul>
                  </div>                      
                </div>   
            {% endcase %}
          {% endfor %}
        </div>
        <div class="footer-top-images-wrap-{{ section.id }}" style="overflow: hidden;">
