                <div class="footer-image-{{ section.id }}">
                  {% if block.settings.image != blank %}
                    {%- capture sizes -%}(min-width: {{ content_width }}px) {{ content_width | minus: 100 | divided_by: 2 }}px, (min-width: 750px) calc((100vw - 130px) / 2), calc((100vw - 50px) / 2){%- endcapture -%}
                    {{ block.settings.image | image_url: width: 1000 | image_tag:
                      loading: lazy_load_setting,
                      width: block.settings.image.width,
                      height: block.settings.image.height,
                      sizes: sizes,
                      widths: '360, 535, 750, 1070, 1500'
                    }}
                  {% else %}
                    {{ 'image' | placeholder_svg_tag }}
                  {% endif %}
                </div>
              {% endif %}
            {% endfor %}
          </div> 
          {% for block in section.blocks %}               
            {% if block.type == 'scrolling_text' %}             
              {% if block.settings.scrolling_text != blank %}            
                <div class="footer-scrolling-wrap-{{ block.id }}">                 
                  <div class="footer-scrolling-list-{{ block.id }}">
                    {% for item in (1..10) %}
                      <p class="footer-scrolling-{{ block.id }}">{{ block.settings.scrolling_text }}</p>
                      {% if block.settings.use_dot %}
                        <div class="footer-scrolling-dot-{{ block.id }}"></div>
                      {% endif %}
                    {% endfor %}
                  </div>
                  <div class="footer-scrolling-list-{{ block.id }}">
                    {% for item in (1..10) %}
                      <p class="footer-scrolling-{{ block.id }}">{{ block.settings.scrolling_text }}</p>
                      {% if block.settings.use_dot %}
                        <div class="footer-scrolling-dot-{{ block.id }}"></div>
                      {% endif %}
                    {% endfor %}
                  </div>         
                </div>                  
              {% endif %}            
            {% endif %}        
          {% endfor %}
        </div>
      </div>    
     
      
      <div class="footer-bottom-{{ section.id }}">       
        {% for block in section.blocks %}
          {% case block.type %}
            {% when 'bottom_menu' %}
              {% if block.settings.menu != blank %}
                <ul class="footer-menu-list-{{ block.id }} footer-bottom-menu-list-{{ section.id }}">
                  {% for link in block.settings.menu.links %}
                    <li class="footer-menu-item-{{ section.id }}">
                      <a href="{{ link.url }}" class="footer-menu-link-{{ section.id }} footer-bottom-menu-link-{{ block.id }}">{{ link.title }}</a>
                    </li>
                  {% endfor %}
                </ul>
              {% endif %}

              {% endif %}  

          {% endcase %}
        {% endfor %}       
      </div>
    </div>
</div>

<script>
  function initFooter17() {
    let thanksText = document.querySelector('.footer-newsletter-thanks-text-{{ section.id }}');
    let submitButton = document.querySelector('.footer-newsletter-submit-{{ section.id }}');
    const url = window.location.href;
    if (url.includes('customer_posted=true')) {
      if(thanksText) { 
        thanksText.classList.add('active');
      }
      if(submitButton) {
        submitButton.setAttribute('disabled', true);
      }
    }

    const answerPaddingTop = {{ menu_mt_mobile }};
    const answerPaddingBottom = 0;

    const buttons = document.querySelectorAll(
      '.footer-tab-toggle-{{ section.id }}'
    );
    const answers = document.querySelectorAll(
      '.footer-answer-{{ section.id }}'
    );
    const answersHeightCalElements = document.querySelectorAll(
      '.footer-height-cal-{{ section.id }}'
    );
    const faqIcons = document.querySelectorAll(
      '.footer-icon-{{ section.id }}'
    ); 

    let activeIndex = -1;

    function openAnswer(index) {
      console.log(answersHeightCalElements[index]);
      const height = answersHeightCalElements[index].offsetHeight;

      answers[index].style.display = 'block';
      setTimeout(() => {
        answers[index].style.paddingTop = `${answerPaddingTop}px`;
        answers[index].style.paddingBottom = `${answerPaddingBottom}px`;
        answers[index].style.height = `${height + 15}px`;
      });

      items[index].classList.add('active');
      faqIcons[index].classList.add('active');
    }

    function closeAnswer(index) {
      answers[index].style.paddingTop = 0;
      answers[index].style.paddingBottom = 0;
      answers[index].style.height = 0;

      setTimeout(() => {
        answers[index].style.display = 'none';
      }, 300);

      items[index].classList.remove('active');
      faqIcons[index].classList.remove('active');
    }

    function toggleAnswer(index) {
      if (answers[index].style.display === 'none') {
        openAnswer(index);
        activeIndex = index;
      } else {
        closeAnswer(index);
        activeIndex = -1;
      }
    }

    function closeAllAnswerBut(index) {
      answers.forEach((answer, i) => {
        if (i !== index) {
          closeAnswer(i);
        }
      });
    }

    buttons.forEach((button, index) => {
      button.addEventListener('click', () => {
        toggleAnswer(index);
        closeAllAnswerBut(index);
      });
    });

    let resizeTimer;
    window.addEventListener('resize', () => {
      clearTimeout(resizeTimer);
      resizeTimer = setTimeout(() => {
        if (activeIndex !== -1) {
          const height = answersHeightCalElements[activeIndex].offsetHeight;
          answers[activeIndex].style.height = `${height}px`;
        }
      }, 250);
    });    
  }

  document.addEventListener('DOMContentLoaded', initFooter17);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:unload', initFooter17);
     document.addEventListener('shopify:section:load', initFooter17);
  }
</script>
