{%- style -%}
  {{  input_font | font_face: font_display: 'swap' }}
  {{  submit_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}
  {{  link_font | font_face: font_display: 'swap' }}
  {{  title_font | font_face: font_display: 'swap' }}
  {{  copy_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .footer-top-{{ section.id }} {
    display: flex;
    flex-direction: column;
    gap: 24px;
    padding-bottom: 40px;
  }

  .footer-newsletter-row-{{ section.id }} {
    position: relative;
    display: flex;
    align-items: center;
    gap: 10px;
  }

  .footer-newsletter-input-{{ section.id }} {
    width: 100%;
    margin: 0;
    box-shadow: none;
    border: 0px;
    padding: {{ input_padding_vertical | times: 0.75 | round: 0 }}px {{ input_padding_horizontal | times: 0.75 | round: 0 }}px;
    font-size: {{ input_size_mobile }}px;
    color: {{ input_color }};
    line-height: {{ input_height }}%;
    text-transform: unset;
    border-radius: {{ input_radius }}px;
    border: {{ input_border_thickness }}px solid {{ input_border_color | hex_to_rgba: 0.7 }};
    background-color: transparent;
  }

  .footer-newsletter-input-{{ section.id }}:focus {
    border: {{ input_border_thickness }}px solid {{ input_border_color | hex_to_rgba: 1 }};
  }

  .footer-newsletter-input-{{ section.id }}::placeholder {
    color: {{ input_placeholder_color }};
  }

  .footer-newsletter-submit-{{ section.id }} {
    margin-top: 0px;
    position: static !important;
    font-weight: 700;
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: {{ submit_size_mobile }}px;
    color: {{ submit_color }};
    line-height: {{ submit_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ submit_padding_vertical | times: 0.75 | round: 0 }}px {{ submit_padding_horizontal | times: 0.75 | round: 0 }}px;
    border-radius: {{ submit_radius }}px;
    border: 0px;
    border: {{ submit_border_thickness }}px solid {{ submit_border_color }};
    background-color: transparent;
    background-color: {{ submit_bg_color }};
    transition: all 0.25s ease 0s;
    box-shadow: none;
    cursor: pointer;
  }

  .footer-newsletter-submit-{{ section.id }}:hover {
    color: {{ submit_hover_color }};
    border: {{ submit_border_thickness }}px solid {{ submit_border_hover_color }};
    background-color: {{ submit_bg_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .footer-newsletter-first-row-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 10px;
    margin-bottom: 10px;
  }

  .footer-main-{{ section.id }} {
    display: grid;
  }

  .footer-second-main-{{ section.id }} {
    padding-top: 30px;
    padding-bottom: 30px;
    gap: 30px;
  }

  .footer-main-left-{{ section.id }} {
   padding-top: 16px;
    padding-bottom: 16px;
    align-items: center;
    display: flex;
    flex-direction: column;
    justify-content: center;
  }

  .footer-logo-{{ section.id }} {
    display: block;
    width: 100%;
    max-width: {{ logo_width_mobile }}px;
  }

  .footer-logo-{{ section.id }} img,
  .footer-logo-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .footer-logo-{{ section.id }} svg {
    background-color: #DEDEDE;
  }

  .footer-text-{{ section.id }} {
    margin-top: 0px;
    width: 100%;
  }

  .footer-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
  }

  .footer-main-title-{{ section.id }} {
    margin: 0;
    font-size: {{ main_title_size_mobile }}px;
    color: {{ main_title_color }};
    line-height: {{ main_title_height }}%;
    text-transform: unset;
    margin-bottom: 16px;
  }

  .footer-main-right-top-{{ section.id }} {
    padding-bottom: 32px;
  }

  .footer-main-socials-{{ section.id }} {
    margin-top: 30px;
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 10px;
  }

  .footer-main-social-{{ section.id }} {
    flex: 0 1 auto;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .footer-main-social-{{ section.id }} svg {
    width: {{ social_icon_mobile }}px;
    height: {{ social_icon_mobile }}px;
    display: block;
  }

  .footer-main-social-{{ section.id }} svg path {
    fill: {{ social_icon_color }};
  }

  .footer-bottom-{{ section.id }} {
    padding-top: 24px;
    display: flex;
    flex-direction: column;
    gap: 32px;
  }

  .footer-list-{{ section.id }} {
    margin: 0px;
    padding: 0px;
  }

  .footer-list-{{ section.id }} li {
    list-style: none;
  }

  .footer-list-{{ section.id }} li a {
    margin: 0;
    font-size: {{ link_size_mobile }}px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
    position: relative;
  }

  .footer-list-{{ section.id }} li a:after {
    content: '';
    left: 0;
    bottom: -2px;
    position: absolute;
    width: 100%;
    height: 2px;
    background-color: {{ link_hover_color }};
    opacity: 0;
    transtion: all 0.25s ease 0s;
  }

  .footer-list-{{ section.id }} li a:hover:after {
    opacity: 1;
    transtion: all 0.25s ease 0s;
  }

  .footer-list-title-{{ section.id }} {
    margin: 0;
    margin-bottom: 16px;
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-transform: unset;
  }

  .footer-second-top-{{ section.id }} {
    padding-bottom: 30px;
    border-bottom: 1px solid {{ footer_border_color }};
    display: grid;
    grid-template-columns: repeat(60, minmax(0, 1fr));
    grid-auto-rows: auto;
    grid-auto-flow: dense;
    row-gap: 32px;
  }

  .footer-second-top-item-{{ section.id }} {
    text-align: center;
    grid-column: span 60;
    padding: 16px 32px 0px;
  }

  .footer-second-top-item-logo-{{ section.id }} .footer-logo-{{ section.id }} {
    margin-left: auto;
    margin-right: auto;
  }

  .footer-payments-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    flex-wrap: wrap;
    gap: 8px;
    margin: 0px;
    padding: 0px;
  }

  .footer-payment-{{ section.id }} {
    flex: 0 1 38px;
    list-style: none;
  }

  .footer-payment-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .footer-copy-text-{{ section.id }} {
    margin-top: 16px;
    text-align: center;
    width: 100%;
  }

  .footer-copy-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ copy_size_mobile }}px;
    color: {{ copy_color }};
    line-height: {{ copy_height }}%;
    text-transform: unset;
    text-decoration: none;
  }

  .footer-copy-text-{{ section.id }} a:hover {
    text-decoration: underline;
  }

  .footer-main-left-top-{{ section.id }} {
    display: grid;
    gap: 20px;
  }


 .footer-newsletter-thanks-text-{{ section.id }} {
    display: none;
    margin: 0px;
    margin-top: {{ thanks_text_mt_mobile }}px;
    font-size: {{ thanks_text_size_mobile }}px;
    color: {{ thanks_text_color }};
    line-height: {{ thanks_text_height }}%;
    text-transform: unset;
 }

 .footer-newsletter-thanks-text-{{ section.id }}.active {
   display: block;
 }


  @media(min-width: 768px) {

    .footer-first-main-socials-{{ section.id }} svg {
      width: {{ social_icon_mobile | times: 2 }}px;
      height: {{ social_icon_mobile | times: 2 }}px;
    }

    .footer-newsletter-submit-{{ section.id }} {
      margin-top: 0px;
      max-width: 200px;
    }
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .footer-top-{{ section.id }} {
      flex-direction: row;
      justify-content: space-between;
      align-items: center;
    }

    .footer-newsletter-text-{{ section.id }} {
      flex: 0 1 55%;
    }

    .footer-newsletter-form-{{ section.id }} {
      flex: 0 1 512px;
    }

    .footer-newsletter-input-{{ section.id }} {
      padding: {{ input_padding_vertical }}px {{ input_padding_horizontal }}px;
      font-size: {{ input_size }}px;
    }

    .footer-newsletter-submit-{{ section.id }} {
      padding: {{ submit_padding_vertical }}px {{ submit_padding_horizontal }}px;
      font-size: {{ submit_size }}px;
    }

    .footer-main-{{ section.id }} {
      grid-template-columns: 1fr 1fr;
    }

    .footer-logo-{{ section.id }} {
      max-width: {{ logo_width }}px;
    }

    .footer-text-{{ section.id }} {
      max-width: 100%;
    }

    .footer-text-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }

    .footer-main-title-{{ section.id }} {
      font-size: {{ main_title_size }}px;
    }

    .footer-main-right-top-{{ section.id }} {
      padding-top: 32px;
      padding-bottom: 32px;
      padding-left: 10%;
      padding-right: 10%;
    }

    .footer-main-right-{{ section.id }} {
      border-left: 1px solid {{ footer_border_color }};
    }

    .footer-four-main-{{ section.id }} .footer-main-right-{{ section.id }} {
      border-left: 0px;
    }

    .footer-main-social-{{ section.id }} svg {
      width: {{ social_icon }}px;
      height: {{ social_icon }}px;
    }

    .footer-main-socials-{{ section.id }}.footer-main-second-socials-{{ section.id }} {
      margin-top: 5px;
    }

    .footer-list-{{ section.id }} li a {
      font-size: {{ link_size }}px;
    }

    .footer-bottom-{{ section.id }} {
      align-items: center;
      justify-content: space-between;
      flex-wrap: wrap;
      flex-direction: row;
    }

    .footer-list-title-{{ section.id }} {
      font-size: {{ title_size }}px;
    }

    .footer-second-top-{{ section.id }} {
      row-gap: 15px;
    }

    .footer-second-top-item-{{ section.id }}:not(:first-child) {
      border-left: 1px solid {{ footer_border_color }};
    }

    .footer-second-top-item-{{ section.id }} {
      grid-column: span 12;
    }

    .footer-second-top-item-{{ section.id }}.footer-second-top-item-logo-{{ section.id }} {
      grid-column: span 24;
    }

    .footer-newsletter-thanks-text-{{ section.id }} {
      margin-top: {{ thanks_text_mt }}px;
      font-size: {{ thanks_text_size }}px;
    }

    .footer-copy-text-{{ section.id }} * {
      font-size: {{ copy_size }}px;
    }

    .footer-second-main-{{ section.id }} .footer-main-right-{{ section.id }} {
      padding: 16px 32px;
    }

  }
{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if input_custom %}
  <style>
    .footer-newsletter-input-{{ section.id }} {
      font-family: {{ input_font.family }}, {{ input_font.fallback_families }};
      font-weight: {{ input_font.weight }};
      font-style: {{ input_font.style }};
    }
  </style>
{% endif %}

{% if submit_custom %}
  <style>
    .footer-newsletter-submit-{{ section.id }} {
      font-family: {{ submit_font.family }}, {{ submit_font.fallback_families }};
      font-weight: {{ submit_font.weight }};
      font-style: {{ submit_font.style }};
    }
  </style>
{% endif %}

{% if thanks_text_custom %}
  <style>
    .footer-newsletter-thanks-text-{{ section.id }} {
      font-family: {{ thanks_text_font.family }}, {{ thanks_text_font.fallback_families }};
      font-weight: {{ thanks_text_font.weight }};
      font-style: {{ thanks_text_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .footer-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if main_title_custom %}
  <style>
    .footer-main-title-{{ section.id }} {
      font-family: {{ main_title_font.family }}, {{ main_title_font.fallback_families }};
      font-weight: {{ main_title_font.weight }};
      font-style: {{ main_title_font.style }};
    }
  </style>
{% endif %}

{% if link_custom %}
  <style>
    .footer-list-{{ section.id }} li a {
      font-family: {{ link_font.family }}, {{ link_font.fallback_families }};
      font-weight: {{ link_font.weight }};
      font-style: {{ link_font.style }};
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .footer-list-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if copy_custom %}
  <style>
    .footer-copy-text-{{ section.id }} * {
      font-family: {{ copy_font.family }}, {{ copy_font.fallback_families }};
      font-weight: {{ copy_font.weight }};
      font-style: {{ copy_font.style }};
    }
  </style>
{% endif %}

<div
  class="section-{{ section.id }} footer-{{ section.id }}"
  style="background-color:{{ background_color }}; background-image: {{ background_gradient }};"
>
<div class="section-{{ section.id }}-settings">
    <div class="footer-second-top-{{ section.id }}">
      <div class="footer-second-top-item-{{ section.id }} footer-second-top-item-logo-{{ section.id }}">
        <a href="/" class="footer-logo-{{ section.id }}">
          {% if logo != blank %}
            <img
              src="{{ logo | image_url }}"
              alt="{{ logo.alt }}"
              {% if lazy %}
                loading="lazy"
              {% endif %}
            >
            {% else %}
              {{ 'image' | placeholder_svg_tag }}
            {% endif %}
        </a>
      </div>

      <div class="footer-second-top-item-{{ section.id }}">
        {% if first_menu_title != blank %}
          <h3 class="footer-list-title-{{ section.id }}">{{ first_menu_title }}</h3>
        {% endif %}

        <ul class="footer-list-{{ section.id }} footer-second-list-{{ section.id }}">
          {% if first_menu != blank %}
            {% for link in linklists[first_menu].links %}
              <li>{{ link.title | link_to: link.url }}</li>
            {% endfor %}
          {% else %}
            {% for linklist in linklists %}
              {% if forloop.first %}
                {% for link in linklist.links %}
                  <li>{{ link.title | link_to: link.url }}</li>
                {% endfor %}
              {% endif %}
            {% endfor %}
          {% endif %}
        </ul>
      </div>

      {% if text != blank %}
        <div class="footer-second-top-item-{{ section.id }}">
          {% if title != blank %}
            <h3 class="footer-main-title-{{ section.id }}">{{ title }}</h3>
          {% endif %}
          <div class="footer-text-{{ section.id }}">
            {{ text }}
          </div>
        </div>
      {% endif %}

      {% if second_text != blank %}
        <div class="footer-second-top-item-{{ section.id }}">
          {% if second_title != blank %}
            <h3 class="footer-main-title-{{ section.id }}">{{ second_title }}</h3>
          {% endif %}
          <div class="footer-text-{{ section.id }}">
            {{ second_text }}
          </div>
        </div>
      {% endif %}
    </div>

    <div class="footer-main-{{ section.id }} footer-second-main-{{ section.id }}">
      <div class="footer-main-left-{{ section.id }}">
        {% if use_payments %}
          <ul class="footer-payments-{{ section.id }}">
            {%- for type in shop.enabled_payment_types -%}
              <li class="footer-payment-{{ section.id }}">
                {{ type | payment_type_svg_tag }}
              </li>
            {%- endfor -%}
          </ul>
        {% endif %}
        {% if copy != blank %}
          <div class="footer-copy-text-{{ section.id }}">
            {{ copy }}
          </div>
        {% endif %}
      </div>
      {% if use_newsletter %}
        <div class="footer-main-right-{{ section.id }}">
          {% form 'customer', class: 'footer-newsletter-form' %}
            <input type="hidden" name="contact[tags]" value="newsletter">

            <div class="footer-newsletter-row-{{ section.id }}">
              <input
                type="email"
                name="contact[email]"
                class="footer-newsletter-input-{{ section.id }}"
                value="{{ form.email }}"
                aria-required="true"
                autocorrect="off"
                autocapitalize="off"
                autocomplete="email"
