  assign scrolling_text_size = section.settings.scrolling_text_size
  assign scrolling_text_size_mobile = section.settings.scrolling_text_size_mobile
  assign scrolling_text_height = section.settings.scrolling_text_height
  assign scrolling_text_color = section.settings.scrolling_text_color
  assign scrolling_text_hover_color = section.settings.scrolling_text_hover_color

  assign scrolling_bottom_text = section.settings.scrolling_bottom_text
  assign scrolling_bottom_text_custom = section.settings.scrolling_bottom_text_custom
  assign scrolling_bottom_text_font = section.settings.scrolling_bottom_text_font
  assign scrolling_bottom_text_size = section.settings.scrolling_bottom_text_size
  assign scrolling_bottom_text_size_mobile = section.settings.scrolling_bottom_text_size_mobile
  assign scrolling_bottom_text_height = section.settings.scrolling_bottom_text_height
  assign scrolling_bottom_text_color = section.settings.scrolling_bottom_text_color
  assign scrolling_bottom_text_align = section.settings.scrolling_bottom_text_align
  assign scrolling_bottom_text_align_mobile = section.settings.scrolling_bottom_text_align_mobile
