  assign copy_size = section.settings.copy_size
  assign copy_size_mobile = section.settings.copy_size_mobile
  assign copy_color = section.settings.copy_color
  assign copy_custom = section.settings.copy_custom
  assign copy_font = section.settings.copy_font
  assign copy_height = section.settings.copy_height

  assign menu = section.settings.menu

  assign link_size = section.settings.link_size
  assign link_size_mobile = section.settings.link_size_mobile
  assign link_color = section.settings.link_color
