-%}

{%- style -%}

  {{ scrolling_text_font | font_face: font_display: 'swap' }}
  {{ scrolling_bottom_text_font | font_face: font_display: 'swap' }}
  {{ copy_font | font_face: font_display: 'swap' }}
  {{ link_font | font_face: font_display: 'swap' }}
  {{ lang_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .footer-top-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
    gap: {{ layout_gap_mobile }}px;
  }

  .footer-bottom-{{ section.id }} {
    margin-top: {{ layout_gap_mobile }}px;
  }

  .footer-block-{{ section.id }} {
    padding: {{ block_padding_vertical_mobile }}px {{ block_padding_horizontal_mobile }}px;
    border-radius: {{ block_radius }}px;
    border: {{ block_border_thickness }}px solid {{ block_border_color }};
    background: {{ block_bg_color }};
    overflow: hidden;
  }

  .footer-block-{{ section.id }}.footer-bottom-{{ section.id }} {
    padding: {{ bottom_block_padding_vertical_mobile }}px {{ bottom_block_padding_horizontal_mobile }}px;
  }

  .footer-left-{{ section.id }} {
    display: flex;
    flex-direction: column;
    justify-content: center;
  }

  .footer-scrolling-wrap-{{ section.id }} {
    position: relative;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    overflow: hidden;
    -ms-flex-wrap: nowrap;
    flex-wrap: nowrap;
    background-attachment: scroll !important;
    margin-left: -{{ block_padding_horizontal_mobile }}px;
    margin-right: -{{ block_padding_horizontal_mobile }}px;
    -webkit-mask-image: linear-gradient(to right, hsl(0 0% 0% / 0), hsl(0 0% 0% / 1) 20%, hsl(0 0% 0% / 1) 80%, hsl(0 0% 0% / 0));
    mask-image: linear-gradient(to right, hsl(0 0% 0% / 0), hsl(0 0% 0% / 1) 20%, hsl(0 0% 0% / 1) 80%, hsl(0 0% 0% / 0));
    text-decoration: none;
  }

  .footer-scrolling-list-{{ section.id }} {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    white-space: nowrap;
    background-attachment: scroll !important;
    -webkit-animation: ticker{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile }}s infinite linear;
    animation: ticker{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile }}s infinite linear;
    flex-shrink: 0;
  }

  .footer-scrolling-item-{{ section.id }} {
    display: flex;
    align-items: center;
    background-attachment: scroll !important;
    margin-right: {{ scrolling_gap_mobile }}px;
    flex-shrink: 0;
  }

  .footer-scrolling-item-{{ section.id }} svg {
    width: 22px;
    height: 22px;
  }

  .footer-scrolling-item-{{ section.id }} svg path {
    fill: {{ scrolling_text_color }};
    transition: all 0.25s ease 0s;
  }

  .footer-scrolling-wrap-{{ section.id }}:hover .footer-scrolling-item-{{ section.id }} svg path {
    fill: {{ scrolling_text_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .footer-scrolling-text-{{ section.id }} {
    display: inline-block;
    margin: 0;
    font-size: {{ scrolling_text_size_mobile }}px;
    color: {{ scrolling_text_color }};
    line-height: {{ scrolling_text_height }}%;
    text-transform: unset;
    margin-right: {{ scrolling_gap_mobile }}px;
    font-weight: 700;
    transition: all 0.25s ease 0s;
  }

  .footer-scrolling-wrap-{{ section.id }}:hover .footer-scrolling-text-{{ section.id }} {
    color: {{ scrolling_text_hover_color }};
     transition: all 0.25s ease 0s;
  }

  .footer-scrolling-bottom-text-{{ section.id }} {
    margin-top: {{ scrolling_bottom_text_mt_mobile }}px;
    text-align: {{ scrolling_bottom_text_align_mobile }};
  }

  .footer-scrolling-bottom-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ scrolling_bottom_text_size_mobile }}px;
    color: {{ scrolling_bottom_text_color }};
    line-height: {{ scrolling_bottom_text_height }}%;
    text-transform: unset;
    text-decoration: none;
  }

  .footer-copy-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ copy_size_mobile }}px;
    color: {{ copy_color }};
    line-height: {{ copy_height }}%;
    text-transform: unset;
    text-decoration: none;
  }

  .footer-copy-text-{{ section.id }} a:hover {
    text-decoration: underline;
  }

  .footer-menu-{{ section.id }} {
    margin-top: 10px;
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 12px;
    width: 100%;
    list-style: none;
    padding: 0px;
  }

  .footer-menu-{{ section.id }} li a {
    margin: 0;
    font-size: {{ link_size_mobile }}px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease 0s;
  }

  .footer-menu-{{ section.id }} li a:hover {
    color: {{ link_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .footer-bottom-row-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: space-between;
    gap: 20px;
  }

  .footer-bottom-right-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 20px;
  }

  .footer-desktop-menu-{{ section.id }} {
    display: none;
  }

  .footer-languages-list-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 10px;
  }

  .footer-languages-list-{{ section.id }} a {
    margin: 0;
    font-size: {{ lang_size_mobile }}px;
    color: {{ lang_color }};
    line-height: {{ lang_height }}%;
    text-transform: uppercase;
    text-decoration: none;
    transition: all 0.25s ease 0s;
  }

  .footer-languages-list-{{ section.id }} a:hover {
    color: {{ lang_hover_color }};
    transition: all 0.25s ease 0s;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .footer-top-{{ section.id }} {
      grid-template-columns: 1fr 1fr;
      gap: {{ layout_gap }}px;
    }

    .footer-bottom-{{ section.id }} {
      margin-top: {{ layout_gap }}px;
    }

    .footer-block-{{ section.id }} {
      padding: {{ block_padding_vertical }}px {{ block_padding_horizontal }}px;
    }

    .footer-block-{{ section.id }}.footer-bottom-{{ section.id }} {
      padding: {{ bottom_block_padding_vertical }}px {{ bottom_block_padding_horizontal }}px;
    }

    .footer-scrolling-wrap-{{ section.id }} {
      margin-left: -{{ block_padding_horizontal }}px;
      margin-right: -{{ block_padding_horizontal }}px;
    }

    .footer-scrolling-list-{{ section.id }} {
      animation-duration: {{ scrolling_velocity }}s;
    }

    .footer-scrolling-item-{{ section.id }} {
      margin-right: {{ scrolling_gap }}px;
    }

    .footer-scrolling-text-{{ section.id }} {
      font-size: {{ scrolling_text_size }}px;
      margin-right: {{ scrolling_gap }}px;
    }

    .footer-scrolling-bottom-text-{{ section.id }} {
      margin-top: {{ scrolling_bottom_text_mt }}px;
      text-align: {{ scrolling_bottom_text_align }};
    }

    .footer-scrolling-bottom-text-{{ section.id }} * {
      margin: 0;
      font-size: {{ scrolling_bottom_text_size }}px;
    }

    .footer-copy-text-{{ section.id }} * {
      font-size: {{ copy_size }}px;
    }

    .footer-menu-{{ section.id }} {
      margin: 0px;
      display: flex;
      align-items: center;
      justify-content: start;
      width: fit-content;
    }

    .footer-menu-{{ section.id }} li a {
      font-size: {{ link_size }}px;
    }

    .footer-bottom-row-mobile-{{ section.id }} {
      display: none;
    }

    .footer-desktop-menu-{{ section.id }} {
      display: flex;
    }

    .footer-languages-list-{{ section.id }} a {
      font-size: {{ lang_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if scrolling_text_custom %}
  <style>
    .footer-scrolling-text-{{ section.id }} {
      font-family: {{ scrolling_text_font.family }}, {{ scrolling_text_font.fallback_families }};
      font-weight: {{ scrolling_text_font.weight }};
      font-style: {{ scrolling_text_font.style }};
    }
  </style>
{% endif %}

{% if scrolling_bottom_text_custom %}
  <style>
    .footer-scrolling-bottom-text-{{ section.id }} * {
      font-family: {{ scrolling_bottom_text_font.family }}, {{ scrolling_bottom_text_font.fallback_families }};
      font-weight: {{ scrolling_bottom_text_font.weight }};
      font-style: {{ scrolling_bottom_text_font.style }};
    }
  </style>
{% endif %}

{% if copy_custom %}
  <style>
    .footer-copy-text-{{ section.id }} * {
      font-family: {{ copy_font.family }}, {{ copy_font.fallback_families }};
      font-weight: {{ copy_font.weight }};
      font-style: {{ copy_font.style }};
    }
  </style>
{% endif %}

{% if link_custom %}
  <style>
    .footer-menu-{{ section.id }} li a {
      font-family: {{ link_font.family }}, {{ link_font.fallback_families }};
      font-weight: {{ link_font.weight }};
      font-style: {{ link_font.style }};
    }
  </style>
{% endif %}

{% if lang_custom %}
  <style>
    .footer-languages-list-{{ section.id }} a {
      font-family: {{ lang_font.family }}, {{ lang_font.fallback_families }};
      font-weight: {{ lang_font.weight }};
      font-style: {{ lang_font.style }};
    }
  </style>
{% endif %}

{% if reverse_direction %}
  <style>
    @keyframes ticker{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(-50%);
      }
      100% {
        transform: translateX(0%);
      }
    }
  </style>
{% else %}
  <style>
    @keyframes ticker{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(0%);
      }
      100% {
        transform: translateX(-50%);
      }
    }
  </style>
{% endif %}

{% for block in section.blocks %}

  {% if block.type == "heading" %}
        <style>
            {{  block.settings.heading_font | font_face: font_display: 'swap' }}

            .footer-heading-{{ block.id }} {
                text-align: {{ block.settings.heading_align_mobile }};
                margin-top: {{ block.settings.heading_mt_mobile }}px;
            }

            .footer-heading-{{ block.id }} * {
                margin: 0;
                font-size: {{ block.settings.heading_size_mobile }}px;
                color: {{ block.settings.heading_color }};
                line-height: {{ block.settings.heading_height }}%;
                text-transform: unset;
            }

            @media(min-width: 1024px) {

                .footer-heading-{{ block.id }} {
                    text-align: {{ block.settings.heading_align }};
                    margin-top: {{ block.settings.heading_mt }}px;
                }

                .footer-heading-{{ block.id }} * {
                    font-size: {{ block.settings.heading_size }}px;
                }
            }
        </style>

        {% if block.settings.heading_custom %}
            <style>
                .footer-heading-{{ block.id }} * {
                    font-family: {{ block.settings.heading_font.family }}, {{ block.settings.heading_font.fallback_families }};
                    font-weight: {{ block.settings.heading_font.weight }};
                    font-style: {{ block.settings.heading_font.style }};
                }
            </style>
        {% endif %}

    {% elsif block.type == "text" %}
        <style>
            {{ block.settings.text_font | font_face: font_display: 'swap' }}

            .footer-text-{{ block.id }} {
                text-align: {{ block.settings.text_align_mobile }};
                margin-top: {{ block.settings.text_mt_mobile }}px;
            }

            .footer-text-{{ block.id }} * {
                margin: 0;
                font-size: {{ block.settings.text_size_mobile }}px;
                color: {{ block.settings.text_color }};
                line-height: {{ block.settings.text_height }}%;
                text-transform: unset;
            }

            @media(min-width: 1024px) {

                .footer-text-{{ block.id }} {
                    text-align: {{ block.settings.text_align }};
                    margin-top: {{ block.settings.text_mt }}px;
                }

                .footer-text-{{ block.id }} * {
                    font-size: {{ block.settings.text_size }}px;
                }
            }
        </style>

        {% if block.settings.text_custom %}
            <style>
                .footer-text-{{ block.id }} {
                    font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
                    font-weight: {{ block.settings.text_font.weight }};
                    font-style: {{ block.settings.text_font.style }};
                }
            </style>
        {% endif %}
    {% elsif block.type == "contact" %}

      <style>

        {{  block.settings.input_font | font_face: font_display: 'swap' }}
        {{  block.settings.submit_font | font_face: font_display: 'swap' }}

        .footer-form-{{ block.id }} {
          margin-top: {{ block.settings.form_mt_mobile }}px;
        }

        .footer-newsletter-row-{{ block.id }} {
          position: relative;
          display: flex;
          align-items: center;
          gap: 10px;
          flex-direction: column;
        }

        .footer-newsletter-input-{{ block.id }} {
          width: 100%;
          margin: 0;
          box-shadow: none;
          border: 0px;
          padding: {{ block.settings.input_padding_vertical | times: 0.75 | round: 0 }}px {{ block.settings.input_padding_horizontal | times: 0.75 | round: 0 }}px;
          font-size: {{ block.settings.input_size_mobile }}px;
          color: {{ block.settings.input_color }};
          line-height: {{ block.settings.input_height }}%;
          text-transform: unset;
          border-radius: {{ block.settings.input_radius }}px;
          background-color: transparent;
          background-color: {{ block.settings.input_bg_color }};
          border: {{ block.settings.input_border_thickness }}px solid {{ block.settings.input_border_color | hex_to_rgba: 0.7 }};
        }

        .footer-newsletter-input-{{ block.id }}:focus {
          border: {{ block.settings.input_border_thickness }}px solid {{ block.settings.input_border_color | hex_to_rgba: 1 }};
        }

        .footer-newsletter-input-{{ block.id }}:focus-visible {
          outline: .2rem solid {{ block.settings.input_border_focus_color | hex_to_rgba: 0.5 }};
          outline-offset: .3rem; 
        }

        .footer-newsletter-input-{{ block.id }}::placeholder {
          color: {{ block.settings.input_placeholder_color }};
        }

        .footer-newsletter-submit-{{ block.id }} {
          margin-top: 0px;
          position: static !important;
          width: 100%;
          max-width: 100%;
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: {{ block.settings.submit_size_mobile }}px;
          color: {{ block.settings.submit_color }};
          line-height: {{ block.settings.submit_height }}%;
          text-align: center;
          text-transform: unset;
          text-decoration: none;
          padding: {{ block.settings.submit_padding_vertical | times: 0.75 | round: 0 }}px {{ block.settings.submit_padding_horizontal | times: 0.75 | round: 0 }}px;
          border-radius: {{ block.settings.submit_radius }}px;
          border: 0px;
          border: {{ block.settings.submit_border_thickness }}px solid {{ block.settings.submit_border_color }} ;
          background-color: transparent;
          background-color: {{ block.settings.submit_bg_color }};
          transition: all 0.25s ease 0s;
          box-shadow: none;
          cursor: pointer;
        }

        .footer-newsletter-submit-{{ block.id }}:hover {
          color: {{ block.settings.submit_hover_color }};
          border: {{ block.settings.submit_border_thickness }}px solid {{ block.settings.submit_border_hover_color }};
          background-color: {{ block.settings.submit_bg_hover_color }};
          transition: all 0.25s ease 0s;
        }

         .footer-newsletter-thanks-text-{{ block.id }} {
          display: none;
          margin: 0px;
          text-align: {{ block.settings.thanks_text_align_mobile }};
          margin-top: {{ block.settings.thanks_text_mt_mobile }}px;
          font-size: {{ block.settings.thanks_text_size_mobile }}px;
          color: {{ block.settings.thanks_text_color }};
          line-height: {{ block.settings.thanks_text_height }}%;
          text-transform: unset;
       }

       .footer-newsletter-thanks-text-{{ block.id }}.active {
         display: block;
       }

        @media(min-width: 768px) {

          .footer-newsletter-row-{{ block.id }} {
            flex-direction: row;
          }          

          .footer-newsletter-input-{{ block.id }} {
            width: 320px;
            max-width: 320px;
          }

          .footer-newsletter-submit-{{ block.id }} {
            max-width: fit-content;
          }
        }

        @media(min-width: 1024px) {
          .footer-form-{{ block.id }} {
            margin-top: {{ block.settings.form_mt }}px;
          }

          .footer-newsletter-input-{{ block.id }} {
            padding: {{ block.settings.input_padding_vertical }}px {{ block.settings.input_padding_horizontal }}px;
            font-size: {{ block.settings.input_size }}px;
          }

          .footer-newsletter-submit-{{ block.id }} {
            padding: {{ block.settings.submit_padding_vertical }}px {{ block.settings.submit_padding_horizontal }}px;
            font-size: {{ block.settings.submit_size }}px;
            max-width: fit-content;
          }

          .footer-newsletter-thanks-text-{{ block.id }} {
            text-align: {{ block.settings.thanks_text_align }};
            margin-top: {{ block.settings.thanks_text_mt }}px;
            font-size: {{ block.settings.thanks_text_size }}px;
          }
        }
      </style>

      {% if block.settings.input_custom %}
        <style>
