<script>
  function initFooter5() {    
    let thanksText = document.querySelector('.footer-newsletter-thanks-text-{{ section.id }}');
    let submitButton = document.querySelector('.footer-newsletter-submit-{{ section.id }}');
    const url = window.location.href;
    if (url.includes('customer_posted=true')) {
      if(thanksText) { 
        thanksText.classList.add('active');
      }
      if(submitButton) {
        submitButton.setAttribute('disabled', true);
      }
    }

    const localeItems = document.querySelectorAll('#localeItem');
    if(localeItems) {
      localeItems.forEach(item => {
        item.addEventListener('click', event => {
          document.querySelector('#localeCode').value = item.getAttribute('lang');
          document.querySelector('#localization_form_tag').submit();
        })
      })
    }
  }

  {% for block in section.blocks %}
    {% if block.type == 'contact' %}
      {% if block.settings.use_redirect %}
        let thanksText = document.querySelector('.footer-newsletter-thanks-text-{{ block.id }}');

        const redirectUrl = '{{ block.settings.redirect_url }}';
        const delayInMilliseconds = 2000;

        if (thanksText) {
          setTimeout(function() {
            window.location.href = redirectUrl;
          }, delayInMilliseconds);
        }
      {% endif %}
    {% endif %}
  {% endfor %}

  document.addEventListener('DOMContentLoaded', initFooter5);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:unload', initFooter5);
     document.addEventListener('shopify:section:load', initFooter5);
  }
</script>
