{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign padding_top_mobile = section.settings.padding_top_mobile
  assign padding_bottom_mobile = section.settings.padding_bottom_mobile
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign block_padding_vertical = section.settings.block_padding_vertical
  assign block_padding_vertical_mobile = section.settings.block_padding_vertical_mobile
  assign block_padding_horizontal = section.settings.block_padding_horizontal
  assign block_padding_horizontal_mobile = section.settings.block_padding_horizontal_mobile

  assign bottom_block_padding_vertical = section.settings.bottom_block_padding_vertical
  assign bottom_block_padding_vertical_mobile = section.settings.bottom_block_padding_vertical_mobile
  assign bottom_block_padding_horizontal = section.settings.bottom_block_padding_horizontal
  assign bottom_block_padding_horizontal_mobile = section.settings.bottom_block_padding_horizontal_mobile

  assign block_border_thickness = section.settings.block_border_thickness
  assign block_border_color = section.settings.block_border_color

  assign menu_gap_mobile = section.settings.menu_gap_mobile
  assign menu_gap = section.settings.menu_gap

  assign list_gap_mobile = section.settings.list_gap_mobile
  assign list_gap = section.settings.list_gap
  assign list_mt = section.settings.list_mt
  assign list_mt_mobile = section.settings.list_mt_mobile

  assign menu_item_border_color = section.settings.menu_item_border_color

  assign menu_heading_size = section.settings.menu_heading_size
  assign menu_heading_size_mobile = section.settings.menu_heading_size_mobile
  assign menu_heading_color = section.settings.menu_heading_color
  assign menu_heading_custom = section.settings.menu_heading_custom
  assign menu_heading_font = section.settings.menu_heading_font
  assign menu_heading_height = section.settings.menu_heading_height

  assign link_size = section.settings.link_size
  assign link_size_mobile = section.settings.link_size_mobile
  assign link_color = section.settings.link_color
  assign link_hover_color = section.settings.link_hover_color
  assign link_custom = section.settings.link_custom
  assign link_font = section.settings.link_font
  assign link_height = section.settings.link_height

  assign show_privacy_checkbox = section.settings.show_privacy_checkbox

  assign languages = section.settings.languages

  assign lang_size = section.settings.lang_size
  assign lang_size_mobile = section.settings.lang_size_mobile
  assign lang_color = section.settings.lang_color
  assign lang_hover_color = section.settings.lang_hover_color
  assign lang_custom = section.settings.lang_custom
  assign lang_font = section.settings.lang_font
  assign lang_height = section.settings.lang_height

  assign bottom_menu = section.settings.bottom_menu
  assign bottom_menu_hide_mobile = section.settings.bottom_menu_hide_mobile

  assign bottom_list_gap_mobile = section.settings.bottom_list_gap_mobile
  assign bottom_list_gap = section.settings.bottom_list_gap
  assign bottom_list_mt_mobile = section.settings.bottom_list_mt_mobile

  assign bottom_link_size = section.settings.bottom_link_size
  assign bottom_link_size_mobile = section.settings.bottom_link_size_mobile
  assign bottom_link_color = section.settings.bottom_link_color
  assign bottom_link_hover_color = section.settings.bottom_link_hover_color
  assign bottom_link_custom = section.settings.bottom_link_custom
  assign bottom_link_font = section.settings.bottom_link_font
  assign bottom_link_height = section.settings.bottom_link_height  
-%}

{%- style -%}

  {{ menu_heading_font | font_face: font_display: 'swap' }}
  {{ link_font | font_face: font_display: 'swap' }}
  {{ lang_font | font_face: font_display: 'swap' }}
  {{ bottom_link_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top_mobile }}px;
    padding-bottom: {{ padding_bottom_mobile }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .footer-top-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
  }

  .footer-right-{{ section.id }} {
    padding: {{ block_padding_vertical_mobile }}px {{ block_padding_horizontal_mobile }}px;
  }

  .footer-bottom-{{ section.id }} {
    padding: {{ bottom_block_padding_vertical_mobile }}px {{ bottom_block_padding_horizontal_mobile }}px;
  }

  .footer-left-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
    gap: {{ menu_gap_mobile }}px;
  }

  .footer-column-{{ section.id }} {
    border-bottom: 1px solid {{ menu_item_border_color }};
  }

  .footer-column-desktop-{{ section.id }} {
    display: none;
  }

  .footer-menu-heading-{{ section.id }} {
    margin: 0;
    font-size: {{ menu_heading_size_mobile }}px;
    color: {{ menu_heading_color }};
    line-height: {{ menu_heading_height }}%;
    text-transform: unset;
    text-decoration: none;
    font-weight: 700;
  }

  .footer-menu-list-{{ section.id }} {
    padding: 0px;
    margin: 0px;
    display: flex;
    flex-direction: column;
    gap: {{ list_gap_mobile }}px;
    margin-top: {{ list_mt_mobile }}px;
    list-style: none;
  }

  .footer-menu-item-{{ section.id }} {
    line-height: 100%;
  }

  .footer-menu-link-{{ section.id }} {
    margin: 0;
    font-size: {{ link_size_mobile }}px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
  }

  .footer-languages-list-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 10px;
  }

  .footer-languages-list-{{ section.id }} a {
    margin: 0;
    font-size: {{ lang_size_mobile }}px;
    color: {{ lang_color }};
    line-height: {{ lang_height }}%;
    text-transform: uppercase;
    text-decoration: none;
    transition: all 0.25s ease 0s;
  }

  .footer-languages-list-{{ section.id }} a:hover {
    color: {{ lang_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .footer-bottom-list-{{ section.id }} {
    margin: 0px;
    padding: 0px;
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: {{ bottom_list_gap_mobile }}px;
    list-style: none;
  }

  .footer-bottom-list-mobile-{{ section.id }} {
    margin-top: {{ bottom_list_mt_mobile }}px;
  }

  .footer-bottom-list-desktop-{{ section.id }},
  .footer-socials-desktop-{{ section.id }} {
    display: none !important;
  }

  .footer-bottom-item-{{ section.id }} {
    line-height: 100%;
  }

  .footer-bottom-link-{{ section.id }} {
    margin: 0;
    font-size: {{ bottom_link_size_mobile }}px;
    color: {{ bottom_link_color }};
    line-height: {{ bottom_link_height }}%;
    text-transform: unset;
    text-decoration: none;
  }

  .footer-bottom-row-{{ section.id }} {
    display: flex;
    gap: 20px;
    justify-content: space-between;
    align-items: center;
    flex-direction: row-reverse;
  }

  .footer-tab-toggle-{{ section.id }} {
    padding: 13px;
    background-color: transparent;
    border: 0px;
    box-shadow: none;
    display: flex;
    align-items: center;
    justify-content: space-between;
    gap: 10px;
    width: 100%;
    cursor: pointer;
  }

  .footer-tab-toggle-left-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 10px;
    flex: 1 1 auto;
  }

  .footer-tab-toggle-arrow-{{ section.id }} {
    flex: 0 0 15px;
    height: 15px;
    transition: all 0.3s ease 0s;
    transform: rotate(var(--rotate));
  }

  .footer-tab-toggle-arrow-{{ section.id }} path {
    fill: {{ menu_heading_color }};
  }

  .footer-tab-text-{{ section.id }} {
    padding-left: 10px;
    padding-right: 10px;
    transition: all 0.3s ease-in-out;
    overflow: hidden;
  }

  .footer-tab-height-cal-{{ section.id }} {
    visibility: hidden;
    opacity: 0;
    pointer-events: none;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
  }

  .footer-bottom-right-{{ section.id }} {
    flex: 1 1 auto;
  }

  @media(min-width: 768px) {
    .footer-bottom-list-{{ section.id }} {
      display: flex;
      align-items: center;
      justify-content: center;
      flex-wrap: wrap;
    }
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .footer-left-{{ section.id }},
    .footer-right-{{ section.id }} {
      padding: {{ block_padding_vertical }}px {{ block_padding_horizontal }}px;
    }

    .footer-right-{{ section.id }} {
      border-left: {{ block_border_thickness }}px solid {{ block_border_color }};
    }

    .footer-bottom-{{ section.id }} {
      border-top: {{ block_border_thickness }}px solid {{ block_border_color }};
      padding: {{ bottom_block_padding_vertical }}px {{ bottom_block_padding_horizontal }}px;
    }

    .footer-top-{{ section.id }} {
      grid-template-columns: 1fr 1fr;
    }

    .footer-left-{{ section.id }} {
      grid-template-columns: 1fr 1fr 1fr;
      gap: {{ menu_gap }}px;
    }

    .footer-column-{{ section.id }} {
      margin-left: 0px;
      margin-right: 0px;
      border: 0px;
    }

    .footer-column-mobile-{{ section.id }} {
      display: none;
    }

    .footer-column-desktop-{{ section.id }} {
      display: block;
    }

    .footer-menu-heading-{{ section.id }} {
      font-size: {{ menu_heading_size }}px;
    }

    .footer-menu-list-{{ section.id }} {
      gap: {{ list_gap }}px;
      margin-top: {{ list_mt }}px;
    }

    .footer-menu-link-{{ section.id }} {
      font-size: {{ link_size }}px;
      transition: all 0.25s ease 0s;
    }

    .footer-menu-link-{{ section.id }}:hover {
      transition: all 0.25s ease 0s;
      color: {{ link_hover_color }};
    }

    .footer-languages-list-{{ section.id }} a {
      font-size: {{ lang_size }}px;
    }

    .footer-bottom-list-{{ section.id }} {
      justify-content: start;
      gap: {{ bottom_list_gap }}px;
    }

    .footer-bottom-list-mobile-{{ section.id }},
    .footer-socials-mobile-{{ section.id }} {
      display: none !important;
    }

    .footer-bottom-list-desktop-{{ section.id }} {
      display: flex !important;
    }

    .footer-socials-desktop-{{ section.id }} {
      display: block !important;
    }

    .footer-bottom-link-{{ section.id }} {
      font-size: {{ bottom_link_size }}px;
      transition: all 0.25s ease 0s;
    }

    .footer-bottom-link-{{ section.id }}:hover {
      transition: all 0.25s ease 0s;
      color: {{ bottom_link_hover_color }};
    }

    .footer-bottom-row-{{ section.id }} {
      flex-direction: row;
    }

    .footer-bottom-right-{{ section.id }} {
     flex: none;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if menu_heading_custom %}
  <style>
    .footer-menu-heading-{{ section.id }} {
      font-family: {{ menu_heading_font.family }}, {{ menu_heading_font.fallback_families }};
      font-weight: {{ menu_heading_font.weight }};
      font-style: {{ menu_heading_font.style }};
    }
  </style>
{% endif %}

{% if link_custom %}
  <style>
    .footer-menu-link-{{ section.id }} {
      font-family: {{ link_font.family }}, {{ link_font.fallback_families }};
      font-weight: {{ link_font.weight }};
      font-style: {{ link_font.style }};
    }
  </style>
{% endif %}

{% if lang_custom %}
  <style>
    .footer-languages-list-{{ section.id }} a {
      font-family: {{ lang_font.family }}, {{ lang_font.fallback_families }};
      font-weight: {{ lang_font.weight }};
      font-style: {{ lang_font.style }};
    }
  </style>
{% endif %}

{% if bottom_link_custom %}
  <style>
    .footer-bottom-link-{{ section.id }} {
      font-family: {{ bottom_link_font.family }}, {{ bottom_link_font.fallback_families }};
      font-weight: {{ bottom_link_font.weight }};
      font-style: {{ bottom_link_font.style }};
    }
  </style>
{% endif %}

{% capture socials %}
  {% for block in section.blocks %}
    {% if block.type == "socials" %}
      <div class="footer-main-socials-{{ block.id }}">
      {% if block.settings.instagram != blank %}
        <div class="footer-main-social-{{ block.id }}">
          <a href="{{ block.settings.instagram }}">
            <svg role="img" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <title>Instagram icon</title><path d="M12 0C8.74 0 8.333.015 7.053.072 5.775.132 4.905.333 4.14.63c-.789.306-1.459.717-2.126 1.384S.935 3.35.63 4.14C.333 4.905.131 5.775.072 7.053.012 8.333 0 8.74 0 12s.015 3.667.072 4.947c.06 1.277.261 2.148.558 2.913.306.788.717 1.459 1.384 2.126.667.666 1.336 1.079 2.126 1.384.766.296 1.636.499 2.913.558C8.333 23.988 8.74 24 12 24s3.667-.015 4.947-.072c1.277-.06 2.148-.262 2.913-.558.788-.306 1.459-.718 2.126-1.384.666-.667 1.079-1.335 1.384-2.126.296-.765.499-1.636.558-2.913.06-1.28.072-1.687.072-4.947s-.015-3.667-.072-4.947c-.06-1.277-.262-2.149-.558-2.913-.306-.789-.718-1.459-1.384-2.126C21.319 1.347 20.651.935 19.86.63c-.765-.297-1.636-.499-2.913-.558C15.667.012 15.26 0 12 0zm0 2.16c3.203 0 3.585.016 4.85.071 1.17.055 1.805.249 2.227.415.562.217.96.477 1.382.896.419.42.679.819.896 1.381.164.422.36 1.057.413 2.227.057 1.266.07 1.646.07 4.85s-.015 3.585-.074 4.85c-.061 1.17-.256 1.805-.421 2.227-.224.562-.479.96-.899 1.382-.419.419-.824.679-1.38.896-.42.164-1.065.36-2.235.413-1.274.057-1.649.07-4.859.07-3.211 0-3.586-.015-4.859-.074-1.171-.061-1.816-.256-2.236-.421-.569-.224-.96-.479-1.379-.899-.421-.419-.69-.824-.9-1.38-.165-.42-.359-1.065-.42-2.235-.045-1.26-.061-1.649-.061-4.844 0-3.196.016-3.586.061-4.861.061-1.17.255-1.814.42-2.234.21-.57.479-.96.9-1.381.419-.419.81-.689 1.379-.898.42-.166 1.051-.361 2.221-.421 1.275-.045 1.65-.06 4.859-.06l.045.03zm0 3.678c-3.405 0-6.162 2.76-6.162 6.162 0 3.405 2.76 6.162 6.162 6.162 3.405 0 6.162-2.76 6.162-6.162 0-3.405-2.76-6.162-6.162-6.162zM12 16c-2.21 0-4-1.79-4-4s1.79-4 4-4 4 1.79 4 4-1.79 4-4 4zm7.846-10.405c0 .795-.646 1.44-1.44 1.44-.795 0-1.44-.646-1.44-1.44 0-.794.646-1.439 1.44-1.439.793-.001 1.44.645 1.44 1.439z"></path>
            </svg>
          </a>
        </div>
      {% endif %}
      {% if block.settings.linkedin != blank %}
        <div class="footer-main-social-{{ block.id }}">
          <a href="{{ block.settings.linkedin }}">
            <svg class="footer-social-link" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg">
        		<path d="M1.82 0C.82 0 0 .82 0 1.82v12.36C0 15.18.82 16 1.82 16h12.36c1 0 1.82-.82 1.82-1.82V1.82c0-1-.82-1.82-1.82-1.82H1.82Zm0 1.45h12.36c.2 0 .37.16.37.37v12.36c0 .2-.16.37-.37.37H1.82a.36.36 0 0 1-.37-.37V1.82c0-.2.16-.37.37-.37ZM3.95 2.7a1.25 1.25 0 1 0 0 2.5 1.25 1.25 0 0 0 0-2.5Zm6.57 3.32c-1.04 0-1.73.57-2.02 1.12h-.05v-.96H6.41v6.91h2.14V9.7c0-.9.17-1.78 1.3-1.78 1.09 0 1.1 1.02 1.1 1.82v3.36h2.14V9.3c0-1.85-.4-3.27-2.57-3.27Zm-7.61.16v6.91h2.16V6.2H2.9Z" fill="currentColor"></path>
        	</svg>
          </a>
        </div>
      {% endif %}
      {% if block.settings.twitter != blank %}
        <div class="footer-main-social-{{ block.id }}">
          <a href="{{ block.settings.twitter }}">
            <svg
              class="block m-auto footer-social-link"
              alt=""
              width="19"
              height="19"
              viewBox="0 0 19 19"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path fill-rule="evenodd" clip-rule="evenodd" d="M19 9.5C19 14.7467 14.7467 19 9.5 19C4.25329 19 0 14.7467 0 9.5C0 4.25329 4.25329 0 9.5 0C14.7467 0 19 4.25329 19 9.5ZM8.18721 10.0675L3.64633 3.99603H7.14609L10.123 7.97639L13.8073 3.99603H14.8358L10.5825 8.59099L15.379 15.0039H11.8792L8.64689 10.6819L4.6462 15.0039H3.61771L8.18721 10.0675ZM6.76655 4.75353H5.15877L12.2586 14.2464H13.8663L6.76655 4.75353Z" fill="{{ footer_border_color }}"></path>
            </svg>
          </a>
        </div>
      {% endif %}
      {% if block.settings.facebook != blank %}
        <div class="footer-main-social-{{ block.id }}">
          <a href="{{ block.settings.facebook }}">
            <svg  class="footer-social-link" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 8 18" fill="none" class="w-16 h-16">
        		<path d="M5.15 17.14h-3.1v-8.5H0V5.72h2.06V3.99c0-2.4.62-3.85 3.35-3.85h2.27v2.94H6.26c-1.06 0-1.11.4-1.11 1.16v1.47H7.7l-.3 2.93H5.14v8.5Z" fill="currentColor"></path>
        	</svg>
          </a>
        </div>
      {% endif %}
      {% if block.settings.youtube != blank %}
        <div class="footer-main-social-{{ block.id }}">
          <a href="{{ block.settings.youtube }}">
            <svg role="img" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
              <title>YouTube icon</title><path d="M23.499 6.203a3.008 3.008 0 00-2.089-2.089c-1.87-.501-9.4-.501-9.4-.501s-7.509-.01-9.399.501a3.008 3.008 0 00-2.088 2.09A31.258 31.26 0 000 12.01a31.258 31.26 0 00.523 5.785 3.008 3.008 0 002.088 2.089c1.869.502 9.4.502 9.4.502s7.508 0 9.399-.502a3.008 3.008 0 002.089-2.09 31.258 31.26 0 00.5-5.784 31.258 31.26 0 00-.5-5.808zm-13.891 9.4V8.407l6.266 3.604z"></path>
            </svg>
          </a>
        </div>
      {% endif %}

      {% if block.settings.pinterest != blank %}
        <div class="footer-main-social-{{ block.id }}">
          <a href="{{ block.settings.pinterest }}">
            <svg role="img" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <title>Pinterest icon</title><path d="M12.017 0C5.396 0 .029 5.367.029 11.987c0 5.079 3.158 9.417 7.618 11.162-.105-.949-.199-2.403.041-3.439.219-.937 1.406-5.957 1.406-5.957s-.359-.72-.359-1.781c0-1.663.967-2.911 2.168-2.911 1.024 0 1.518.769 1.518 1.688 0 1.029-.653 2.567-.992 3.992-.285 1.193.6 2.165 1.775 2.165 2.128 0 3.768-2.245 3.768-5.487 0-2.861-2.063-4.869-5.008-4.869-3.41 0-5.409 2.562-5.409 5.199 0 1.033.394 2.143.889 2.741.099.12.112.225.085.345-.09.375-.293 1.199-.334 1.363-.053.225-.172.271-.401.165-1.495-.69-2.433-2.878-2.433-4.646 0-3.776 2.748-7.252 7.92-7.252 4.158 0 7.392 2.967 7.392 6.923 0 4.135-2.607 7.462-6.233 7.462-1.214 0-2.354-.629-2.758-1.379l-.749 2.848c-.269 1.045-1.004 2.352-1.498 3.146 1.123.345 2.306.535 3.55.535 6.607 0 11.985-5.365 11.985-11.987C23.97 5.39 18.592.026 11.985.026L12.017 0z"></path>
            </svg>
          </a>
        </div>
      {% endif %}

      {% if block.settings.whatsapp != blank %}
        <div class="footer-main-social-{{ block.id }}">
          <a href="{{ block.settings.whatsapp }}">
