{%- style -%}

  {{  text_font | font_face: font_display: 'swap' }}
  {{  input_font | font_face: font_display: 'swap' }}
  {{  submit_font | font_face: font_display: 'swap' }}
  {{  privacy_text_font | font_face: font_display: 'swap' }}
  {{  column_heading_font | font_face: font_display: 'swap' }}
  {{  link_font | font_face: font_display: 'swap' }}
  {{  column_text_font | font_face: font_display: 'swap' }}
  {{  thanks_text_font | font_face: font_display: 'swap' }}      

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .footer-body-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
  }

  .footer-left-{{ section.id }},
  .footer-right-{{ section.id }} {
    padding: {{ block_padding_vertical_mobile }}px {{ block_padding_horizontal_mobile }}px;
  }

  .footer-right-{{ section.id }} {
    display: flex;
    align-items: stretch;
    gap: 20px;
    flex-wrap: wrap;
    border-top: {{ block_border_thickness }}px solid {{ block_border_color }};
  }

  .footer-column-{{ section.id }} {
    flex: 1 0;
  }

  .footer-text-column-{{ section.id }} {
    flex: 1 0 100%;
  }

  .footer-text-{{ section.id }} {
    text-align: {{ text_align_mobile }};
  }

  .footer-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
  }

  .footer-newsletter-row-{{ section.id }} {
    position: relative;
    display: flex;
    align-items: stretch;
    gap: {{ newsletter_gap_mobile }}px;
    border-radius: {{ newsletter_radius }}px;
    overflow: hidden;
    margin-top: {{ newsletter_mt_mobile }}px;
  }

  .footer-newsletter-input-{{ section.id }} {
    width: 100%;
    margin: 0;
    box-shadow: none;
    border: 0px;
    padding: {{ input_padding_vertical_mobile }}px {{ input_padding_horizontal_mobile }}px;
    font-size: {{ input_size_mobile }}px;
    color: {{ input_color }};
    line-height: {{ input_height }}%;
    text-transform: unset;
    border-radius: {{ input_radius }}px;
    border: {{ input_border_thickness }}px solid {{ input_border_color }};
    background-color: transparent;
    background-color: {{ input_bg_color }};
  }

  .footer-newsletter-input-{{ section.id }}:focus,
  .footer-newsletter-input-{{ section.id }}:focus-visible {
    outline: none;
    box-shadow: none;
  }

  .footer-newsletter-input-{{ section.id }}::placeholder {
    color: {{ input_placeholder_color }};
  }

  .footer-newsletter-submit-{{ section.id }} {
    margin-top: 0px;
    width: 100%;
    max-width: fit-content;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: {{ submit_size_mobile }}px;
    color: {{ submit_color }};
    line-height: {{ submit_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ submit_padding_vertical_mobile }}px {{ submit_padding_horizontal_mobile }}px;
    border-radius: {{ submit_radius }}px;
    border: 0px;
    border: {{ submit_border_thickness }}px solid {{ submit_border_color }};
    background-color: transparent;
    background-color: {{ submit_bg_color }};
    transition: all 0.25s ease 0s;
    box-shadow: none;
    cursor: pointer;
    position: relative;
  }

  .footer-newsletter-submit-{{ section.id }}:hover {
    color: {{ submit_hover_color }};
    border: {{ submit_border_thickness }}px solid {{ submit_border_hover_color }};
    background-color: {{ submit_bg_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .footer-newsletter-submit-{{ section.id }}::before {
    content: '';
    position: absolute;
    left: -{{ newsletter_border_thickness }}px;
    top: 0;
    display: block;
    width: {{ newsletter_border_thickness }}px;
    height: 100%;
    background-color: {{ newsletter_border_color }};
  }

 .footer-newsletter-thanks-text-{{ section.id }} {
    display: none;
    margin: 0px;
    margin-top: {{ thanks_text_mt_mobile }}px;
    font-size: {{ thanks_text_size_mobile }}px;
    color: {{ thanks_text_color }};
    line-height: {{ thanks_text_height }}%;
    text-transform: unset;
 }

 .footer-newsletter-thanks-text-{{ section.id }}.active {
   display: block;
 }


  .footer-privacy-text-{{ section.id }} {
    margin-top: {{ privacy_text_mt_mobile }}px;
    text-align: {{ privacy_text_align_mobile }};
  }

  .footer-privacy-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ privacy_text_size_mobile }}px;
    color: {{ privacy_text_color }};
    line-height: {{ privacy_text_height }}%;
    text-transform: unset;
  }

  .footer-column-heading-{{ section.id }} {
    margin: 0;
    font-size: {{ column_heading_size_mobile }}px;
    color: {{ column_heading_color }};
    line-height: {{ column_heading_height }}%;
    text-transform: unset;
    text-decoration: none;
    font-weight: 700;
  }

  .footer-menu-list-{{ section.id }},
  .footer-social-list-{{ section.id }} {
    padding: 0px;
    margin: 0px;
    display: flex;
    flex-direction: column;
    gap: {{ list_gap_mobile }}px;
    margin-top: {{ list_mt_mobile }}px;
    list-style: none;
  }

  .footer-menu-item-{{ section.id }},
  .footer-social-item-{{ section.id }} {
    line-height: 100%;
  }

  .footer-menu-link-{{ section.id }},
  .footer-social-link-{{ section.id }} {
    margin: 0;
    font-size: {{ link_size_mobile }}px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
  }

  .footer-social-link-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 8px;
  }

  .footer-social-link-{{ section.id }} svg {
    display: block;
    width: 20px;
    height: 20px;
  }

  .footer-social-link-{{ section.id }} svg path {
    fill: {{ link_color }};
    transition: all 0.25s ease 0s;
  }

  .footer-column-text-{{ section.id }} {
    margin-top: {{ column_text_mt_mobile }}px;
  }

  .footer-column-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ column_text_size_mobile }}px;
    color: {{ column_text_color }};
    line-height: {{ column_text_height }}%;
    text-transform: unset;
    text-decoration: none;
  }

  .footer-column-text-{{ section.id }} *:not(:first-child) {
    margin-top: 10px;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .footer-body-{{ section.id }} {
      grid-template-columns: 40% 60%;
    }

    .footer-left-{{ section.id }},
    .footer-right-{{ section.id }}{
      padding: {{ block_padding_vertical }}px {{ block_padding_horizontal }}px;
    }

    .footer-right-{{ section.id }} {
      border-top: 0px;
      border-left: {{ block_border_thickness }}px solid {{ block_border_color }};
    }

    .footer-text-column-{{ section.id }} {
      flex: 1 0 15%;
    }

    .footer-text-{{ section.id }} {
      text-align: {{ text_align }};
    }

    .footer-text-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }

    .footer-newsletter-row-{{ section.id }} {
      margin-top: {{ newsletter_mt }}px;
      gap: {{ newsletter_gap }}px;
    }

    .footer-newsletter-input-{{ section.id }} {
      padding: {{ input_padding_vertical }}px {{ input_padding_horizontal }}px;
      font-size: {{ input_size }}px;
    }

    .footer-newsletter-submit-{{ section.id }} {
      padding: {{ submit_padding_vertical }}px {{ submit_padding_horizontal }}px;
      font-size: {{ submit_size }}px;
    }

     .footer-newsletter-thanks-text-{{ section.id }} {
     margin-top: {{ thanks_text_mt }}px;
     font-size: {{ thanks_text_size }}px;
   }

    .footer-privacy-text-{{ section.id }} {
      margin-top: {{ privacy_text_mt }}px;
      text-align: {{ privacy_text_align }};
    }

    .footer-privacy-text-{{ section.id }} * {
      font-size: {{ privacy_text_size }}px;
    }

    .footer-column-heading-{{ section.id }} {
      font-size: {{ column_heading_size }}px;
    }

    .footer-menu-list-{{ section.id }},
    .footer-social-list-{{ section.id }}{
      gap: {{ list_gap }}px;
      margin-top: {{ list_mt }}px;
    }

    .footer-menu-link-{{ section.id }},
    .footer-social-link-{{ section.id }} {
      font-size: {{ link_size }}px;
      transition: all 0.25s ease 0s;
    }

    .footer-menu-link-{{ section.id }}:hover,
    .footer-social-link-{{ section.id }}:hover {
      transition: all 0.25s ease 0s;
      color: {{ link_hover_color }};
    }

    .footer-social-link-{{ section.id }}:hover svg path {
       fill: {{ link_hover_color }};
       transition: all 0.25s ease 0s;
    }

    .footer-column-text-{{ section.id }} {
      margin-top: {{ column_text_mt }}px;
    }

    .footer-column-text-{{ section.id }} * {
      font-size: {{ column_text_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if input_custom %}
  <style>
    .footer-newsletter-input-{{ section.id }} {
      font-family: {{ input_font.family }}, {{ input_font.fallback_families }};
      font-weight: {{ input_font.weight }};
      font-style: {{ input_font.style }};
    }
  </style>
{% endif %}

{% if thanks_text_custom %}
  <style>
    .footer-newsletter-thanks-text-{{ section.id }} {
      font-family: {{ thanks_text_font.family }}, {{ thanks_text_font.fallback_families }};
      font-weight: {{ thanks_text_font.weight }};
      font-style: {{ thanks_text_font.style }};
    }
  </style>
{% endif %}


{% if submit_custom %}
  <style>
    .footer-newsletter-submit-{{ section.id }} {
      font-family: {{ submit_font.family }}, {{ submit_font.fallback_families }};
      font-weight: {{ submit_font.weight }};
      font-style: {{ submit_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .footer-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if privacy_text_custom %}
  <style>
    .footer-privacy-text-{{ section.id }} * {
      font-family: {{ privacy_text_font.family }}, {{ privacy_text_font.fallback_families }};
      font-weight: {{ privacy_text_font.weight }};
      font-style: {{ privacy_text_font.style }};
    }
  </style>
{% endif %}

{% if column_heading_custom %}
  <style>
    .footer-column-heading-{{ section.id }} {
      font-family: {{ column_heading_font.family }}, {{ column_heading_font.fallback_families }};
      font-weight: {{ column_heading_font.weight }};
      font-style: {{ column_heading_font.style }};
    }
  </style>
{% endif %}

{% if link_custom %}
  <style>
    .footer-menu-link-{{ section.id }},
    .footer-social-link-{{ section.id }} {
      font-family: {{ link_font.family }}, {{ link_font.fallback_families }};
      font-weight: {{ link_font.weight }};
      font-style: {{ link_font.style }};
    }
  </style>
{% endif %}

{% if column_text_custom %}
  <style>
    .footer-column-text-{{ section.id }} {
      font-family: {{ column_text_font.family }}, {{ column_text_font.fallback_families }};
      font-weight: {{ column_text_font.weight }};
      font-style: {{ column_text_font.style }};
    }
  </style>
{% endif %}

{% for block in section.blocks %}
  {% case block.type %}
    {% when 'logo' %}

      <style>

      {{ block.settings.text_font | font_face: font_display: 'swap' }}

      .footer-logo-{{ block.id }} {
        padding: {{ block_padding_vertical_mobile }}px {{ block_padding_horizontal_mobile }}px;
        border-bottom: {{ block_border_thickness }}px solid {{ block_border_color }};
      }

      .footer-logo-image-{{ block.id }} {
        display: block;
        margin-left: auto;
        margin-right: auto;
        width: 100%;
        max-width: {{ block.settings.image_width_mobile }}%;
      }

      .footer-logo-image-{{ block.id }} img {
        display: block;
        width: 100%;
        height: 100%;
        object-fit: cover;
      }

      .footer-logo-text-{{ block.id }} {
        display: block;
        text-decoration: none;
        max-width: fit-content;
        margin-left: auto;
        margin-right: auto;
      }

      .footer-logo-text-{{ block.id }} * {
        margin: 0;
        font-size: {{ block.settings.text_size_mobile }}px;
        color: {{ block.settings.text_color }};
        line-height: {{ block.settings.text_height }}%;
        text-transform: unset;
        text-decoration: none;
      }

      @media(min-width: 1024px) {

        .footer-logo-{{ block.id }} {
          padding: {{ block_padding_vertical }}px {{ block_padding_horizontal }}px;
        }

        .footer-logo-image-{{ block.id }} {
          max-width: {{ block.settings.image_width }}%;
        }

        .footer-logo-text-{{ block.id }} * {
          font-size: {{ block.settings.text_size }}px;
        }
      }
    </style>

    {% if block.settings.text_custom %}
      <style>
        .footer-logo-text-{{ block.id }} * {
          font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
          font-weight: {{ block.settings.text_font.weight }};
          font-style: {{ block.settings.text_font.style }};
        }
      </style>
    {% endif %}


  {% endcase %}
{% endfor %}

<div class="section-{{ section.id }} footer-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      {% for block in section.blocks %}
        {% if block.type == "logo" %}
          <div class="footer-logo-{{ block.id }}">
            {% if block.settings.logo != blank %}
              <a href="{{ shop.url }}" class="footer-logo-image-{{ block.id }}">
                <img src="{{ block.settings.logo | image_url }}" alt="{{ block.settings.logo.alt }}" {% if lazy %}loading="lazy"{% endif %}>
              </a>
            {% endif %}
            {% if block.settings.text != blank %}
              <a href="{{ shop.url }}" class="footer-logo-text-{{ block.id }}">{{ block.settings.text }}</a>
            {% endif %}
          </div>
        {% endif %}
      {% endfor %}

      <div class="footer-body-{{ section.id }}">
        <div class="footer-left-{{ section.id }}">
          {% if text != blank %}
            <div class="footer-text-{{ section.id }}">
              {{ text }}
            </div>
          {% endif %}
          {% if use_newsletter %}
            {% form 'customer', class: 'footer-newsletter-form' %}
              <input type="hidden" name="contact[tags]" value="newsletter">

              <div class="footer-newsletter-row-{{ section.id }}">
                <input
                  type="email"
                  name="contact[email]"
                  class="footer-newsletter-input-{{ section.id }}"
                  value="{{ form.email }}"
                  aria-required="true"
