<script>
  function initFooter7() {
    let thanksText = document.querySelector('.footer-newsletter-thanks-text-{{ section.id }}');
    let submitButton = document.querySelector('.footer-newsletter-submit-{{ section.id }}');

    const url = window.location.href;
    if (url.includes('customer_posted=true')) {
      if(thanksText) { 
        thanksText.classList.add('active');
        setTimeout(() => {
          thanksText.classList.remove('active');
        }, 5000);
      }
      if(submitButton) {
        submitButton.setAttribute('disabled', true);
      }
    }

    {% if use_redirect %}
      const redirectUrl = '{{ redirect_url }}';
      const delayInMilliseconds = 2000;

      if (thanksText) {
        setTimeout(function() {
          window.location.href = redirectUrl;
        }, delayInMilliseconds);
      }
    {% endif %}
  }

  document.addEventListener('DOMContentLoaded', initFooter7);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:unload', initFooter7);
     document.addEventListener('shopify:section:load', initFooter7);
  }
</script>
