{%- style -%}

  {{ newsletter_heading_font | font_face: font_display: 'swap' }}
  {{ input_font | font_face: font_display: 'swap' }}
  {{ logo_text_font | font_face: font_display: 'swap' }}
  {{ text_font | font_face: font_display: 'swap' }}
  {{ menu_heading_font | font_face: font_display: 'swap' }}
  {{ link_font | font_face: font_display: 'swap' }}
  {{ copy_text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .footer-newsletter-heading-{{ section.id }} {
    text-align: {{ newsletter_heading_align_mobile }};
  }

  .footer-newsletter-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ newsletter_heading_size_mobile }}px;
    color: {{ newsletter_heading_color }};
    line-height: {{ newsletter_heading_height }}%;
    text-transform: unset;
    text-decoration: none;
    font-weight: 700;
  }

  .footer-newsletter-input-wrapper-{{ section.id }} {
    margin-top: {{ input_mt_mobile }}px;
    position: relative;
    z-index: 1;
  }

  .footer-newsletter-input-{{ section.id }} {
    width: 100%;
    margin: 0;
    box-shadow: none;
    border: 0px;
    border-radius: 0px;
    font-size: {{ input_size_mobile }}px;
    color: {{ input_color }};
    line-height: {{ input_height }}%;
    text-transform: unset;
    border-bottom: {{ input_border_thickness }}px solid {{ input_border_color }};
    background-color: transparent;
    padding-top: 8px;
    padding-bottom: 8px;
    padding-right: 50px;
  }

  .footer-newsletter-input-{{ section.id }}:focus,
  .footer-newsletter-input-{{ section.id }}:focus-visible {
    box-shadow: none;
    outline: none;
  }

  .footer-newsletter-input-{{ section.id }}::placeholder {
    color: {{ input_placeholder_color }};
  }

  .footer-newsletter-btn-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: transparent;
    padding: 5px;
    width: 44px;
    height: 100%;
    border: 0px;
    box-shadow: none;
    position: absolute;
    top: 0;
    right: 0;
    z-index: 2;
    cursor: pointer;
  }

  .footer-newsletter-btn-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .footer-logo-{{ section.id }} {
    margin-top: {{ logo_mt_mobile }}px;
  }

  .footer-logo-image-{{ section.id }} {
    width: 100%;
    max-width: {{ logo_width_mobile }}%;
  }

  .footer-logo-image-{{ section.id }} img {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .footer-logo-text-{{ section.id }} {
    text-align: {{ logo_align_mobile }};
  }

  .footer-logo-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ logo_text_size_mobile }}px;
    color: {{ logo_text_color }};
    line-height: {{ logo_text_height }}%;
    text-transform: unset;
  }

  .footer-text-{{ section.id }} {
    margin-top: {{ text_mt_mobile }}px;
    text-align: {{ text_align_mobile }};
  }

  .footer-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
  }

  .footer-body-{{ section.id }} {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: {{ menus_gap_mobile }}px;
  }

  .footer-images-{{ section.id }} {
    margin-top: {{ menus_mt_mobile }}px;
    display: grid;
    gap: 16px;
    grid-template-columns: repeat({{ image_grid_mobile }}, 1fr);
  }

  .footer-image-{{ section.id }} {
    border-radius: {{ image_radius }}px;
    overflow: hidden;
  }

  .footer-image-{{ section.id }} img,
  .footer-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .footer-image-{{ section.id }} svg {
    background-color: white;
  }

  .footer-image-{{ section.id }} svg path{
    fill: {{ background_color }}; 
  }

  .footer-menu-{{ section.id }} .footer-image-{{ section.id }} {
    display: none;
  }

  .footer-menu-heading-{{ section.id }} {
    margin: 0;
    margin-top: {{ menu_heading_mt_mobile }}px;
    font-size: {{ menu_heading_size_mobile }}px;
    color: {{ menu_heading_color }};
    line-height: {{ menu_heading_height }}%;
    text-transform: unset;
    text-decoration: none;
    font-weight: 700;
  }

  .footer-menu-list-{{ section.id }} {
    padding: 0px;
    margin: 0px;
    display: flex;
    flex-direction: column;
    gap: {{ list_gap_mobile }}px;
    margin-top: {{ list_mt_mobile }}px;
    list-style: none;
  }

  .footer-menu-item-{{ section.id }} {
    line-height: 100%;
  }

  .footer-menu-link-{{ section.id }} {
    margin: 0;
    font-size: {{ link_size_mobile }}px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
  }

  .footer-copy-text-{{ section.id }} {
    margin-top: {{ copy_text_mt_mobile }}px;
    text-align: {{ copy_text_align_mobile }};
  }

  .footer-copy-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ copy_text_size_mobile }}px;
    color: {{ copy_text_color }};
    line-height: {{ copy_text_height }}%;
    text-transform: unset;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .footer-newsletter-heading-{{ section.id }} {
      text-align: {{ newsletter_heading_align }};
    }

    .footer-newsletter-heading-{{ section.id }} * {
      font-size: {{ newsletter_heading_size }}px;
    }

    .footer-newsletter-input-wrapper-{{ section.id }} {
      margin-top: {{ input_mt }}px;
    }

    .footer-newsletter-input-{{ section.id }} {
      font-size: {{ input_size}}px;
    }

    .footer-logo-{{ section.id }} {
      margin-top: {{ logo_mt }}px;
    }

    .footer-logo-image-{{ section.id }} {
      max-width: {{ logo_width }}%;
    }

    .footer-logo-text-{{ section.id }} {
      text-align: {{ logo_align }};
    }

    .footer-logo-text-{{ section.id }} * {
      font-size: {{ logo_text_size }}px;
    }

    .footer-text-{{ section.id }} {
      margin-top: {{ text_mt }}px;
      text-align: {{ text_align }};
    }

    .footer-text-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }

    .footer-body-{{ section.id }} {
      display: flex;
      gap: {{ menus_gap }}px;
      margin-top: {{ menus_mt }}px;
      flex-wrap: wrap;
    }

    .footer-menu-{{ section.id }} {
      flex: 1 0;
    }

    .footer-images-{{ section.id }} {
      display: none;
    }

    .footer-menu-{{ section.id }} .footer-image-{{ section.id }} {
      display: block;
    }

    .footer-menu-heading-{{ section.id }} {
      margin-top: {{ menu_heading_mt }}px;
      font-size: {{ menu_heading_size }}px;
    }

    .footer-menu-list-{{ section.id }} {
      gap: {{ list_gap }}px;
      margin-top: {{ list_mt }}px;
    }

    .footer-menu-link-{{ section.id }} {
      font-size: {{ link_size }}px;
      transition: all 0.25s ease 0s;
    }

    .footer-menu-link-{{ section.id }}:hover {
      transition: all 0.25s ease 0s;
      color: {{ link_hover_color }};
    }

    .footer-copy-text-{{ section.id }} {
      margin-top: {{ copy_text_mt }}px;
      text-align: {{ copy_text_align }};
    }

    .footer-copy-text-{{ section.id }} * {
      font-size: {{ copy_text_size }}px;
    }

  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if newsletter_heading_custom %}
  <style>
    .footer-newsletter-heading-{{ section.id }} * {
      font-family: {{ newsletter_heading_font.family }}, {{ newsletter_heading_font.fallback_families }};
      font-weight: {{ newsletter_heading_font.weight }};
      font-style: {{ newsletter_heading_font.style }};
    }
  </style>
{% endif %}

{% if input_custom %}
  <style>
    .footer-newsletter-input-{{ section.id }} {
      font-family: {{ input_font.family }}, {{ input_font.fallback_families }};
      font-weight: {{ input_font.weight }};
      font-style: {{ input_font.style }};
    }
  </style>
{% endif %}

{% if logo_text_custom %}
  <style>
    .footer-logo-text-{{ section.id }} * {
      font-family: {{ logo_text_font.family }}, {{ logo_text_font.fallback_families }};
      font-weight: {{ logo_text_font.weight }};
      font-style: {{ logo_text_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .footer-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if menu_heading_custom %}
  <style>
    .footer-menu-heading-{{ section.id }} {
      font-family: {{ menu_heading_font.family }}, {{ menu_heading_font.fallback_families }};
      font-weight: {{ menu_heading_font.weight }};
      font-style: {{ menu_heading_font.style }};
    }
  </style>
{% endif %}

{% if link_custom %}
  <style>
    .footer-menu-link-{{ section.id }} {
      font-family: {{ link_font.family }}, {{ link_font.fallback_families }};
      font-weight: {{ link_font.weight }};
      font-style: {{ link_font.style }};
    }
  </style>
{% endif %}

{% if copy_text_custom %}
  <style>
    .footer-copy-text-{{ section.id }} * {
      font-family: {{ copy_text_font.family }}, {{ copy_text_font.fallback_families }};
      font-weight: {{ copy_text_font.weight }};
      font-style: {{ copy_text_font.style }};
    }
  </style>
{% endif %}

{% if logo_align_mobile == "center" %}
  <style>
    .footer-logo-image-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    }
  </style>
{% elsif logo_align_mobile == "right" %}
  <style>
    .footer-logo-image-{{ section.id }} {
      margin-left: auto;
    }
  </style>
{% endif %}

{% if logo_align == "center" %}
  <style>
    @media(min-width: 1024px) {
      .footer-logo-image-{{ section.id }} {
        margin-left: auto;
        margin-right: auto;
      } 
    }
  </style>
{% elsif logo_align == "right" %}
  <style>
    @media(min-width: 1024px) {
      .footer-logo-image-{{ section.id }} {
        margin-left: auto;
        margin-right: 0px;
      } 
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
      .footer-logo-image-{{ section.id }} {
        margin-left: 0px;
        margin-right: 0px;
      } 
    }
  </style>
{% endif %}

{% if image_ratio_mobile == "portrait" %}
	<style>
    .footer-image-{{ section.id }} {
      aspect-ratio: 9.6/14;
    }
	</style>
{% elsif image_ratio_mobile == "landscape" %}
	<style>
    .footer-image-{{ section.id }} {
      aspect-ratio: 12/6;
    }
	</style>
{% elsif image_ratio_mobile == "square" %}
	<style>
    .footer-image-{{ section.id }} {
      aspect-ratio: 12/12;
    }
	</style>
{% else %}
	<style>
    .footer-image-{{ section.id }} {
      aspect-ratio: auto;
    }
	</style>
{% endif %}

{% if image_ratio == "portrait" %}
	<style>
    @media (min-width: 1024px) {
        .footer-image-{{ section.id }} {
          aspect-ratio: 9.6/14;
        }
    }
	</style>
{% elsif image_ratio == "landscape" %}
	<style>
    @media (min-width: 1024px) {
        .footer-image-{{ section.id }} {
          aspect-ratio: 12/6;
        }
    }
	</style>
{% elsif image_ratio == "square" %}
	<style>
    @media (min-width: 1024px) {
        .footer-image-{{ section.id }} {
          aspect-ratio: 12/12;
        }
    }
	</style>
{% else %}
	<style>
    @media (min-width: 1024px) {
        .footer-image-{{ section.id }} {
          aspect-ratio: auto;
        }
    }
	</style>
{% endif %}

<div class="section-{{ section.id }} footer-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="footer-newsletter-{{ section.id }}">
        <div class="footer-newsletter-heading-{{ section.id }}">{{ newsletter_heading }}</div>
        {% form 'customer', class: 'footer-newsletter-form' %}

          <div class="footer-newsletter-input-wrapper-{{ section.id }}">
            <input
              type="email"
              name="contact[email]"
              class="footer-newsletter-input-{{ section.id }}"
              value="{{ form.email }}"
              aria-required="true"
              autocorrect="off"
              autocapitalize="off"
              autocomplete="email"
              {% if form.errors %}
                autofocus
                aria-invalid="true"
                aria-describedby="Newsletter-error--{{ section.id }}"
              {% elsif form.posted_successfully? %}
                aria-describedby="Newsletter-success--{{ section.id }}"
              {% endif %}
              placeholder="{{ input_placeholder }}"
              required
            >
            <button type="submit" class="footer-newsletter-btn-{{ section.id }}" name="commit">
              <svg width="66" height="44" viewBox="0 0 66 44" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M43.4483 41.7695L63 21.7695M63 21.7695L43.4483 1.76953M63 21.7695L7.94781e-07 21.7695" stroke="{{ btn_arrow_color }}" stroke-width="4"></path>
              </svg>
            </button>
          </div>

          <input type="hidden" name="contact[tags]" value="newsletter">
        {% endform %}
      </div>

      <div class="footer-logo-{{ section.id }}">
        {% if logo != blank %}
          <div class="footer-logo-image-{{ section.id }}">
            <img src="{{ logo | image_url }}" alt="{{ logo.alt }}" {% if lazy %}loading="lazy"{% endif %}>
          </div>
        {% else %}
          <div class="footer-logo-text-{{ section.id }}">
            {% if logo_text != blank %}
              {{ logo_text }}
            {% else %}
              <h2>{{ shop.name }}</h2>
            {% endif %}
          </div>
        {% endif %}
      </div>

      <div class="footer-text-{{ section.id }}">{{ text }}</div>

      <div class="footer-images-{{ section.id }}">
        {% for block in section.blocks %}
          <div class="footer-image-{{ section.id }}">
            {% if block.settings.image != blank %}
              <img src="{{ block.settings.image | image_url }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
            {% else %}
              {{ 'image' | placeholder_svg_tag }}
            {% endif %}
          </div>
        {% endfor %}
      </div>

      <div class="footer-body-{{ section.id }}">
        {% for block in section.blocks %}
          <div class="footer-menu-{{ section.id }}">
            <div class="footer-image-{{ section.id }}">
              {% if block.settings.image != blank %}
                <img src="{{ block.settings.image | image_url }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
              {% else %}
                {{ 'image' | placeholder_svg_tag }}
              {% endif %}
            </div>

            <h3 class="footer-menu-heading-{{ section.id }}">{{ block.settings.heading }}</h3>

            {% if block.settings.menu != blank %}
              <ul class="footer-menu-list-{{ section.id }}">
                {% for link in block.settings.menu.links %}
                  <li class="footer-menu-item-{{ section.id }}">
                    <a href="{{ link.url }}" class="footer-menu-link-{{ section.id }}">{{ link.title }}</a>
                  </li>
                {% endfor %}
              </ul>
            {% endif %}
          </div>
        {% endfor %}
      </div>

      <div class="footer-copy-text-{{ section.id }}">{{ copy_text }}</div>
    </div>
</div>
