{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius
  assign radius_top = section.settings.radius_top

  assign layout_gap_mobile = section.settings.layout_gap_mobile
  assign layout_gap = section.settings.layout_gap

  assign menu_mt = section.settings.menu_mt
  assign menu_mt_mobile = section.settings.menu_mt_mobile
  assign menu_gap = section.settings.menu_gap
  assign menu_gap_mobile = section.settings.menu_gap_mobile

  assign column_heading_size = section.settings.column_heading_size
  assign column_heading_size_mobile = section.settings.column_heading_size_mobile
  assign column_heading_color = section.settings.column_heading_color
  assign column_heading_custom = section.settings.column_heading_custom
  assign column_heading_font = section.settings.column_heading_font
  assign column_heading_height = section.settings.column_heading_height

  assign list_gap_mobile = section.settings.list_gap_mobile
  assign list_gap = section.settings.list_gap
  assign list_mt_mobile = section.settings.list_mt_mobile
  assign list_mt = section.settings.list_mt

  assign link_size = section.settings.link_size
  assign link_size_mobile = section.settings.link_size_mobile
  assign link_color = section.settings.link_color
  assign link_hover_color = section.settings.link_hover_color
  assign link_custom = section.settings.link_custom
  assign link_font = section.settings.link_font
  assign link_height = section.settings.link_height

  assign image_radius = section.settings.image_radius
  assign image_border_thickness = section.settings.image_border_thickness
  assign image_border_color = section.settings.image_border_color
  assign image_overlay = section.settings.image_overlay
  assign image_overlay_color = section.settings.image_overlay_color

  assign image_title_size = section.settings.image_title_size
  assign image_title_size_mobile = section.settings.image_title_size_mobile
  assign image_title_color = section.settings.image_title_color
  assign image_title_custom = section.settings.image_title_custom
  assign image_title_font = section.settings.image_title_font
  assign image_title_height = section.settings.image_title_height

  assign image_btn_size = section.settings.image_btn_size
  assign image_btn_size_mobile = section.settings.image_btn_size_mobile
  assign image_btn_color = section.settings.image_btn_color
  assign image_btn_hover_color = section.settings.image_btn_hover_color
  assign image_btn_custom = section.settings.image_btn_custom
  assign image_btn_font = section.settings.image_btn_font
  assign image_btn_height = section.settings.image_btn_height
  assign image_btn_padding_vertical = section.settings.image_btn_padding_vertical
  assign image_btn_padding_vertical_mobile = section.settings.image_btn_padding_vertical_mobile
  assign image_btn_padding_horizontal = section.settings.image_btn_padding_horizontal
  assign image_btn_padding_horizontal_mobile = section.settings.image_btn_padding_horizontal_mobile
  assign image_btn_border_thickness = section.settings.image_btn_border_thickness
  assign image_btn_border_color = section.settings.image_btn_border_color
  assign image_btn_border_hover_color = section.settings.image_btn_border_hover_color
  assign image_btn_bg_color = section.settings.image_btn_bg_color
  assign image_btn_bg_hover_color = section.settings.image_btn_bg_hover_color
  assign image_btn_radius = section.settings.image_btn_radius

-%}

{%- style -%}
  {{ link_font | font_face: font_display: 'swap' }}
  {{ image_title_font | font_face: font_display: 'swap' }}
  {{ image_btn_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .footer-body-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
    gap: {{ layout_gap_mobile }}px;
  }

  .footer-content-{{ section.id }} {
    order: 2;
  }

  .footer-columns-{{ section.id }} {
    display: flex;
    margin-top: {{ menu_mt_mobile }}px;
    gap: {{ menu_gap_mobile }}px;
    flex-wrap: wrap;
  }

  .footer-columns-{{ section.id }} > * {
    flex: 1 0;
  }

  .footer-column-heading-{{ section.id }} {
    margin: 0;
    font-size: {{ column_heading_size_mobile }}px;
    color: {{ column_heading_color }};
    line-height: {{ column_heading_height }}%;
    text-transform: unset;
    text-decoration: none;
    font-weight: 700;
  }

  .footer-menu-list-{{ section.id }} {
    padding: 0px;
    margin: 0px;
    margin-top: {{ list_mt_mobile }}px;
    display: flex;
    flex-wrap: wrap;
    gap: {{ list_gap_mobile }}px;
    list-style: none;
  }

  .footer-menu-item-{{ section.id }} {
    line-height: 100%;
  }

  .footer-menu-link-{{ section.id }} {
    margin: 0;
    font-size: {{ link_size_mobile }}px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
  }

  .footer-images-{{ section.id }} {
    order: 1;
    display: grid;
    gap: {{ images_gap_mobile }}px;
    grid: auto-flow dense auto / repeat(2,minmax(0,1fr));
  }

  .footer-image-{{ section.id }} {
    position: relative;
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
    border-radius: {{ image_radius }};
    text-decoration: none;
  }

  .footer-image-{{ section.id }}:after {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    z-index: 1;
    top: 0;
    left: 0;
    background: {{ image_overlay_color | hex_to_rgba: image_overlay }};
  }

  .footer-image-{{ section.id }} img,
  .footer-image-{{ section.id }} svg {
    position: absolute;
    top: 0;
    left: 0;
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .footer-image-content-{{ section.id }} {
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    position: relative;
    z-index: 2;
  }

  .footer-image-title-{{ section.id }} {
    margin: 0;
    font-size: {{ image_title_size_mobile }}px;
    color: {{ image_title_color }};
    line-height: {{ image_title_height }}%;
    text-transform: unset;  
  }

  .footer-image-btn-{{ section.id }} {
    margin: 0;
    font-size: {{ image_btn_size_mobile }}px;
    color: {{ image_btn_color }};
    line-height: {{ image_btn_height }}%;
    text-transform: unset;  
    border: {{ image_btn_border_thickness }}px solid {{ image_btn_border_color }};
    padding: {{ image_btn_padding_vertical_mobile }}px solid {{ image_btn_padding_horizontal_mobile }}px;
    border-radius: {{ image_btn_radius }}px;
    background-color: {{ image_btn_bg_color }}px;
    overflow: hidden;
    transition: all 0.25s ease 0s;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .footer-body-{{ section.id }} {
      grid-template-columns: 1fr 1fr;
      gap: {{ layout_gap }}px;
    }

    .footer-content-{{ section.id }} {
      order: 1;
    }

    .footer-columns-{{ section.id }} {
      margin-top: {{ menu_mt }}px;
      gap: {{ menu_gap }}px;
    }

    .footer-column-heading-{{ section.id }} {
      font-size: {{ column_heading_size }}px;
    }

    .footer-menu-list-{{ section.id }}{
      margin-top: {{ list_mt }}px;
      gap: {{ list_gap }}px;
    }

    .footer-menu-link-{{ section.id }} {
      font-size: {{ link_size }}px;
      transition: all 0.25s ease 0s;
    }

    .footer-menu-link-{{ section.id }}:hover {
      transition: all 0.25s ease 0s;
      color: {{ link_hover_color }};
    }

    .footer-images-{{ section.id }} {
      order: 2;
      grid: auto-flow dense auto / repeat(2,minmax(0,1fr));
    }

    .footer-image-title-{{ section.id }} {
      font-size: {{ image_title_size }}px;
    }

    .footer-image-btn-{{ section.id }} {
      font-size: {{ image_btn_size }}px;
      padding: {{ image_btn_padding_vertical }}px solid {{ image_btn_padding_horizontal }}px;
    }

    .footer-image-btn-{{ section.id }}:hover {
      color: {{ image_btn_hover_color }};
      border-color: {{ image_btn_border_hover_color }}px;
      background-color: {{ image_btn_bg_hover_color }}px;
      transition: all 0.25s ease 0s;
    }

  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if column_heading_custom %}
  <style>
    .footer-column-heading-{{ section.id }} {
      font-family: {{ column_heading_font.family }}, {{ column_heading_font.fallback_families }};
      font-weight: {{ column_heading_font.weight }};
      font-style: {{ column_heading_font.style }};
    }
  </style>
{% endif %}

{% if link_custom %}
  <style>
    .footer-menu-link-{{ section.id }} {
      font-family: {{ link_font.family }}, {{ link_font.fallback_families }};
      font-weight: {{ link_font.weight }};
      font-style: {{ link_font.style }};
    }
  </style>
{% endif %}

{% if image_title_custom %}
  <style>
    .footer-image-title-{{ section.id }} {
      font-family: {{ image_title_font.family }}, {{ image_title_font.fallback_families }};
      font-weight: {{ image_title_font.weight }};
      font-style: {{ image_title_font.style }};
    }
  </style>
{% endif %}

{% if image_btn_custom %}
  <style>
    .footer-image-btn-{{ section.id }} {
      font-family: {{ image_btn_font.family }}, {{ image_btn_font.fallback_families }};
      font-weight: {{ image_btn_font.weight }};
      font-style: {{ image_btn_font.style }};
    }
  </style>
{% endif %}

{% if image_ratio_mobile == "portrait" %}
  <style>
    .footer-image-{{ section.id }} {
      aspect-ratio: 9.6/13;
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
  .footer-image-{{ section.id }} {
    aspect-ratio: 12/8;
  }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .footer-image-{{ section.id }} {
      aspect-ratio: 12/12;
    }
  </style>
{% else %}
  <style>
    .footer-image-{{ section.id }} {
      aspect-ratio: auto;
    }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
      .footer-image-{{ section.id }} {
        aspect-ratio: 9.6/13;
      }
    }

  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .footer-image-{{ section.id }} {
        aspect-ratio: 12/8;
      }
    }  
  </style>
{% elsif image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .footer-image-{{ section.id }} {
        aspect-ratio: 12/12;
      }
    }    
  </style>
{% else %}
  <style>
     @media(min-width: 1024px) {
      .footer-image-{{ section.id }} {
        aspect-ratio: auto;
      }
    }
  </style>
{% endif %}

{% if radius_top %}
  <style>
    .footer-{{ section.id }} {
      border-bottom-left-radius: 0px;
      border-bottom-right-radius: 0px;
    }
  </style>
{% endif %}

{% for block in section.blocks %}
  {% if block.type == "main_menu" %}

    <style>

      {{ block.settings.menu_link_font | font_face: font_display: 'swap' }}

      .footer-menu-{{ block.id }} {
        margin-top: {{ block.settings.menu_mt_mobile }}px;
      }

      .footer-main-menu-list-{{ block.id }} {
        padding: 0px;
        margin: 0px;
        display: flex;
        flex-wrap: wrap;
        gap: {{  block.settings.menu_list_gap_mobile }}px;
        list-style: none;
      }

      .footer-main-menu-item-{{ block.id }} {
        line-height: 100%;
      }

      .footer-main-menu-link-{{ block.id }} {
        margin: 0;
        font-size: {{  block.settings.menu_link_size_mobile }}px;
        color: {{  block.settings.menu_link_color }};
        line-height: {{  block.settings.menu_link_height }}%;
        text-transform: unset;
        text-decoration: none;
      }

      @media(min-width: 1024px) {

        .footer-menu-{{ block.id }} {
          margin-top: {{ block.settings.menu_mt }}px;
        }

        .footer-main-menu-list-{{ block.id }}{
          gap: {{  block.settings.menu_list_gap }}px;
        }

        .footer-main-menu-link-{{ block.id }} {
          font-size: {{  block.settings.menu_link_size }}px;
          transition: all 0.25s ease 0s;
        }

        .footer-main-menu-link-{{ block.id }}:hover {
          transition: all 0.25s ease 0s;
          color: {{  block.settings.menu_link_hover_color }};
        }

      }
    </style>

    {% if block.settings.menu_link_custom %}
      <style>
        .footer-main-menu-link-{{ block.id }} {
          font-family: {{ block.settings.menu_link_font.family }}, {{ block.settings.menu_link_font.fallback_families }};
          font-weight: {{ block.settings.menu_link_font.weight }};
          font-style: {{ block.settings.menu_link_font.style }};
        }
      </style>
    {% endif %}

  {% elsif block.type == "socials" %}

    <style>

      .footer-social-list-{{ block.id }} {
        display: flex;
        margin-top: {{ block.settings.social_list_mt_mobile }}px;
        gap: {{ block.settings.social_list_gap_mobile }}px;
        list-style: none;
        padding: 0px;
      }

      .footer-social-link-{{ block.id }} {
        display: flex;
        align-items: center;
        justify-content: center;
        width: {{ block.settings.social_icon_size_mobile }}px;
        height: {{ block.settings.social_icon_size_mobile }}px;
        background-color: {{ block.settings.social_icon_bg_color }};
        padding: 7.5px;
        border-radius: {{ block.settings.social_icon_radius }}px;
        border: {{ block.settings.social_icon_border_thickness }}px solid {{ block.settings.social_icon_border_color }};
        overflow: hidden;
        transition: all 0.25s ease 0s;
      }

      .footer-social-link-{{ block.id }} svg {
        display: block;
        width: 100%;
        height: 100%;
        object-fit: cover;
      }

      .footer-social-link-{{ block.id }} svg path {
        fill: {{ block.settings.social_icon_color }};
        transition: all 0.25s ease 0s;
      }

      @media(min-width: 1024px) {
        .footer-social-list-{{ block.id }} {
          margin-top: {{ block.settings.social_list_mt }}px;
          gap: {{ block.settings.social_menu_list_gap }}px;
        }

        .footer-social-link-{{ block.id }} {
          width: {{ block.settings.social_icon_size }}px;
          height: {{ block.settings.social_icon_size }}px;
        }

        .footer-social-link-{{ block.id }}:hover {
          background-color: {{ block.settings.social_icon_bg_hover_color }};
          border-color: {{ block.settings.social_icon_border_hover_color }};
          transition: all 0.25s ease 0s;
        }

        .footer-social-link-{{ block.id }}:hover svg path {
          fill: {{ block.settings.social_icon_hover_color }};
          transition: all 0.25s ease 0s;
        }
      }
    </style>

  {% elsif block.type == "text" %}

    <style>

      {{ block.settings.column_text_font | font_face: font_display: 'swap' }}


      .footer-column-text-{{ block.id }} {
        margin-top: {{ block.settings.column_text_mt_mobile }}px;
      }

      .footer-column-text-{{ block.id }} * {
        margin: 0;
        font-size: {{ block.settings.column_text_size_mobile }}px;
        color: {{ block.settings.column_text_color }};
        line-height: {{ block.settings.column_text_height }}%;
        text-transform: unset;
        text-decoration: none;
      }

      .footer-column-text-{{ block.id }} *:not(:first-child) {
        margin-top: 10px;
      }

      @media(min-width: 1024px) {

        .footer-column-text-{{ block.id }} {
          margin-top: {{ block.settings.column_text_mt }}px;
        }

        .footer-column-text-{{ block.id }} * {
          font-size: {{ block.settings.column_text_size }}px;
        }
      }
    </style>

    {% if block.settings.column_text_custom %}
      <style>
        .footer-column-text-{{ block.id }} * {
          font-family: {{ block.settings.column_text_font.family }}, {{ block.settings.column_text_font.fallback_families }};
          font-weight: {{ block.settings.column_text_font.weight }};
          font-style: {{ block.settings.column_text_font.style }};
        }
      </style>
    {% endif %}

  {% elsif block.type == "image" %}

    <style>
      .footer-image-{{ block.id }} {
        grid-area: span min(1, {{ block.settings.row_width_mobile }}) / span min(1,1);
      }

      @media(min-width: 1024px) {
        .footer-image-{{ block.id }} {
          grid-area: span min(1, {{ block.settings.row_width }}) / span min(1,{{ block.settings.column_width }});
        }
      }
    </style>

  {% endif %}
{% endfor %}

<div class="section-{{ section.id }} footer-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="footer-body-{{ section.id }}">
        <div class="footer-content-{{ section.id }}">   
          {% assign columns_open = false %}

          {% for block in section.blocks %}
            {% if block.type == "menu" %}

              {% if block.settings.menu != blank %}
                <div class="footer-menu-{{ block.id }}">
                    <ul class="footer-main-menu-list-{{ block.id }}">
                      {% for link in block.settings.menu.links %}
                        <li class="footer-main-menu-item-{{ block.id }}">
                          <a href="{{ link.url }}" class="footer-main-menu-link-{{ block.id }}">{{ link.title }}</a>
                        </li>
                      {% endfor %}
                    </ul>
                </div>
              {% endif %}

            {% elsif block.type == "column" %}

              {% assign next_index = forloop.index0 | plus: 1 %}
              {% assign next_block = section.blocks[next_index] %}

              {% unless columns_open %}
                <div class="footer-columns-{{ section.id }}">
              {% endunless %} 

              <div class="footer-column-{{ section.id }}">
                  {% if block.settings.heading != blank %}
                    <p class="footer-column-heading-{{ section.id }}">{{ block.settings.heading }}</p>
                  {% endif %}

                  {% if block.settings.menu != blank %}
                    <ul class="footer-menu-list-{{ section.id }}">
                      {% for link in block.settings.menu.links %}
                        <li class="footer-menu-item-{{ section.id }}">
                          <a href="{{ link.url }}" class="footer-menu-link-{{ section.id }}">{{ link.title }}</a>
                        </li>
                      {% endfor %}
                    </ul>
                  {% endif %}

                </div>

              {% assign columns_open = true %}

              {% if next_block.type != "column" %}
                </div>
                {% assign columns_open = false %}
              {% endif %}

            {% elsif block.type == "socials" %}
              <div class="footer-socials-{{ block.id }}">
                <ul class="footer-social-list-{{ block.id }}">
                  {% if block.settings.instagram != blank %}
                    <li class="footer-social-item-{{ block.id }}">
