{%- style -%}
  {{  newsletter_font | font_face: font_display: 'swap' }}
  {{  input_font | font_face: font_display: 'swap' }}
  {{  submit_font | font_face: font_display: 'swap' }}
  {{  scrolling_text_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}
  {{  social_text_font | font_face: font_display: 'swap' }}
  {{  link_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .footer-top-{{ section.id }} {
    display: flex;
    flex-direction: column;
    gap: 24px;
    padding-bottom: 40px;
  }

  .footer-newsletter-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ newsletter_size_mobile }}px;
    color: {{ newsletter_color }};
    line-height: {{ newsletter_height }}%;
    text-transform: unset;
  }

  .footer-newsletter-row-{{ section.id }} {
    position: relative;
  }

  .footer-newsletter-input-{{ section.id }} {
    width: 100%;
    margin: 0;
    box-shadow: none;
    border: 0px;
    padding: {{ input_padding_vertical | times: 0.75 | round: 0 }}px {{ input_padding_horizontal | times: 0.75 | round: 0 }}px;
    font-size: {{ input_size_mobile }}px;
    color: {{ input_color }};
    line-height: {{ input_height }}%;
    text-transform: unset;
    border-radius: {{ input_radius }}px;
    border: {{ input_border_thickness }}px solid {{ input_border_color | hex_to_rgba: 0.7 }};
    background-color: transparent;
  }

  .footer-newsletter-input-{{ section.id }}:focus {
    border: {{ input_border_thickness }}px solid {{ input_border_color | hex_to_rgba: 1 }};
  }

  .footer-newsletter-input-{{ section.id }}::placeholder {
    color: {{ input_placeholder_color }};
  }

  .footer-newsletter-submit-{{ section.id }} {
    margin-top: 16px;
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: {{ submit_size_mobile }}px;
    color: {{ submit_color }};
    line-height: {{ submit_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ submit_padding_vertical | times: 0.75 | round: 0 }}px {{ submit_padding_horizontal | times: 0.75 | round: 0 }}px;
    border-radius: {{ submit_radius }}px;
    border: 0px;
    border: {{ submit_border_thickness }}px solid {{ submit_border_color }};
    background-color: transparent;
    background-color: {{ submit_bg_color }};
    transition: all 0.25s ease 0s;
    box-shadow: none;
    cursor: pointer;
  }

  .footer-newsletter-submit-{{ section.id }}:hover {
    color: {{ submit_hover_color }};
    border: {{ submit_border_thickness }}px solid {{ submit_border_hover_color }};
    background-color: {{ submit_bg_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .footer-newsletter-first-row-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 10px;
    margin-bottom: 10px;
  }

  .footer-scrolling-{{ section.id }} {
    position: relative;
    z-index: 2;
    display: -webkit-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    overflow: hidden;
    width: 100%;
    height: 60px;
    -webkit-box-pack: start;
    -webkit-justify-content: flex-start;
    -ms-flex-pack: start;
    justify-content: flex-start;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -ms-flex-align: center;
    align-items: center;
    background-image: {{ scrolling_bg_gradient }};
  }

  .footer-scrolling-inner-{{ section.id }} {
    position: absolute;
    white-space: nowrap;
    top: 0;
    bottom: 0;
    will-change: transform;
    display: -webkit-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-pack: start;
    -webkit-justify-content: flex-start;
    -ms-flex-pack: start;
    justify-content: flex-start;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -ms-flex-align: center;
    align-items: center;
    animation: marquee-horizontal-left {{ scrolling_velocity }}s linear infinite;
  }

  .footer-scrolling-item-{{ section.id }} {
    margin-right: 1vw;
    -webkit-box-flex: 0;
    -webkit-flex: 0 0 auto;
    -ms-flex: 0 0 auto;
    flex: 0 0 auto;
    color: #fff;
    text-transform: uppercase;
    text-align: center;
  }

  @keyframes marquee-horizontal-left {
    from { transform: translateX(0); }
    to { transform: translateX(-50%); }
  }

  .footer-scrolling-text-{{ section.id }} {
    margin: 0;
    font-size: {{ scrolling_text_size_mobile }}px;
    color: {{ scrolling_text_color }};
    line-height: {{ scrolling_text_height }}%;
    text-transform: unset;
    font-weight: 700;
  }

  .footer-main-{{ section.id }} {
    display: grid;
  }

  .footer-second-main-{{ section.id }} {
    padding-top: 30px;
    padding-bottom: 30px;
    gap: 30px;
  }

  .footer-main-left-{{ section.id }} {
    padding-top: 40px;
    padding-bottom: 40px;
    display: flex;
    flex-direction: column;
    justify-content: center;
  }

  .footer-logo-{{ section.id }} {
    display: block;
    width: 100%;
    max-width: {{ logo_width_mobile }}px;
  }

  .footer-logo-{{ section.id }} img,
  .footer-logo-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .footer-logo-{{ section.id }} svg {
    background-color: #DEDEDE;
  }

  .footer-text-{{ section.id }} {
    margin-top: 16px;
    width: 100%;
  }

  .footer-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
  }

  .footer-main-right-top-{{ section.id }} {
    padding-bottom: 32px;
  }

  .footer-main-right-top-{{ section.id }} *{
    margin: 0;
    font-size: {{ social_text_size_mobile }}px;
    color: {{ social_text_color }};
    line-height: {{ social_text_height }}%;
    text-transform: unset;
  }

  .footer-main-right-top-{{ section.id }} a,
  .footer-socilas-text-{{ section.id }} a {
    color: {{ social_text_link_color }};
    text-decoration: none;
  }

  .footer-main-socials-{{ section.id }} {
    position: relative;
    display: flex;
  }

  .footer-main-socials-{{ section.id }}:after {
    content: '';
    position: absolute;
    top: 0;
    left: 50%;
    transform: translateX(-50%);
    width: 200%;
    height: 1px;
    background-color: {{ footer_border_color }};
  }

  .footer-main-social-{{ section.id }} {
    flex: 1 0;
    display: flex;
    align-items: center;
    justify-content: center;
    aspect-ratio: 1/1;
  }

  .footer-main-social-{{ section.id }}:not(:first-child) {
    border-left: 1px solid {{ footer_border_color }};
  }

  .footer-main-social-{{ section.id }} svg {
    width: {{ social_icon_mobile }}px;
    height: {{ social_icon_mobile }}px;
    display: block;
  }

  .footer-main-social-{{ section.id }} svg path {
    fill: {{ social_icon_color }};
  }

  .footer-bottom-{{ section.id }} {
    border-top: 1px solid {{ footer_border_color }};
    padding-top: 32px;
  }

  .footer-bottom-body-{{ section.id }} {
    display: flex;
    flex-direction: column;
    gap: 32px;
  }

  .footer-list-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 16px;
  }

  .footer-list-{{ section.id }} {
    margin: 0px;
    padding: 0px;
  }

  .footer-list-{{ section.id }} li {
    list-style: none;
  }

  .footer-list-{{ section.id }} li a {
    margin: 0;
    font-size: {{ link_size_mobile }}px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
    position: relative;
  }

  .footer-list-{{ section.id }} li a:after {
    content: '';
    left: 0;
    bottom: -2px;
    position: absolute;
    width: 100%;
    height: 2px;
    background-color: {{ link_hover_color }};
    opacity: 0;
    transtion: all 0.25s ease 0s;
  }

  .footer-list-{{ section.id }} li a:hover:after {
    opacity: 1;
    transtion: all 0.25s ease 0s;
  }


 .footer-newsletter-thanks-text-{{ section.id }} {
    display: none;
    margin: 0px;
    margin-top: {{ thanks_text_mt_mobile }}px;
    font-size: {{ thanks_text_size_mobile }}px;
    color: {{ thanks_text_color }};
    line-height: {{ thanks_text_height }}%;
    text-transform: unset;
 }

 .footer-newsletter-thanks-text-{{ section.id }}.active {
   display: block;
 }

  @media(min-width: 768px) {

    .footer-first-main-socials-{{ section.id }} svg {
      width: {{ social_icon_mobile | times: 2 }}px;
      height: {{ social_icon_mobile | times: 2 }}px;
    }

    .footer-newsletter-submit-{{ section.id }} {
      margin-top: 0px;
      height: 88%;
      right: 4px;
      position: absolute;
      top: 50%;
      transform: translateY(-50%);
      max-width: 166px;
    }
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .footer-top-{{ section.id }} {
      flex-direction: row;
      justify-content: space-between;
      align-items: center;
    }

    .footer-newsletter-text-{{ section.id }} {
      flex: 0 1 55%;
    }

    .footer-newsletter-form-{{ section.id }} {
      flex: 0 1 512px;
    }

    .footer-newsletter-text-{{ section.id }} * {
      font-size: {{ newsletter_size }}px;
    }

    .footer-newsletter-input-{{ section.id }} {
      padding: {{ input_padding_vertical }}px {{ input_padding_horizontal }}px;
      font-size: {{ input_size }}px;
    }

    .footer-newsletter-submit-{{ section.id }} {
      padding: {{ submit_padding_vertical }}px {{ submit_padding_horizontal }}px;
      font-size: {{ submit_size }}px;
    }

    .footer-scrolling-text-{{ section.id }} {
      font-size: {{ scrolling_text_size }}px;
    }

    .footer-main-{{ section.id }} {
      grid-template-columns: 1fr 1fr;
    }


     .footer-newsletter-thanks-text-{{ section.id }} {
     margin-top: {{ thanks_text_mt }}px;
     font-size: {{ thanks_text_size }}px;
   }


    .footer-logo-{{ section.id }} {
      max-width: {{ logo_width }}px;
    }

    .footer-text-{{ section.id }} {
      max-width: {{ text_width }}px;
    }

    .footer-text-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }

    .footer-main-right-top-{{ section.id }} {
      padding-top: 32px;
      padding-bottom: 32px;
      padding-left: 10%;
      padding-right: 10%;
    }

    .footer-main-right-top-{{ section.id }} * {
      margin: 0;
      font-size: {{ social_text_size }}px;
    }

    .footer-main-right-{{ section.id }} {
      border-left: 1px solid {{ footer_border_color }};
      display: flex;
      flex-direction: column;
      justify-content: space-between;
    }

    .footer-main-socials-{{ section.id }}:before {
      content: '';
      position: absolute;
      top: 0;
      left: 0;
      width: 200%;
      height: 1px;
      background-color: {{ footer_border_color }};
    }

    .footer-main-socials-{{ section.id }}:after {
      display: none;
    }

    .footer-main-social-{{ section.id }} svg {
      width: {{ social_icon }}px;
      height: {{ social_icon }}px;
    }

    .footer-list-{{ section.id }} {
      display: flex;
      align-items: center;
      gap: 16px;
    }

    .footer-list-{{ section.id }} li a {
      font-size: {{ link_size }}px;
    }

    .footer-bottom-body-{{ section.id }} {
      align-items: center;
      justify-content: space-between;
      flex-wrap: wrap;
      flex-direction: row;
    }

  }
{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if newsletter_custom %}
  <style>
    .footer-newsletter-text-{{ section.id }} * {
      font-family: {{ newsletter_font.family }}, {{ newsletter_font.fallback_families }};
      font-weight: {{ newsletter_font.weight }};
      font-style: {{ newsletter_font.style }};
    }
  </style>
{% endif %}

{% if input_custom %}
  <style>
    .footer-newsletter-input-{{ section.id }} {
      font-family: {{ input_font.family }}, {{ input_font.fallback_families }};
      font-weight: {{ input_font.weight }};
      font-style: {{ input_font.style }};
    }
  </style>
{% endif %}

{% if submit_custom %}
  <style>
    .footer-newsletter-submit-{{ section.id }} {
      font-family: {{ submit_font.family }}, {{ submit_font.fallback_families }};
      font-weight: {{ submit_font.weight }};
      font-style: {{ submit_font.style }};
    }
  </style>
{% endif %}

