    .footer-scrolling-text-{{ section.id }} {
      font-family: {{ scrolling_text_font.family }}, {{ scrolling_text_font.fallback_families }};
      font-weight: {{ scrolling_text_font.weight }};
      font-style: {{ scrolling_text_font.style }};
    }
