    .footer-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if social_text_custom %}
  <style>
    .footer-main-right-top-{{ section.id }} * {
      font-family: {{ social_text_font.family }}, {{ social_text_font.fallback_families }};
      font-weight: {{ social_text_font.weight }};
      font-style: {{ social_text_font.style }};
    }
  </style>
{% endif %}

{% if thanks_text_custom %}
  <style>
    .footer-newsletter-thanks-text-{{ section.id }} {
      font-family: {{ thanks_text_font.family }}, {{ thanks_text_font.fallback_families }};
      font-weight: {{ thanks_text_font.weight }};
      font-style: {{ thanks_text_font.style }};
    }
  </style>
{% endif %}

{% if link_custom %}
  <style>
    .footer-list-{{ section.id }} li a {
      font-family: {{ link_font.family }}, {{ link_font.fallback_families }};
      font-weight: {{ link_font.weight }};
      font-style: {{ link_font.style }};
    }
  </style>
{% endif %}

<div
  class="section-{{ section.id }} footer-{{ section.id }}"
  style="background-color:{{ background_color }}; background-image: {{ background_gradient }};"
>
  <div class="section-{{ section.id }}-settings">
    {% if use_newsletter %}
      <div class="footer-top-{{ section.id }}">
        {% if newsletter != blank %}
          <div class="footer-newsletter-text-{{ section.id }}">
            {{ newsletter }}
          </div>
        {% endif %}

        <div class="footer-newsletter-form-{{ section.id }}">
          {% form 'customer', class: 'footer-newsletter-form' %}
            <input type="hidden" name="contact[tags]" value="newsletter">

            <div class="footer-newsletter-row-{{ section.id }}">
              <input
                type="email"
                name="contact[email]"
                class="footer-newsletter-input-{{ section.id }}"
                value="{{ form.email }}"
                aria-required="true"
                autocorrect="off"
                autocapitalize="off"
                autocomplete="email"
                {% if form.errors %}
                  autofocus
                  aria-invalid="true"
                  aria-describedby="Newsletter-error--{{ section.id }}"
                {% elsif form.posted_successfully? %}
                  aria-describedby="Newsletter-success--{{ section.id }}"
                {% endif %}
                placeholder="{{ input_placeholder }}"
                required
              >
              <button type="submit" class="footer-newsletter-submit-{{ section.id }}" name="commit">
                {{ submit }}
              </button>

            </div>
            {% if form.posted_successfully? %}
              <p class="footer-newsletter-thanks-text-{{ section.id }}">{{ thanks_text }}</p>
            {% endif %}
          {% endform %}
        </div>
      </div>
    {% endif %}
  </div>

  {% if scrolling_text != blank %}
    <div class="footer-scrolling-{{ section.id }}">
      <div class="footer-scrolling-inner-{{ section.id }}">
        {% for item in (1..6) %}
          <div class="footer-scrolling-item-{{ section.id }}">
            <p class="footer-scrolling-text-{{ section.id }}">{{ scrolling_text }}</p>
          </div>
        {% endfor %}

      </div>
    </div>
  {% endif %}

  <div class="section-{{ section.id }}-settings">
    <div class="footer-main-{{ section.id }}">
      <div class="footer-main-left-{{ section.id }}">
        {% if logo != blank %}
