  else
    assign submenu_item_horizontal_align = "start"
  endif

  assign submenu_button = section.settings.submenu_button
  assign submenu_button_size = section.settings.submenu_button_size
  assign submenu_button_size_mobile = section.settings.submenu_button_size_mobile
  assign submenu_button_color = section.settings.submenu_button_color
  assign submenu_button_hover_color = section.settings.submenu_button_hover_color
  assign submenu_button_custom = section.settings.submenu_button_custom
  assign submenu_button_font = section.settings.submenu_button_font
  assign submenu_button_height = section.settings.submenu_button_height
  assign submenu_button_padding_vertical = section.settings.submenu_button_padding_vertical
  assign submenu_button_padding_horizontal = section.settings.submenu_button_padding_horizontal
  assign submenu_button_padding_vertical_mobile = section.settings.submenu_button_padding_vertical_mobile
  assign submenu_button_padding_horizontal_mobile = section.settings.submenu_button_padding_horizontal_mobile
  assign submenu_button_radius = section.settings.submenu_button_radius
  assign submenu_button_border_thickness = section.settings.submenu_button_border_thickness
  assign submenu_button_border_color = section.settings.submenu_button_border_color
  assign submenu_button_border_hover_color = section.settings.submenu_button_border_hover_color
  assign submenu_button_bg_color = section.settings.submenu_button_bg_color
  assign submenu_button_bg_hover_color = section.settings.submenu_button_bg_hover_color
  assign submenu_button_style = section.settings.submenu_button_style
  assign submenu_button_mt = section.settings.submenu_button_mt
  assign submenu_button_mt_mobile = section.settings.submenu_button_mt_mobile

  assign menu_icon_size_mobile = section.settings.menu_icon_size_mobile

  assign title_size = section.settings.title_size
  assign title_color = section.settings.title_color
  assign title_custom = section.settings.title_custom
  assign title_font = section.settings.title_font
  assign title_height = section.settings.title_height
  assign title_mt = section.settings.title_mt
  assign title_align = section.settings.title_align

  assign product_image_border_thickness = section.settings.product_image_border_thickness
  assign product_image_border_color = section.settings.product_image_border_color
  assign product_image_ratio = section.settings.product_image_ratio
  assign product_image_radius = section.settings.product_image_radius

  assign content_align = section.settings.content_align

  assign content_horizontal_align = ""
  if content_align == "center"
    assign content_horizontal_align = "center"
  elsif content_align == "right"
    assign content_horizontal_align = "end"
  else
    assign content_horizontal_align = "start"
  endif

  assign product_title_size = section.settings.product_title_size
  assign product_title_color = section.settings.product_title_color
  assign product_title_custom = section.settings.product_title_custom
  assign product_title_font = section.settings.product_title_font
  assign product_title_height = section.settings.product_title_height
  assign product_title_mt = section.settings.product_title_mt

  assign price_size = section.settings.price_size
  assign price_color = section.settings.price_color
  assign price_original_color = section.settings.price_original_color
  assign price_custom = section.settings.price_custom
  assign price_font = section.settings.price_font
  assign price_height = section.settings.price_height
  assign price_compare = section.settings.price_compare
  assign price_mt = section.settings.price_mt

  assign button = section.settings.button
  assign button_size = section.settings.button_size
  assign button_size_mobile = section.settings.button_size_mobile
  assign button_color = section.settings.button_color
  assign button_hover_color = section.settings.button_hover_color
  assign button_custom = section.settings.button_custom
  assign button_font = section.settings.button_font
  assign button_height = section.settings.button_height
  assign button_padding_vertical = section.settings.button_padding_vertical
  assign button_padding_horizontal = section.settings.button_padding_horizontal
  assign button_padding_vertical_mobile = section.settings.button_padding_vertical_mobile
  assign button_padding_horizontal_mobile = section.settings.button_padding_horizontal_mobile
  assign button_radius = section.settings.button_radius
  assign button_border_thickness = section.settings.button_border_thickness
  assign button_border_color = section.settings.button_border_color
  assign button_border_hover_color = section.settings.button_border_hover_color
  assign button_bg_color = section.settings.button_bg_color
  assign button_bg_hover_color = section.settings.button_bg_hover_color
  assign button_style = section.settings.button_style
  assign button_mt = section.settings.button_mt

  assign logo = section.settings.logo
  assign second_logo = section.settings.second_logo
  assign logo_mobile = section.settings.logo_mobile
  assign second_logo_mobile = section.settings.second_logo_mobile
  assign logo_width = section.settings.logo_width
  assign logo_width_mobile = section.settings.logo_width_mobile

  assign name_size = section.settings.name_size
  assign name_size_mobile = section.settings.name_size_mobile
  assign name_color = section.settings.name_color
  assign name_hover_color = section.settings.name_hover_color
  assign name_hero_color = section.settings.name_hero_color
  assign name_hover_hero_color = section.settings.name_hover_hero_color
  assign name_custom = section.settings.name_custom
  assign name_font = section.settings.name_font
  assign name_height = section.settings.name_height

  assign right_gap_mobile = section.settings.right_gap_mobile
  assign right_gap = section.settings.right_gap

  assign right_link_size = section.settings.right_link_size
  assign right_link_color = section.settings.right_link_color
  assign right_link_hover_color = section.settings.right_link_hover_color
  assign right_link_hero_color = section.settings.right_link_hero_color
  assign right_link_hover_hero_color = section.settings.right_link_hover_hero_color
  assign right_link_custom = section.settings.right_link_custom
  assign right_link_font = section.settings.right_link_font
  assign right_link_height = section.settings.right_link_height

  assign icon_size_mobile = section.settings.icon_size_mobile
  assign icon_size = section.settings.icon_size  

  assign use_search = section.settings.use_search
  assign search_text  = section.settings.search_text
  assign search_icon_desktop  = section.settings.search_icon_desktop
  assign search_menu_mobile = section.settings.search_menu_mobile

  assign use_account = section.settings.use_account
  assign account_text  = section.settings.account_text
  assign account_icon_desktop  = section.settings.account_icon_desktop
  assign account_menu_mobile = section.settings.account_menu_mobile

  assign cart_text  = section.settings.cart_text
  assign cart_icon_desktop  = section.settings.cart_icon_desktop

  assign mobile_menu_button = section.settings.mobile_menu_button
  assign mobile_menu_button_url = section.settings.mobile_menu_button_url
-%}

{%- style -%}

  {{ link_font | font_face: font_display: 'swap' }}
  {{ button_font | font_face: font_display: 'swap' }}
  {{ name_font | font_face: font_display: 'swap' }}
  {{ right_link_font | font_face: font_display: 'swap' }}
  {{ product_title_font | font_face: font_display: 'swap' }}
  {{ button_font | font_face: font_display: 'swap' }}
  {{ submenu_button_font | font_face: font_display: 'swap' }}


  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color_mobile }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;

    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    position: relative;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  {% if transparent_bg == "all" %}
    .header-{{ section.id }}.transparent {
      background-color: transparent !important;
      background-image: none !important;
      position: absolute;
      width: calc(100% - {{margin_horizontal_mobile | times: 2 }}rem);
      top: 0;
      left: 0;
      z-index: 999;
    }
  {% elsif transparent_bg == "home"  %}
    {% if template.name == "index" %}
    .header-{{ section.id }}.transparent {
      background-color: transparent !important;
      background-image: none !important;
      position: absolute;
      width: calc(100% - {{margin_horizontal_mobile | times: 2 }}rem);
      top: 0;
      left: 0;
      z-index: 999;
    }
    {% endif %}
  {% endif %}

  .header-{{ section.id }}.onscroll {
    background-color:{{ background_color }} !important;
    background-image: {{ background_gradient }} !important;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    transition: all 0.25s ease 0s;
  }

  .header-{{ section.id }}.fixed {
    position: fixed;
    top: 0;
    left: 0;
    width: calc(100% - {{margin_horizontal_mobile | times: 2 }}rem);
    z-index: 999;
  }

  .header-{{ section.id }}.scroll {
    transform: translateY(-150%);
    transition: all 0.25s ease 0s;
  }

  .header-{{ section.id }}.scroll.active {
    transform: translateY(0);
    transition: all 0.25s ease 0s;
  }

  .header-{{ section.id }}.open {
    background-color:{{ background_color }} !important;
    transition: all 0.25s ease 0s;
  }

  .header-body-{{ section.id }} {
    display: grid;
    align-items: center;
    gap: {{ layout_gap_mobile }}px;
    grid-template-columns: 1fr {{ logo_width_mobile }}px 1fr;
  }

  .header-logo-{{ section.id }} {
    text-align: center;
  }

  .header-submenu-menu-{{ section.id }} {
    padding: 25px 0px;
    position: relative;
    flex: 1 1 auto;
    display: flex;
    flex-direction: column;
    overflow-y: scroll;
    scrollbar-width: none;
    justify-content: space-between;
  }

  .header-menu-list-{{ section.id }} {
    padding: 0px;
    margin: 0px;
    display: none;
    align-items: center;
    gap: 0px;
    flex-wrap: wrap;
    list-style: none;
  }

  .header-menu-mobile-{{ section.id }} .header-menu-list-{{ section.id }} {
    display: flex;
    flex-direction: column;
    align-items: start;
  }

  .header-menu-item-{{ section.id }} {
    line-height: 100%;
    cursor: pointer;
    box-sizing: border-box;
    padding-bottom: 3px;
  }

  .header-menu-sublinks-{{ section.id }} {
    padding: 0;
    padding-top: 20px;
    display: flex;
    flex-direction: column;
    list-style: none;
    gap: {{ list_gap_mobile }}px;
  }

  .header-menu-sublinks-{{ section.id }} .header-menu-link-{{ section.id }} {
    margin: 0;
    font-size: {{ link_size_mobile | minus: 2 }}px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease 0s;
    position: relative;
    font-weight: 700;
  }

  .header-menu-sublinks-{{ section.id }} .header-menu-sublinks-{{ section.id }} .header-menu-link-{{ section.id }} {
    font-size: {{ link_size_mobile | minus: 4 }}px;
    font-weight: 400;
  }

  .header-menu-sublinks-{{ section.id }} svg {
    width: 16px;
  }

  .header-menu-link-wrapper-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: space-between;
  }

  .header-menu-link-wrapper-{{ section.id }} svg {
    width: 16px;
  }

  .header-menu-link-{{ section.id }} {
    margin: 0;
    font-size: {{ link_size_mobile }}px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease 0s;
    position: relative;
    font-weight: 700;
    z-index: 3;
  }

  .header-menu-link-{{ section.id }}:after {
    content: '';
    position: absolute;
    bottom: -2px;
    left: 0;
    width: 100%;
    height: 1px;
    background-color: {{ link_hover_color }};
    opacity: 0;
    transition: all 0.25s ease 0s;
  }

  .header-submenu-list-{{ section.id }} {
    display: flex;
    flex-direction: column !important;
    gap: {{ submenu_gap }}px;
  }

  {% if transparent_bg == "all" %}
    .header-{{ section.id }}:not(.onscroll).transparent .header-menu-link-{{ section.id }} {
      color: {{ link_hero_color }};
    }
  {% elsif transparent_bg == "home"  %}
    {% if template.name == "index" %}
    .header-{{ section.id }}:not(.onscroll).transparent .header-menu-link-{{ section.id }} {
      color: {{ link_hero_color }};
    }
    {% endif %}
  {% endif %}

  .header-{{ section.id }}.open.transparent .header-menu-link-{{ section.id }} {
    color: {{ link_color }};
    transition: all 0.25s ease 0s;
  }

  .header-menu-link-open-{{ section.id }} path {
    stroke: {{ link_color }};
  }

  .header-submenu-{{ section.id }} .header-menu-link-{{ section.id }} {
    color: {{ submenu_link_color }} !important;
    font-weight: 400;
  }

  .header-menu-item-parent-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 10px;
    max-width: fit-content;
    flex-basis: 0;
  }

  .header-menu-item-icon-{{ section.id }} {
    display: block;
    flex: 0 0 8px;
    width: 8px;
    transition: all 0.25s ease 0s;
    color: {{ link_color }};
  }

  .header-menu-item-icon-{{ section.id }} svg path {
    stroke: {{ link_color }}
  }

  .header-{{ section.id }}:not(.onscroll).transparent.open .header-menu-item-icon-{{ section.id }} svg path {
    stroke: {{ link_color }};
    transition: all 0.25s ease 0s;
  }

  .header-menu-item-icon-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .header-submenu-{{ section.id }} {
    position: absolute;
    z-index: 3;
    top: calc(100% - {{ padding_bottom | times: 2 }}px);
    left: 0;
    width: 100%;
    opacity: 0;
    pointer-events: none;
    transition: all 0.25s ease 0s;
    list-style: none;
    margin: 0px;
  }

  .header-submenu-{{ section.id }}::after {
    content: "";
    position: absolute;
    left: 0;
    top: calc({{ padding_bottom | times: 2 }}px - {{ border_thickness }}px);
    height: {{ border_thickness }}px;
    background-color: {{ border_color }};
    width: 100%;
  }

  .header-submenu-bg-{{ section.id }} {
    display: block !important;
    height: {{ padding_bottom }}px;
    pointer-events: none;
  }

  .header-submenu-inner-{{ section.id }} {
    background-color: {{ background_color }};
    background-image: {{ background_gradient }};
    border-bottom: {{ border_thickness }}px solid {{ border_color }};
  }

  .header-submenu-{{ section.id }} .section-{{ section.id }}-settings {
    margin-top: {{ padding_top }}px;
    padding-top: 0px;
    padding-right: 0;
    padding-bottom: 0;
    overflow: hidden;  
    display: flex;
  }

  .header-menu-item-parent-{{ section.id }}.active .header-submenu-{{ section.id }} {
    opacity: 1;
    pointer-events: all;
    transition: all 0.25s ease 0s;
  }

  .header-menu-burger-{{ section.id }} {
    display: block;
    width: {{ menu_icon_size_mobile }}px;
    height: 16px;
    position: relative;
    z-index: 3;
    transition: all 0.25s ease 0s;
    cursor: pointer;
    background-color: transparent;
    border: 0px;
    box-shadow: none;
  }

  .header-menu-burger-icon-{{ section.id }} {
    position: absolute;
    top: 50%;
    left: 0;
    margin-top: -1px;
    width: 100%;
    height: 1px;
    background: {{ link_color }};
    transition: transform .25s ease; 
  }

  {% if transparent_bg == "all" %}
    .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }} {
      background: {{ link_hero_color }};
    }
  {% elsif transparent_bg == "home"  %}
    {% if template.name == "index" %}
      .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }} {
        background: {{ link_hero_color }};
      }
    {% endif %}
  {% endif %}

  .header-{{ section.id }}.open .header-menu-burger-icon-{{ section.id }} {
    transform: rotate(45deg);
    transition-delay: .25s;
    background: {{ link_color }} !important;
  }

  .header-menu-burger-icon-{{ section.id }}::before,
  .header-menu-burger-icon-{{ section.id }}::after{
    content: "";
    display: block;
    position: absolute;
    width: 100%;
    height: 1px;
    background-color: inherit;
    background: {{ link_color }};
  }

  .header-menu-burger-icon-{{ section.id }}::before {
    top: -7px;
    transition: top .25s ease .27s, opacity .25s ease, width .25s ease;
  }

  .header-{{ section.id }}.open .header-menu-burger-icon-{{ section.id }}::before {
    top: 0;
    opacity: 0;
    width: 100%;
    transition: top .2s ease, opacity .2s ease .22s, width .1s ease;
    background: {{ link_color }};
  }

  .header-menu-burger-icon-{{ section.id }}::after {
    bottom: -7px;
    transition: bottom .25s ease .27s, transform .25s ease, width .25s ease;
  }

  .header-{{ section.id }}.open .header-menu-burger-icon-{{ section.id }}::after {
    bottom: 0;
    transform: rotate(-90deg);
    width: 100%;
    transition: bottom .2s ease, transform .2s ease .22s, width .1s ease;
    background: {{ link_color }} !important;
  }

  {% if transparent_bg == "all" %}
    .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }}::before,
    .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }}::after{
      background: {{ link_hero_color }};
    }
  {% elsif transparent_bg == "home"  %}
    {% if template.name == "index" %}
      .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }}::before,
      .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }}::after{
        background: {{ link_hero_color }};
      }
    {% endif %}
  {% endif %}

  .header-logo-{{ section.id }} {
    width: 100%;
    text-align: center;
    position: relative;
    z-index: 3;
    transition: all 0.25s ease 0s;
  }

  .header-{{ section.id }}.open .header-logo-{{ section.id }} {
    transition: all 0.25s ease 0s;
  }

  .header-logo-image-{{ section.id }} {
    max-width: {{ logo_width_mobile }}px;
  }

  .header-logo-image-{{ section.id }} img,
  .header-logo-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .header-logo-image-desktop-{{ section.id }} {
    display: none;
  }

  .header-logo-text-{{ section.id }} {
    margin: 0;
    font-size: {{ name_size_mobile }}px;
    color: {{ name_color }};
    line-height: {{ name_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease 0s;
  }
  {% if transparent_bg == "all" %}
  .header-{{ section.id }}:not(.onscroll).transparent .header-logo-text-{{ section.id }} {
    color: {{ name_hero_color }};
  }

  .header-{{ section.id }}:not(.onscroll).transparent .header-logo-first-{{ section.id }} {
    display: none !important;
  }

  .header-{{ section.id }}:not(.onscroll).transparent .header-logo-second-{{ section.id }} {
    display: block !important;
  }
{% elsif transparent_bg == "home" and template.name == "index" %}
  .header-{{ section.id }}:not(.onscroll).transparent .header-logo-text-{{ section.id }} {
    color: {{ name_hero_color }};
  }

  .header-{{ section.id }}:not(.onscroll).transparent .header-logo-first-{{ section.id }} {
    display: none !important;
  }

  .header-{{ section.id }}:not(.onscroll).transparent .header-logo-second-{{ section.id }} {
    display: block !important;
  }
{% endif %}

.header-{{ section.id }}.open.transparent .header-logo-text-{{ section.id }} {
  color: {{ name_color }};
  transition: all 0.25s ease 0s;
}

.header-{{ section.id }} .header-logo-first-{{ section.id }} {
  display: block !important;
}

.header-{{ section.id }} .header-logo-second-{{ section.id }} {
  display: none !important;
}

.header-{{ section.id }}.open.transparent .header-logo-first-{{ section.id }} {
  display: block !important;
}

.header-{{ section.id }}.open.transparent .header-logo-second-{{ section.id }} {
  display: none !important;
}

  .header-right-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: end;
    gap: {{ right_gap_mobile }}px;
    flex-wrap: nowrap;
    position: relative;
    z-index: 3;
    order: 3;
    transition: all 0.25s ease 0s;
  }

  .header-{{ section.id }}.open .header-right-{{ section.id }} {
    transition: all 0.25s ease 0s;
  }

  .header-search-{{ section.id }},
  .header-account-{{ section.id }},
  .header-cart-{{ section.id }}{
    position: relative;
  }

  .header-search-text-{{ section.id }},
  .header-account-text-{{ section.id }},
  .header-cart-text-{{ section.id }}{
