<script>
  function initHeader10() {
    let header = document.querySelector('.header-{{ section.id }}');
    let menuMobile = document.querySelector('.header-menu-mobile-{{ section.id }}');

    let mobileSubMenu = document.querySelectorAll('.header-menu-mobile-submenu-{{ section.id }}');
    let parentItems = document.querySelectorAll('.header-menu-item-parent-{{ section.id }}');
    if (header) {
      {% if show_type != 'top' %}
        if (window.scrollY > 0) {
            header.classList.add('onscroll');
        } else {
          header.classList.add('top');
        }

        let lastScrollTop = window.scrollY;

        document.addEventListener('scroll', () => {
          if (parentItems) {
            parentItems.forEach(el => el.classList.remove('active'));
          }
          const currentScrollTop = window.scrollY;
          const maxScroll = document.documentElement.scrollHeight - window.innerHeight;  
          if (currentScrollTop === 0) {
            console.log(" At the TOP of the page");
            header.classList.add('top');
            header.style.transition = "margin-top 0.3s ease-in-out";
            header.style.marginTop = "{{ section.settings.margin_top }}px";
          } else {
            header.classList.remove('top');
          }
          if (currentScrollTop >= maxScroll) {
            console.log(" At the BOTTOM of the page");
            header.classList.remove('active');
            return;   
          }
          let announcementBar = document.querySelector('.{{ announcement_bar_selector }}');
          if (announcementBar) {
            let announcementHeight = announcementBar.offsetHeight;
            let rect = announcementBar.getBoundingClientRect();
            let isVisible = rect.bottom > 0;
            header.style.transition = "margin-top 0.3s ease-in-out";
            if (!isVisible) {
              console.log(" Announcement bar is NOT visible, removing margin");
              header.style.marginTop = "0px";
            }
          } else {
            header.style.marginTop = "0px";
          }
          if (currentScrollTop > lastScrollTop) {
            header.classList.remove('active');
          } else {
            header.classList.add('active');
            if (currentScrollTop < 20) {
              header.style.transition = "margin-top 0.3s ease-in-out"; 
              header.style.marginTop = "";
            }   
          }
          lastScrollTop = currentScrollTop;
          if (header.classList.contains('transparent')) {
            if (currentScrollTop < 20) {
              header.classList.remove('onscroll');
              header.classList.add('top');
            } else {
              header.classList.add('onscroll');
              header.classList.remove('top');
            }
          }
        });
      {% endif %}

      let burger_icon = header.querySelector('.header-menu-burger-{{ section.id }}');
      if(burger_icon) {
        burger_icon.addEventListener('click', () => {
          header.classList.toggle('open');
          header.classList.remove('onscroll');
          if(header.classList.contains('open')) {
            document.body.style.overflow = 'hidden';
          } else {
            document.body.style.overflow = '';
          }
        })
      }

      document.addEventListener('click', (e) => {
        if(e.target == menuMobile) {
          document.body.style.overflow = '';
          header.classList.remove('open');           
        }
      });

      if (parentItems) {
        parentItems.forEach(el => {
          el.addEventListener('mouseenter', () => {
            header.classList.add('onscroll');
            el.classList.add('active');
          });

          el.addEventListener('mouseleave', () => {
            el.classList.remove('active');
            if (window.scrollY === 0) {
              header.classList.remove('onscroll');
            }
          });

          document.addEventListener('click', (event) => {
            if (!el.contains(event.target) && !el.classList.contains('active')) {  
              return;
            }  

            if (!el.contains(event.target) && el.classList.contains('active')) {  
              el.classList.remove('active');  
            }   
          });
        });
      }
    }

    const openButtons = document.querySelectorAll('.header-menu-link-open-{{ section.id }}');
    const submenus = document.querySelectorAll('.header-mobile-submenu-{{ section.id }}');

    const submenuHierarchy = {};

    submenus.forEach(submenu => {
      submenu.style.display = 'none';
      submenu.style.opacity = '0';
      submenu.style.height = '0';
      submenu.style.overflow = 'hidden';

      submenu.isOpen = false;

      const parentSubmenu = submenu.parentElement.closest('.header-mobile-submenu-{{ section.id }}');
      if (parentSubmenu) {
        submenu.setAttribute('data-level', 'nested');

        const parentIndex = Array.from(submenus).indexOf(parentSubmenu);
        const childIndex = Array.from(submenus).indexOf(submenu);

        if (!submenuHierarchy[parentIndex]) {
          submenuHierarchy[parentIndex] = [];
        }
        submenuHierarchy[parentIndex].push(childIndex);
      } else {
        submenu.setAttribute('data-level', 'root');
      }
    });

    openButtons.forEach((button, index) => {
      const listItem = button.closest('li');
      if (!listItem) return;

      const targetSubmenu = listItem.querySelector('.header-mobile-submenu-{{ section.id }}');
      if (!targetSubmenu) return;

      button.submenuIndex = Array.from(submenus).indexOf(targetSubmenu);

      button.addEventListener('click', (e) => {
        e.stopPropagation();
        toggleSubmenu(button.submenuIndex);
      });
    });

    function openSubmenu(index) {
      const submenu = submenus[index];
      if (!submenu) return;

      if (submenu.isOpen) return;

      const isNested = submenu.getAttribute('data-level') === 'nested';
      const parentSubmenu = isNested ? submenu.parentElement.closest('.header-mobile-submenu-{{ section.id }}') : null;


      submenu.style.display = 'block';
      submenu.isOpen = true;

      submenu.style.height = 'auto';
      const submenuHeight = submenu.offsetHeight + 10;
      submenu.style.height = '0';

      setTimeout(() => {
        submenu.style.transition = 'all 0.25s ease 0s';
        submenu.style.height = `${submenuHeight}px`;

        if (isNested && parentSubmenu && parentSubmenu.isOpen) {
          const parentCurrentHeight = parseInt(parentSubmenu.style.height);
          const newParentHeight = parentCurrentHeight + submenuHeight;
          parentSubmenu.style.height = `${newParentHeight}px`;
        }

        setTimeout(() => {
          submenu.style.opacity = '1';
        }, 10);
      }, 10);

      const button = findButtonForSubmenu(index);
      if (button) {
        button.classList.add('active');
        const paths = button.querySelectorAll('path');
        if (paths.length > 0) {
          paths[0].style.opacity = '0';
        }
      }
    }

    function closeSubmenu(index) {
      const submenu = submenus[index];
      if (!submenu) return;


      if (!submenu.isOpen) return;

      const isNested = submenu.getAttribute('data-level') === 'nested';
      const parentSubmenu = isNested ? submenu.parentElement.closest('.header-mobile-submenu-{{ section.id }}') : null;

      const submenuHeight = parseInt(submenu.style.height);

      if (submenuHierarchy[index]) {
        submenuHierarchy[index].forEach(nestedIndex => {
          closeSubmenu(nestedIndex);
        });
      }

      const button = findButtonForSubmenu(index);
      if (button) {
        button.classList.remove('active');
        const paths = button.querySelectorAll('path');
        if (paths.length > 0) {
          paths[0].style.opacity = '1';
        }
      }

      submenu.style.transition = 'all 0.25s ease 0s';
      submenu.style.height = '0';

      if (isNested && parentSubmenu && parentSubmenu.isOpen) {
        const parentCurrentHeight = parseInt(parentSubmenu.style.height);
        const newParentHeight = parentCurrentHeight - submenuHeight;
        parentSubmenu.style.height = `${newParentHeight}px`;
      }

      setTimeout(() => {
        submenu.style.opacity = '0';
      }, 250);

      setTimeout(() => {
        submenu.style.display = 'none';
        submenu.isOpen = false;
      }, 300);
    }

    function toggleSubmenu(index) {
      const submenu = submenus[index];
      if (!submenu) return;

      if (submenu.isOpen) {
        closeSubmenu(index);
      } else {
        openSubmenu(index);
      }
    }

    function findButtonForSubmenu(submenuIndex) {
      for (let i = 0; i < openButtons.length; i++) {
        if (openButtons[i].submenuIndex === submenuIndex) {
          return openButtons[i];
        }
      }
      return null;
    }

    let resizeTimer;
    window.addEventListener('resize', () => {
      clearTimeout(resizeTimer);
      resizeTimer = setTimeout(() => {

        submenus.forEach((submenu, index) => {
          if (submenu.isOpen) {

            const currentHeight = submenu.style.height;
            submenu.style.height = 'auto';
            let calculatedHeight = submenu.offsetHeight;

            submenu.style.height = currentHeight;
            setTimeout(() => {
              submenu.style.transition = 'all 0.25s ease 0s';
              submenu.style.height = `${calculatedHeight}px`;

              if (submenu.getAttribute('data-level') === 'root' && submenuHierarchy[index]) {
                let additionalHeight = 0;
                submenuHierarchy[index].forEach(nestedIndex => {
                  const nestedSubmenu = submenus[nestedIndex];
                  if (nestedSubmenu.isOpen) {
                    additionalHeight += parseInt(nestedSubmenu.style.height);
                  }
                });

                submenu.style.height = `${calculatedHeight + additionalHeight}px`;
              }
            }, 10);
          }
        });
      }, 250);
    });
  }

  document.addEventListener('DOMContentLoaded', initHeader10);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:unload', initHeader10);
     document.addEventListener('shopify:section:load', initHeader10);
  }
</script>
