liquid_code:
{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign border_bottom = section.settings.border_bottom
  assign margin_top = section.settings.margin_top
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign show_type = section.settings.show_type

  assign transparent_bg = section.settings.transparent_bg

  assign layout_gap_mobile = section.settings.layout_gap_mobile
  assign layout_gap = section.settings.layout_gap

  assign logo = section.settings.logo
  assign second_logo = section.settings.second_logo
  assign logo_mobile = section.settings.logo_mobile
  assign second_logo_mobile = section.settings.second_logo_mobile
  assign logo_width = section.settings.logo_width
  assign logo_width_mobile = section.settings.logo_width_mobile
  assign logo_width_sidebar = section.settings.logo_width_sidebar

  assign name_size = section.settings.name_size
  assign name_size_mobile = section.settings.name_size_mobile
  assign name_color = section.settings.name_color
  assign name_hover_color = section.settings.name_hover_color
  assign name_hero_color = section.settings.name_hero_color
  assign name_hover_hero_color = section.settings.name_hover_hero_color
  assign name_custom = section.settings.name_custom
  assign name_font = section.settings.name_font
  assign name_height = section.settings.name_height

  assign main_padding_vertical = section.settings.main_padding_vertical
  assign main_padding_vertical_mobile = section.settings.main_padding_vertical_mobile
  assign main_padding_horizontal = section.settings.main_padding_horizontal
  assign main_padding_horizontal_mobile = section.settings.main_padding_horizontal_mobile
  assign main_radius = section.settings.main_radius
  assign main_border_thickness = section.settings.main_border_thickness
  assign main_border_color = section.settings.main_border_color
  assign main_bg_color = section.settings.main_bg_color

  assign menu = section.settings.menu

  assign list_gap_mobile = section.settings.list_gap_mobile
  assign list_gap = section.settings.list_gap
  assign sidebar_list_gap = section.settings.sidebar_list_gap
  assign sidebar_list_padding_vertical = section.settings.sidebar_list_padding_vertical
  assign sidebar_list_padding_vertical_mobile = section.settings.sidebar_list_padding_vertical_mobile

  assign link_size = section.settings.link_size
  assign sidebar_link_size = section.settings.sidebar_link_size
  assign link_size_mobile = section.settings.link_size_mobile
  assign link_color = section.settings.link_color
  assign link_hover_color = section.settings.link_hover_color
  assign link_hero_color = section.settings.link_hero_color
  assign link_hover_hero_color = section.settings.link_hover_hero_color
  assign link_custom = section.settings.link_custom
  assign link_font = section.settings.link_font
  assign link_height = section.settings.link_height

  assign menu_icon_size = section.settings.menu_icon_size
  assign menu_icon_size_mobile = section.settings.menu_icon_size_mobile

  assign sidebar_width = section.settings.sidebar_width
  assign sidebar_radius = section.settings.sidebar_radius
  assign sidebar_border_thickness = section.settings.sidebar_border_thickness
  assign sidebar_border_color = section.settings.sidebar_border_color
  assign sidebar_bg_color = section.settings.sidebar_bg_color
  assign sidebar_padding_vertical = section.settings.sidebar_padding_vertical
  assign sidebar_padding_vertical_mobile = section.settings.sidebar_padding_vertical_mobile
  assign sidebar_padding_horizontal = section.settings.sidebar_padding_horizontal
  assign sidebar_padding_horizontal_mobile = section.settings.sidebar_padding_horizontal_mobile

  assign bottom_text_size = section.settings.bottom_text_size
  assign bottom_text_size_mobile = section.settings.bottom_text_size_mobile
  assign bottom_text_color = section.settings.bottom_text_color
  assign bottom_text_custom = section.settings.bottom_text_custom
  assign bottom_text_font = section.settings.bottom_text_font
  assign bottom_text_height = section.settings.bottom_text_height

  assign socials_gap_mobile = section.settings.socials_gap_mobile
  assign socials_gap = section.settings.socials_gap

  assign social_icon_size = section.settings.social_icon_size
  assign social_icon_size_mobile = section.settings.social_icon_size_mobile
  assign social_icon_color = section.settings.social_icon_color
  assign social_icon_hover_color = section.settings.social_icon_hover_color

  assign right_gap_mobile = section.settings.right_gap_mobile
  assign right_gap = section.settings.right_gap

  assign right_link_size = section.settings.right_link_size
  assign right_link_color = section.settings.right_link_color
  assign right_link_hover_color = section.settings.right_link_hover_color
  assign right_link_hero_color = section.settings.right_link_hero_color
  assign right_link_hover_hero_color = section.settings.right_link_hover_hero_color
  assign right_link_custom = section.settings.right_link_custom
  assign right_link_font = section.settings.right_link_font
  assign right_link_height = section.settings.right_link_height

  assign icon_size_mobile = section.settings.icon_size_mobile
  assign icon_size = section.settings.icon_size  

  assign use_search = section.settings.use_search
  assign search_text  = section.settings.search_text
  assign search_icon_desktop  = section.settings.search_icon_desktop
  assign search_menu_mobile = section.settings.search_menu_mobile

  assign use_account = section.settings.use_account
  assign account_text  = section.settings.account_text
  assign account_icon_desktop  = section.settings.account_icon_desktop
  assign account_menu_mobile = section.settings.account_menu_mobile

  assign cart_text  = section.settings.cart_text
  assign cart_icon_desktop  = section.settings.cart_icon_desktop
  assign count_bg_color = section.settings.count_bg_color
  assign count_color = section.settings.count_color

-%}

{%- style -%}

  {{ link_font | font_face: font_display: 'swap' }}
  {{ name_font | font_face: font_display: 'swap' }}
  {{ bottom_text_font | font_face: font_display: 'swap' }}
  {{ right_link_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;

    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    position: relative;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  {% if transparent_bg == "all" %}
    .header-{{ section.id }}.transparent {
      background-color: transparent !important;
      background-image: none !important;
      position: absolute;
      width: calc(100% - {{margin_horizontal_mobile | times: 2 }}rem);
      top: 0;
      left: 0;
      z-index: 999;
    }
  {% elsif transparent_bg == "home"  %}
    {% if template.name == "index" %}
    .header-{{ section.id }}.transparent {
      background-color: transparent !important;
      background-image: none !important;
      position: absolute;
      width: calc(100% - {{margin_horizontal_mobile | times: 2 }}rem);
      top: 0;
      left: 0;
      z-index: 999;
    }
    {% endif %}
  {% endif %}

  .header-{{ section.id }}.onscroll {
    background-color:{{ background_color }} !important;
    background-image: {{ background_gradient }} !important;
    transition: all 0.25s ease 0s;
  }

  .header-{{ section.id }}.fixed {
    position: fixed;
    top: 0;
    left: 0;
    width: calc(100% - {{margin_horizontal_mobile | times: 2 }}rem);
    z-index: 999;
  }

  .header-{{ section.id }}.scroll {
    transform: translateY(-150%);
    transition: all 0.25s ease 0s;
  }

  .header-{{ section.id }}.scroll.active {
    transform: translateY(0);
    transition: all 0.25s ease 0s;
  }

  .header-body-{{ section.id }} {
    display: flex;
    align-items: center;
  }

  .header-main-{{ section.id }} {
    display: grid;
    align-items: center;
    grid-template-columns: 1fr auto 1fr;
    gap: {{ layout_gap_mobile }}px;
    flex: 1 1 auto;
    padding: {{ main_padding_vertical_mobile }}px {{ main_padding_horizontal_mobile }}px;
    border-radius: {{ main_radius }}px;
    border: {{ main_border_thickness }}px solid {{ main_border_color }};
    background-color: {{ main_bg_color }};
    transition: all 0.25s ease 0s;
  }

  {% if transparent_bg == "all" %}
    .header-{{ section.id }}:not(.onscroll).transparent .header-main-{{ section.id }} {
      border-color: transparent;
      background-color: transparent;
      transition: all 0.25s ease 0s;
    }
  {% elsif transparent_bg == "home"  %}
    {% if template.name == "index" %}
    .header-{{ section.id }}:not(.onscroll).transparent .header-main-{{ section.id }} {
      border-color: transparent;
      background-color: transparent;
      transition: all 0.25s ease 0s;
    }
    {% endif %}
  {% endif %}

  .header-menu-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 10px;
  }

  .header-menu-list-{{ section.id }} {
    padding: 0px;
    margin: 0px;
    display: none;
    align-items: center;
    gap: {{ list_gap_mobile }}px;
    list-style: none;
  }

  .header-sidebar-{{ section.id }} .header-menu-list-{{ section.id }} {
    display: flex;
    flex-direction: column;
    padding: {{ sidebar_list_padding_vertical_mobile }}px 0px;
    align-items: start;
  }

  .header-menu-item-{{ section.id }} {
    line-height: 100%;
  }

  .header-menu-link-{{ section.id }} {
    margin: 0;
    font-size: {{ link_size_mobile }}px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease 0s;
    font-weight: 400;
  }

  {% if transparent_bg == "all" %}
    .header-{{ section.id }}:not(.onscroll).transparent .header-menu-link-{{ section.id }} {
      color: {{ link_hero_color }};
    }
  {% elsif transparent_bg == "home"  %}
    {% if template.name == "index" %}
    .header-{{ section.id }}:not(.onscroll).transparent .header-menu-link-{{ section.id }} {
      color: {{ link_hero_color }};
    }
    {% endif %}
  {% endif %}

  .header-menu-item-icon-{{ section.id }} {
    display: block;
    flex: 0 0 8px;
    width: 8px;
    transition: all 0.25s ease 0s;
    color: {{ link_color }};
  }

  .header-menu-item-icon-{{ section.id }} svg path {
    stroke: {{ link_color }}
  }

  .header-menu-item-icon-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .header-menu-burger-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    width: {{ menu_icon_size_mobile }}px;
    height: {{ menu_icon_size_mobile }}px;
    border-radius: 100px;
    padding: 0px;
    overflow: hidden;
    background-color: rgba(26, 26, 26, 0.03);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    position: relative;
    z-index: 3;
    transition: all 0.25s ease 0s;
    cursor: pointer;
    border: 0px;
    box-shadow: none;
    flex-shrink: 0;
  }

  .header-menu-burger-icon-{{ section.id }} {
    display: block;
    width: 50%;
    height: 1px;
    position: relative;
    background-color: {{ link_color }};
    transition: background-color 0.25s ease 0s;
  }

  {% if transparent_bg == "all" %}
    .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }} {
      background: {{ link_hero_color }};
    }

    .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-{{ section.id }} {
      background-color: rgba(255, 255, 255, 0.08);
    }
  {% elsif transparent_bg == "home"  %}
    {% if template.name == "index" %}
      .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }} {
        background: {{ link_hero_color }};
      }

      .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-{{ section.id }} {
        background-color: rgba(255, 255, 255, 0.08);
      }
    {% endif %}
  {% endif %}

  .header-{{ section.id }}.open .header-menu-burger-icon-{{ section.id }} {
    transform: rotate(45deg);
  }

  .header-menu-burger-icon-{{ section.id }}::before,
  .header-menu-burger-icon-{{ section.id }}::after{
    content: "";
    display: block;
    position: absolute;
    width: 100%;
    height: 1px;
    background-color: {{ link_color }};
    transition: background-color 0.25s ease 0s;
  }

  .header-menu-burger-icon-{{ section.id }}::before {
    top: -4px;
  }

  .header-{{ section.id }}.open .header-menu-burger-icon-{{ section.id }}::before {
    top: 0;
    opacity: 0;
    width: 100%;
  }

  .header-menu-burger-icon-{{ section.id }}::after {
    bottom: -4px;
  }

  .header-{{ section.id }}.open .header-menu-burger-icon-{{ section.id }}::after {
    bottom: 0;
    transform: rotate(-90deg);
    width: 100%;
  }

  {% if transparent_bg == "all" %}
    .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }}::before,
    .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }}::after{
      background: {{ link_hero_color }};
    }
  {% elsif transparent_bg == "home"  %}
    {% if template.name == "index" %}
      .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }}::before,
      .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }}::after{
        background: {{ link_hero_color }};
      }
    {% endif %}
  {% endif %}

  .header-logo-{{ section.id }} {
    width: 100%;
    text-align: left;
    position: relative;
    z-index: 3;
    transition: all 0.25s ease 0s;
    max-width: fit-content;
  }

  .header-logo-wrapper-mobile-{{ section.id }} {
    display: flex;
  }

  .header-logo-wrapper-desktop-{{ section.id }} {
    display: none;
  }

  .header-logo-image-{{ section.id }} {
    max-width: {{ logo_width_mobile }}px;
  }

  .header-sidebar-{{ section.id }} .header-logo-image-{{ section.id }} {
    display: block;
    max-width: {{ logo_width_sidebar }}px;
  }

  .header-logo-image-{{ section.id }} img,
  .header-logo-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .header-logo-image-desktop-{{ section.id }} {
    display: none;
  }

  .header-logo-text-{{ section.id }} {
    margin: 0;
    font-size: {{ name_size_mobile }}px;
    color: {{ name_color }};
    line-height: {{ name_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease 0s;
    font-weight: 700;
    text-align: center;
  }

  {% if transparent_bg == "all" %}
    .header-{{ section.id }}:not(.onscroll).transparent .header-logo-text-{{ section.id }} {
      color: {{ name_hero_color }};
    }
  {% elsif transparent_bg == "home"  %}
    {% if template.name == "index" %}
      .header-{{ section.id }}:not(.onscroll).transparent .header-logo-text-{{ section.id }} {
        color: {{ name_hero_color }};
      }
    {% endif %}
  {% endif %}

  .header-{{ section.id }} .header-logo-first-{{ section.id }} {
    display: block !important;
  }

  .header-{{ section.id }} .header-logo-second-{{ section.id }} {
    display: none !important;
  }

  .header-{{ section.id }}:not(.onscroll).transparent .header-logo-first-{{ section.id }} {
    display: none !important;
  }

  .header-{{ section.id }}:not(.onscroll).transparent .header-sidebar-{{ section.id }} .header-logo-first-{{ section.id }} {
    display: block !important;
  }

  .header-{{ section.id }}:not(.onscroll).transparent .header-logo-second-{{ section.id }} {
    display: block !important;
  }

  .header-{{ section.id }}:not(.onscroll).transparent .header-sidebar-{{ section.id }} .header-logo-second-{{ section.id }} {
    display: none !important;
  }

  .header-right-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: end;
    gap: {{ right_gap_mobile }}px;
    flex-wrap: nowrap;
    position: relative;
    z-index: 3;
    order: 3;
    transition: all 0.25s ease 0s;
  }

  .header-search-text-{{ section.id }},
  .header-account-text-{{ section.id }},
  .header-cart-text-{{ section.id }}{
    display: none;
    margin: 0;
    color: {{ right_link_color }};
    line-height: {{ right_link_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease;
  }

  .header-cart-{{ section.id }} .cart-count-bubble {
    padding: 0px;
    background-color: transparent;
    border: 0px;
    margin: 0px;
    width: fit-content;
    height: fit-content;
    position: static  !important;
    background-color: {{ count_bg_color }};
    padding: 6px;
    border-radius: 100px;
    overflow: hidden;
    right: -2px;
    top: -2px;
    aspect-ratio: 1/1;
  }

  .header-cart-{{ section.id }} .cart-count-bubble span {
    margin: 0;  
    font-size: {{ right_link_size | times: 0.75 | round: 0 }}px;
    color: {{ count_color }};
    line-height: {{ right_link_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all .25s ease;
  }

  {% if transparent_bg == "all" %}
    .header-{{ section.id }}:not(.onscroll).transparent .header-search-text-{{ section.id }},
    .header-{{ section.id }}:not(.onscroll).transparent .header-account-text-{{ section.id }},
    .header-{{ section.id }}:not(.onscroll).transparent .header-cart-text-{{ section.id }} {
      color: {{ right_link_hero_color }};
    }
  {% elsif transparent_bg == "home"  %}
    {% if template.name == "index" %}
      .header-{{ section.id }}:not(.onscroll).transparent .header-search-text-{{ section.id }},
      .header-{{ section.id }}:not(.onscroll).transparent .header-account-text-{{ section.id }},
      .header-{{ section.id }}:not(.onscroll).transparent .header-cart-text-{{ section.id }} {
        color: {{ right_link_hero_color }};
      }
    {% endif %}
  {% endif %}

  .header-search-icon-{{ section.id }},
  .header-account-icon-{{ section.id }},
  .header-cart-icon-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    width: {{ icon_size_mobile }}px;
    height: {{ icon_size_mobile }}px;
    border-radius: 50%;
    background-color: rgba(26,26,26,0.03);
    backdrop-filter: blur(10px);
    -webit-backdrop-filter: blur(10px);
  }

  {% if transparent_bg == "all" %}
    .header-{{ section.id }}:not(.onscroll).transparent .header-search-icon-{{ section.id }},
    .header-{{ section.id }}:not(.onscroll).transparent .header-account-icon-{{ section.id }},
    .header-{{ section.id }}:not(.onscroll).transparent .header-cart-icon-{{ section.id }} {
      background-color: rgba(255,255,255,0.08);
    }
  {% elsif transparent_bg == "home"  %}
    {% if template.name == "index" %}
      .header-{{ section.id }}:not(.onscroll).transparent .header-search-icon-{{ section.id }},
      .header-{{ section.id }}:not(.onscroll).transparent .header-account-icon-{{ section.id }},
      .header-{{ section.id }}:not(.onscroll).transparent .header-cart-icon-{{ section.id }} {
        background-color: rgba(255,255,255,0.08);
      }
    {% endif %}
  {% endif %}

  .header-search-icon-{{ section.id }} svg,
  .header-account-icon-{{ section.id }} svg,
  .header-cart-icon-{{ section.id }} svg {
    display: block;
    width: 50%;
  }

  .header-search-icon-{{ section.id }} svg path,
  .header-account-icon-{{ section.id }} svg path {
    stroke: {{ right_link_color }};
    transition: all .25s ease;
  }

  .header-cart-icon-{{ section.id }} svg path {
    stroke: {{ right_link_color }};
    transition: all .25s ease;
  }

  {% if transparent_bg == "all" %}
    .header-{{ section.id }}:not(.onscroll).transparent .header-search-icon-{{ section.id }} svg path,
    .header-{{ section.id }}:not(.onscroll).transparent .header-account-icon-{{ section.id }} svg path {
      stroke: {{ right_link_hero_color }};
    }

    .header-{{ section.id }}:not(.onscroll).transparent .header-cart-icon-{{ section.id }} svg path {
      stroke: {{ right_link_hero_color }}
    }
  {% elsif transparent_bg == "home"  %}
    {% if template.name == "index" %}
      .header-{{ section.id }}:not(.onscroll).transparent .header-search-icon-{{ section.id }} svg path,
      .header-{{ section.id }}:not(.onscroll).transparent .header-account-icon-{{ section.id }} svg path {
        stroke: {{ right_link_hero_color }};
      }

      .header-{{ section.id }}:not(.onscroll).transparent .header-cart-icon-{{ section.id }} svg path {
        stroke: {{ right_link_hero_color }}
      }
    {% endif %}
  {% endif %}

  .header-cart-{{ section.id }},
  .header-account-{{ section.id }} {
    text-decoration: none;
  }

  .header-search-{{ section.id }} {
    padding: 0px;
    margin: 0px;
    background-color: transparent;
    border: 0px;
    box-shadow: none;
    cursor: pointer;
    text-decoration: none;
  }

  .header-cart-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 5px;
    position: relative;
  }

  {% if cart_icon_desktop %}
    .header-cart-{{ section.id }} .cart-count-bubble {
      margin-left: -10px;
      z-index: 2;
    }

    .header-cart-{{ section.id }} {
      gap: 0px;
      align-items: start;
    }
  {% endif %}

  .header-sidebar-{{ section.id }} {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100dvh;
    overflow-y: auto;
    background-color: rgba(0,0,0,0.5);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    transition: all 0.25s ease 0s;
    z-index: 99;
    padding: 40px 40px 40px 0px;
    transition: all 0.25s ease 0s;
    opacity: 0;
    pointer-events: none;
  }

  .header-{{ section.id }}.open .header-sidebar-{{ section.id }} {
    align-items: stretch;
    display: flex;
    pointer-events: all;
    opacity: 1;
    transition: all 0.25s ease 0s;
  }

  .header-sidebar-inner-{{ section.id }} {
    background-color: {{ sidebar_bg_color }};
    border-top-right-radius: {{ sidebar_radius }}px;
    border-bottom-right-radius: {{ sidebar_radius }}px;
