            {%- endif -%}

            <a href="{{ routes.cart_url }}" class="header-cart-{{ section.id }}">
              <span class="header-cart-icon-{{ section.id }}">
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="-0.5 -0.5 16 16" stroke-linecap="round" stroke-linejoin="round" stroke="#121212" height="16" width="16"><path d="M10.3125 13.125a0.9375 0.9375 0 1 0 0 -1.875 0.9375 0.9375 0 0 0 0 1.875m-5 0a0.9375 0.9375 0 1 0 0 -1.875 0.9375 0.9375 0 0 0 0 1.875M2.31875 3.375h9.508750000000001c0.86125 0 1.4831250000000002 0.79375 1.2468750000000002 1.5925l-1.03375 3.5C11.881250000000001 9.004999999999999 11.3725 9.375 10.79375 9.375H5.07c-0.579375 0 -1.08875 -0.370625 -1.2475 -0.9075zm0 0L1.875 1.875" stroke-width="1"></path></svg>
              </span>
              <span class="header-cart-text-{{ section.id }}">{{ cart_text }}</span>       
              <div class="cart-count-bubble">
                {%- if cart.item_count < 100 -%}
                  <span aria-hidden="true">{{ cart.item_count }}</span>
                {%- endif -%}
              </div>      
            </a>
          </div>
        </div>
      </div>
    </div>
</div>

<script>
  function initHeader12() {
    let header = document.querySelector('.header-{{ section.id }}');
    let menuMobile = document.querySelector('.header-sidebar-{{ section.id }}');

    if (header) {
      {% if show_type != 'top' %}
        if (window.scrollY > 0) {
            header.classList.add('onscroll');
        } else {
          header.classList.add('top');
        }

        let lastScrollTop = window.scrollY;

        document.addEventListener('scroll', () => {

            const currentScrollTop = window.scrollY;
            const maxScroll = document.documentElement.scrollHeight - window.innerHeight;

            if (currentScrollTop === 0) {
              header.classList.add('top');
            }

            if (currentScrollTop <= 0) {
                header.classList.add('active');
                header.classList.remove('onscroll');
                return;
            }

            if (currentScrollTop >= maxScroll) {
                header.classList.remove('active');
                return;
            }

            if (currentScrollTop !== lastScrollTop) {
                if (header.classList.contains('scroll')) {
                    if (currentScrollTop > lastScrollTop) {
                        header.classList.remove('active');
                    } else {
                        header.classList.add('active');
                    }
                }

                if (header.classList.contains('transparent')) {
                    if (currentScrollTop === 0) {
                        header.classList.remove('onscroll');
                        header.classList.add('top');
                    } else {
                        header.classList.add('onscroll');
                        header.classList.remove('top');
                    }
                }

                lastScrollTop = currentScrollTop;
            }
        });
      {% endif %}

      let burger_icons = header.querySelectorAll('.header-menu-burger-{{ section.id }}');
      if(burger_icons) {
        burger_icons.forEach(burger_icon => {
          burger_icon.addEventListener('click', () => {
            header.classList.toggle('open');
            if(header.classList.contains('open')) {
              document.body.style.overflow = 'hidden';
            } else {
              document.body.style.overflow = '';
            }
          })
        })
      }

      document.addEventListener('click', (e) => {
        if(e.target == menuMobile) {
          document.body.style.overflow = '';
          header.classList.remove('open');           
        }
      });

    }

    const openButtons = document.querySelectorAll('.header-menu-link-wrapper-{{ section.id }}');
    const submenus = document.querySelectorAll('.header-mobile-submenu-{{ section.id }}');
    const menuIcons = document.querySelectorAll('.header-menu-link-open-{{ section.id }}');

    submenus.forEach(submenu => {
      submenu.style.display = 'none';
      submenu.style.opacity = '0';
      submenu.style.height = '0';
      submenu.style.overflow = 'hidden';
    });

    let activeIndex = -1;

    function openSubmenu(index) {
      submenus[index].style.display = 'block';
      submenus[index].style.height = 'auto';
      let height = submenus[index].offsetHeight;
      submenus[index].style.height = '0';

      setTimeout(() => {
        submenus[index].style.transition = 'all 0.25s ease 0s';
        submenus[index].style.height = `${height}px`;
        setTimeout(() => {
          submenus[index].style.opacity = '1';
        }, 10);
      }, 10);

      menuIcons[index].classList.add('active');
    }


    function closeSubmenu(index) {
      menuIcons[index].classList.remove('active');
      submenus[index].style.transition = 'all 0.25s ease 0s';
      submenus[index].style.height = '0';
      setTimeout(() => {
        submenus[index].style.opacity = '0';
      }, 250);
      setTimeout(() => {
        submenus[index].style.display = 'none';
      }, 300);
    }

    function toggleSubmenu(index) {
      if (submenus[index].style.display === 'none' || submenus[index].style.display === '') {
        openSubmenu(index);
        activeIndex = index;
      } else {
        closeSubmenu(index);
        activeIndex = -1;
      }
    }

    function closeAllSubmenusExcept(index) {
      submenus.forEach((submenu, i) => {
        if (i !== index) {
          closeSubmenu(i);
        }
      });
    }

    openButtons.forEach((button, index) => {
      button.addEventListener('click', () => {
        toggleSubmenu(index);
        closeAllSubmenusExcept(index);
      });
    });

    let resizeTimer;
    window.addEventListener('resize', () => {
      clearTimeout(resizeTimer);
      resizeTimer = setTimeout(() => {
        if (activeIndex !== -1) {
          submenus[activeIndex].style.height = 'auto';
          const height = submenus[activeIndex].offsetHeight;
          submenus[activeIndex].style.height = '0';
          setTimeout(() => {
            submenus[activeIndex].style.height = `${height}px`;
          }, 10);
        }
      }, 250);
    });  
  }

  document.addEventListener('DOMContentLoaded', initHeader12);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:unload', initHeader12);
     document.addEventListener('shopify:section:load', initHeader12);
  }
</script>
