{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign border_bottom = section.settings.border_bottom
  assign margin_top = section.settings.margin_top
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy

  assign show_type = section.settings.show_type

  assign announcement_bg_color = section.settings.announcement_bg_color
  assign announcement_bg_color_mobile = section.settings.announcement_bg_color_mobile
  assign announcement_menu = section.settings.announcement_menu

  assign announcement_link_size = section.settings.announcement_link_size
  assign announcement_link_size_mobile = section.settings.announcement_link_size_mobile
  assign announcement_link_color = section.settings.announcement_link_color
  assign announcement_link_color_mobile = section.settings.announcement_link_color_mobile
  assign announcement_link_hover_color = section.settings.announcement_link_hover_color
  assign announcement_link_custom = section.settings.announcement_link_custom
  assign announcement_link_font = section.settings.announcement_link_font
  assign announcement_link_height = section.settings.announcement_link_height
  assign announcement_link_divider_color = section.settings.announcement_link_divider_color
  assign announcement_link_divider = section.settings.announcement_link_divider

  assign layout_gap_mobile = section.settings.layout_gap_mobile
  assign layout_gap = section.settings.layout_gap

  assign menu = section.settings.menu
  assign mobile_menu_divider_color = section.settings.mobile_menu_divider_color

  assign submenu_padding_bottom = section.settings.submenu_padding_bottom
  assign submenu_gap = section.settings.submenu_gap
  assign submenu_list_gap = section.settings.submenu_list_gap
  assign submenu_link_hover_color = section.settings.submenu_link_hover_color
  assign submenu_link_color = section.settings.submenu_link_color

  assign list_gap_mobile = section.settings.list_gap_mobile
  assign list_gap = section.settings.list_gap

  assign list_align = section.settings.list_align

  assign list_horizontal_align = ""
  if list_align == "center"
    assign list_horizontal_align = "center"
  elsif list_align == "right"
    assign list_horizontal_align = "end"
  else
    assign list_horizontal_align = "start"
  endif

  assign link_size = section.settings.link_size
  assign link_size_mobile = section.settings.link_size_mobile
  assign link_color = section.settings.link_color
  assign link_hover_color = section.settings.link_hover_color
  assign link_custom = section.settings.link_custom
  assign link_font = section.settings.link_font
  assign link_height = section.settings.link_height

  assign menu_icon_size_mobile = section.settings.menu_icon_size_mobile

  assign mobile_menu_heading = section.settings.mobile_menu_heading
  assign mobile_menu_heading_size = section.settings.mobile_menu_heading_size
  assign mobile_menu_heading_color = section.settings.mobile_menu_heading_color
  assign mobile_menu_heading_hover_color = section.settings.mobile_menu_heading_hover_color
  assign mobile_menu_heading_custom = section.settings.mobile_menu_heading_custom
  assign mobile_menu_heading_font = section.settings.mobile_menu_heading_font
  assign mobile_menu_heading_height = section.settings.mobile_menu_heading_height

  assign toggles_align_mobile = section.settings.toggles_align_mobile

  assign collection_gap = section.settings.collection_gap
  assign collection_gap_mobile = section.settings.collection_gap_mobile
  assign collection_padding_vertical = section.settings.collection_padding_vertical
  assign collection_padding_horizontal = section.settings.collection_padding_horizontal
  assign collection_bg_color = section.settings.collection_bg_color

  assign collection_heading = section.settings.collection_heading
  assign collection_heading_size = section.settings.collection_heading_size
  assign collection_heading_color = section.settings.collection_heading_color
  assign collection_heading_hover_color = section.settings.collection_heading_hover_color
  assign collection_heading_custom = section.settings.collection_heading_custom
  assign collection_heading_font = section.settings.collection_heading_font
  assign collection_heading_height = section.settings.collection_heading_height

  assign collection_image_border_thickness = section.settings.collection_image_border_thickness
  assign collection_image_border_color = section.settings.collection_image_border_color
  assign collection_image_ratio = section.settings.collection_image_ratio
  assign collection_image_ratio_mobile = section.settings.collection_image_ratio_mobile
  assign collection_image_radius = section.settings.collection_image_radius
  assign collection_image_hover = section.settings.collection_image_hover

  assign collection_title_size = section.settings.collection_title_size
  assign collection_title_color = section.settings.collection_title_color
  assign collection_title_custom = section.settings.collection_title_custom
  assign collection_title_font = section.settings.collection_title_font
  assign collection_title_height = section.settings.collection_title_height
  assign collection_title_align = section.settings.collection_title_align

  assign logo = section.settings.logo
  assign logo_mobile = section.settings.logo_mobile
  assign logo_width = section.settings.logo_width
  assign logo_width_mobile = section.settings.logo_width_mobile

  assign name_size = section.settings.name_size
  assign name_size_mobile = section.settings.name_size_mobile
  assign name_color = section.settings.name_color
  assign name_hover_color = section.settings.name_hover_color
  assign name_hero_color = section.settings.name_hero_color
  assign name_custom = section.settings.name_custom
  assign name_font = section.settings.name_font
  assign name_height = section.settings.name_height

  assign right_gap_mobile = section.settings.right_gap_mobile
  assign right_gap = section.settings.right_gap

  assign right_link_size = section.settings.right_link_size
  assign right_link_color = section.settings.right_link_color
  assign right_link_hover_color = section.settings.right_link_hover_color
  assign right_link_custom = section.settings.right_link_custom
  assign right_link_font = section.settings.right_link_font
  assign right_link_height = section.settings.right_link_height

  assign icon_size_mobile = section.settings.icon_size_mobile
  assign icon_size = section.settings.icon_size  

  assign use_search = section.settings.use_search
  assign search_text  = section.settings.search_text
  assign search_icon_desktop  = section.settings.search_icon_desktop
  assign search_menu_mobile = section.settings.search_menu_mobile

  assign use_account = section.settings.use_account
  assign account_text  = section.settings.account_text
  assign account_icon_desktop  = section.settings.account_icon_desktop
  assign account_menu_mobile = section.settings.account_menu_mobile

  assign cart_text  = section.settings.cart_text
  assign cart_icon_desktop  = section.settings.cart_icon_desktop

-%}

{%- style -%}

  {{ announcement_link_font | font_face: font_display: 'swap' }}
  {{ link_font | font_face: font_display: 'swap' }}
  {{ mobile_menu_heading_font | font_face: font_display: 'swap' }}
  {{ name_font | font_face: font_display: 'swap' }}
  {{ right_link_font | font_face: font_display: 'swap' }}
  {{ collection_heading_font | font_face: font_display: 'swap' }}
  {{ collection_title_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    position: relative;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .header-{{ section.id }}.onscroll {
    background-color:{{ background_color }} !important;
    background-image: {{ background_gradient }} !important;
    transition: all 0.25s ease 0s;
  }

  .header-{{ section.id }}.fixed {
    position: fixed;
    top: 0;
    left: 0;
    width: calc(100% - {{margin_horizontal_mobile | times: 2 }}rem);
    z-index: 999;
  }

  .header-{{ section.id }}.scroll {
    transform: translateY(-150%);
    transition: all 0.25s ease 0s;
  }

  .header-{{ section.id }}.scroll.active {
    transform: translateY(0);
    transition: all 0.25s ease 0s;
  }

  .header-announcement-{{ section.id }} {
    display: none;
    margin: 0 auto;
    max-width: 100%;
    width: 100%;
    background-color: {{ announcement_bg_color }};
  }

  .header-announcement-list-{{ section.id }} {
    margin: 0px;
    padding: 0px;
    list-style: none;
    display: flex;
    align-items: center;
    justify-content: end;
  }

  .header-announcement-item-{{ section.id }} {
    padding: 5px 10px;
  }

  .header-announcement-item-{{ section.id }}:not(:first-child) {
    border-left: {{ announcement_link_divider }}px solid {{ announcement_link_divider_color }};
  }

  .header-announcement-link-{{ section.id }} {
    margin: 0;
    font-size: {{ announcement_link_size_mobile }}px;
    color: {{ announcement_link_color_mobile }};
    line-height: {{ announcement_link_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease 0s;
    position: relative;
    z-index: 3;
  }

  .header-announcement-link-{{ section.id }}:hover {
    color: {{ announcement_link_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .header-body-{{ section.id }} {
    display: grid;
    align-items: center;
    gap: {{ layout_gap_mobile }}px;
    grid-template-columns: 1fr {{ logo_width_mobile }}px 1fr;
  }

  .header-menu-{{ section.id }} {
    order: 1;
    display: flex;
    align-items: center;
    gap: 10px;
  }

  .header-logo-{{ section.id }} {
    order: 2;
    text-align: center;
  }

  .header-menu-list-{{ section.id }} {
    padding: 0px;
    margin: 0px;
    display: none;
    align-items: center;
    gap: {{ list_gap_mobile }}px;
    flex-wrap: wrap;
    list-style: none;
  }

  .header-menu-mobile-{{ section.id }} .header-menu-list-{{ section.id }} {
    display: flex;
    flex-direction: column;
    align-items: start;
  }

  .header-menu-item-{{ section.id }} {
    line-height: 100%;
    cursor: pointer;
    box-sizing: border-box;
    padding-bottom: 3px;
  }

  .header-menu-link-{{ section.id }} {
    margin: 0;
    font-size: {{ link_size_mobile }}px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease 0s;
    text-wrap: nowrap;
    position: relative;
    z-index: 3;
  }

  .header-menu-item-main-{{ section.id }} > .header-menu-link-{{ section.id }}:after,
  .header-menu-mobile-toggle-{{ section.id }}:after{
    content: '';
    display: block;
    position: absolute;
    bottom: -2px;
    height: 2px;
    left: 50%;
    transform: translateX(-50%);
    width: 0;
    transition: all 0.25s ease 0s;
  }

  .header-menu-item-main-{{ section.id }} > .header-menu-link-{{ section.id }}:after {
    background-color: {{ link_hover_color }};
  }

  .header-menu-mobile-toggle-{{ section.id }}:after{
    background-color: {{ link_color }};
  }

  .header-submenu-{{ section.id }} .header-menu-link-{{ section.id }} {
    color: {{ submenu_link_color }} !important;
    font-weight: 400;
  }

  .header-menu-item-parent-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 10px;
    max-width: fit-content;
    flex-basis: 0;
  }

  .header-menu-item-icon-{{ section.id }} {
    display: block;
    flex: 0 0 8px;
    width: 8px;
    transition: all 0.25s ease 0s;
    color: {{ link_color }};
  }

  .header-menu-item-icon-{{ section.id }} svg path {
    stroke: {{ link_color }}
  }

  .header-{{ section.id }}:not(.onscroll).transparent.open .header-menu-item-icon-{{ section.id }} svg path {
    stroke: {{ link_color }};
    transition: all 0.25s ease 0s;
  }

  .header-menu-item-icon-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .header-submenu-{{ section.id }} {
    position: absolute;
    z-index: 3;
    top: calc(100% - {{ padding_bottom | times: 2 }}px);
    left: 0;
    width: 100%;
    transform: translateY(30px);
    opacity: 0;
    pointer-events: none;
    transition: all 0.25s ease 0s;
    list-style: none;
    margin: 0px;
  }

  .header-submenu-{{ section.id }}::after {
    content: "";
    position: absolute;
    left: 0;
    top: calc({{ padding_bottom | times: 2 }}px - {{ border_thickness }}px);
    height: {{ border_thickness }}px;
    background-color: {{ border_color }};
    width: 100%;
  }

  .header-submenu-bg-{{ section.id }} {
    display: block !important;
    height: {{ padding_bottom }}px;
    pointer-events: none;
  }

  .header-submenu-inner-{{ section.id }} {
    background-color: {{ background_color }};
    background-image: {{ background_gradient }};
    border-bottom: {{ border_thickness }}px solid {{ border_color }};
  }

  .header-submenu-{{ section.id }} .section-{{ section.id }}-settings {
    display: flex;
    justify-content: space-between;
    gap: {{ submenu_gap }}px;
    padding-bottom: {{ submenu_padding_bottom }}px;
    padding-right: 0;
  }

  .header-menu-item-parent-{{ section.id }}.active .header-submenu-{{ section.id }} {
    transform: translateY(0px);
    opacity: 1;
    pointer-events: all;
    transition: all 0.25s ease 0s;
  }

  .header-menu-burger-{{ section.id }} {
    display: block;
    width: {{ menu_icon_size_mobile }}px;
    height: 16px;
    position: relative;
    z-index: 3;
    transition: all 0.25s ease 0s;
    cursor: pointer;
    background-color: transparent;
    border: 0px;
    box-shadow: none;
  }

  .header-menu-burger-icon-{{ section.id }} {
    position: absolute;
    top: 50%;
    left: 0;
    margin-top: -1px;
    width: 100%;
    height: 1px;
    background: {{ link_color }};
    transition: transform .4s ease; 
  }


  .header-{{ section.id }}.open .header-menu-burger-icon-{{ section.id }} {
    transform: rotate(45deg);
    transition-delay: .22s;
    background: {{ link_color }} !important;
  }

  .header-menu-burger-icon-{{ section.id }}::before,
  .header-menu-burger-icon-{{ section.id }}::after{
    content: "";
    display: block;
    position: absolute;
    width: 100%;
    height: 1px;
    background-color: inherit;
    background: {{ link_color }};
  }

  .header-menu-burger-icon-{{ section.id }}::before {
    top: -6px;
    transition: top .25s ease .27s, opacity .25s ease, width .25s ease;
  }

  .header-{{ section.id }}.open .header-menu-burger-icon-{{ section.id }}::before {
    top: 0;
    opacity: 0;
    width: 100%;
    transition: top .2s ease, opacity .2s ease .22s, width .1s ease;
    background: {{ link_color }};
  }

  .header-menu-burger-icon-{{ section.id }}::after {
    bottom: -6px;
    transition: bottom .25s ease .27s, transform .25s ease, width .25s ease;
  }

  .header-{{ section.id }}.open .header-menu-burger-icon-{{ section.id }}::after {
    bottom: 0;
    transform: rotate(-90deg);
    width: 100%;
    transition: bottom .2s ease, transform .2s ease .22s, width .1s ease;
    background: {{ link_color }} !important;
  }

  .header-logo-{{ section.id }} {
    width: 100%;
    text-align: center;
    position: relative;
    z-index: 3;
    transition: all 0.25s ease 0s;
  }

  .header-logo-image-{{ section.id }} {
    max-width: {{ logo_width_mobile }}px;
  }

  .header-logo-image-{{ section.id }} img,
  .header-logo-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .header-logo-image-desktop-{{ section.id }} {
    display: none;
  }

  .header-logo-text-{{ section.id }} {
    margin: 0;
    font-size: {{ name_size_mobile }}px;
    color: {{ name_color }};
    line-height: {{ name_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease 0s;
  }

  .header-{{ section.id }} .header-logo-first-{{ section.id }} {
    display: block !important;
  }

  .header-{{ section.id }}:not(.onscroll).transparent .header-logo-first-{{ section.id }} {
    display: none !important;
  }

  .header-right-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: end;
    gap: {{ right_gap_mobile }}px;
    flex-wrap: nowrap;
    position: relative;
    z-index: 3;
    order: 3;
    transition: all 0.25s ease 0s;
  }

  .header-search-text-{{ section.id }},
  .header-account-text-{{ section.id }},
  .header-cart-text-{{ section.id }}{
    display: none;
    margin: 0;
    color: {{ right_link_color }};
    line-height: {{ right_link_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s cubic-bezier(.76,0,.24,1);
  }

  .cart-count-bubble {
    position: initial !important;
  }

  .header-cart-{{ section.id }} .cart-count-bubble span {
    margin: 0;  
    font-size: {{ right_link_size }}px;
    color: {{ right_link_color }};
    line-height: {{ right_link_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all .25s cubic-bezier(.76,0,.24,1);
  }

  .header-search-icon-{{ section.id }},
  .header-account-icon-{{ section.id }},
  .header-cart-icon-{{ section.id }} {
    display: block;
    width: {{ icon_size_mobile }}px;
  }

  .header-search-icon-{{ section.id }} svg,
  .header-account-icon-{{ section.id }} svg,
  .header-cart-icon-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .header-search-icon-{{ section.id }} svg path,
  .header-account-icon-{{ section.id }} svg path {
    stroke: {{ right_link_color }};
    transition: all .25s cubic-bezier(.76,0,.24,1);
  }

  .header-cart-icon-{{ section.id }} svg path {
    stroke: {{ right_link_color }};
    transition: all .25s cubic-bezier(.76,0,.24,1);
  }

  .header-{{ section.id }}:not(.onscroll).transparent.open .header-search-icon-{{ section.id }} svg path,
  .header-{{ section.id }}:not(.onscroll).transparent.open .header-account-icon-{{ section.id }} svg path {
    stroke: {{ right_link_color }};
    transition: all .25s cubic-bezier(.76,0,.24,1);
  }

  .header-{{ section.id }}:not(.onscroll).transparent.open .header-cart-icon-{{ section.id }} svg path{
    stroke: {{ right_link_color }};
    transition: all .25s cubic-bezier(.76,0,.24,1);
  }

  .header-cart-{{ section.id }},
  .header-account-{{ section.id }} {
    text-decoration: none;
  }

  .header-search-{{ section.id }} {
    padding: 0px;
    margin: 0px;
    background-color: transparent;
    border: 0px;
    box-shadow: none;
    cursor: pointer;
    text-decoration: none;
  }

  .header-cart-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 5px;
    position: relative;
  }

  .header-cart-{{ section.id }} .cart-count-bubble {
    padding: 0px;
    background-color: transparent;
    border: 0px;
    margin: 0px;
    width: fit-content;
    height: fit-content;
  }

  .header-menu-mobile-{{ section.id }} {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100vh;
    overflow-y: auto;
    background-color: rgba(0,0,0,0.5);
    transform:translateX(-120%);
    transition: all 0.25s ease 0s;
    z-index: 9999;
  }

  .header-{{ section.id }}.open .header-menu-mobile-{{ section.id }} {
    transform:translateX(0%);
  }

  .header-menu-mobile-{{ section.id }} .header-menu-burger-{{ section.id }} {
    position: absolute;
    top: 20px;
    right: 20px;
    z-index: 9999;
  }

  .header-menu-mobile-inner-{{ section.id }} {
    background-color: {{ background_color }};
    max-width: 100%;
    height: 100%;
    padding: 0px 20px 100px 20px;
    position: relative;
    overflow-x: hidden;
    overflow-y: auto;
    display: flex;
    flex-direction: column;
    overflow
  }

  .header-menu-mobile-heading-{{ section.id }} {
    margin: 0px;
    font-size: {{ mobile_menu_heading_size }}px;
    color: {{ mobile_menu_heading_color }};
    line-height: {{ mobile_menu_heading_height }}%;
    text-transform: unset;
    text-decoration: none;
    font-weight: 700;
    padding: 0px 20px;
  }

  .header-mobile-menu-item-parent-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 10px;
  }

  .header-menu-mobile-{{ section.id }} .header-menu-item-{{ section.id }} {
    max-width: 100%;
    width: 100%;
    justify-content: space-between;
    position: static;
    padding: 20px 0px;
  }

  .header-menu-mobile-tab-{{ section.id }} .header-menu-list-{{ section.id }} .header-menu-item-{{ section.id }} {
      border-bottom: 1px solid {{ mobile_menu_divider_color }} !important;
  }

  .header-menu-mobile-{{ section.id }} .header-menu-item-{{ section.id }}:not(:last-child) {
    border-bottom: 1px solid {{ mobile_menu_divider_color }};
  }

  .header-menu-mobile-{{ section.id }} .header-menu-item-icon-{{ section.id }} {
    flex: 0 0 18px;
    width: 18px;
  }

  .header-menu-mobile-submenu-{{ section.id }} {
    position: absolute;
    background-color: {{ background_color }};
    z-index: 4;
    padding: 20px;
    top: 0;
    left: 0;
    height: 100%;
    width: 100%;
    overflow-x: hidden;
    overflow-y: auto;
    transform: translateX(110%);
    transition: all 0.25s ease 0s;
  }

  .header-mobile-menu-item-parent-{{ section.id }}.active > .header-menu-mobile-submenu-{{ section.id }} {
    transform: translateX(0%);
    transition: all 0.25s ease 0s;
  }

  .header-menu-mobile-submenu-heading-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 20px;
    width: 100%;
    background-color: transparent;
    border: 0px;
    border-top: 1px solid {{ mobile_menu_divider_color }};
    padding: 0px;
    padding: 10px 0px;
    box-shadow: none;
    font-size: {{ link_size_mobile }}px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
  }

  .header-menu-mobile-submenu-heading-{{ section.id }} .header-menu-item-icon-{{ section.id }} {
    transform: rotate(180deg);
  }

  .header-mobile-submenu-list-{{ section.id }} {
    margin: 0px;
    margin-top: {{ list_gap_mobile }}px;
    display: flex;
    flex-direction: column;
    gap: {{ list_gap_mobile }}px;
    padding: 0px;
    list-style: none;
  }

  .header-logo-desktop-{{ section.id }} {
    display: none;
  }

  .header-submenu-collections-{{ section.id }} {
    padding-top: 20px;
    display: flex;
    gap: {{ collection_gap_mobile }}px;
    overflow-x: auto;
    scrollbar-width: none;
    margin-left: -20px;
    padding-left: 20px;
    margin-right: -20px;
    padding-right: 20px;
  }

  .header-submenu-collections-{{ section.id }}::-webkit-scrollbar {
    display: none;
  }

  .header-submenu-collection-heading-{{ section.id }} {
    margin: 0px;
    font-size: {{ collection_heading_size }}px;
    color: {{ collection_heading_color }};
    line-height: {{ collection_heading_height }}%;
    text-transform: unset;
    text-decoration: none;
    font-weight: 700;
  }

  .header-submenu-collection-{{ section.id }} {
    display: block;
    position: relative;
    overflow: hidden;
    text-decoration: none;
    width: 100%;
    flex-shrink: 0;
  }

  .header-submenu-collection-image-{{ section.id }} {
    border: {{ collection_image_border_thickness }}px solid {{ collection_image_border_color }};
    border-radius: {{ collection_image_radius }}px;
    overflow: hidden;
    position: relative;
    z-index: 1;
  }

  .header-submenu-collection-image-{{ section.id }}:after {
    position: absolute;
    content: "";
    bottom: 0;
    left: 0;
    right: 0;
    width: 100%;
    height: 24rem;
    max-height: 70%;
    opacity: .8;
    background: linear-gradient(180deg, transparent 0, #000000 100%);
  }

  .header-submenu-collection-image-{{ section.id }} img {  
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: all 0.25s ease 0s;
  }

  .header-submenu-collection-title-{{ section.id }} {
    position: absolute;
    z-index: 2;
    bottom: 16px;
    left: 20px;
    transform: translateX(-20px);
    margin: 0px;
    width: 100%;
    text-align: {{ collection_title_align }};
    font-size: {{ collection_title_size }}px;
    color: {{ collection_title_color }};
    line-height: {{ collection_title_height }}%;
    text-decoration: none;
    font-weight: 700;
    padding: 0 16px;
  }

  .header-menu-bottom-content-{{ section.id }} {
    margin-left: -20px;
    margin-right: -20px;
    padding: 20px;
    background-color: {{ announcement_bg_color_mobile }};
  }

  .header-menu-bottom-list-{{ section.id }} {
    margin: 0px;
    list-style: none;
    padding: 0px;
    display: flex;
    flex-direction: column;
    gap: 5px;
  }

  .header-search-desktop-{{ section.id }} {
    display: none !important;
  }

  .header-menu-mobile-toggles-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 30px;
    padding: 0px 20px;
    height: 100%;
    position: relative;
    overflow-x: scroll;
    overflow-y: hidden;
    scrollbar-width: none;
    /* padding-bottom: 20px; */
    box-shadow: 0px 14px 15px 0 rgba(0, 0, 0, 0.04);
    background-color: {{ background_color }};
  }

  .header-menu-mobile-toggles-{{ section.id }}::-webkit-scrollbar {
    display: none;
  }

  .header-menu-mobile-toggles-{{ section.id }}:after {
    content: "";
    height: 5rem;
    width: 100%;
    background: linear-gradient(0deg, transparent 65.58%, rgba(0, 0, 0, .66) 132.82%);
    position: absolute;
    top: 100%;
    opacity: .1;
  }

  .header-menu-mobile-toggle-{{ section.id }} {
    padding: 6px 0px;
    background-color: transparent;
    font-family: Montserrat, sans-serif;
    border: 0px;
    font-weight: 700;
    text-transform: uppercase;
  }

  .header-menu-mobile-toggle-{{ section.id }}.active:after {
    width: 100%;
    transition: all 0.25s ease 0s;
  }

  .header-menu-sublinks-{{ section.id }} {
    padding: 0;
    padding-top: 10px;
    display: flex;
    flex-direction: column;
    list-style: none;
    gap: {{ list_gap_mobile }}px;
  }

  .header-menu-link-wrapper-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: space-between;
    width: 100%;
  }

  .header-menu-link-wrapper-{{ section.id }} > .header-menu-link-{{ section.id }} {
    text-transform: uppercase;
    font-weight: 700;
  }

  .header-menu-link-open-{{ section.id }} {
    width: 10px;
    flex-shrink: 0;
    transition: all 0.25s ease 0s;
  }

  .header-menu-link-open-{{ section.id }}.active {
    transform: rotate(180deg);
    transition: all 0.25s ease 0s;
  }

  .header-menu-link-open-{{ section.id }} path {
    fill: {{ link_color }};
  }

  .header-menu-mobile-tab-{{ section.id }} {
    display: none;
  }

  .header-menu-mobile-tab-{{ section.id }}.active {
    display: block;
  }

  .header-menu-mobile-inner-{{ section.id }} > .header-menu-list-{{ section.id }} > .header-menu-item-{{ section.id }} > .header-menu-link-{{ section.id }} {
    font-weight: 700;
    text-transform: uppercase;
  }

  .header-menu-sublinks-{{ section.id }} .header-menu-item-{{ section.id }}:last-child {
    padding-bottom: 0px;
    border-bottom: 0px !important;
  }

  .header-menu-mobile-top-{{ section.id }} {
    position: sticky;
    top: 0px;
    z-index: 999;
    background-color: {{ background_color }};
    padding-top: 50px;
    padding-bottom: 20px;
    margin-left: -20px;
    margin-right: -20px;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .header-{{ section.id }}.transparent {
      width: calc(100% - {{ margin_horizontal | times: 2 }}rem);
    }

    .header-{{ section.id }}.fixed {
      width: calc(100% - {{ margin_horizontal | times: 2 }}rem);
    }

    .header-announcement-{{ section.id }} {
      display: block;
    }

    .header-announcement-{{ section.id }} .section-{{ section.id }}-settings {
      padding-top: 0px;
      padding-bottom: 0px;
    }

    .header-announcement-link-{{ section.id }} {
      font-size: {{ announcement_link_size }}px;
      color: {{ announcement_link_color }};
    }

    .header-body-{{ section.id }} {
      display: grid;
      gap: {{ layout_gap }}px;
      grid-template-columns: {{ logo_width }}px 1fr auto;
    }

    .header-menu-{{ section.id }} {
      order: 2;
    }

    .header-logo-{{ section.id }} {
      order: 1;
      text-align: left;
    }

    .header-menu-burger-{{ section.id }} {
      display: none;
    }

    .header-menu-list-{{ section.id }} {
      display: flex;
      gap: {{ list_gap }}px;
      justify-content: center;
      width: 100%;
    }

    .header-submenu-{{ section.id }} .header-menu-list-{{ section.id }} {
      padding-top: 20px;
      align-items: start;
      gap: {{ submenu_gap }}px;
    }

    .header-menu-item-with-submenu-{{ section.id }} .header-menu-list-{{ section.id }} {
      flex-direction: column;
      gap: {{ submenu_list_gap }}px;
    }

    .header-menu-link-{{ section.id }} {
      font-size: {{ link_size }}px;
      transition: all 0.25s ease;
    }

    .header-submenu-menu-{{ section.id }} {
      overflow-y: auto;
      scrollbar-width: none;
      flex: 1 1 0;
    }

    .header-submenu-menu-{{ section.id }} .header-menu-list-{{ section.id }} {
      justify-content: {{ list_horizontal_align }};
    }

    .header-submenu-menu-{{ section.id }} > .header-menu-list-{{ section.id }} > .header-menu-item-{{ section.id }} > .header-menu-link-{{ section.id }},
    .header-menu-{{ section.id }} > .header-menu-list-{{ section.id }} > .header-menu-item-{{ section.id }} > .header-menu-link-{{ section.id }} {
      font-weight: 700;
    }

    .header-menu-item-main-{{ section.id }}:hover > .header-menu-link-{{ section.id }}:after {
      width: 100%;
      transition: all 0.25s ease 0s;
    }

    .header-menu-item-{{ section.id }}:hover > .header-menu-link-{{ section.id }} {
      transition: all 0.25s ease;
      color: {{ link_hover_color }};
    }

    .header-submenu-{{ section.id }} .header-menu-item-parent-{{ section.id }} {
      max-width: 100%;
    }

    .header-submenu-{{ section.id }} .header-menu-item-{{ section.id }}::after {
      display: none;
    }

    .header-menu-item-parent-{{ section.id }}.active > .header-menu-item-icon-{{ section.id }} {
      transform: rotate(180deg);
      transition: all 0.25s ease 0s;
    }

    .header-logo-image-{{ section.id }} {
      max-width: {{ logo_width }}px;
    }

    .header-logo-image-desktop-{{ section.id }} {
      display: block;
    }

    .header-logo-image-mobile-{{ section.id }} {
      display: none;
    }

    .header-logo-text-{{ section.id }} {
      font-size: {{ name_size }}px;
      transition: all 0.25s ease;
    }    

    .header-submenu-{{ section.id }} .header-menu-item-{{ section.id }}:hover > .header-menu-link-{{ section.id }} {
      color: {{ submenu_link_hover_color }} !important;
    }

    .header-search-icon-{{ section.id }}:hover svg path,
    .header-account-icon-{{ section.id }}:hover svg path {
      stroke: {{ right_link_hover_color }};
      transition: all .25s cubic-bezier(.76,0,.24,1);
    }

    .header-cart-icon-{{ section.id }}:hover svg path {
      stroke: {{ right_link_hover_color }};
    }

    .header-logo-text-{{ section.id }}:hover {
      transition: all 0.25s ease;
      color: {{ name_hover_color }};
    }

    .header-right-{{ section.id }} {
      gap: {{ right_gap }}px;
      order: 3;
    }

    .header-search-text-{{ section.id }},
    .header-account-text-{{ section.id }},
    .header-cart-text-{{ section.id }} {
      display: inline;
      font-size: {{ right_link_size }}px;
    }

    .header-search-text-{{ section.id }}:hover,
    .header-account-text-{{ section.id }}:hover,
    .header-cart-text-{{ section.id }}:hover {
      transition: all .7s cubic-bezier(.76,0,.24,1);
      color: {{ right_link_hover_color }}!important;
    }

    .header-search-icon-{{ section.id }},
    .header-account-icon-{{ section.id }},
    .header-cart-icon-{{ section.id }} {
      display: none;
      width: {{ icon_size }}px;
    }

    .header-search-icon-{{ section.id }}:hover svg path,
    .header-account-icon-{{ section.id }}:hover svg path {
      stroke: {{ right_link_hover_color }};
      transition: all .7s cubic-bezier(.76,0,.24,1);
    }

    .header-cart-icon-{{ section.id }}:hover svg path {
      stroke: {{ right_link_hover_color }};
    }

    .header-cart-{{ section.id }} .cart-count-bubble span {
      font-size: {{ right_link_size }}px;
      position: relative;
    }

    .header-cart-{{ section.id }} .cart-count-bubble {
      position: static;
      transform: translateX(0);
    }

    .header-cart-{{ section.id }}:hover .cart-count-bubble span {
      transition: all .7s cubic-bezier(.76,0,.24,1);
      color: {{ right_link_hover_color }};
    }

    .header-menu-mobile-{{ section.id }} {
      display: none;
    }

    .header-logo-desktop-{{ section.id }} {
      display: block;
    }

    .header-logo-mobile-{{ section.id }} {
      display: none;
    }

    .header-right-button-{{ section.id }} {
      display: flex;
    }

    .header-search-desktop-{{ section.id }} {
      display: flex !important;
    }

    .header-search-mobile-{{ section.id }} {
      display: none !important;
    }

    .header-submenu-collections-{{ section.id }} {
      margin: 0px;
      max-height: 80vh;
      height: 100%;
      flex-direction: column;
      max-width: 350px;
      padding: {{ collection_padding_vertical }}px {{ collection_padding_horizontal }}px;
      background-color: {{ collection_bg_color }};
      gap: {{ collection_gap }}px;
      overflow: hidden;
      overflow-y: scroll;
    }

  }

